INSERT INTO `pre_news` (`news_id`, `news_title`, `news_pic`, `news_content`, `news_time`, `news_like`, `news_cate_id`) VALUES
(1, '美国西南航空又出事:客机窗户破裂迫降 机上人员平安', 'uploads/bKw8-fytnfyp707105348547.jpg', '<p class="art_p"><span style="font-family: KaiTi_GB2312, KaiTi;">西南航空又出事：客机窗户破裂迫降 机上人员平安</span></p><p class="art_p">中国新闻网</p><p class="art_p">中新网5月3日电 据外媒报道，美国西南航空公司一架客机2日在飞行途中因舷窗突然破裂，被迫改降俄亥俄州克利夫兰机场，机上人员平安。</p><p class="art_p">两周前，西南航空一架波音737客机才因引擎爆炸，碎片击破窗户而迫降，该事件造成一人丧生。</p><p class="art_p">报道称，2日发生事故的西南航空公司957航班事发时载有76名乘客，原本是要从芝加哥飞往新泽西州纽瓦克机场；目前还不清楚导致客机窗户破裂的原因。</p><p class="art_p">据一名乘客阿吉纳在推特上说，客机的一个舷窗在途中突然破裂，“因为只是（舷窗）外层破碎，所以我们全都平安”。</p><p class="art_p">这架波音737客机随即改降克利夫兰霍普金斯国际机场。西南航空公司在声明中说，客机平安降落，目前已停飞并接受维修检查，包括“一个多层玻璃舷窗的维修”。</p><p class="art_p">美国联邦航空局已就事故原因展开调查。</p><p class="art_p">上月17日，西南航空公司一架从纽约市飞往得克萨斯州达拉斯市的波音737客机因引擎爆炸、机身损坏等原因在宾夕法尼亚州费城国际机场迫降，事故导致一名乘客死亡。</p>', 1525312020, 0, 1),
(2, '特朗普提议美军增设“太空部队” 招致五角大楼质疑', 'uploads/w640slw20503.jpg', '<p class="art_p">参考消息网5月3日报道 美国有线电视新闻网网站5月1日发表题为《特朗普提议为美军增设“太空部队”》的报道称，特朗普总统1日再次表示，他希望为美军创建一个专门进行外太空作战的新分支。特朗普在白宫对西点军校橄榄球队队员发表讲话时提议为美军增设一个分支——“太空部队”。这一想法已在国会山得到一些支持，但招致五角大楼的质疑。</p><p class="art_p">特朗普在玫瑰园向这支陆军橄榄球队颁发“总司令杯”时说：“你们今后会成为美国武装部队令人骄傲的五个分支中的一员，这五个分支是陆军、海军、海军陆战队、空军和海岸警卫队。其实，我们当前在考虑增设一个分支，那就是太空部队。”</p><p class="art_p">他说：“我们目前在太空非常强大，不论从军事上讲还是出于其他原因。我们当前在慎重考虑增设太空部队一事。”</p><p class="art_p">特朗普3月在加利福尼亚州对军属发表讲话时也曾表示支持创建“太空部队”。特朗普当时说：“太空是一个作战领域，与陆海空一样。我们或许会有一支太空部队。”</p>', 1525311568, 0, 1),
(3, '外媒称多米尼加企业界对中国充满热情：迎来巨大商机', 'uploads/YWUq-fzyqqiq179184685396.jpg', '<p class="art_p">外媒称，多米尼加共和国主要的企业家协会5月1日纷纷表态称，希望与中国刚刚建立的外交关系能够给本国带来更多贸易、投资和旅游机会。</p><p class="art_p">据多米尼加《今日报》5月1日报道，“这样的关系非常积极。”多米尼加企业家协会主席坎波斯·德莫亚对媒体表示。他认为，多米尼加可以凭借其海港优势逐渐成为中国制造业进入该地区的中转站。</p><p class="art_p">报道称，多米尼加政府4月30日晚间宣布与中国建立外交关系，同时与台湾当局“断交”。</p><p class="art_p">与此同时，中国国务委员兼外交部长王毅与多米尼加外长巴尔加斯在北京签署联合公报，宣布双方相互承认并建立大使级外交关系。</p><p class="art_p">联合公报称，多米尼加共和国政府承认世界上只有一个中国，中华人民共和国政府是代表全中国的唯一合法政府，台湾是中国领土不可分割的一部分。</p><p class="art_p">“这是一个重大的决定，打开了一个充满机遇的世界。”多米尼加私营企业委员会负责人佩德罗·布拉奇表示。自多米尼加在中国设立贸易发展办事处以来，尽管两国间没有建立外交关系，但双边贸易过去十年逐年增长。</p><p class="art_p">就在双方正式建交前不久，多米尼加总统梅迪纳在多米尼加保税区出席了中国金汤铝业有限公司开业剪彩仪式，该公司是涉足多米尼加工业领域的第一家中资制造企业，投资约5.3亿比索，直接和间接创造400个和逾千个就业岗位。</p><p class="art_p">多米尼加酒店协会主席乔尔·桑托斯相信，随着关系的开放，两国间很快就将开辟直航，中国游客也将涌入多米尼加天堂般的沿海旅游胜地，这对于多米尼加来说“是一个巨大机遇”。</p><p class="art_p">报道指出，多米尼加在外交上的这一转变是对台湾当局的一大打击，使台湾当局的所谓“友邦”只剩19个。</p>', 1525311365, 0, 1),
(4, '解放军陆续配发一批新式被装 制式布鞋亮相(图)', 'uploads/6SyB-fzyqqiq192169495588.png', '<p class="art_p">据《解放军报》5月3日报道，今年夏服发放工作陆续在全军展开。记者近日从军委后勤保障部获悉，17长袖体能训练服、编织内腰带、布鞋、轻便防寒鞋、单兵睡具等一批新式被装陆续配发全军官兵，既有日常穿用系列又有训练防护品种，这标志着我军被装日趋体系化、系列化、配套化，为练兵备战提供有力保障。</p><p class="art_p">17长袖体能训练服 图源：解放军记者部</p><p class="art_p">据了解，除正常换发品种外，今夏被装发放包括一批新品种。其中长袖体能训练服满足春秋季节户外体能训练需要，配合其他保暖内衣也可在冬季穿用，采用棉纤维和纯涤纶材料，穿着时皮肤触感舒适，具备吸湿快干性能。编织内腰带与作训服装配套穿着，按照人体工效学原理设计，适体性好，战时还可相互连接捆绑，具备束缚攀登、临时替代止血带等多种功能。布鞋采用飞织网布面料，一次性浇铸模压而成，更加轻质透气，是对我军鞋类系列的有益补充与完善。单兵睡具包括单兵帐篷，轻薄型、保暖型睡袋和充气、泡沫睡垫等品种，体积小、重量轻、防风保暖，便携性和舒适性更好，搭配使用可满足官兵在不同气候条件下野外宿营保暖需求。</p><p class="art_p">编织内腰带</p><p class="art_p">轻便防寒鞋</p><p class="art_p">轻便布鞋，由于条例中可以配夏季常服，小编觉得这是个神器</p><p class="art_p">单兵睡袋</p><p class="art_p">单兵帐篷</p><p class="art_p">身着长袖体能服训练的解放军</p>', 1525311060, 0, 1),
(5, '2017年小米手机平均售价881.3元 销量9141万部', NULL, '<p>　　新浪科技讯 5月3日早间消息，小米向港交所递交招股书，其中显示，小米手机2017年平均售价881.3元，销量9141万部，贡献总收入约805.64亿元。</p><p>　　招股书称，由于不同价格机型的销量及销售渠道组合等各种因素，过去几年智能手机的平均售价有所波动。2016年至2017年，智能手机分部收入增长65.2%，而2015年至2016年下降9.2%。</p>', 1525310280, 0, 1),
(6, '中国将承建莫斯科地铁站 俄媒：中国效率远近闻名', NULL, '<p class="art_p"><img  src="https://n.sinaimg.cn/translate/200/w600h400/20180503/75Ma-fzyqqiq1667175.jpg" alt=""></p><p class="art_p">在俄罗斯首都莫斯科，一位市民走出地铁站。资料图</p><p class="art_p">俄媒称，俄罗斯首都莫斯科副市长胡斯努林向记者表示，中国铁建股份有限公司（CRCC）将于5月启动莫斯科西南部三座地铁站修建工程。</p><p class="art_p">据俄罗斯卫星通讯社莫斯科4月28日报道，胡斯努林说，中国铁建5月将启动首条站点间隧道修建工程，6月将开建第二条。</p><p class="art_p">据此前报道，中国铁建将在莫斯科修建三座地铁站及站点间隧道。</p><p class="art_p">俄罗斯卫星通讯社2017年曾援引莫斯科市政府新闻处消息称，中国铁建股份有限公司将把4台直径6米的隧道掘进机运到莫斯科。除此以外，大约500名工人和工程师也将前往莫斯科从事地铁建设。</p><p class="art_p">另据《俄罗斯报》网站2017年1月25日报道，莫斯科城建局局长博奇卡廖夫宣布，中国铁建日前在莫斯科工程设计公司组织的竞标中胜出，并已签署合同，将承建地铁大环线南端的“阿米尼耶沃公路”、“米丘林大街”、“韦尔纳茨基大街”三座地铁站以及车站之间的两段隧道，工程起标价为250亿卢布（1卢布约合0.1元人民币——本网注），中国铁建以229.8亿卢布（约合22.98亿人民币）的价格中标。</p><p class="art_p">报道称，这是莫斯科乃至整个俄罗斯首次在地铁施工中引进外国企业，即便在苏联时代也未曾有过。众所周知，中国人的地铁建设效率远近闻名，他国无人能及。2016年，算上地面轻轨，莫斯科新增城铁及地铁里程78公里，但仍屈居中国之后。</p><p class="art_p">博奇卡廖夫表示，如今我们不只有机会目睹中国人的职业水准，还能与他们共事。他强调：“这首先意味着新技术、新经验的引入，从而提升我国地铁的施工水平，另一点也相当重要，即行业竞争环境业已形成。的确，欲与中国铁建这样的企业比肩绝非易事，它拥有30万员工，拥有自己的盾构机，而我们只能从国外进口。中国人很快就会将盾构技术带到莫斯科，春天他们便会开挖隧道。按合同规定，他们将在2017-2019年完成整个工程。”</p><p class="art_p">莫斯科的外环换乘地铁线全长62.3公里，共设有28站。它是莫斯科地铁的第二条环线，计划于2020年正式投入使用。</p><p class="art_p">（原题为《中国将承建莫斯科地铁站 俄媒：中国效率远近闻名》）</p>', 1525309740, 0, 1),
(7, '韩团体将劳工像放置在日本领馆附近 警方实施警戒', NULL, '<p class="art_p">[环球网报道 记者 王欢]关于韩国劳动团体试图在日本驻釜山总领事馆前设置“二战”劳工像一事，韩国警察5月2日包围了被放置在领馆附近人行道上的雕像，对韩方团体强行设置雕像的动向实施警戒。管辖该地段的釜山市东区的负责人表示，希望继续与韩国政府和团体方面协商如何处理劳工像。</p><p class="art_p">据日本共同社5月2日报道，韩国团体1日欲在总领事馆前象征慰安妇问题的少女像旁设置劳工像，在距其数十米的人行道上被警方阻止。团体干部强调就在原地固定。2日的韩国《釜山日报》报道称已经“设置”。</p><p class="art_p">不过，东区方面则以“妨碍通行”为由否定会固定下来。韩国政府出于保护外交公馆等的观点，认为设置在总领事馆前不妥当，建议设置到釜山市内的“国立日帝强制动员历史馆”。</p><p class="art_p">日本政府相关人士2日表示“认为胶着状态正在持续，将继续关注”。东区相关人士称“（团体方）还没有同意协商的意思”，事态也可能久拖不决。</p>', 1525308240, 0, 1),
(8, '青瓦台：韩朝首脑最早本周通电话 拟提朝美会谈', NULL, '<p class="art_p">[环球网综合报道]据韩联社3日报道，青瓦台当天表示，韩国总统文在寅和朝鲜国务委员会委员长金正恩最早本周通热线电话，但不是今天。</p><p class="art_p">韩国总统府青瓦台高官在接受韩联社电话采访时表示，韩朝领导人可能在本周通电话，但今天不会通话。</p><p class="art_p">被问及美国总统特朗普表示近期发布美朝首脑会谈日程，朝韩首脑通话是否会提及此事时，另一名青瓦台核心人士表示，如果韩朝领导人通过首脑热线沟通，朝美首脑会谈也将在讨论之列。该官员称，朝美首脑会谈日程确定以后，才能安排韩美首脑会谈的日程，如果“金特会”突然敲定，“文特会”可能想开却没法开。</p><p class="art_p">该青瓦台核心人士还介绍，此前由总统府幕僚长领导的韩朝首脑会谈筹备委员会今天改组为“韩朝首脑会谈协议执行委员会”，并召开第一次会议。执委会可能由国务总理或国务调整室室长领导。</p><p class="art_p">4月20日，韩朝首次开通首脑热线电话，文在寅可使用安装于青瓦台三楼办公桌上的座机与身在平壤的金正恩通电话。韩联社透露称，韩朝曾依据2000年首次首脑会谈的共识开通韩国情报机构和朝鲜统战部之间的热线电话，但据报道，时任首脑金大中和金正日并没有通过电话。该热线曾在偶发军事冲突等情况下发挥缓冲作用，不过李明博于2008年执政后断联至今年初，直到金与正作为金正恩的特使访韩时才恢复畅通。</p><p class="art_p">点击进入专题：<br/> 朝韩会谈专题|朝鲜5月5日起改用首尔时间 统一韩朝时区</p>', 1525308060, 0, 1),
(9, '40岁女演员何时迎春天', NULL, '<p>　　她们的新作让人尴尬</p><p>　　李小冉、蔡少芬的新剧正在热播，但是她们在剧中的角色，要么装嫩，要么成了小几岁的演员的妈妈，再次凸显一个现实：中年女演员的发挥空间越来越小。为此，有人自己做导演，有人愤然转战电视圈。只有以中年女性为主角的好戏多了，这一批人才会和同辈男演员一样，迎来真正的春天，否则只能给年轻人做配角，一味尬演。</p><p>　　最近，演员李小冉的出镜率很高。前不久的热播剧《美好生活》，观众原以为会是一部细腻的中年情感剧，但结果让人意外。她饰演一名刚失去丈夫的杂志社编辑，张嘉译换上了她丈夫的心脏，因为这种关系，他们上演了一段纠缠的爱情。</p><p>　　在这部戏里，李小冉还算是适龄出演，而到了正在热播的《下一站，别离》中，她是在减龄出演。戏里李小冉是单身未婚女性，年龄设定在30岁左右，这与她实际年龄相差10岁左右。因为遗传的原因，她提前进入更年期，不得不与于和伟协议结婚，两人在日常相处中，慢慢读懂对方、收获爱情。不少观众认为，该剧表面上看是都市剧，但走的是偶像剧的老套路，两位中年实力演员演起来很尴尬，“剧本没有逻辑，打光和化妆的风格过于偶像化，让人出戏”。</p><p>　　最近蔡少芬出演一个妈妈角色时也遭遇了尴尬，不过，与李小冉要“装嫩”的境况不同，她只能“往老了演”。前日收官的《海上嫁女记》中，她扮演的何念晴诠释了那一代保守与焦虑的女性，敢于踏出追爱的脚步，与沈方舟获得圆满爱情。奇怪的是，剧中她的女儿由张檬出演，两位演员只相差15岁，且在扮相上并没有母女的感觉，更像姐妹，蔡少芬不得不“往老了演”，这点引起观众吐槽。</p><p>　　这样的尴尬在国产剧中并不罕见。宋丹丹在《美好生活》中演过张嘉译的母亲，前者只比后者大9岁，就此，两人同框的画面充满违和感。戚薇在《北京女子图鉴》中前期扮过刚毕业的大学生，在观众看来，再好的化妆术也无法让她有青涩稚嫩的感觉，好在这个角色迅速成长，演员才避免了更多的抨击。</p><p>　　她们只能当绿叶？</p><p>　　在互联网谈话节目《圆桌派》里，这几年已经无戏可拍的蒋雯丽，面对目前的情形只好感慨“怀念自己的青春时光”。更多同龄女演员只好在一部戏里给自带流量的年轻演员当绿叶。回归家庭、生下两个孩子之前，梅婷的最新作品是《父母爱情》，她凭借戏里的角色拿下多个表演奖项，奠定实力派的地位，但是复出之后，她就只能饰演“皇后”这类角色，不仅是配角，角色还非常脸谱化，留给演员的发挥余地实在太小。</p><p>　　此外，陈数、秦海璐分别在《择天记》和《南方有乔木》里给流量演员做陪衬，观众评价一般。既然没有满意的剧本角色可演，创作上有想法的秦海璐，索性自己做导演，她执导的处女作《一意孤行》正在筹备中，将于年内开机。</p><p>　　不过，有的中年女演员则在主动为自己争取表演的空间，表现中年女性的状态。因为早前专心于家庭，姚晨错过了事业上的发展机会。她目前正在与正午阳光团队合作拍摄《都挺好》，和其组成“父女档”的是老戏骨倪大红。剧中，姚晨饰演的苏明玉是集干练、坚韧、忠诚于一身的事业女性，同时又有面对家庭问题时的凌厉与纠结。演员有实力，如果剧本优秀，该剧必定不负期待。《我的前半生》中，马伊琍作为中年失婚女人，袁泉作为自由独立的女强人，都演出了现代女性的困境和挣扎，只是故事掺杂了狗血元素，导致纯粹性打了折扣。</p><p>　　殷桃也一度没戏可拍，去年凭借《鸡毛飞上天》获得好口碑，她已经是现实主义题材女主角的热门人选。她和王雷、李乃文合作的《爱情的边疆》即将播出，编剧是高满堂，导演是《平凡的世界》导演毛卫宁，主创阵容强大，就看故事能否打动人心了。她和黄晓明合作的军旅剧《你迟到的许多年》改编自严歌苓的小说，因而也备受关注。</p><p>　　当然，由中年女演员挑大梁的现实主义题材，要想好看，就必须在贴近社会真实和注意尺度之间寻找平衡。</p><p>　　娱评</p><p>　　鼓励多样化探索</p><p>　　曾俊</p><p>　　和老戏骨一样，不少中年女演员都成了年轻演员的黄金搭档，是当前行业中的标准配置。一部戏光有年轻演员是撑不起来的，而有那么一批人爱看上了年纪的演员，所以中年女演员就慢慢在演前任、妈妈、婆婆，久而久之，对这些女演员来说，有些无奈，但毕竟还有戏可演，会有开心的时候。</p><p>　　这肯定不是长久之计，文艺作品需要更丰富多样。按道理，中年女性的爱情、事业和生活都很有嚼头，更能让人体会到人生的况味。但只有有了深入、深刻刻画中年女性的剧本，中年女演员才会感觉到大有作为，才能和同辈男演员一样，不用担心自己就此沉寂。</p><p>　　目前电视台扛收视的压力很大，剧集采购者会顺从传统，不太敢轻易冒险，但是，网络在这些戏探索上的自由度更大。那就期望不同的影视制作公司更加努力，多一些花样，向其他国家的优秀同行学习，认真花时间创作出合乎每个年龄段的好戏。</p><p>　　转战电视圈的她们前景如何？</p><p>　　很难想象，名气和演技皆过硬的宋丹丹尚且遇到“无戏可演”的尴尬，何况别人？节目里，她坦言，在35岁到48岁这个自身经验、阅历最好的时间段里，却没有合乎年龄的角色找她出演。因此，她不停鼓励中年演员要在寂寞中坚持。事实上，李小冉、马伊琍等演员靠保养得宜模糊了年龄界限，可以接比自己年龄小很多的角色，其他中年女演员就只能“扮老”，慢慢走上出演妈妈、婆婆之类角色的道路。</p><p>　　在观众看来，这些描绘中年女性生活、事业和家庭的戏里，剧本是最大问题：职场戏部分像过家家，编剧缺乏相应的行业积累，都市戏又极为啰嗦琐碎，不符合逻辑的桥段横行，各种俗套情节让人失望。</p><p>　　国产剧没有给中年女演员很多发挥空间，国产片也是如此。只不过，电影演员到了小荧屏里，就在创作中获得了很强的话语权，担任热门网络小说的大女主。从2014年开始，周迅多次在电影里“减龄”出演，但这张脸也在慢慢失去说服力，作品评分也在走低。</p><p>　　走入人生后半程的章子怡、汤唯都遭遇了同样的困惑，选择了同样的大女主路线，但由于多种限制和观众口味变化，其后续效果有待观察。</p><p>　　中年女性的戏口碑好收视不行？</p><p>　　有人认为，中年女演员之所以机会不多，是因为观众不爱看中年女性的戏。他们更容易接受高颜值的年轻女演员，所以，这导致当今的荧屏被杨幂、赵丽颖、Angelababy等年轻一辈女演员占据。</p><p>　　可是，仍有许多观众对中年女性题材非常有兴趣。《经常请吃饭的漂亮姐姐》最近成了刷屏的爆款，它再次激发了观众的少女心：不论自己处在哪个年龄段，谈一场浪漫恋爱的心始终都在。况且这部剧还足够真实、细致。高口碑美剧《傲骨之战》第二季正在热播，它主要涉及老中青三个年龄段的女性，人物的饱满丰富、戏剧的强大张力让观众热血沸腾。</p><p>　　一个残酷的事实是，在网络上支持这些高分剧的观众，平时爱看美剧、英剧，对戏剧品质有高要求，却不是撑起国产剧收视的群体。这两部分人群似乎是割裂的，这也能解释为什么一些国产剧在网络上被吐槽厉害，但收视很高；诸如《和平饭店》这样的剧，口碑很好，但收视不行。</p><p>　　国产影视剧很少出现关于中年女性的好题材，除了观众原因，也与上游的制作喜好有关。因为从投资的角度看，相比年轻演员，中年女演员的确难以占据市场，这意味着投资有风险。在业内，一部电视剧在只有一个创意时，就要拉投资、签演员、初步确定播出平台，这时候播出平台只能依据故事类型和演员阵容来判断播出效果，因此流量明星就有了天然优势。这也是这么多年，流量明星备受争议，但依然被争抢的主要原因。</p><p>　　更多精彩内容敬请关注@新浪女性（微博）</p>', 1525307700, 0, 1),
(10, '你的意象是痛的解药', NULL, '<p>　　文章来源微信公众号：心理有你</p><p>　　雅琪躺在床上，看着天花板，成功的失眠了。她的头脑中，电影片段般反复播放着一个场景。</p><p>　　浑身湿透的自己坐在书桌前，水滴滴答答地落在地板上，脸上的水珠静静地向下滑，却没有去擦。窗帘拉得严严实实，只微微透出一点光。雅琪就这么呆呆地坐着，悲伤却没有哭泣，默默地。</p><p>　　雅琪是一位中学老师，她告诉我最近情绪很低落，做什么都提不起精神。问她为什么，她说工作上遇到阻碍，备受否定。父母又希望自己回去，不能理解为什么非要走这么远。没有人理解和支持的自己，就像被泼了一盆又一盆的冷水，站在那里，无人问询，好尴尬。</p><p>　　“那如果让你想象一下自己的处境，你会想到些什么？”我问道。“你走进一间屋子，你会看到些什么？”</p><p>　　她愣愣地想了一会儿，“屋子里很暗，潮潮的。”</p><p>　　“你在这间屋子里吗？”</p><p>　　“在。”</p><p>　　“那你在做什么呢？”</p><p>　　“我坐在书桌前，好像全身都湿透了，好难受。”这也就是文章开头的那一幕。</p><p>　　雅琪在脑海中所想到的画面，我们把它称作意象，是我们对并不实际存在的事物的心理描摹。意象是具有象征性的，它可以表达意义，而且这个意义并非形象直接的意义。通过这种想象，我们可以看到意识或潜意识中的心理活动。</p><p>　　房间里的雅琪，湿淋淋地、无力地坐在书桌前，但她却不去换衣服。没有人支持的雅琪是悲伤的、孤独的，换与不换，又怎样呢，心都是冷冷的。</p><p>　　这时候的她，好像缺了电的电器，能量down到最低值。</p><p>　　其实，内心的能量也是可以分类的，愤怒的、快乐的、也有悲伤的。这能量像一汪活水，可以不断地流动，能量之间会发生感应继而相互吸引。所以，当一个人心情愉悦的时候，会想起很多开心的事情；而感觉悲伤时，头脑中满满的都是痛彻心扉的记忆。我们常说触景伤情：感时花溅泪，恨别鸟惊心，就是此意。</p><p>　　还有一种相反的的吸引。花开正好时，感慨终归泥土；酒兴正酣时，遗憾总有一别。</p><p>　　这些都会转化成心理能量，流动在我们的内心，影响我们的情绪。</p><p>　　意象不仅仅反映一个人心理能量的状态，其本身就是心理能量的载体。当我们心中所存在的矛盾、冲突或长久以来存在的情结，以一个意象的形式出现时，矛盾或情结的能量就会附着在它的身上。在以后的情绪发展过程中，当意象发生转换时，能量又会转移到新的意象上。</p><p>　　从这个角度来说，如果我们能够调节和改变已经存在的意象，就可以调节这个意象上附着的能量。</p><p>　　有一位咨询师曾举过这样一个形象的例子：在做意向的对话活动时，我总是先让来访者做一个想象。抑郁的来访者所想象的环境，经常布满灰尘，灰尘象征着消沉的情绪。在对话时，我们可以让来访者想象擦洗掉这些灰尘。在他们这样做的时候，会发现一件很奇怪的事情，他们本以为想象是可以由自己控制的，但事实并非如此。布满灰尘的桌子在擦完之后，很快又脏了。于是，我会让他们反复地想象擦桌子。在多次的想象之后，桌子才会被擦干净。一旦在想象中灰尘被清除干净了，来访者会感到自己轻松许多。被擦掉的这些灰尘，其实也就代表着被一点点消除的抑郁情绪。</p><p>　　所以，我问雅琪“你把湿衣服换掉，把头发擦干，会不会好一些？”</p><p>　　“可是还是很冷啊。”</p><p>　　“那再添一件衣服呢？”</p><p>　　“把帘子打开吧，也许窗外就是明媚的阳光。”</p><p>　　“一定会很刺眼。”</p><p>　　意象可以唤起一个人相应的感受。从某种意义上来说，改变了意象，也就改变了人。当然这不是一个简单的颠覆的过程，而是变消极为积极，直至最后的正本清源。这需要反复的想象和强化。</p><p>　　我说：“如果是你今天没有带伞，被浇了个落汤鸡，你会怎么做呢？会不会赶快脱掉湿衣服，冲个热水澡？”</p><p>　　“也许会吧。”</p><p>　　“只是下了一场大雨，说不定这会儿就晴了呢，会有大太阳。要不要打开窗帘，看一看？”</p><p>　　“你只是淋了一场雨，下次带伞就好。给朋友家人打个电话，也许他们能帮你送来。”</p><p>　　“他们会来帮我？”</p><p>　　“你不说，他们当然不知道你需要帮助，你有告诉他们吗？”</p><p>　　“没有。”</p><p>　　“你的孤单也是你想象出来的意象。”</p><p>　　在意象的转化过程中，心里的能量也随之发生了改变，流动的情绪会成为一种力量，支撑起我们的行动。你心中的意象，或许是毒，也或许是药。</p><p>　　我想雅琪，也会慢慢好起来。淋过一场大雨，擦干头发，拉开窗帘，说不定就是满眼阳光。身后，还有朋友和家人。</p><p>　　我在说别人的故事，这故事里有你 有我。</p><p>　　更多精彩内容敬请关注@新浪女性（微博）</p>', 1525307640, 0, 1),
(11, '美国西南航空又出事:客机窗户破裂迫降 机上人员平安', 'uploads/bKw8-fytnfyp707105348547.jpg', '<p class="art_p"><span style="font-family: KaiTi_GB2312, KaiTi;">西南航空又出事：客机窗户破裂迫降 机上人员平安</span></p><p class="art_p">中国新闻网</p><p class="art_p">中新网5月3日电 据外媒报道，美国西南航空公司一架客机2日在飞行途中因舷窗突然破裂，被迫改降俄亥俄州克利夫兰机场，机上人员平安。</p><p class="art_p">两周前，西南航空一架波音737客机才因引擎爆炸，碎片击破窗户而迫降，该事件造成一人丧生。</p><p class="art_p">报道称，2日发生事故的西南航空公司957航班事发时载有76名乘客，原本是要从芝加哥飞往新泽西州纽瓦克机场；目前还不清楚导致客机窗户破裂的原因。</p><p class="art_p">据一名乘客阿吉纳在推特上说，客机的一个舷窗在途中突然破裂，“因为只是（舷窗）外层破碎，所以我们全都平安”。</p><p class="art_p">这架波音737客机随即改降克利夫兰霍普金斯国际机场。西南航空公司在声明中说，客机平安降落，目前已停飞并接受维修检查，包括“一个多层玻璃舷窗的维修”。</p><p class="art_p">美国联邦航空局已就事故原因展开调查。</p><p class="art_p">上月17日，西南航空公司一架从纽约市飞往得克萨斯州达拉斯市的波音737客机因引擎爆炸、机身损坏等原因在宾夕法尼亚州费城国际机场迫降，事故导致一名乘客死亡。</p>', 1525312020, 0, 1),
(12, '为什么掰手指会“咔咔”响？到底能不能掰？', NULL, '<p>　　生活中，很多人动不动就喜欢把手指掰得“咔咔”响，尤其是小学生和中学生。在别人的面前把手指头弄出“咔咔”的响声，觉得很酸爽，认为这既是一种技能，还能炫耀。</p><p>　　但是，你有没有想过掰手指为什么会发出“咔咔”的声音呢？让人酸爽的掰手指会带来哪些危害呢？一起了解一下。</p><p>　　<strong>掰手指为什么会响？&nbsp;</strong></p><p>　　关于掰手指引起的弹响，其实有很多理论，其中以<strong>气泡理论</strong>支持者最多。该理论认为，手指关节发出的声音<strong>与润滑和缓冲关节的滑液中出现的二氧化碳气泡有关。</strong></p><p>　　掰手指会发出响声的原因，主要分为以下两种：</p><p>　　<strong>手指的生理性弹响</strong></p><p>　　生理性弹响有其发生的机制。简单的来说，生理性弹响就是<strong>不会引起手指关节任何疼痛，任何不适的弹响。</strong></p><p>　　一般生理性弹响问题不大，所以不用太过担心。要做的只是观察就行了。</p><p>　　<strong>病理性弹响</strong></p><p>　　比如，手指的骨性关节炎，手指的腱鞘炎都<strong>会引起手指间关节的弹性</strong>，这种弹响是由于手指的病变引起。</p><p>　　手指骨关节炎，是手指软骨退变，骨质增生，导致手指间关节的摩擦阻力增大，从而导致弹响。腱鞘炎是由于手指屈肌腱被压迫导致的局部炎症，从而引起弹响。</p><p>　　病理性弹响，需要尽量让手指休息制动，避免弹响的发生。一般要治疗原发病，最好到医院看一下，结合医生的体格检查，然后进一步处理。</p><p>　　<strong>掰手指会导致关节炎吗？</strong><strong>&nbsp;</strong></p><p>　　两者之间有一定的关系，但是<strong>手指间关节适当的生理性弹响不会引起手指间关节炎，也不会引起手指间关节的其他毛病。</strong></p><p>　　但是，有些人喜欢特意把手指掰出一些弹响，认为这些动作非常酷。有些人甚至对弹响有瘾，每天就不停的让手指间关节弹响。一定要注意手指间关节弹响的适度适量。</p><p>　　<strong>掰手指还有哪些危害？&nbsp;</strong></p><p>　　从医学角度讲，弹响其实本身也是一种摩擦，适度的弹响对关节没有坏处，但弹响的发生率高了，摩擦增多了，<strong>慢慢会产生手指间关节的慢性炎症，从而也会慢慢导致手指间关节炎。</strong></p><p>　　即使是生理性弹响，也要掌握适度适量。因为一旦生理性弹响增多，或者人为的使生理性弹响大大的增多，<strong>最终容易导致病理性弹响。</strong></p><p>　　所以，掰手指要适度。</p>', 1525429200, 0, 1),
(13, '曝皇马计划派替补打巴萨', NULL, '<p>　　西班牙综合性日报《ABC报》周四报道，<strong>齐达内和他的教练组正考虑让替补球员出战国家德比，以避免在欧冠决赛前遭遇不必要的伤病。</strong>“最重要的是第13冠。”俱乐部内部表示。</p><p>　　在欧冠半决赛淘汰拜仁后，皇马教练组即开始准备欧冠决赛。在球队庆祝欧冠晋级的欢乐气氛中，齐达内召集更衣室的领袖们开了一个会。他问了大家一个问题：“我们周日对巴塞罗那怎么踢？大家都上，还是避免风险为重？对我们来说，最重要的还是基辅的决赛。”</p><p>　　齐达内是皇马更衣室内做决定的人，很难让他改变计划，比如他敢于将贝尔和本泽马放到替补席上，不过这一次皇马的情况有些不一样，齐达内希望能听取大家的意见，群策群力踢好欧冠决赛。</p><p>　　齐达内想要听听拉莫斯、C罗、马塞洛、克罗斯、莫德里奇、本泽马这些球员的意见。如今皇马队内伤病众多，莫德里奇、瓦拉内、阿森西奥都有小伤病，而伊斯科和卡瓦哈尔伤情则比较严重。</p><p>　　每场比赛都有几名球员受伤让齐达内感到担心。是让主力球员都在诺坎普上场呢？还是明智地将好钢用在刀刃上，让主力阵容都无伤病地参加欧冠决赛？</p><p>　　“我们非要让C罗、莫德里奇、拉莫斯、纳瓦斯、马塞洛冒着受伤的危险上场吗？”齐达内对教练组成员表示，“把风险降到最低，这不是更好吗？这个问题我们必须在内部解决。”</p><p>　　在与教练组商议后，齐达内问了球员们一个问题：“为诺坎普的比赛以及其他的西甲比赛而冒重伤的危险，究竟值不值得？”</p><p>　　拉莫斯和C罗都想参加重大的比赛，很难说服他们不进入比赛大名单或者首发。毕竟皇马对阵的是巴萨，这是皇马在国内最大的死敌。眼下，C罗、拉莫斯和齐达内还在继续考虑这个问题。除了这两位球员，齐达内可以自由安排其他球员进行轮换。</p><p>　　据悉，齐达内希望目前身体有伤病的球员，如莫德里奇、克罗斯、马塞洛、阿森西奥、瓦拉内、本泽马都在国家德比中获得休息，包括纳瓦斯也将休息。而伊斯科和卡瓦哈尔都在养伤，本身就不能上场。在这些球员中，有多位都将在欧冠决赛中首发。纳瓦斯可能要到欧冠决赛才上场，接下去一段时间，他会把位置让给卡西利亚。如果C罗和拉莫斯也都休息，那皇马等于派出全替补阵容出战。</p><p>　　齐达内将给纳乔、贝尔、塞瓦略斯、略伦特、特奥、卡塞米罗、科瓦契奇这些球员机会。卡塞米罗近期因为状态不佳，被齐达内排除在首发阵容外。如果C罗也休战，那贝尔将成为引领皇马进攻的旗帜。</p><p>　　不过皇马部分高层对齐达内的计划表示了一些担忧。他们知道欧冠对皇马来说是至关重要的，但也非常清楚，国家德比对皇马的形象以及自己的球迷来说也是非常重要的。如果比分不利，那对皇马备战欧冠也会造成打击。（塞尔吉奥）</p>', 1525427820, 0, 1),
(14, '乒联：韩朝组队是为了和平', NULL, '<p>　　5月3日，朝韩两队原本应该在世乒赛的1/4决赛中一决高下，但她们却没有进行任何比赛，而是突然宣布组成朝韩联队，在晋级4强的同时至少确保一枚铜牌到手。</p><p>　　这是朝韩自1991年以来再次联手组队参加世乒赛，那一次他们曾击败了邓亚萍领衔的中国队夺冠。</p><p>　　不过，这一次在比赛中的突然组队还是引发了各界舆论的激烈讨论。</p><p>　　国际乒联和朝韩两队在当天下午3点（北京时间晚上9点）共同召开了新闻发布会，对于这些外界关注的问题进行了说明和解释。</p><p>　　“在短短的24小时内，我们创造了历史，大家进行了坦诚布公的讨论和商议。”国际乒联主席托马斯透露，在做出这个决定后，他们同样得到了中国、日本等其他协会的同意。</p><p>　　<strong>“我们得到了其他协会的同意”</strong></p><p>　　在比赛中宣布放弃竞争，并与对手握手、拥抱、组队。。。。。。这一幕在竞技体育的历史上，足以载入史册。</p><p>　　虽然朝韩联手的消息从前一天晚上就开始不胫而走，但双方在比赛中突然做出的决定还是令在场的所有人感到错愕。</p><p>　　在记者不停地追问中，国际乒联发表了一份声明。文中强调，“朝韩联队”的组成是国际乒联、朝鲜乒乓球队和韩国乒乓球队三方决定。</p><p>　　随后，国际乒联与朝韩双方一起举行了简短的新闻发布会。在发布会现场，他们的会议桌前还特意摆放着曾在平昌奥运会使用过的“朝鲜半岛旗”。</p><p>　　国际乒联主席托马斯介绍了整个决议的准备过程，“（瑞典时间5月3日）早晨，我们通知了IOC国际奥组委（国际奥组委），这一切都是为了世界和平，因为在平昌奥运会上，朝韩就曾携手参加了女子冰球比赛。”</p><p>　　托马斯透露，三方在做出决定后也通知了其他协会，并征得了他们的同意，“我们也通知了中国、中国香港、罗马尼亚、澳大利亚、日本和乌克兰等协会。”</p><p>　　“我可以肯定地说，他们对这个促进世界和平的决议表示赞成。”</p><p>　　此次朝韩联合组队的形式大致与平昌冬奥会一致。联队的队名是“KOREA（COR）”，比赛穿着统一队服，现场将升起朝鲜半岛旗，如果最终夺冠将在现场奏响《阿里郎》。</p><p>　　韩朝联队，这样的“剧本”并不新鲜。</p><p>　　在27年前的日本千叶世乒赛上，朝韩就曾组成联队并最终击败中国夺冠，她们的事迹还被拍成了电影《朝韩梦之队》。</p><p>　　“我坚信体育可以影响政治。”作为那一届世乒赛的主力成员，韩国名将玄静和在今年2月接受ESPN采访时，依然显露着坚定的信念，“我现在依然坚信。”</p><p>　　27年后，从韩国女队主教练位置退下来的玄静和不再是朝韩“乒乓外交”的亲历者，但她的后辈们正在踏着前辈们走过路，期待继续创造当时的辉煌。</p><p>　　两队合并后，朝韩联队将由10人组成，这与其他协会只能选派5人参赛不同。在比赛中，每场比赛只能从中选3名队员上场，双方教练员将共同坐在现场指导队员。</p><p>　　作为混双世界冠军，韩国名将梁夏银对于此次联合组队充满期待。在她看来，自己更有动力去准备接下来和日本的比赛，“团结会让我们更强大，争取打进决赛。”</p><p>　　朝鲜选手金宋依是朝韩联队中实力最为强劲的一位，她曾在里约奥运会上击败过石川佳纯而夺得铜牌，“（组队后）感觉力量变强大，离目标更近了。”</p><p>　　国乒女队教练李隼在此前接受采访时也坦言，朝韩组队虽然突然，但并不会对中国队造成实际上的影响，他认为朝韩联队想要赢下日本都很困难。</p><p>　　“虽然是联合组队整体实力有提升，但是（如何）出人还是有很大的问题，我认为我们主要对手还是日本队，联队想要赢日本队还是非常难的事情。”</p><p>　　不过，联手进入4强就意味着，朝韩联队已经至少确保一枚铜牌入账。5月4日，朝韩联队将在的半决赛中，向实力更为强大的日本队发起冲击。</p><p>　　<strong>修改规则，为了世界和平</strong></p><p>　　虽然朝韩两国在历史上曾多次携手参加比赛，但与之前不同的是，这次的组队并不是在世乒赛开始之前就决定好的，而是在1/4决赛前一天“临时起意”。</p><p>　　一位在世乒赛现场的工作人员告诉澎湃新闻记者，国际乒联和朝韩双方是在凌晨进行的磋商，“队员们是早上才知道的，而且表情都很惊讶。”</p><p>　　央视记者李武军就在现场质疑国际乒联是否做到了“沟通到位”，“他们说通知到了每一个协会，但我采访日本队的队员时，她们看上去并不知道情况，还特意叫来了翻译。”</p><p>　　国际乒联临时修改规则也在网络上引起了巨大的争议。</p><p>　　一些网友认为，这样的决定有助于推动半岛局势的缓和；而另一些则认为，此举不尊重竞赛规则和公平公正的原则。</p><p>　　“朝韩联队在世乒赛的这一幕，我认为是极其荒诞的。不要把它和亚运会朝韩组队联系到一起，这是已经打了一半了，8强比赛，你说突然两个体育协会合并为一个，共同晋级！这是践踏体育规则！”一位网友提出了自己的质疑。</p><p>　　而在当天新闻发布会的现场，有媒体也质问了国际乒联修改规则的原因，国际乒联主席托马斯解释道：</p><p>　　“是的，我们尊重规则；是的，我们改变了规则；是的，这比规则更重要，这就是答案，为了和平的决定。”</p>', 1525425780, 0, 1),
(15, '烦!詹姆斯又双叒超越乔丹了 还顺便超了个帕克', NULL, '<p>　　北京时间5月4日，勒布朗-詹姆斯不愧是“活在纪录中的男人”。在今天克利夫兰骑士客场以128-110大胜多伦多猛龙，取得总比分2-0的领先的比赛中，詹姆斯砍下43分14次助攻，生涯季后赛得分30+助攻10+次数超越迈克尔-乔丹，升至历史第一。</p><p>　　本场前三节战罢，詹姆斯已达成了里程碑。他拿下31分13次助攻，骑士也反超11分进入末节。</p><p>　　根据统计，这是詹姆斯季后赛生涯第16次单场得分30+助攻10+，由此超越乔丹（15次），高居NBA历史首位。杰里-韦斯特和奥斯卡-罗伯特森分别以14次和12次位列第三、四名。</p><p>　　此外，本场也是詹姆斯生涯第226场季后赛，由此追平托尼-帕克，并列NBA历史第5位，下一场就将完成对帕克的超越。目前排在前4位的分别是德里克-费舍尔（259场）、蒂姆-邓肯（251场）、罗伯特-霍里（244场）和卡里姆-阿卜杜-贾巴尔（237场）。</p><p>　　恭喜詹皇！（魑魅）</p>', 1525424880, 0, 1),
(16, '马斯克：共享模式电动无人车是“显而易见”的未来', NULL, '<p>　　新浪科技讯 北京时间5月4日早间消息，特斯拉CEO埃隆·马斯克（Elon musk）在周三的电话会议上阐述了他所谓汽车行业“显而易见的”未来。</p><p>　　马斯克表示，有朝一日，人们将会共享电动无人驾驶汽车。他在电话会议上说：“共享电动无人驾驶模式显然是未来的方向。”</p><p>　　人们会“分享自己的汽车，还能有效地提供自己的汽车——有点像机器人Lyft或机器人Uber，有点像Uber、Lyft和Airbnb的合体。”马斯克说。</p><p>　　他还表示，无人驾驶汽车的车主可以根据自己的需要和接受程度对外出租自己的汽车。</p><p>　　“你可以拥有自己的汽车，然后100%使用自动驾驶电动车。也可以供他人使用，在你不用的时候租给别人用。你可以按照自己的意愿召唤汽车，还可以只允许朋友和家人使用，或者仅限5星用户使用。”他说。</p><p>　　要让这些变成现实，汽车需要具备全自动驾驶功能，也就是四级或五级自动驾驶。根据美国国家高速公路交通安全管理局（NHTSA）的定义，四级自动驾驶可以在特定环境中完成所有驾驶功能，而作为最高等级的五级自动驾驶则能够在所有环境中完成所有驾驶功能。</p><p>　　马斯克认为，此外还需要一套“软件基础设施”方便汽车和驾驶员相互交流。但具体何时能实现这番愿景目前还很难判断，因为马斯克也不知道配套的行业监管何时能够出台。</p><p>　　“棘手之处在于，无人驾驶不能把事故率或死亡率降到零。它只能大幅改善现状。”马斯克表示，当人们非常关注无人驾驶的时候，这种技术仍然会发生事故。</p><p>　　“如果是无人驾驶技术出事故，那就会成为新闻头条，媒体不会提到他们其实不应该写这样的报道，而是应该写报道阐述无人驾驶汽车其实很安全。但这样的报道并不吸引人。”马斯克说。</p><p>　　监管者也需要考虑公众情绪，因此放慢了审批速度。(书聿)</p>', 1525421820, 0, 1),
(17, '小白加盟中超曝光惊人条款', NULL, '<p>　　虽然伊涅斯塔加盟重庆斯威的转会还没有正式官方宣布，但是据西班牙媒体的最新报道，伊涅斯塔的转会已经敲定。巴萨队长除了获得丰厚的年薪之外，他还有其他收入。</p><p>　　据《世界报》的爆料，伊涅斯塔的年新闻2700万欧元，与重庆斯威签约三年，换而言之他能从重庆斯威获得8100万欧元的收入。除此以外，重庆斯威将购买伊涅斯塔酒庄600万瓶红酒，平均每瓶红酒6欧元，即共支付3600万欧元。</p><p>　　此前，多家西班牙媒体爆料，伊涅斯塔很大程度选择前往中国踢球，正是为了自己的红酒生意。（雷欧）</p>', 1525420440, 0, 1);
INSERT INTO `pre_news` (`news_id`, `news_title`, `news_pic`, `news_content`, `news_time`, `news_like`, `news_cate_id`) VALUES
(18, '中国舰载通用气垫登陆艇数量终超日本 但却不载坦克', NULL, '<p>　　关心人民海军发展的军迷，应该都经历过拿我军现役“中华神盾”驱逐舰的数量和海自“宙斯盾”战舰数量比较的阶段。最近，又有一型海军装备可以进行这样的比较了。</p><p>　　2007年11月，人民海军首艘大型综合登陆舰071型首舰“昆仑山”号入列南海舰队某登陆舰支队，这是我军首次拥有世界先进水平的多功能大型两栖登陆舰艇，拥有宽大的飞行甲板可操作数架直-8舰载直升机，坞舱可容纳数艘大型气垫登陆艇，当然此时我军还并没有可用的这类装备。</p><p>　　<strong>而早在1998年，日本海上自卫队就为其“大隅”级运输舰采购了6艘美制LCAC-1型气垫登陆艇。</strong>隶属于驻扎吴港的第1输送队下的第1气垫船队，舷号分别为2101-2106。相比之下，这就反映了当时我军两栖登陆装备当时依然较为落后的状况，而跨海登岛维护祖国主权统一的作战目标，也使得我军需要一种能够配合大型两栖船坞舰艇使用的快速登陆载具。</p><p>　　2009年底，726型气垫登陆艇首艇3320号艇下水，这是一款与美制LCAC-1类似的舰载通用气垫登陆艇，两台燃气轮机分列两侧，左侧前方为驾驶室（LCAC-1则在右侧），右侧为一挺54式12.7毫米高平两用机枪，艇体中央布置有全长车辆甲板，理论上可运载一辆主战坦克或多辆装甲车等军用车辆进行快速突击登陆。</p><p>　　具有试验性质的首批艇建造速度并不快，长期以来，3320艇一直作为726型气垫登陆艇的“独苗”存在。在这段时间内，首批3艘071型综合登陆舰陆续入列海军南海舰队某登陆舰支队。而缓慢建造的首批次726型登陆艇显然无法满足日常训练值班需要，长期只有3320这一艘“独苗”，况且作为首艇其还承担着大量的试验任务，于是071宽大的坞舱就出现了各式各样、却又不大正常的装载配置。</p><p>　　时光飞逝，到了2013年，第二艘3321号艇才曝光，而第三艘3322号艇则要到2016年才出现在官方图片中，足见首批艇建造缓慢程度。</p><p>　　直到2017年初，二批次首艇3330号公开亮相，同样装备海军南海舰队某登陆舰支队。本批次艇的动力舱外观与一批次三艘有着明显的外观变化，这是由于从二批开始，艇载主机由乌克兰的GTD-6000更换为中航工业黎明生产的QC-70型燃气轮机，主要区别在动力舱布局、排气口外观等等。</p><p>　　<strong>由于QC-70是一台由FWS-10核心机发展而来的航改燃气轮机，二批次726型也被戏称为一架贴海飞行的歼-11B</strong>。以燃气轮机为主机的登陆载具，其维护难度与使用方式确实不同于我军之前所拥有的任何类型登陆船艇，以一架飞机打比方倒也不是完全的戏谑，它确实需要一定的时间，以让部队建立起完整的使用维护体系。</p><p>　　二批次以及之后的726型气垫登陆艇的生产建造，进入了一个相对速度较快的节奏，同年年内，3331与3332两艇也相继亮相。</p><p>　　而就在前不久，隶属于东海舰队某登陆舰支队的首艘071型“沂蒙山”舰与气垫登陆艇合练的图片中，首次出现了隶属于该支队的726型气垫登陆艇，舷号为3239。<strong>这是已知的最新批入列的726系列气垫登陆艇，在经历了9年的时间之后，我军终于拥有了亚洲最大规模的舰载通用气垫登陆艇队伍。</strong></p><p>　　在726系列生产建造逐渐走上正轨的同时，它的训练作战使用方面同样值得关注。尽管首艇下水已经9年，但对于南海舰队与东海舰队下属的两支登陆舰支队来说，它的批量服役入列，乃至参加实战化演训任务却仍然是近两年来的事情。</p><p>　　作为快速突击登陆载具，LCAC这类气垫登陆艇经典的使用方式是用高航速冲上滩头，卸下高价值作战单元比如主战坦克等，以期快速瓦解岸滩抵抗，为后续部队进一步巩固登陆场。</p><p>　　然而我军的该系列艇在此之前的历次演习当中，出现了许多非常不“LCAC”的使用方式。尽管确实有长期以来这类装备数量不多、部队使用机会较少的因素，但依然出现了很多令人觉得怪异的画面。</p><p>　　特别是自从当初那张搭载96式坦克进行装载试验的图曝光后至今，也再未见726系列在演习中运载主战坦克进行突击登陆的场景。相比之下，更晚引进装备的“野牛”级大型气垫登陆舰却并不缺乏类似画面，这就更显得奇怪了。</p><p>　　当然从装备上来说，曾经与海军登陆舰部队配合过的陆军两栖机械化合成旅们，也确实是全05车族配置（或者还有使用63A的未完成换装部队），并没有主战坦克，而东南方向的海军陆战队合成旅，自己也已经完成05车族的换装，同样没有主战坦克。</p><p>　　对于05系车族来说，其优异的航渡能力本身就足以自行航渡冲滩，完全没必要动用726；而若要进一步巩固登陆场，形成具备优势防护与火力的战场桥头堡，则需要及时将主战坦克送上滩头，这活儿现在看又有被生存能力更强的“野牛”承包之势。这可能是使得726们长期处于较为尴尬的使用方式的原因。</p><p>　　而接下来想要发挥726系列的真正作用，究竟是与两栖机械化合成旅之外的其他陆军部队扩大联训范围，亦或者是为海军陆战队配备真正的铁甲矛头——主战坦克？在部队逐渐调整编制体制、新型装备不断入列的今天，无论怎样的方向，都是值得期待的。（作者署名：扬基帧察站）</p><p>　　《出鞘》完整内容请关注新浪军事官方微信抢先查看（查看详情请搜索微信公众号：sinamilnews），《出鞘》每天在新浪军事官方微信完整首发。</p>', 1525420080, 0, 1),
(19, '连续3年季后赛效率飙升!15季詹皇秒杀所有超巨', NULL, '<p>　　北京时间5月4日，NBA数据专家汤姆-哈伯斯特罗在最新一期的《大数据》栏目里指出，勒布朗-詹姆斯已经连续第3年季后赛的PER效率值超过常规赛的PER效率值，他也是当今NBA里唯一一位做到这点的超级巨星。</p><p>　　这期《大数据》的关键数字是33.2，这是詹姆斯在今年季后赛的PER效率值，联盟排名第一，排在他之后的依次是：哈登（30.8）、卡培拉（28.3）、戴维斯（28.1）和字母哥（26.6）。</p><p>　　最关键的是，这是詹姆斯连续第3年季后赛的PER效率值超过常规赛的PER效率值。2015-16赛季，他的季后赛PER值比常规赛提升了2.5；2016-17赛季提升了3.1；今年季后赛提升了4.6！</p><p>　　就这点来说，当今NBA的其他超级巨星都没有做到。举例来说：哈登在2015-16赛季和2016-17赛季的季后赛PER值比常规赛分别下降了3.5和4.4，今年他只是比常规赛提升了1.0；库里在2014-15赛季和2015-16赛季的季后赛PER值比常规赛分别下降了3.5和9.2，去年季后赛，库里的PER值才比常规赛提升了2.5。今年季后赛，库里只打了1场比赛，所以没有计算；威斯布鲁克，过去3年季后赛的PER值分别比常规赛下降了0.7、2.9和4.3；杜兰特，过去5年季后赛的PER值分别比常规赛下降了2.3、7.2、7.9、0.1和2.8。</p><p>　　“这是勒布朗的第15个赛季，这太疯狂了！”汤姆-哈伯斯特罗忍不住在节目里大喊，“他真的太夸张了！”</p><p>　　（罗森）</p>', 1525416120, 0, 1),
(20, '西安千架无人机尬舞背后：创始人称年入过亿', NULL, '<p>　　5月1日，1374架无人机在西安城墙南门编队飞行表演，摆出西安城墙、丝绸之路、四十周年等图案，创下“数量最多无人机表演吉尼斯世界纪录”。不过，一些网友从现场拍到的视频显示，当天演出现场出现意外，图案的左半部分变成了“乱码”。</p><p>　　演出现场图案的左半部分变成了“乱码“ 图 视觉中国</p><p>　　一份来自采购招标网的《关于春舞大西安城墙千架无人机光影盛典活动无人机表演项目成交公告》显示，此次成交供应商为广东亿航白鹭传媒科技有限公司（简称：亿航白鹭），表演项目的成交金额为1050万元。上千万的“出场费”也让不少网友咂舌。</p><p>　　前所未有的大规模无人机编队表演与高额演出费用背后，是消费级无人机的转型探索。在大疆一家独大的无人机领域，受场景适应能力、售后保障、续航时间等的掣肘，无人机发展一直受限。各家公司纷纷在消防、监控、植保、电力等工业领域探索，但盈利一直是难题。</p><p>　　这次出现失误的表演和高额的“出场费”，把无人机编队表演从幕后推到了台前，这并非第一次文艺演出中的无人机表演，但一定是它最出名的一次。不少人惊叹演出费用之高，编队表演，也似乎成了一些无人机企业消耗库存，寻找新的盈利点的利器。</p><p>　　<strong>表演报价一台无人机一万</strong></p><p>　　1050万的费用是一次演出还是常态化的多场演出？西安城墙管理委员工作人员向媒体证实，这一款项包括5月1日当天的演出费用，以及4月29日的彩排和申请吉尼斯纪录等的费用。</p><p>　　寻找中国创客（ID：xjbmaker）致电亿航白鹭客服得知，无人机编队表演报价为一台无人机一万元，超过200台可打八折。但具体需要的台数根据表演的元素以及甲方的预算来设定。具体流程包括，甲方提供表演的内容，亿航白鹭出设计方案，数字、字母、简单图案基本能实现，太复杂的图案则不太好呈现，后续还要看场地，并需甲方报批空域申请。</p><p>　　提供无人机编队飞行表演的团队不止亿航白鹭一家，寻找中国创客（ID：xjbmaker）在另一家演出提供商，也是某无人机生产商处了解到，500架无人机编队的价格，随着造型和图案难度的不同，报价大概在200万到300万之间。有业内人士称，编队越庞大，演出的难度和成本也会不断增大。</p><p>　　<strong>彩排时的完美演出</strong></p><p>　　<strong>上千架无人机在空中编队表演</strong></p><p>　　<strong>技术难度有多大呢？</strong></p><p>　　无人机技术领域专业人士刘飞（化名）介绍，编队表演是在无人机控制技术日趋成熟的情况下发展出来的一个新的应用方向，需要涉及飞控系统、飞行平台硬件、地面站三个部分的技术支撑。</p><p>　　“要实现编队飞行还需要射频通信技术使每个独立飞行的无人机实现相互配合的飞行动作，这次不理想的效果有可能就是由于这个环节出现了问题，成因可能有现场电磁阀环境的因素，也不排除有流程操作因素。”刘飞分析到。</p><p>　　一位不愿具名的无人机公司创始人分析，无人机编队需要解决授时和同步、导航和定位、数据、通讯和抗干扰、路径协调等多个难题；多架无人机协同运动需要精确的定位也需要精确同步的时间，编队还需要规划合理的路线同时要应对相互间的干扰；如果是做特技飞行表演，对控制要求很高，如果只是静态或准静态的LED灯光秀控制难度相对较低。</p><p>　　在星瀚资本运营总监看来，无人机编队表演为主要技术难点在于多机的引导和协同，队形设计、气动耦合、队形的动态调整、航迹规划、信息互换以及编队飞行控制策略等问题都非常关键，成本会伴随机体数量增加而大幅提高。</p><p>　　千架无人机亮相西安城墙 图 视觉中国</p><p>　　<strong>收入超出预期的编队表演</strong></p><p>　　<strong>“去年做了几十场，今年收入会过亿”</strong></p><p>　　承接此次西安无人机表演的亿航白鹭的官网介绍，公司专注于一键式操作多台无人飞机编队在指定空域呈现及变换信息，以空中媒体的形式，将科技与媒体、广告融合，利用无人机、VR、AR、裸眼3D、激光投影和全息技术等科技元素，对传统媒体进行创新和补充。</p><p>　　有报道称，去年以来，亿航白鹭的母公司亿航智能花了很长时间做各种业务的转变，从to C转到包括编队表演、行业应用以及跟政府的合作等业务。</p><p>　　亿航智能创始人兼CEO胡华智在接受采访时表示，今年第一季度，亿航已经接到大量订单，包括来自184（其研发的载人机）的、政府合作的、还有行业应用以及大量编队表演的订单。</p><p>　　2017年以来，亿航白鹭已经承接了多场大型活动的无人机编队表演，包括电影首映礼、香港回归20周年、灯光节等活动，上述业内人士刘飞也表示，亿航在编队表演领域有一定的地位，之前并未出过问题。</p><p>　　千架无人机亮相西安城墙 图 视觉中国</p><p>　　显然，编队飞行表演已经是亿航智能的招牌之一。其CEO胡华智在4月接受采访时表示，飞行编队的发展已经完全超出了公司的预期，本来只是打算拿无人机表演给大家一点空中造景的喜气感觉，同时做一些资金上的回收，没想到甚至扭转了亿航消费级无人机不盈利的颓势。“我们去年做了几十场，今年表演的收入甚至会过亿，这是我们当初没有预料到的。”</p><p>　　此前就有报道称，今年五一开始，无人机编队就将在西安实现常态化表演。</p><p>　　而对于无人机编队表演这一业务的拓展，易瓦特无人机创始人赵国成觉得，这是顺应无人机行业发展趋的“明智选择”。因为在他看来，这条路上需要新的点来刺激市场，无人机原来大家知道的只有航拍，现在大部分人知道还可以做编队，这样的转型或者说新盈利点对行业发展也是一种贡献。</p><p>　　<strong>消费级无人机“去库存”</strong></p><p>　　亿航白鹭所属母公司亿航智能的宣传文案介绍，其由来自中国和美国硅谷的飞行器爱好者创立的智能无人机公司，成立于2014年4月。创立之初，亿航智能推出了智能无人机GHOSTDRONE 2.0，特点在于可直接用手机APP操控。但由于国内对于消费级无人机的管控和限制，加上强大的竞争对手，上述消费级航拍无人机并没有占到多大的市场份额。</p><p>　　此后，亿航智能以低空中短途自动驾驶载人飞行器“亿航184”引起了国内外的关注。不过，这款产品运营的可行性、安全性以法律等问题上让亿航184饱受质疑。</p><p>　　亿航184</p><p>　　快速发展的同时，关于亿航的负面消息时常见诸媒体。2016年末，亿航智能被曝大规模裁员，2017年10月左右，一张亿航被追债的照片现身网络：其办公楼前的横幅上写着“广州亿航欠贷款19个月未还。”2018年3月，据雷锋网报道，亿航美国分公司已于当地时间2017年12月29日在加州申请破产，原因包括为“消费类无人机由于产品质量的问题，基本上销售不出去；公司欠了美国房东（办公室）一百五十万美金，被告了。”</p><p>　　对此亿航方面发布了名为《关于“雷锋网”针对亿航不实报道的郑重声明》的声明，表示由于其正常公司的业务需要调整架构，“将面向美国市场销售和服务等职能收回国内总部统一管理”，于是亿航决定关闭美国分公司。</p><p>　　第一财经在4月18日的一篇报道中称，胡华智坦承，在目前竞争环境下，消费级无人机确实难有较好盈利。他告诉记者，“我们之前有几千台的库存，但是我们把这部分库存利用起来，来从事编队业务，产生了很可观的收益。”</p><p>　　在4月末接受另一家网站的采访时他表示，某种程度上，编队飞行扭转了亿航消费级无人机不盈利的颓势。“我们原来大概有几千台的库存，这些库存占用了我们很大的资金，2015、2016年，我们相当于把很多的钱变成了货，在财务认定上是亏损的，编队表演是我们去库存很好的办法。”</p><p>　　图片来源网络</p><p>　　“我们已经实现了收支平衡”，胡华智在上述第一财经的报道中称，亿航已经完成了C轮融资，但未公布融资细节。此前，亿航在2014年12月完成了由GGV纪源资本领投的1000万美元A轮融资，在2015年8月完成了由金浦投资领投的4200万美元B轮融资。此后便一直没有新的融资消息放出。</p><p>　　经历了一场寒冬，这家“有故事”的无人机厂商，是否可以依靠飞行编队表演和其宣称的无人机物流华丽转身尚未可知，不过能够预见的是，在今后的各类庆典中无人机编队表演会越来越多。相比于其他类型的表演，其自带高科技基因，但却受场地、天气、空域申请等等因素的影响。</p><p>　　在星瀚资本运营总监张昊看来，无人机行业发展至今，常规性应用场景基本已经被头部企业所瓜分，长尾场景的规模和需求迫切程度不足以支撑其他企业的长期发展，作为转型发展的路径之一，无人机编队表演短期看是个不错的选择，但如何发展为长期的业务，如何创造可持续价值，这都是转型者们要考虑的问题。</p><p>　　编辑：赵力</p>', 1525412760, 0, 1),
(21, '婚后购房父母出资咋认定？法官:对夫妻双方的赠与', NULL, '<p class="art_p">北京晨报讯 （记者 黄晓宇）刘女士与王先生婚后共同购买了一套房屋，房屋首付款160万元由男方父母支付，后续贷款由男方偿还，房屋则登记在了二人名下。后刘女士起诉要求离婚，分割该房屋。王先生虽同意离婚，但不同意分割房屋。日前，海淀法院审结了此案，法院判决该房屋归王先生所有，由王先生给付刘女士房屋折价款185万元。</p><p class="art_p">原告刘女士在法庭上表示，该房屋登记在她与王先生名下，且房屋贷款一直由两人共同偿还，王先生父母出资应视为对双方的赠与，故要求依法分割该房屋。而被告王先生不同意该说法，他认为房屋是父母出资专门为他购买的。“我和父母签了一份赠与协议，这份协议写明，他们出的房款只赠与我一人所有，且贷款也是我一人还，我不同意分割房屋。”</p><p class="art_p">法院经审理后认为，该房屋系双方于婚姻关系存续期间购买，且登记在双方名下，故该房屋属于夫妻共同财产。</p><p class="art_p">对于首付款160万元，法院认为，王先生虽主张系其父母赠与其个人，且以协议书为据，但该协议书是其父母与王先生单独签署，王先生未提交证据证明该协议约定之事实有第三方知晓，故该证据对第三方不发生效力。另外，该协议书签订于房屋登记之前，事后该房屋登记在王先生和刘女士两人名下，视为变更了上述约定，故法院对王先生主张该首付款为其父母赠与其个人之主张不予采信，该房屋应当作为夫妻共同财产依法分割。</p><p class="art_p">最后，法院判决该房屋归王先生所有，由王先生给付刘女士房屋折价款185万元。</p><p class="art_p"><strong>■法官释法</strong></p><p class="art_p">根据《婚姻法司法解释二》规定，当事人结婚后，父母为双方购置房屋出资的，该出资应当认定为对夫妻双方的赠与，但父母明确表示赠与一方的除外。根据该条法律规定可知，条文中的“应当认定”是在父母实际出资时，其具体意思表示不明的情况下，从社会常理出发，推定为赠与。若当事人有证据证明其与出资人之间形成的是借贷关系的，则不能适用该条规定，该部分出资应认定为借款，将按照夫妻共同债务或个人债务处理。当然，该证据应当是在当事人离婚诉讼前形成的。</p><p class="art_p">关于父母出资是赠与一方还是双方，法官表示，一方父母在婚后为子女购置不动产出资的，一般应认定为对夫妻双方的赠与，除非父母明确表示赠与给一方。特别是当出现不动产所有权登记在双方子女名下这一事实时，除非当事人能够提供其他证据证明该出资仅为父母对其子女一方的赠与，否则依据产权登记这一事实，一方父母出资的真实意思表示更合理的解释为一方父母对双方子女的赠与而不是父母赠与给一方子女。法官特别提示，这里所称的父母的意思表示应以赠与发生的时间为准。</p><p class="art_p">本案中，王先生提交的其父母与其签署的赠与协议书，内容虽写明系其父母的出资，且赠与王先生一人，但该协议内容未经刘女士确认并同意，且双方争议之房屋登记在王先生和刘女士名下，故本案综合上述事实认定王先生与其父母单方签署的赠与协议不能对抗第三人刘女士，不能视为“父母明确表示赠与一方”的情形，故王先生父母出资的部分，应认定为王先生和刘女士夫妻共同所有。</p><p class="art_p">来源：北京晨报</p>', 1525408984, 0, 1),
(22, '为什么掰手指会“咔咔”响？到底能不能掰？', NULL, '<p>　　生活中，很多人动不动就喜欢把手指掰得“咔咔”响，尤其是小学生和中学生。在别人的面前把手指头弄出“咔咔”的响声，觉得很酸爽，认为这既是一种技能，还能炫耀。</p><p>　　但是，你有没有想过掰手指为什么会发出“咔咔”的声音呢？让人酸爽的掰手指会带来哪些危害呢？一起了解一下。</p><p>　　<strong>掰手指为什么会响？&nbsp;</strong></p><p>　　关于掰手指引起的弹响，其实有很多理论，其中以<strong>气泡理论</strong>支持者最多。该理论认为，手指关节发出的声音<strong>与润滑和缓冲关节的滑液中出现的二氧化碳气泡有关。</strong></p><p>　　掰手指会发出响声的原因，主要分为以下两种：</p><p>　　<strong>手指的生理性弹响</strong></p><p>　　生理性弹响有其发生的机制。简单的来说，生理性弹响就是<strong>不会引起手指关节任何疼痛，任何不适的弹响。</strong></p><p>　　一般生理性弹响问题不大，所以不用太过担心。要做的只是观察就行了。</p><p>　　<strong>病理性弹响</strong></p><p>　　比如，手指的骨性关节炎，手指的腱鞘炎都<strong>会引起手指间关节的弹性</strong>，这种弹响是由于手指的病变引起。</p><p>　　手指骨关节炎，是手指软骨退变，骨质增生，导致手指间关节的摩擦阻力增大，从而导致弹响。腱鞘炎是由于手指屈肌腱被压迫导致的局部炎症，从而引起弹响。</p><p>　　病理性弹响，需要尽量让手指休息制动，避免弹响的发生。一般要治疗原发病，最好到医院看一下，结合医生的体格检查，然后进一步处理。</p><p>　　<strong>掰手指会导致关节炎吗？</strong><strong>&nbsp;</strong></p><p>　　两者之间有一定的关系，但是<strong>手指间关节适当的生理性弹响不会引起手指间关节炎，也不会引起手指间关节的其他毛病。</strong></p><p>　　但是，有些人喜欢特意把手指掰出一些弹响，认为这些动作非常酷。有些人甚至对弹响有瘾，每天就不停的让手指间关节弹响。一定要注意手指间关节弹响的适度适量。</p><p>　　<strong>掰手指还有哪些危害？&nbsp;</strong></p><p>　　从医学角度讲，弹响其实本身也是一种摩擦，适度的弹响对关节没有坏处，但弹响的发生率高了，摩擦增多了，<strong>慢慢会产生手指间关节的慢性炎症，从而也会慢慢导致手指间关节炎。</strong></p><p>　　即使是生理性弹响，也要掌握适度适量。因为一旦生理性弹响增多，或者人为的使生理性弹响大大的增多，<strong>最终容易导致病理性弹响。</strong></p><p>　　所以，掰手指要适度。</p>', 1525429200, 0, 1),
(23, '曝皇马计划派替补打巴萨', NULL, '<p>　　西班牙综合性日报《ABC报》周四报道，<strong>齐达内和他的教练组正考虑让替补球员出战国家德比，以避免在欧冠决赛前遭遇不必要的伤病。</strong>“最重要的是第13冠。”俱乐部内部表示。</p><p>　　在欧冠半决赛淘汰拜仁后，皇马教练组即开始准备欧冠决赛。在球队庆祝欧冠晋级的欢乐气氛中，齐达内召集更衣室的领袖们开了一个会。他问了大家一个问题：“我们周日对巴塞罗那怎么踢？大家都上，还是避免风险为重？对我们来说，最重要的还是基辅的决赛。”</p><p>　　齐达内是皇马更衣室内做决定的人，很难让他改变计划，比如他敢于将贝尔和本泽马放到替补席上，不过这一次皇马的情况有些不一样，齐达内希望能听取大家的意见，群策群力踢好欧冠决赛。</p><p>　　齐达内想要听听拉莫斯、C罗、马塞洛、克罗斯、莫德里奇、本泽马这些球员的意见。如今皇马队内伤病众多，莫德里奇、瓦拉内、阿森西奥都有小伤病，而伊斯科和卡瓦哈尔伤情则比较严重。</p><p>　　每场比赛都有几名球员受伤让齐达内感到担心。是让主力球员都在诺坎普上场呢？还是明智地将好钢用在刀刃上，让主力阵容都无伤病地参加欧冠决赛？</p><p>　　“我们非要让C罗、莫德里奇、拉莫斯、纳瓦斯、马塞洛冒着受伤的危险上场吗？”齐达内对教练组成员表示，“把风险降到最低，这不是更好吗？这个问题我们必须在内部解决。”</p><p>　　在与教练组商议后，齐达内问了球员们一个问题：“为诺坎普的比赛以及其他的西甲比赛而冒重伤的危险，究竟值不值得？”</p><p>　　拉莫斯和C罗都想参加重大的比赛，很难说服他们不进入比赛大名单或者首发。毕竟皇马对阵的是巴萨，这是皇马在国内最大的死敌。眼下，C罗、拉莫斯和齐达内还在继续考虑这个问题。除了这两位球员，齐达内可以自由安排其他球员进行轮换。</p><p>　　据悉，齐达内希望目前身体有伤病的球员，如莫德里奇、克罗斯、马塞洛、阿森西奥、瓦拉内、本泽马都在国家德比中获得休息，包括纳瓦斯也将休息。而伊斯科和卡瓦哈尔都在养伤，本身就不能上场。在这些球员中，有多位都将在欧冠决赛中首发。纳瓦斯可能要到欧冠决赛才上场，接下去一段时间，他会把位置让给卡西利亚。如果C罗和拉莫斯也都休息，那皇马等于派出全替补阵容出战。</p><p>　　齐达内将给纳乔、贝尔、塞瓦略斯、略伦特、特奥、卡塞米罗、科瓦契奇这些球员机会。卡塞米罗近期因为状态不佳，被齐达内排除在首发阵容外。如果C罗也休战，那贝尔将成为引领皇马进攻的旗帜。</p><p>　　不过皇马部分高层对齐达内的计划表示了一些担忧。他们知道欧冠对皇马来说是至关重要的，但也非常清楚，国家德比对皇马的形象以及自己的球迷来说也是非常重要的。如果比分不利，那对皇马备战欧冠也会造成打击。（塞尔吉奥）</p>', 1525427820, 0, 1),
(24, '乒联：韩朝组队是为了和平', NULL, '<p>　　5月3日，朝韩两队原本应该在世乒赛的1/4决赛中一决高下，但她们却没有进行任何比赛，而是突然宣布组成朝韩联队，在晋级4强的同时至少确保一枚铜牌到手。</p><p>　　这是朝韩自1991年以来再次联手组队参加世乒赛，那一次他们曾击败了邓亚萍领衔的中国队夺冠。</p><p>　　不过，这一次在比赛中的突然组队还是引发了各界舆论的激烈讨论。</p><p>　　国际乒联和朝韩两队在当天下午3点（北京时间晚上9点）共同召开了新闻发布会，对于这些外界关注的问题进行了说明和解释。</p><p>　　“在短短的24小时内，我们创造了历史，大家进行了坦诚布公的讨论和商议。”国际乒联主席托马斯透露，在做出这个决定后，他们同样得到了中国、日本等其他协会的同意。</p><p>　　<strong>“我们得到了其他协会的同意”</strong></p><p>　　在比赛中宣布放弃竞争，并与对手握手、拥抱、组队。。。。。。这一幕在竞技体育的历史上，足以载入史册。</p><p>　　虽然朝韩联手的消息从前一天晚上就开始不胫而走，但双方在比赛中突然做出的决定还是令在场的所有人感到错愕。</p><p>　　在记者不停地追问中，国际乒联发表了一份声明。文中强调，“朝韩联队”的组成是国际乒联、朝鲜乒乓球队和韩国乒乓球队三方决定。</p><p>　　随后，国际乒联与朝韩双方一起举行了简短的新闻发布会。在发布会现场，他们的会议桌前还特意摆放着曾在平昌奥运会使用过的“朝鲜半岛旗”。</p><p>　　国际乒联主席托马斯介绍了整个决议的准备过程，“（瑞典时间5月3日）早晨，我们通知了IOC国际奥组委（国际奥组委），这一切都是为了世界和平，因为在平昌奥运会上，朝韩就曾携手参加了女子冰球比赛。”</p><p>　　托马斯透露，三方在做出决定后也通知了其他协会，并征得了他们的同意，“我们也通知了中国、中国香港、罗马尼亚、澳大利亚、日本和乌克兰等协会。”</p><p>　　“我可以肯定地说，他们对这个促进世界和平的决议表示赞成。”</p><p>　　此次朝韩联合组队的形式大致与平昌冬奥会一致。联队的队名是“KOREA（COR）”，比赛穿着统一队服，现场将升起朝鲜半岛旗，如果最终夺冠将在现场奏响《阿里郎》。</p><p>　　韩朝联队，这样的“剧本”并不新鲜。</p><p>　　在27年前的日本千叶世乒赛上，朝韩就曾组成联队并最终击败中国夺冠，她们的事迹还被拍成了电影《朝韩梦之队》。</p><p>　　“我坚信体育可以影响政治。”作为那一届世乒赛的主力成员，韩国名将玄静和在今年2月接受ESPN采访时，依然显露着坚定的信念，“我现在依然坚信。”</p><p>　　27年后，从韩国女队主教练位置退下来的玄静和不再是朝韩“乒乓外交”的亲历者，但她的后辈们正在踏着前辈们走过路，期待继续创造当时的辉煌。</p><p>　　两队合并后，朝韩联队将由10人组成，这与其他协会只能选派5人参赛不同。在比赛中，每场比赛只能从中选3名队员上场，双方教练员将共同坐在现场指导队员。</p><p>　　作为混双世界冠军，韩国名将梁夏银对于此次联合组队充满期待。在她看来，自己更有动力去准备接下来和日本的比赛，“团结会让我们更强大，争取打进决赛。”</p><p>　　朝鲜选手金宋依是朝韩联队中实力最为强劲的一位，她曾在里约奥运会上击败过石川佳纯而夺得铜牌，“（组队后）感觉力量变强大，离目标更近了。”</p><p>　　国乒女队教练李隼在此前接受采访时也坦言，朝韩组队虽然突然，但并不会对中国队造成实际上的影响，他认为朝韩联队想要赢下日本都很困难。</p><p>　　“虽然是联合组队整体实力有提升，但是（如何）出人还是有很大的问题，我认为我们主要对手还是日本队，联队想要赢日本队还是非常难的事情。”</p><p>　　不过，联手进入4强就意味着，朝韩联队已经至少确保一枚铜牌入账。5月4日，朝韩联队将在的半决赛中，向实力更为强大的日本队发起冲击。</p><p>　　<strong>修改规则，为了世界和平</strong></p><p>　　虽然朝韩两国在历史上曾多次携手参加比赛，但与之前不同的是，这次的组队并不是在世乒赛开始之前就决定好的，而是在1/4决赛前一天“临时起意”。</p><p>　　一位在世乒赛现场的工作人员告诉澎湃新闻记者，国际乒联和朝韩双方是在凌晨进行的磋商，“队员们是早上才知道的，而且表情都很惊讶。”</p><p>　　央视记者李武军就在现场质疑国际乒联是否做到了“沟通到位”，“他们说通知到了每一个协会，但我采访日本队的队员时，她们看上去并不知道情况，还特意叫来了翻译。”</p><p>　　国际乒联临时修改规则也在网络上引起了巨大的争议。</p><p>　　一些网友认为，这样的决定有助于推动半岛局势的缓和；而另一些则认为，此举不尊重竞赛规则和公平公正的原则。</p><p>　　“朝韩联队在世乒赛的这一幕，我认为是极其荒诞的。不要把它和亚运会朝韩组队联系到一起，这是已经打了一半了，8强比赛，你说突然两个体育协会合并为一个，共同晋级！这是践踏体育规则！”一位网友提出了自己的质疑。</p><p>　　而在当天新闻发布会的现场，有媒体也质问了国际乒联修改规则的原因，国际乒联主席托马斯解释道：</p><p>　　“是的，我们尊重规则；是的，我们改变了规则；是的，这比规则更重要，这就是答案，为了和平的决定。”</p>', 1525425780, 0, 1),
(25, '烦!詹姆斯又双叒超越乔丹了 还顺便超了个帕克', NULL, '<p>　　北京时间5月4日，勒布朗-詹姆斯不愧是“活在纪录中的男人”。在今天克利夫兰骑士客场以128-110大胜多伦多猛龙，取得总比分2-0的领先的比赛中，詹姆斯砍下43分14次助攻，生涯季后赛得分30+助攻10+次数超越迈克尔-乔丹，升至历史第一。</p><p>　　本场前三节战罢，詹姆斯已达成了里程碑。他拿下31分13次助攻，骑士也反超11分进入末节。</p><p>　　根据统计，这是詹姆斯季后赛生涯第16次单场得分30+助攻10+，由此超越乔丹（15次），高居NBA历史首位。杰里-韦斯特和奥斯卡-罗伯特森分别以14次和12次位列第三、四名。</p><p>　　此外，本场也是詹姆斯生涯第226场季后赛，由此追平托尼-帕克，并列NBA历史第5位，下一场就将完成对帕克的超越。目前排在前4位的分别是德里克-费舍尔（259场）、蒂姆-邓肯（251场）、罗伯特-霍里（244场）和卡里姆-阿卜杜-贾巴尔（237场）。</p><p>　　恭喜詹皇！（魑魅）</p>', 1525424880, 0, 1),
(26, '马斯克：共享模式电动无人车是“显而易见”的未来', NULL, '<p>　　新浪科技讯 北京时间5月4日早间消息，特斯拉CEO埃隆·马斯克（Elon musk）在周三的电话会议上阐述了他所谓汽车行业“显而易见的”未来。</p><p>　　马斯克表示，有朝一日，人们将会共享电动无人驾驶汽车。他在电话会议上说：“共享电动无人驾驶模式显然是未来的方向。”</p><p>　　人们会“分享自己的汽车，还能有效地提供自己的汽车——有点像机器人Lyft或机器人Uber，有点像Uber、Lyft和Airbnb的合体。”马斯克说。</p><p>　　他还表示，无人驾驶汽车的车主可以根据自己的需要和接受程度对外出租自己的汽车。</p><p>　　“你可以拥有自己的汽车，然后100%使用自动驾驶电动车。也可以供他人使用，在你不用的时候租给别人用。你可以按照自己的意愿召唤汽车，还可以只允许朋友和家人使用，或者仅限5星用户使用。”他说。</p><p>　　要让这些变成现实，汽车需要具备全自动驾驶功能，也就是四级或五级自动驾驶。根据美国国家高速公路交通安全管理局（NHTSA）的定义，四级自动驾驶可以在特定环境中完成所有驾驶功能，而作为最高等级的五级自动驾驶则能够在所有环境中完成所有驾驶功能。</p><p>　　马斯克认为，此外还需要一套“软件基础设施”方便汽车和驾驶员相互交流。但具体何时能实现这番愿景目前还很难判断，因为马斯克也不知道配套的行业监管何时能够出台。</p><p>　　“棘手之处在于，无人驾驶不能把事故率或死亡率降到零。它只能大幅改善现状。”马斯克表示，当人们非常关注无人驾驶的时候，这种技术仍然会发生事故。</p><p>　　“如果是无人驾驶技术出事故，那就会成为新闻头条，媒体不会提到他们其实不应该写这样的报道，而是应该写报道阐述无人驾驶汽车其实很安全。但这样的报道并不吸引人。”马斯克说。</p><p>　　监管者也需要考虑公众情绪，因此放慢了审批速度。(书聿)</p>', 1525421820, 0, 1),
(27, '小白加盟中超曝光惊人条款', NULL, '<p>　　虽然伊涅斯塔加盟重庆斯威的转会还没有正式官方宣布，但是据西班牙媒体的最新报道，伊涅斯塔的转会已经敲定。巴萨队长除了获得丰厚的年薪之外，他还有其他收入。</p><p>　　据《世界报》的爆料，伊涅斯塔的年新闻2700万欧元，与重庆斯威签约三年，换而言之他能从重庆斯威获得8100万欧元的收入。除此以外，重庆斯威将购买伊涅斯塔酒庄600万瓶红酒，平均每瓶红酒6欧元，即共支付3600万欧元。</p><p>　　此前，多家西班牙媒体爆料，伊涅斯塔很大程度选择前往中国踢球，正是为了自己的红酒生意。（雷欧）</p>', 1525420440, 0, 1),
(28, '中国舰载通用气垫登陆艇数量终超日本 但却不载坦克', NULL, '<p>　　关心人民海军发展的军迷，应该都经历过拿我军现役“中华神盾”驱逐舰的数量和海自“宙斯盾”战舰数量比较的阶段。最近，又有一型海军装备可以进行这样的比较了。</p><p>　　2007年11月，人民海军首艘大型综合登陆舰071型首舰“昆仑山”号入列南海舰队某登陆舰支队，这是我军首次拥有世界先进水平的多功能大型两栖登陆舰艇，拥有宽大的飞行甲板可操作数架直-8舰载直升机，坞舱可容纳数艘大型气垫登陆艇，当然此时我军还并没有可用的这类装备。</p><p>　　<strong>而早在1998年，日本海上自卫队就为其“大隅”级运输舰采购了6艘美制LCAC-1型气垫登陆艇。</strong>隶属于驻扎吴港的第1输送队下的第1气垫船队，舷号分别为2101-2106。相比之下，这就反映了当时我军两栖登陆装备当时依然较为落后的状况，而跨海登岛维护祖国主权统一的作战目标，也使得我军需要一种能够配合大型两栖船坞舰艇使用的快速登陆载具。</p><p>　　2009年底，726型气垫登陆艇首艇3320号艇下水，这是一款与美制LCAC-1类似的舰载通用气垫登陆艇，两台燃气轮机分列两侧，左侧前方为驾驶室（LCAC-1则在右侧），右侧为一挺54式12.7毫米高平两用机枪，艇体中央布置有全长车辆甲板，理论上可运载一辆主战坦克或多辆装甲车等军用车辆进行快速突击登陆。</p><p>　　具有试验性质的首批艇建造速度并不快，长期以来，3320艇一直作为726型气垫登陆艇的“独苗”存在。在这段时间内，首批3艘071型综合登陆舰陆续入列海军南海舰队某登陆舰支队。而缓慢建造的首批次726型登陆艇显然无法满足日常训练值班需要，长期只有3320这一艘“独苗”，况且作为首艇其还承担着大量的试验任务，于是071宽大的坞舱就出现了各式各样、却又不大正常的装载配置。</p><p>　　时光飞逝，到了2013年，第二艘3321号艇才曝光，而第三艘3322号艇则要到2016年才出现在官方图片中，足见首批艇建造缓慢程度。</p><p>　　直到2017年初，二批次首艇3330号公开亮相，同样装备海军南海舰队某登陆舰支队。本批次艇的动力舱外观与一批次三艘有着明显的外观变化，这是由于从二批开始，艇载主机由乌克兰的GTD-6000更换为中航工业黎明生产的QC-70型燃气轮机，主要区别在动力舱布局、排气口外观等等。</p><p>　　<strong>由于QC-70是一台由FWS-10核心机发展而来的航改燃气轮机，二批次726型也被戏称为一架贴海飞行的歼-11B</strong>。以燃气轮机为主机的登陆载具，其维护难度与使用方式确实不同于我军之前所拥有的任何类型登陆船艇，以一架飞机打比方倒也不是完全的戏谑，它确实需要一定的时间，以让部队建立起完整的使用维护体系。</p><p>　　二批次以及之后的726型气垫登陆艇的生产建造，进入了一个相对速度较快的节奏，同年年内，3331与3332两艇也相继亮相。</p><p>　　而就在前不久，隶属于东海舰队某登陆舰支队的首艘071型“沂蒙山”舰与气垫登陆艇合练的图片中，首次出现了隶属于该支队的726型气垫登陆艇，舷号为3239。<strong>这是已知的最新批入列的726系列气垫登陆艇，在经历了9年的时间之后，我军终于拥有了亚洲最大规模的舰载通用气垫登陆艇队伍。</strong></p><p>　　在726系列生产建造逐渐走上正轨的同时，它的训练作战使用方面同样值得关注。尽管首艇下水已经9年，但对于南海舰队与东海舰队下属的两支登陆舰支队来说，它的批量服役入列，乃至参加实战化演训任务却仍然是近两年来的事情。</p><p>　　作为快速突击登陆载具，LCAC这类气垫登陆艇经典的使用方式是用高航速冲上滩头，卸下高价值作战单元比如主战坦克等，以期快速瓦解岸滩抵抗，为后续部队进一步巩固登陆场。</p><p>　　然而我军的该系列艇在此之前的历次演习当中，出现了许多非常不“LCAC”的使用方式。尽管确实有长期以来这类装备数量不多、部队使用机会较少的因素，但依然出现了很多令人觉得怪异的画面。</p><p>　　特别是自从当初那张搭载96式坦克进行装载试验的图曝光后至今，也再未见726系列在演习中运载主战坦克进行突击登陆的场景。相比之下，更晚引进装备的“野牛”级大型气垫登陆舰却并不缺乏类似画面，这就更显得奇怪了。</p><p>　　当然从装备上来说，曾经与海军登陆舰部队配合过的陆军两栖机械化合成旅们，也确实是全05车族配置（或者还有使用63A的未完成换装部队），并没有主战坦克，而东南方向的海军陆战队合成旅，自己也已经完成05车族的换装，同样没有主战坦克。</p><p>　　对于05系车族来说，其优异的航渡能力本身就足以自行航渡冲滩，完全没必要动用726；而若要进一步巩固登陆场，形成具备优势防护与火力的战场桥头堡，则需要及时将主战坦克送上滩头，这活儿现在看又有被生存能力更强的“野牛”承包之势。这可能是使得726们长期处于较为尴尬的使用方式的原因。</p><p>　　而接下来想要发挥726系列的真正作用，究竟是与两栖机械化合成旅之外的其他陆军部队扩大联训范围，亦或者是为海军陆战队配备真正的铁甲矛头——主战坦克？在部队逐渐调整编制体制、新型装备不断入列的今天，无论怎样的方向，都是值得期待的。（作者署名：扬基帧察站）</p><p>　　《出鞘》完整内容请关注新浪军事官方微信抢先查看（查看详情请搜索微信公众号：sinamilnews），《出鞘》每天在新浪军事官方微信完整首发。</p>', 1525420080, 0, 1),
(29, '连续3年季后赛效率飙升!15季詹皇秒杀所有超巨', NULL, '<p>　　北京时间5月4日，NBA数据专家汤姆-哈伯斯特罗在最新一期的《大数据》栏目里指出，勒布朗-詹姆斯已经连续第3年季后赛的PER效率值超过常规赛的PER效率值，他也是当今NBA里唯一一位做到这点的超级巨星。</p><p>　　这期《大数据》的关键数字是33.2，这是詹姆斯在今年季后赛的PER效率值，联盟排名第一，排在他之后的依次是：哈登（30.8）、卡培拉（28.3）、戴维斯（28.1）和字母哥（26.6）。</p><p>　　最关键的是，这是詹姆斯连续第3年季后赛的PER效率值超过常规赛的PER效率值。2015-16赛季，他的季后赛PER值比常规赛提升了2.5；2016-17赛季提升了3.1；今年季后赛提升了4.6！</p><p>　　就这点来说，当今NBA的其他超级巨星都没有做到。举例来说：哈登在2015-16赛季和2016-17赛季的季后赛PER值比常规赛分别下降了3.5和4.4，今年他只是比常规赛提升了1.0；库里在2014-15赛季和2015-16赛季的季后赛PER值比常规赛分别下降了3.5和9.2，去年季后赛，库里的PER值才比常规赛提升了2.5。今年季后赛，库里只打了1场比赛，所以没有计算；威斯布鲁克，过去3年季后赛的PER值分别比常规赛下降了0.7、2.9和4.3；杜兰特，过去5年季后赛的PER值分别比常规赛下降了2.3、7.2、7.9、0.1和2.8。</p><p>　　“这是勒布朗的第15个赛季，这太疯狂了！”汤姆-哈伯斯特罗忍不住在节目里大喊，“他真的太夸张了！”</p><p>　　（罗森）</p>', 1525416120, 0, 1);
INSERT INTO `pre_news` (`news_id`, `news_title`, `news_pic`, `news_content`, `news_time`, `news_like`, `news_cate_id`) VALUES
(30, '西安千架无人机尬舞背后：创始人称年入过亿', NULL, '<p>　　5月1日，1374架无人机在西安城墙南门编队飞行表演，摆出西安城墙、丝绸之路、四十周年等图案，创下“数量最多无人机表演吉尼斯世界纪录”。不过，一些网友从现场拍到的视频显示，当天演出现场出现意外，图案的左半部分变成了“乱码”。</p><p>　　演出现场图案的左半部分变成了“乱码“ 图 视觉中国</p><p>　　一份来自采购招标网的《关于春舞大西安城墙千架无人机光影盛典活动无人机表演项目成交公告》显示，此次成交供应商为广东亿航白鹭传媒科技有限公司（简称：亿航白鹭），表演项目的成交金额为1050万元。上千万的“出场费”也让不少网友咂舌。</p><p>　　前所未有的大规模无人机编队表演与高额演出费用背后，是消费级无人机的转型探索。在大疆一家独大的无人机领域，受场景适应能力、售后保障、续航时间等的掣肘，无人机发展一直受限。各家公司纷纷在消防、监控、植保、电力等工业领域探索，但盈利一直是难题。</p><p>　　这次出现失误的表演和高额的“出场费”，把无人机编队表演从幕后推到了台前，这并非第一次文艺演出中的无人机表演，但一定是它最出名的一次。不少人惊叹演出费用之高，编队表演，也似乎成了一些无人机企业消耗库存，寻找新的盈利点的利器。</p><p>　　<strong>表演报价一台无人机一万</strong></p><p>　　1050万的费用是一次演出还是常态化的多场演出？西安城墙管理委员工作人员向媒体证实，这一款项包括5月1日当天的演出费用，以及4月29日的彩排和申请吉尼斯纪录等的费用。</p><p>　　寻找中国创客（ID：xjbmaker）致电亿航白鹭客服得知，无人机编队表演报价为一台无人机一万元，超过200台可打八折。但具体需要的台数根据表演的元素以及甲方的预算来设定。具体流程包括，甲方提供表演的内容，亿航白鹭出设计方案，数字、字母、简单图案基本能实现，太复杂的图案则不太好呈现，后续还要看场地，并需甲方报批空域申请。</p><p>　　提供无人机编队飞行表演的团队不止亿航白鹭一家，寻找中国创客（ID：xjbmaker）在另一家演出提供商，也是某无人机生产商处了解到，500架无人机编队的价格，随着造型和图案难度的不同，报价大概在200万到300万之间。有业内人士称，编队越庞大，演出的难度和成本也会不断增大。</p><p>　　<strong>彩排时的完美演出</strong></p><p>　　<strong>上千架无人机在空中编队表演</strong></p><p>　　<strong>技术难度有多大呢？</strong></p><p>　　无人机技术领域专业人士刘飞（化名）介绍，编队表演是在无人机控制技术日趋成熟的情况下发展出来的一个新的应用方向，需要涉及飞控系统、飞行平台硬件、地面站三个部分的技术支撑。</p><p>　　“要实现编队飞行还需要射频通信技术使每个独立飞行的无人机实现相互配合的飞行动作，这次不理想的效果有可能就是由于这个环节出现了问题，成因可能有现场电磁阀环境的因素，也不排除有流程操作因素。”刘飞分析到。</p><p>　　一位不愿具名的无人机公司创始人分析，无人机编队需要解决授时和同步、导航和定位、数据、通讯和抗干扰、路径协调等多个难题；多架无人机协同运动需要精确的定位也需要精确同步的时间，编队还需要规划合理的路线同时要应对相互间的干扰；如果是做特技飞行表演，对控制要求很高，如果只是静态或准静态的LED灯光秀控制难度相对较低。</p><p>　　在星瀚资本运营总监看来，无人机编队表演为主要技术难点在于多机的引导和协同，队形设计、气动耦合、队形的动态调整、航迹规划、信息互换以及编队飞行控制策略等问题都非常关键，成本会伴随机体数量增加而大幅提高。</p><p>　　千架无人机亮相西安城墙 图 视觉中国</p><p>　　<strong>收入超出预期的编队表演</strong></p><p>　　<strong>“去年做了几十场，今年收入会过亿”</strong></p><p>　　承接此次西安无人机表演的亿航白鹭的官网介绍，公司专注于一键式操作多台无人飞机编队在指定空域呈现及变换信息，以空中媒体的形式，将科技与媒体、广告融合，利用无人机、VR、AR、裸眼3D、激光投影和全息技术等科技元素，对传统媒体进行创新和补充。</p><p>　　有报道称，去年以来，亿航白鹭的母公司亿航智能花了很长时间做各种业务的转变，从to C转到包括编队表演、行业应用以及跟政府的合作等业务。</p><p>　　亿航智能创始人兼CEO胡华智在接受采访时表示，今年第一季度，亿航已经接到大量订单，包括来自184（其研发的载人机）的、政府合作的、还有行业应用以及大量编队表演的订单。</p><p>　　2017年以来，亿航白鹭已经承接了多场大型活动的无人机编队表演，包括电影首映礼、香港回归20周年、灯光节等活动，上述业内人士刘飞也表示，亿航在编队表演领域有一定的地位，之前并未出过问题。</p><p>　　千架无人机亮相西安城墙 图 视觉中国</p><p>　　显然，编队飞行表演已经是亿航智能的招牌之一。其CEO胡华智在4月接受采访时表示，飞行编队的发展已经完全超出了公司的预期，本来只是打算拿无人机表演给大家一点空中造景的喜气感觉，同时做一些资金上的回收，没想到甚至扭转了亿航消费级无人机不盈利的颓势。“我们去年做了几十场，今年表演的收入甚至会过亿，这是我们当初没有预料到的。”</p><p>　　此前就有报道称，今年五一开始，无人机编队就将在西安实现常态化表演。</p><p>　　而对于无人机编队表演这一业务的拓展，易瓦特无人机创始人赵国成觉得，这是顺应无人机行业发展趋的“明智选择”。因为在他看来，这条路上需要新的点来刺激市场，无人机原来大家知道的只有航拍，现在大部分人知道还可以做编队，这样的转型或者说新盈利点对行业发展也是一种贡献。</p><p>　　<strong>消费级无人机“去库存”</strong></p><p>　　亿航白鹭所属母公司亿航智能的宣传文案介绍，其由来自中国和美国硅谷的飞行器爱好者创立的智能无人机公司，成立于2014年4月。创立之初，亿航智能推出了智能无人机GHOSTDRONE 2.0，特点在于可直接用手机APP操控。但由于国内对于消费级无人机的管控和限制，加上强大的竞争对手，上述消费级航拍无人机并没有占到多大的市场份额。</p><p>　　此后，亿航智能以低空中短途自动驾驶载人飞行器“亿航184”引起了国内外的关注。不过，这款产品运营的可行性、安全性以法律等问题上让亿航184饱受质疑。</p><p>　　亿航184</p><p>　　快速发展的同时，关于亿航的负面消息时常见诸媒体。2016年末，亿航智能被曝大规模裁员，2017年10月左右，一张亿航被追债的照片现身网络：其办公楼前的横幅上写着“广州亿航欠贷款19个月未还。”2018年3月，据雷锋网报道，亿航美国分公司已于当地时间2017年12月29日在加州申请破产，原因包括为“消费类无人机由于产品质量的问题，基本上销售不出去；公司欠了美国房东（办公室）一百五十万美金，被告了。”</p><p>　　对此亿航方面发布了名为《关于“雷锋网”针对亿航不实报道的郑重声明》的声明，表示由于其正常公司的业务需要调整架构，“将面向美国市场销售和服务等职能收回国内总部统一管理”，于是亿航决定关闭美国分公司。</p><p>　　第一财经在4月18日的一篇报道中称，胡华智坦承，在目前竞争环境下，消费级无人机确实难有较好盈利。他告诉记者，“我们之前有几千台的库存，但是我们把这部分库存利用起来，来从事编队业务，产生了很可观的收益。”</p><p>　　在4月末接受另一家网站的采访时他表示，某种程度上，编队飞行扭转了亿航消费级无人机不盈利的颓势。“我们原来大概有几千台的库存，这些库存占用了我们很大的资金，2015、2016年，我们相当于把很多的钱变成了货，在财务认定上是亏损的，编队表演是我们去库存很好的办法。”</p><p>　　图片来源网络</p><p>　　“我们已经实现了收支平衡”，胡华智在上述第一财经的报道中称，亿航已经完成了C轮融资，但未公布融资细节。此前，亿航在2014年12月完成了由GGV纪源资本领投的1000万美元A轮融资，在2015年8月完成了由金浦投资领投的4200万美元B轮融资。此后便一直没有新的融资消息放出。</p><p>　　经历了一场寒冬，这家“有故事”的无人机厂商，是否可以依靠飞行编队表演和其宣称的无人机物流华丽转身尚未可知，不过能够预见的是，在今后的各类庆典中无人机编队表演会越来越多。相比于其他类型的表演，其自带高科技基因，但却受场地、天气、空域申请等等因素的影响。</p><p>　　在星瀚资本运营总监张昊看来，无人机行业发展至今，常规性应用场景基本已经被头部企业所瓜分，长尾场景的规模和需求迫切程度不足以支撑其他企业的长期发展，作为转型发展的路径之一，无人机编队表演短期看是个不错的选择，但如何发展为长期的业务，如何创造可持续价值，这都是转型者们要考虑的问题。</p><p>　　编辑：赵力</p>', 1525412760, 0, 1),
(31, '婚后购房父母出资咋认定？法官:对夫妻双方的赠与', NULL, '<p class="art_p">北京晨报讯 （记者 黄晓宇）刘女士与王先生婚后共同购买了一套房屋，房屋首付款160万元由男方父母支付，后续贷款由男方偿还，房屋则登记在了二人名下。后刘女士起诉要求离婚，分割该房屋。王先生虽同意离婚，但不同意分割房屋。日前，海淀法院审结了此案，法院判决该房屋归王先生所有，由王先生给付刘女士房屋折价款185万元。</p><p class="art_p">原告刘女士在法庭上表示，该房屋登记在她与王先生名下，且房屋贷款一直由两人共同偿还，王先生父母出资应视为对双方的赠与，故要求依法分割该房屋。而被告王先生不同意该说法，他认为房屋是父母出资专门为他购买的。“我和父母签了一份赠与协议，这份协议写明，他们出的房款只赠与我一人所有，且贷款也是我一人还，我不同意分割房屋。”</p><p class="art_p">法院经审理后认为，该房屋系双方于婚姻关系存续期间购买，且登记在双方名下，故该房屋属于夫妻共同财产。</p><p class="art_p">对于首付款160万元，法院认为，王先生虽主张系其父母赠与其个人，且以协议书为据，但该协议书是其父母与王先生单独签署，王先生未提交证据证明该协议约定之事实有第三方知晓，故该证据对第三方不发生效力。另外，该协议书签订于房屋登记之前，事后该房屋登记在王先生和刘女士两人名下，视为变更了上述约定，故法院对王先生主张该首付款为其父母赠与其个人之主张不予采信，该房屋应当作为夫妻共同财产依法分割。</p><p class="art_p">最后，法院判决该房屋归王先生所有，由王先生给付刘女士房屋折价款185万元。</p><p class="art_p"><strong>■法官释法</strong></p><p class="art_p">根据《婚姻法司法解释二》规定，当事人结婚后，父母为双方购置房屋出资的，该出资应当认定为对夫妻双方的赠与，但父母明确表示赠与一方的除外。根据该条法律规定可知，条文中的“应当认定”是在父母实际出资时，其具体意思表示不明的情况下，从社会常理出发，推定为赠与。若当事人有证据证明其与出资人之间形成的是借贷关系的，则不能适用该条规定，该部分出资应认定为借款，将按照夫妻共同债务或个人债务处理。当然，该证据应当是在当事人离婚诉讼前形成的。</p><p class="art_p">关于父母出资是赠与一方还是双方，法官表示，一方父母在婚后为子女购置不动产出资的，一般应认定为对夫妻双方的赠与，除非父母明确表示赠与给一方。特别是当出现不动产所有权登记在双方子女名下这一事实时，除非当事人能够提供其他证据证明该出资仅为父母对其子女一方的赠与，否则依据产权登记这一事实，一方父母出资的真实意思表示更合理的解释为一方父母对双方子女的赠与而不是父母赠与给一方子女。法官特别提示，这里所称的父母的意思表示应以赠与发生的时间为准。</p><p class="art_p">本案中，王先生提交的其父母与其签署的赠与协议书，内容虽写明系其父母的出资，且赠与王先生一人，但该协议内容未经刘女士确认并同意，且双方争议之房屋登记在王先生和刘女士名下，故本案综合上述事实认定王先生与其父母单方签署的赠与协议不能对抗第三人刘女士，不能视为“父母明确表示赠与一方”的情形，故王先生父母出资的部分，应认定为王先生和刘女士夫妻共同所有。</p><p class="art_p">来源：北京晨报</p>', 1525408984, 0, 1),
(32, '为什么掰手指会“咔咔”响？到底能不能掰？', NULL, '<p>　　生活中，很多人动不动就喜欢把手指掰得“咔咔”响，尤其是小学生和中学生。在别人的面前把手指头弄出“咔咔”的响声，觉得很酸爽，认为这既是一种技能，还能炫耀。</p><p>　　但是，你有没有想过掰手指为什么会发出“咔咔”的声音呢？让人酸爽的掰手指会带来哪些危害呢？一起了解一下。</p><p>　　<strong>掰手指为什么会响？&nbsp;</strong></p><p>　　关于掰手指引起的弹响，其实有很多理论，其中以<strong>气泡理论</strong>支持者最多。该理论认为，手指关节发出的声音<strong>与润滑和缓冲关节的滑液中出现的二氧化碳气泡有关。</strong></p><p>　　掰手指会发出响声的原因，主要分为以下两种：</p><p>　　<strong>手指的生理性弹响</strong></p><p>　　生理性弹响有其发生的机制。简单的来说，生理性弹响就是<strong>不会引起手指关节任何疼痛，任何不适的弹响。</strong></p><p>　　一般生理性弹响问题不大，所以不用太过担心。要做的只是观察就行了。</p><p>　　<strong>病理性弹响</strong></p><p>　　比如，手指的骨性关节炎，手指的腱鞘炎都<strong>会引起手指间关节的弹性</strong>，这种弹响是由于手指的病变引起。</p><p>　　手指骨关节炎，是手指软骨退变，骨质增生，导致手指间关节的摩擦阻力增大，从而导致弹响。腱鞘炎是由于手指屈肌腱被压迫导致的局部炎症，从而引起弹响。</p><p>　　病理性弹响，需要尽量让手指休息制动，避免弹响的发生。一般要治疗原发病，最好到医院看一下，结合医生的体格检查，然后进一步处理。</p><p>　　<strong>掰手指会导致关节炎吗？</strong><strong>&nbsp;</strong></p><p>　　两者之间有一定的关系，但是<strong>手指间关节适当的生理性弹响不会引起手指间关节炎，也不会引起手指间关节的其他毛病。</strong></p><p>　　但是，有些人喜欢特意把手指掰出一些弹响，认为这些动作非常酷。有些人甚至对弹响有瘾，每天就不停的让手指间关节弹响。一定要注意手指间关节弹响的适度适量。</p><p>　　<strong>掰手指还有哪些危害？&nbsp;</strong></p><p>　　从医学角度讲，弹响其实本身也是一种摩擦，适度的弹响对关节没有坏处，但弹响的发生率高了，摩擦增多了，<strong>慢慢会产生手指间关节的慢性炎症，从而也会慢慢导致手指间关节炎。</strong></p><p>　　即使是生理性弹响，也要掌握适度适量。因为一旦生理性弹响增多，或者人为的使生理性弹响大大的增多，<strong>最终容易导致病理性弹响。</strong></p><p>　　所以，掰手指要适度。</p>', 1525429200, 0, 1),
(33, '曝皇马计划派替补打巴萨', NULL, '<p>　　西班牙综合性日报《ABC报》周四报道，<strong>齐达内和他的教练组正考虑让替补球员出战国家德比，以避免在欧冠决赛前遭遇不必要的伤病。</strong>“最重要的是第13冠。”俱乐部内部表示。</p><p>　　在欧冠半决赛淘汰拜仁后，皇马教练组即开始准备欧冠决赛。在球队庆祝欧冠晋级的欢乐气氛中，齐达内召集更衣室的领袖们开了一个会。他问了大家一个问题：“我们周日对巴塞罗那怎么踢？大家都上，还是避免风险为重？对我们来说，最重要的还是基辅的决赛。”</p><p>　　齐达内是皇马更衣室内做决定的人，很难让他改变计划，比如他敢于将贝尔和本泽马放到替补席上，不过这一次皇马的情况有些不一样，齐达内希望能听取大家的意见，群策群力踢好欧冠决赛。</p><p>　　齐达内想要听听拉莫斯、C罗、马塞洛、克罗斯、莫德里奇、本泽马这些球员的意见。如今皇马队内伤病众多，莫德里奇、瓦拉内、阿森西奥都有小伤病，而伊斯科和卡瓦哈尔伤情则比较严重。</p><p>　　每场比赛都有几名球员受伤让齐达内感到担心。是让主力球员都在诺坎普上场呢？还是明智地将好钢用在刀刃上，让主力阵容都无伤病地参加欧冠决赛？</p><p>　　“我们非要让C罗、莫德里奇、拉莫斯、纳瓦斯、马塞洛冒着受伤的危险上场吗？”齐达内对教练组成员表示，“把风险降到最低，这不是更好吗？这个问题我们必须在内部解决。”</p><p>　　在与教练组商议后，齐达内问了球员们一个问题：“为诺坎普的比赛以及其他的西甲比赛而冒重伤的危险，究竟值不值得？”</p><p>　　拉莫斯和C罗都想参加重大的比赛，很难说服他们不进入比赛大名单或者首发。毕竟皇马对阵的是巴萨，这是皇马在国内最大的死敌。眼下，C罗、拉莫斯和齐达内还在继续考虑这个问题。除了这两位球员，齐达内可以自由安排其他球员进行轮换。</p><p>　　据悉，齐达内希望目前身体有伤病的球员，如莫德里奇、克罗斯、马塞洛、阿森西奥、瓦拉内、本泽马都在国家德比中获得休息，包括纳瓦斯也将休息。而伊斯科和卡瓦哈尔都在养伤，本身就不能上场。在这些球员中，有多位都将在欧冠决赛中首发。纳瓦斯可能要到欧冠决赛才上场，接下去一段时间，他会把位置让给卡西利亚。如果C罗和拉莫斯也都休息，那皇马等于派出全替补阵容出战。</p><p>　　齐达内将给纳乔、贝尔、塞瓦略斯、略伦特、特奥、卡塞米罗、科瓦契奇这些球员机会。卡塞米罗近期因为状态不佳，被齐达内排除在首发阵容外。如果C罗也休战，那贝尔将成为引领皇马进攻的旗帜。</p><p>　　不过皇马部分高层对齐达内的计划表示了一些担忧。他们知道欧冠对皇马来说是至关重要的，但也非常清楚，国家德比对皇马的形象以及自己的球迷来说也是非常重要的。如果比分不利，那对皇马备战欧冠也会造成打击。（塞尔吉奥）</p>', 1525427820, 0, 1),
(34, '乒联：韩朝组队是为了和平', NULL, '<p>　　5月3日，朝韩两队原本应该在世乒赛的1/4决赛中一决高下，但她们却没有进行任何比赛，而是突然宣布组成朝韩联队，在晋级4强的同时至少确保一枚铜牌到手。</p><p>　　这是朝韩自1991年以来再次联手组队参加世乒赛，那一次他们曾击败了邓亚萍领衔的中国队夺冠。</p><p>　　不过，这一次在比赛中的突然组队还是引发了各界舆论的激烈讨论。</p><p>　　国际乒联和朝韩两队在当天下午3点（北京时间晚上9点）共同召开了新闻发布会，对于这些外界关注的问题进行了说明和解释。</p><p>　　“在短短的24小时内，我们创造了历史，大家进行了坦诚布公的讨论和商议。”国际乒联主席托马斯透露，在做出这个决定后，他们同样得到了中国、日本等其他协会的同意。</p><p>　　<strong>“我们得到了其他协会的同意”</strong></p><p>　　在比赛中宣布放弃竞争，并与对手握手、拥抱、组队。。。。。。这一幕在竞技体育的历史上，足以载入史册。</p><p>　　虽然朝韩联手的消息从前一天晚上就开始不胫而走，但双方在比赛中突然做出的决定还是令在场的所有人感到错愕。</p><p>　　在记者不停地追问中，国际乒联发表了一份声明。文中强调，“朝韩联队”的组成是国际乒联、朝鲜乒乓球队和韩国乒乓球队三方决定。</p><p>　　随后，国际乒联与朝韩双方一起举行了简短的新闻发布会。在发布会现场，他们的会议桌前还特意摆放着曾在平昌奥运会使用过的“朝鲜半岛旗”。</p><p>　　国际乒联主席托马斯介绍了整个决议的准备过程，“（瑞典时间5月3日）早晨，我们通知了IOC国际奥组委（国际奥组委），这一切都是为了世界和平，因为在平昌奥运会上，朝韩就曾携手参加了女子冰球比赛。”</p><p>　　托马斯透露，三方在做出决定后也通知了其他协会，并征得了他们的同意，“我们也通知了中国、中国香港、罗马尼亚、澳大利亚、日本和乌克兰等协会。”</p><p>　　“我可以肯定地说，他们对这个促进世界和平的决议表示赞成。”</p><p>　　此次朝韩联合组队的形式大致与平昌冬奥会一致。联队的队名是“KOREA（COR）”，比赛穿着统一队服，现场将升起朝鲜半岛旗，如果最终夺冠将在现场奏响《阿里郎》。</p><p>　　韩朝联队，这样的“剧本”并不新鲜。</p><p>　　在27年前的日本千叶世乒赛上，朝韩就曾组成联队并最终击败中国夺冠，她们的事迹还被拍成了电影《朝韩梦之队》。</p><p>　　“我坚信体育可以影响政治。”作为那一届世乒赛的主力成员，韩国名将玄静和在今年2月接受ESPN采访时，依然显露着坚定的信念，“我现在依然坚信。”</p><p>　　27年后，从韩国女队主教练位置退下来的玄静和不再是朝韩“乒乓外交”的亲历者，但她的后辈们正在踏着前辈们走过路，期待继续创造当时的辉煌。</p><p>　　两队合并后，朝韩联队将由10人组成，这与其他协会只能选派5人参赛不同。在比赛中，每场比赛只能从中选3名队员上场，双方教练员将共同坐在现场指导队员。</p><p>　　作为混双世界冠军，韩国名将梁夏银对于此次联合组队充满期待。在她看来，自己更有动力去准备接下来和日本的比赛，“团结会让我们更强大，争取打进决赛。”</p><p>　　朝鲜选手金宋依是朝韩联队中实力最为强劲的一位，她曾在里约奥运会上击败过石川佳纯而夺得铜牌，“（组队后）感觉力量变强大，离目标更近了。”</p><p>　　国乒女队教练李隼在此前接受采访时也坦言，朝韩组队虽然突然，但并不会对中国队造成实际上的影响，他认为朝韩联队想要赢下日本都很困难。</p><p>　　“虽然是联合组队整体实力有提升，但是（如何）出人还是有很大的问题，我认为我们主要对手还是日本队，联队想要赢日本队还是非常难的事情。”</p><p>　　不过，联手进入4强就意味着，朝韩联队已经至少确保一枚铜牌入账。5月4日，朝韩联队将在的半决赛中，向实力更为强大的日本队发起冲击。</p><p>　　<strong>修改规则，为了世界和平</strong></p><p>　　虽然朝韩两国在历史上曾多次携手参加比赛，但与之前不同的是，这次的组队并不是在世乒赛开始之前就决定好的，而是在1/4决赛前一天“临时起意”。</p><p>　　一位在世乒赛现场的工作人员告诉澎湃新闻记者，国际乒联和朝韩双方是在凌晨进行的磋商，“队员们是早上才知道的，而且表情都很惊讶。”</p><p>　　央视记者李武军就在现场质疑国际乒联是否做到了“沟通到位”，“他们说通知到了每一个协会，但我采访日本队的队员时，她们看上去并不知道情况，还特意叫来了翻译。”</p><p>　　国际乒联临时修改规则也在网络上引起了巨大的争议。</p><p>　　一些网友认为，这样的决定有助于推动半岛局势的缓和；而另一些则认为，此举不尊重竞赛规则和公平公正的原则。</p><p>　　“朝韩联队在世乒赛的这一幕，我认为是极其荒诞的。不要把它和亚运会朝韩组队联系到一起，这是已经打了一半了，8强比赛，你说突然两个体育协会合并为一个，共同晋级！这是践踏体育规则！”一位网友提出了自己的质疑。</p><p>　　而在当天新闻发布会的现场，有媒体也质问了国际乒联修改规则的原因，国际乒联主席托马斯解释道：</p><p>　　“是的，我们尊重规则；是的，我们改变了规则；是的，这比规则更重要，这就是答案，为了和平的决定。”</p>', 1525425780, 0, 1),
(35, '烦!詹姆斯又双叒超越乔丹了 还顺便超了个帕克', NULL, '<p>　　北京时间5月4日，勒布朗-詹姆斯不愧是“活在纪录中的男人”。在今天克利夫兰骑士客场以128-110大胜多伦多猛龙，取得总比分2-0的领先的比赛中，詹姆斯砍下43分14次助攻，生涯季后赛得分30+助攻10+次数超越迈克尔-乔丹，升至历史第一。</p><p>　　本场前三节战罢，詹姆斯已达成了里程碑。他拿下31分13次助攻，骑士也反超11分进入末节。</p><p>　　根据统计，这是詹姆斯季后赛生涯第16次单场得分30+助攻10+，由此超越乔丹（15次），高居NBA历史首位。杰里-韦斯特和奥斯卡-罗伯特森分别以14次和12次位列第三、四名。</p><p>　　此外，本场也是詹姆斯生涯第226场季后赛，由此追平托尼-帕克，并列NBA历史第5位，下一场就将完成对帕克的超越。目前排在前4位的分别是德里克-费舍尔（259场）、蒂姆-邓肯（251场）、罗伯特-霍里（244场）和卡里姆-阿卜杜-贾巴尔（237场）。</p><p>　　恭喜詹皇！（魑魅）</p>', 1525424880, 0, 1),
(36, '马斯克：共享模式电动无人车是“显而易见”的未来', NULL, '<p>　　新浪科技讯 北京时间5月4日早间消息，特斯拉CEO埃隆·马斯克（Elon musk）在周三的电话会议上阐述了他所谓汽车行业“显而易见的”未来。</p><p>　　马斯克表示，有朝一日，人们将会共享电动无人驾驶汽车。他在电话会议上说：“共享电动无人驾驶模式显然是未来的方向。”</p><p>　　人们会“分享自己的汽车，还能有效地提供自己的汽车——有点像机器人Lyft或机器人Uber，有点像Uber、Lyft和Airbnb的合体。”马斯克说。</p><p>　　他还表示，无人驾驶汽车的车主可以根据自己的需要和接受程度对外出租自己的汽车。</p><p>　　“你可以拥有自己的汽车，然后100%使用自动驾驶电动车。也可以供他人使用，在你不用的时候租给别人用。你可以按照自己的意愿召唤汽车，还可以只允许朋友和家人使用，或者仅限5星用户使用。”他说。</p><p>　　要让这些变成现实，汽车需要具备全自动驾驶功能，也就是四级或五级自动驾驶。根据美国国家高速公路交通安全管理局（NHTSA）的定义，四级自动驾驶可以在特定环境中完成所有驾驶功能，而作为最高等级的五级自动驾驶则能够在所有环境中完成所有驾驶功能。</p><p>　　马斯克认为，此外还需要一套“软件基础设施”方便汽车和驾驶员相互交流。但具体何时能实现这番愿景目前还很难判断，因为马斯克也不知道配套的行业监管何时能够出台。</p><p>　　“棘手之处在于，无人驾驶不能把事故率或死亡率降到零。它只能大幅改善现状。”马斯克表示，当人们非常关注无人驾驶的时候，这种技术仍然会发生事故。</p><p>　　“如果是无人驾驶技术出事故，那就会成为新闻头条，媒体不会提到他们其实不应该写这样的报道，而是应该写报道阐述无人驾驶汽车其实很安全。但这样的报道并不吸引人。”马斯克说。</p><p>　　监管者也需要考虑公众情绪，因此放慢了审批速度。(书聿)</p>', 1525421820, 0, 1),
(37, '小白加盟中超曝光惊人条款', NULL, '<p>　　虽然伊涅斯塔加盟重庆斯威的转会还没有正式官方宣布，但是据西班牙媒体的最新报道，伊涅斯塔的转会已经敲定。巴萨队长除了获得丰厚的年薪之外，他还有其他收入。</p><p>　　据《世界报》的爆料，伊涅斯塔的年新闻2700万欧元，与重庆斯威签约三年，换而言之他能从重庆斯威获得8100万欧元的收入。除此以外，重庆斯威将购买伊涅斯塔酒庄600万瓶红酒，平均每瓶红酒6欧元，即共支付3600万欧元。</p><p>　　此前，多家西班牙媒体爆料，伊涅斯塔很大程度选择前往中国踢球，正是为了自己的红酒生意。（雷欧）</p>', 1525420440, 0, 1),
(38, '中国舰载通用气垫登陆艇数量终超日本 但却不载坦克', NULL, '<p>　　关心人民海军发展的军迷，应该都经历过拿我军现役“中华神盾”驱逐舰的数量和海自“宙斯盾”战舰数量比较的阶段。最近，又有一型海军装备可以进行这样的比较了。</p><p>　　2007年11月，人民海军首艘大型综合登陆舰071型首舰“昆仑山”号入列南海舰队某登陆舰支队，这是我军首次拥有世界先进水平的多功能大型两栖登陆舰艇，拥有宽大的飞行甲板可操作数架直-8舰载直升机，坞舱可容纳数艘大型气垫登陆艇，当然此时我军还并没有可用的这类装备。</p><p>　　<strong>而早在1998年，日本海上自卫队就为其“大隅”级运输舰采购了6艘美制LCAC-1型气垫登陆艇。</strong>隶属于驻扎吴港的第1输送队下的第1气垫船队，舷号分别为2101-2106。相比之下，这就反映了当时我军两栖登陆装备当时依然较为落后的状况，而跨海登岛维护祖国主权统一的作战目标，也使得我军需要一种能够配合大型两栖船坞舰艇使用的快速登陆载具。</p><p>　　2009年底，726型气垫登陆艇首艇3320号艇下水，这是一款与美制LCAC-1类似的舰载通用气垫登陆艇，两台燃气轮机分列两侧，左侧前方为驾驶室（LCAC-1则在右侧），右侧为一挺54式12.7毫米高平两用机枪，艇体中央布置有全长车辆甲板，理论上可运载一辆主战坦克或多辆装甲车等军用车辆进行快速突击登陆。</p><p>　　具有试验性质的首批艇建造速度并不快，长期以来，3320艇一直作为726型气垫登陆艇的“独苗”存在。在这段时间内，首批3艘071型综合登陆舰陆续入列海军南海舰队某登陆舰支队。而缓慢建造的首批次726型登陆艇显然无法满足日常训练值班需要，长期只有3320这一艘“独苗”，况且作为首艇其还承担着大量的试验任务，于是071宽大的坞舱就出现了各式各样、却又不大正常的装载配置。</p><p>　　时光飞逝，到了2013年，第二艘3321号艇才曝光，而第三艘3322号艇则要到2016年才出现在官方图片中，足见首批艇建造缓慢程度。</p><p>　　直到2017年初，二批次首艇3330号公开亮相，同样装备海军南海舰队某登陆舰支队。本批次艇的动力舱外观与一批次三艘有着明显的外观变化，这是由于从二批开始，艇载主机由乌克兰的GTD-6000更换为中航工业黎明生产的QC-70型燃气轮机，主要区别在动力舱布局、排气口外观等等。</p><p>　　<strong>由于QC-70是一台由FWS-10核心机发展而来的航改燃气轮机，二批次726型也被戏称为一架贴海飞行的歼-11B</strong>。以燃气轮机为主机的登陆载具，其维护难度与使用方式确实不同于我军之前所拥有的任何类型登陆船艇，以一架飞机打比方倒也不是完全的戏谑，它确实需要一定的时间，以让部队建立起完整的使用维护体系。</p><p>　　二批次以及之后的726型气垫登陆艇的生产建造，进入了一个相对速度较快的节奏，同年年内，3331与3332两艇也相继亮相。</p><p>　　而就在前不久，隶属于东海舰队某登陆舰支队的首艘071型“沂蒙山”舰与气垫登陆艇合练的图片中，首次出现了隶属于该支队的726型气垫登陆艇，舷号为3239。<strong>这是已知的最新批入列的726系列气垫登陆艇，在经历了9年的时间之后，我军终于拥有了亚洲最大规模的舰载通用气垫登陆艇队伍。</strong></p><p>　　在726系列生产建造逐渐走上正轨的同时，它的训练作战使用方面同样值得关注。尽管首艇下水已经9年，但对于南海舰队与东海舰队下属的两支登陆舰支队来说，它的批量服役入列，乃至参加实战化演训任务却仍然是近两年来的事情。</p><p>　　作为快速突击登陆载具，LCAC这类气垫登陆艇经典的使用方式是用高航速冲上滩头，卸下高价值作战单元比如主战坦克等，以期快速瓦解岸滩抵抗，为后续部队进一步巩固登陆场。</p><p>　　然而我军的该系列艇在此之前的历次演习当中，出现了许多非常不“LCAC”的使用方式。尽管确实有长期以来这类装备数量不多、部队使用机会较少的因素，但依然出现了很多令人觉得怪异的画面。</p><p>　　特别是自从当初那张搭载96式坦克进行装载试验的图曝光后至今，也再未见726系列在演习中运载主战坦克进行突击登陆的场景。相比之下，更晚引进装备的“野牛”级大型气垫登陆舰却并不缺乏类似画面，这就更显得奇怪了。</p><p>　　当然从装备上来说，曾经与海军登陆舰部队配合过的陆军两栖机械化合成旅们，也确实是全05车族配置（或者还有使用63A的未完成换装部队），并没有主战坦克，而东南方向的海军陆战队合成旅，自己也已经完成05车族的换装，同样没有主战坦克。</p><p>　　对于05系车族来说，其优异的航渡能力本身就足以自行航渡冲滩，完全没必要动用726；而若要进一步巩固登陆场，形成具备优势防护与火力的战场桥头堡，则需要及时将主战坦克送上滩头，这活儿现在看又有被生存能力更强的“野牛”承包之势。这可能是使得726们长期处于较为尴尬的使用方式的原因。</p><p>　　而接下来想要发挥726系列的真正作用，究竟是与两栖机械化合成旅之外的其他陆军部队扩大联训范围，亦或者是为海军陆战队配备真正的铁甲矛头——主战坦克？在部队逐渐调整编制体制、新型装备不断入列的今天，无论怎样的方向，都是值得期待的。（作者署名：扬基帧察站）</p><p>　　《出鞘》完整内容请关注新浪军事官方微信抢先查看（查看详情请搜索微信公众号：sinamilnews），《出鞘》每天在新浪军事官方微信完整首发。</p>', 1525420080, 0, 1),
(39, '连续3年季后赛效率飙升!15季詹皇秒杀所有超巨', NULL, '<p>　　北京时间5月4日，NBA数据专家汤姆-哈伯斯特罗在最新一期的《大数据》栏目里指出，勒布朗-詹姆斯已经连续第3年季后赛的PER效率值超过常规赛的PER效率值，他也是当今NBA里唯一一位做到这点的超级巨星。</p><p>　　这期《大数据》的关键数字是33.2，这是詹姆斯在今年季后赛的PER效率值，联盟排名第一，排在他之后的依次是：哈登（30.8）、卡培拉（28.3）、戴维斯（28.1）和字母哥（26.6）。</p><p>　　最关键的是，这是詹姆斯连续第3年季后赛的PER效率值超过常规赛的PER效率值。2015-16赛季，他的季后赛PER值比常规赛提升了2.5；2016-17赛季提升了3.1；今年季后赛提升了4.6！</p><p>　　就这点来说，当今NBA的其他超级巨星都没有做到。举例来说：哈登在2015-16赛季和2016-17赛季的季后赛PER值比常规赛分别下降了3.5和4.4，今年他只是比常规赛提升了1.0；库里在2014-15赛季和2015-16赛季的季后赛PER值比常规赛分别下降了3.5和9.2，去年季后赛，库里的PER值才比常规赛提升了2.5。今年季后赛，库里只打了1场比赛，所以没有计算；威斯布鲁克，过去3年季后赛的PER值分别比常规赛下降了0.7、2.9和4.3；杜兰特，过去5年季后赛的PER值分别比常规赛下降了2.3、7.2、7.9、0.1和2.8。</p><p>　　“这是勒布朗的第15个赛季，这太疯狂了！”汤姆-哈伯斯特罗忍不住在节目里大喊，“他真的太夸张了！”</p><p>　　（罗森）</p>', 1525416120, 0, 1),
(40, '西安千架无人机尬舞背后：创始人称年入过亿', NULL, '<p>　　5月1日，1374架无人机在西安城墙南门编队飞行表演，摆出西安城墙、丝绸之路、四十周年等图案，创下“数量最多无人机表演吉尼斯世界纪录”。不过，一些网友从现场拍到的视频显示，当天演出现场出现意外，图案的左半部分变成了“乱码”。</p><p>　　演出现场图案的左半部分变成了“乱码“ 图 视觉中国</p><p>　　一份来自采购招标网的《关于春舞大西安城墙千架无人机光影盛典活动无人机表演项目成交公告》显示，此次成交供应商为广东亿航白鹭传媒科技有限公司（简称：亿航白鹭），表演项目的成交金额为1050万元。上千万的“出场费”也让不少网友咂舌。</p><p>　　前所未有的大规模无人机编队表演与高额演出费用背后，是消费级无人机的转型探索。在大疆一家独大的无人机领域，受场景适应能力、售后保障、续航时间等的掣肘，无人机发展一直受限。各家公司纷纷在消防、监控、植保、电力等工业领域探索，但盈利一直是难题。</p><p>　　这次出现失误的表演和高额的“出场费”，把无人机编队表演从幕后推到了台前，这并非第一次文艺演出中的无人机表演，但一定是它最出名的一次。不少人惊叹演出费用之高，编队表演，也似乎成了一些无人机企业消耗库存，寻找新的盈利点的利器。</p><p>　　<strong>表演报价一台无人机一万</strong></p><p>　　1050万的费用是一次演出还是常态化的多场演出？西安城墙管理委员工作人员向媒体证实，这一款项包括5月1日当天的演出费用，以及4月29日的彩排和申请吉尼斯纪录等的费用。</p><p>　　寻找中国创客（ID：xjbmaker）致电亿航白鹭客服得知，无人机编队表演报价为一台无人机一万元，超过200台可打八折。但具体需要的台数根据表演的元素以及甲方的预算来设定。具体流程包括，甲方提供表演的内容，亿航白鹭出设计方案，数字、字母、简单图案基本能实现，太复杂的图案则不太好呈现，后续还要看场地，并需甲方报批空域申请。</p><p>　　提供无人机编队飞行表演的团队不止亿航白鹭一家，寻找中国创客（ID：xjbmaker）在另一家演出提供商，也是某无人机生产商处了解到，500架无人机编队的价格，随着造型和图案难度的不同，报价大概在200万到300万之间。有业内人士称，编队越庞大，演出的难度和成本也会不断增大。</p><p>　　<strong>彩排时的完美演出</strong></p><p>　　<strong>上千架无人机在空中编队表演</strong></p><p>　　<strong>技术难度有多大呢？</strong></p><p>　　无人机技术领域专业人士刘飞（化名）介绍，编队表演是在无人机控制技术日趋成熟的情况下发展出来的一个新的应用方向，需要涉及飞控系统、飞行平台硬件、地面站三个部分的技术支撑。</p><p>　　“要实现编队飞行还需要射频通信技术使每个独立飞行的无人机实现相互配合的飞行动作，这次不理想的效果有可能就是由于这个环节出现了问题，成因可能有现场电磁阀环境的因素，也不排除有流程操作因素。”刘飞分析到。</p><p>　　一位不愿具名的无人机公司创始人分析，无人机编队需要解决授时和同步、导航和定位、数据、通讯和抗干扰、路径协调等多个难题；多架无人机协同运动需要精确的定位也需要精确同步的时间，编队还需要规划合理的路线同时要应对相互间的干扰；如果是做特技飞行表演，对控制要求很高，如果只是静态或准静态的LED灯光秀控制难度相对较低。</p><p>　　在星瀚资本运营总监看来，无人机编队表演为主要技术难点在于多机的引导和协同，队形设计、气动耦合、队形的动态调整、航迹规划、信息互换以及编队飞行控制策略等问题都非常关键，成本会伴随机体数量增加而大幅提高。</p><p>　　千架无人机亮相西安城墙 图 视觉中国</p><p>　　<strong>收入超出预期的编队表演</strong></p><p>　　<strong>“去年做了几十场，今年收入会过亿”</strong></p><p>　　承接此次西安无人机表演的亿航白鹭的官网介绍，公司专注于一键式操作多台无人飞机编队在指定空域呈现及变换信息，以空中媒体的形式，将科技与媒体、广告融合，利用无人机、VR、AR、裸眼3D、激光投影和全息技术等科技元素，对传统媒体进行创新和补充。</p><p>　　有报道称，去年以来，亿航白鹭的母公司亿航智能花了很长时间做各种业务的转变，从to C转到包括编队表演、行业应用以及跟政府的合作等业务。</p><p>　　亿航智能创始人兼CEO胡华智在接受采访时表示，今年第一季度，亿航已经接到大量订单，包括来自184（其研发的载人机）的、政府合作的、还有行业应用以及大量编队表演的订单。</p><p>　　2017年以来，亿航白鹭已经承接了多场大型活动的无人机编队表演，包括电影首映礼、香港回归20周年、灯光节等活动，上述业内人士刘飞也表示，亿航在编队表演领域有一定的地位，之前并未出过问题。</p><p>　　千架无人机亮相西安城墙 图 视觉中国</p><p>　　显然，编队飞行表演已经是亿航智能的招牌之一。其CEO胡华智在4月接受采访时表示，飞行编队的发展已经完全超出了公司的预期，本来只是打算拿无人机表演给大家一点空中造景的喜气感觉，同时做一些资金上的回收，没想到甚至扭转了亿航消费级无人机不盈利的颓势。“我们去年做了几十场，今年表演的收入甚至会过亿，这是我们当初没有预料到的。”</p><p>　　此前就有报道称，今年五一开始，无人机编队就将在西安实现常态化表演。</p><p>　　而对于无人机编队表演这一业务的拓展，易瓦特无人机创始人赵国成觉得，这是顺应无人机行业发展趋的“明智选择”。因为在他看来，这条路上需要新的点来刺激市场，无人机原来大家知道的只有航拍，现在大部分人知道还可以做编队，这样的转型或者说新盈利点对行业发展也是一种贡献。</p><p>　　<strong>消费级无人机“去库存”</strong></p><p>　　亿航白鹭所属母公司亿航智能的宣传文案介绍，其由来自中国和美国硅谷的飞行器爱好者创立的智能无人机公司，成立于2014年4月。创立之初，亿航智能推出了智能无人机GHOSTDRONE 2.0，特点在于可直接用手机APP操控。但由于国内对于消费级无人机的管控和限制，加上强大的竞争对手，上述消费级航拍无人机并没有占到多大的市场份额。</p><p>　　此后，亿航智能以低空中短途自动驾驶载人飞行器“亿航184”引起了国内外的关注。不过，这款产品运营的可行性、安全性以法律等问题上让亿航184饱受质疑。</p><p>　　亿航184</p><p>　　快速发展的同时，关于亿航的负面消息时常见诸媒体。2016年末，亿航智能被曝大规模裁员，2017年10月左右，一张亿航被追债的照片现身网络：其办公楼前的横幅上写着“广州亿航欠贷款19个月未还。”2018年3月，据雷锋网报道，亿航美国分公司已于当地时间2017年12月29日在加州申请破产，原因包括为“消费类无人机由于产品质量的问题，基本上销售不出去；公司欠了美国房东（办公室）一百五十万美金，被告了。”</p><p>　　对此亿航方面发布了名为《关于“雷锋网”针对亿航不实报道的郑重声明》的声明，表示由于其正常公司的业务需要调整架构，“将面向美国市场销售和服务等职能收回国内总部统一管理”，于是亿航决定关闭美国分公司。</p><p>　　第一财经在4月18日的一篇报道中称，胡华智坦承，在目前竞争环境下，消费级无人机确实难有较好盈利。他告诉记者，“我们之前有几千台的库存，但是我们把这部分库存利用起来，来从事编队业务，产生了很可观的收益。”</p><p>　　在4月末接受另一家网站的采访时他表示，某种程度上，编队飞行扭转了亿航消费级无人机不盈利的颓势。“我们原来大概有几千台的库存，这些库存占用了我们很大的资金，2015、2016年，我们相当于把很多的钱变成了货，在财务认定上是亏损的，编队表演是我们去库存很好的办法。”</p><p>　　图片来源网络</p><p>　　“我们已经实现了收支平衡”，胡华智在上述第一财经的报道中称，亿航已经完成了C轮融资，但未公布融资细节。此前，亿航在2014年12月完成了由GGV纪源资本领投的1000万美元A轮融资，在2015年8月完成了由金浦投资领投的4200万美元B轮融资。此后便一直没有新的融资消息放出。</p><p>　　经历了一场寒冬，这家“有故事”的无人机厂商，是否可以依靠飞行编队表演和其宣称的无人机物流华丽转身尚未可知，不过能够预见的是，在今后的各类庆典中无人机编队表演会越来越多。相比于其他类型的表演，其自带高科技基因，但却受场地、天气、空域申请等等因素的影响。</p><p>　　在星瀚资本运营总监张昊看来，无人机行业发展至今，常规性应用场景基本已经被头部企业所瓜分，长尾场景的规模和需求迫切程度不足以支撑其他企业的长期发展，作为转型发展的路径之一，无人机编队表演短期看是个不错的选择，但如何发展为长期的业务，如何创造可持续价值，这都是转型者们要考虑的问题。</p><p>　　编辑：赵力</p>', 1525412760, 0, 1);
INSERT INTO `pre_news` (`news_id`, `news_title`, `news_pic`, `news_content`, `news_time`, `news_like`, `news_cate_id`) VALUES
(41, '婚后购房父母出资咋认定？法官:对夫妻双方的赠与', NULL, '<p class="art_p">北京晨报讯 （记者 黄晓宇）刘女士与王先生婚后共同购买了一套房屋，房屋首付款160万元由男方父母支付，后续贷款由男方偿还，房屋则登记在了二人名下。后刘女士起诉要求离婚，分割该房屋。王先生虽同意离婚，但不同意分割房屋。日前，海淀法院审结了此案，法院判决该房屋归王先生所有，由王先生给付刘女士房屋折价款185万元。</p><p class="art_p">原告刘女士在法庭上表示，该房屋登记在她与王先生名下，且房屋贷款一直由两人共同偿还，王先生父母出资应视为对双方的赠与，故要求依法分割该房屋。而被告王先生不同意该说法，他认为房屋是父母出资专门为他购买的。“我和父母签了一份赠与协议，这份协议写明，他们出的房款只赠与我一人所有，且贷款也是我一人还，我不同意分割房屋。”</p><p class="art_p">法院经审理后认为，该房屋系双方于婚姻关系存续期间购买，且登记在双方名下，故该房屋属于夫妻共同财产。</p><p class="art_p">对于首付款160万元，法院认为，王先生虽主张系其父母赠与其个人，且以协议书为据，但该协议书是其父母与王先生单独签署，王先生未提交证据证明该协议约定之事实有第三方知晓，故该证据对第三方不发生效力。另外，该协议书签订于房屋登记之前，事后该房屋登记在王先生和刘女士两人名下，视为变更了上述约定，故法院对王先生主张该首付款为其父母赠与其个人之主张不予采信，该房屋应当作为夫妻共同财产依法分割。</p><p class="art_p">最后，法院判决该房屋归王先生所有，由王先生给付刘女士房屋折价款185万元。</p><p class="art_p"><strong>■法官释法</strong></p><p class="art_p">根据《婚姻法司法解释二》规定，当事人结婚后，父母为双方购置房屋出资的，该出资应当认定为对夫妻双方的赠与，但父母明确表示赠与一方的除外。根据该条法律规定可知，条文中的“应当认定”是在父母实际出资时，其具体意思表示不明的情况下，从社会常理出发，推定为赠与。若当事人有证据证明其与出资人之间形成的是借贷关系的，则不能适用该条规定，该部分出资应认定为借款，将按照夫妻共同债务或个人债务处理。当然，该证据应当是在当事人离婚诉讼前形成的。</p><p class="art_p">关于父母出资是赠与一方还是双方，法官表示，一方父母在婚后为子女购置不动产出资的，一般应认定为对夫妻双方的赠与，除非父母明确表示赠与给一方。特别是当出现不动产所有权登记在双方子女名下这一事实时，除非当事人能够提供其他证据证明该出资仅为父母对其子女一方的赠与，否则依据产权登记这一事实，一方父母出资的真实意思表示更合理的解释为一方父母对双方子女的赠与而不是父母赠与给一方子女。法官特别提示，这里所称的父母的意思表示应以赠与发生的时间为准。</p><p class="art_p">本案中，王先生提交的其父母与其签署的赠与协议书，内容虽写明系其父母的出资，且赠与王先生一人，但该协议内容未经刘女士确认并同意，且双方争议之房屋登记在王先生和刘女士名下，故本案综合上述事实认定王先生与其父母单方签署的赠与协议不能对抗第三人刘女士，不能视为“父母明确表示赠与一方”的情形，故王先生父母出资的部分，应认定为王先生和刘女士夫妻共同所有。</p><p class="art_p">来源：北京晨报</p>', 1525408984, 0, 1),
(42, '为什么掰手指会“咔咔”响？到底能不能掰？', 'uploads/Ri2v-fzyqqiq713360729348.gif', '<p>　　生活中，很多人动不动就喜欢把手指掰得“咔咔”响，尤其是小学生和中学生。在别人的面前把手指头弄出“咔咔”的响声，觉得很酸爽，认为这既是一种技能，还能炫耀。</p><p>　　但是，你有没有想过掰手指为什么会发出“咔咔”的声音呢？让人酸爽的掰手指会带来哪些危害呢？一起了解一下。</p><p>　　<strong>掰手指为什么会响？&nbsp;</strong></p><p>　　关于掰手指引起的弹响，其实有很多理论，其中以<strong>气泡理论</strong>支持者最多。该理论认为，手指关节发出的声音<strong>与润滑和缓冲关节的滑液中出现的二氧化碳气泡有关。</strong></p><p>　　掰手指会发出响声的原因，主要分为以下两种：</p><p>　　<strong>手指的生理性弹响</strong></p><p>　　生理性弹响有其发生的机制。简单的来说，生理性弹响就是<strong>不会引起手指关节任何疼痛，任何不适的弹响。</strong></p><p>　　一般生理性弹响问题不大，所以不用太过担心。要做的只是观察就行了。</p><p>　　<strong>病理性弹响</strong></p><p>　　比如，手指的骨性关节炎，手指的腱鞘炎都<strong>会引起手指间关节的弹性</strong>，这种弹响是由于手指的病变引起。</p><p>　　手指骨关节炎，是手指软骨退变，骨质增生，导致手指间关节的摩擦阻力增大，从而导致弹响。腱鞘炎是由于手指屈肌腱被压迫导致的局部炎症，从而引起弹响。</p><p>　　病理性弹响，需要尽量让手指休息制动，避免弹响的发生。一般要治疗原发病，最好到医院看一下，结合医生的体格检查，然后进一步处理。</p><p>　　<strong>掰手指会导致关节炎吗？</strong><strong>&nbsp;</strong></p><p>　　两者之间有一定的关系，但是<strong>手指间关节适当的生理性弹响不会引起手指间关节炎，也不会引起手指间关节的其他毛病。</strong></p><p>　　但是，有些人喜欢特意把手指掰出一些弹响，认为这些动作非常酷。有些人甚至对弹响有瘾，每天就不停的让手指间关节弹响。一定要注意手指间关节弹响的适度适量。</p><p>　　<strong>掰手指还有哪些危害？&nbsp;</strong></p><p>　　从医学角度讲，弹响其实本身也是一种摩擦，适度的弹响对关节没有坏处，但弹响的发生率高了，摩擦增多了，<strong>慢慢会产生手指间关节的慢性炎症，从而也会慢慢导致手指间关节炎。</strong></p><p>　　即使是生理性弹响，也要掌握适度适量。因为一旦生理性弹响增多，或者人为的使生理性弹响大大的增多，<strong>最终容易导致病理性弹响。</strong></p><p>　　所以，掰手指要适度。</p>', 1525429200, 0, 1),
(43, '曝皇马计划派替补打巴萨', NULL, '<p>　　西班牙综合性日报《ABC报》周四报道，<strong>齐达内和他的教练组正考虑让替补球员出战国家德比，以避免在欧冠决赛前遭遇不必要的伤病。</strong>“最重要的是第13冠。”俱乐部内部表示。</p><p>　　在欧冠半决赛淘汰拜仁后，皇马教练组即开始准备欧冠决赛。在球队庆祝欧冠晋级的欢乐气氛中，齐达内召集更衣室的领袖们开了一个会。他问了大家一个问题：“我们周日对巴塞罗那怎么踢？大家都上，还是避免风险为重？对我们来说，最重要的还是基辅的决赛。”</p><p>　　齐达内是皇马更衣室内做决定的人，很难让他改变计划，比如他敢于将贝尔和本泽马放到替补席上，不过这一次皇马的情况有些不一样，齐达内希望能听取大家的意见，群策群力踢好欧冠决赛。</p><p>　　齐达内想要听听拉莫斯、C罗、马塞洛、克罗斯、莫德里奇、本泽马这些球员的意见。如今皇马队内伤病众多，莫德里奇、瓦拉内、阿森西奥都有小伤病，而伊斯科和卡瓦哈尔伤情则比较严重。</p><p>　　每场比赛都有几名球员受伤让齐达内感到担心。是让主力球员都在诺坎普上场呢？还是明智地将好钢用在刀刃上，让主力阵容都无伤病地参加欧冠决赛？</p><p>　　“我们非要让C罗、莫德里奇、拉莫斯、纳瓦斯、马塞洛冒着受伤的危险上场吗？”齐达内对教练组成员表示，“把风险降到最低，这不是更好吗？这个问题我们必须在内部解决。”</p><p>　　在与教练组商议后，齐达内问了球员们一个问题：“为诺坎普的比赛以及其他的西甲比赛而冒重伤的危险，究竟值不值得？”</p><p>　　拉莫斯和C罗都想参加重大的比赛，很难说服他们不进入比赛大名单或者首发。毕竟皇马对阵的是巴萨，这是皇马在国内最大的死敌。眼下，C罗、拉莫斯和齐达内还在继续考虑这个问题。除了这两位球员，齐达内可以自由安排其他球员进行轮换。</p><p>　　据悉，齐达内希望目前身体有伤病的球员，如莫德里奇、克罗斯、马塞洛、阿森西奥、瓦拉内、本泽马都在国家德比中获得休息，包括纳瓦斯也将休息。而伊斯科和卡瓦哈尔都在养伤，本身就不能上场。在这些球员中，有多位都将在欧冠决赛中首发。纳瓦斯可能要到欧冠决赛才上场，接下去一段时间，他会把位置让给卡西利亚。如果C罗和拉莫斯也都休息，那皇马等于派出全替补阵容出战。</p><p>　　齐达内将给纳乔、贝尔、塞瓦略斯、略伦特、特奥、卡塞米罗、科瓦契奇这些球员机会。卡塞米罗近期因为状态不佳，被齐达内排除在首发阵容外。如果C罗也休战，那贝尔将成为引领皇马进攻的旗帜。</p><p>　　不过皇马部分高层对齐达内的计划表示了一些担忧。他们知道欧冠对皇马来说是至关重要的，但也非常清楚，国家德比对皇马的形象以及自己的球迷来说也是非常重要的。如果比分不利，那对皇马备战欧冠也会造成打击。（塞尔吉奥）</p>', 1525427820, 0, 1),
(44, '乒联：韩朝组队是为了和平', NULL, '<p>　　5月3日，朝韩两队原本应该在世乒赛的1/4决赛中一决高下，但她们却没有进行任何比赛，而是突然宣布组成朝韩联队，在晋级4强的同时至少确保一枚铜牌到手。</p><p>　　这是朝韩自1991年以来再次联手组队参加世乒赛，那一次他们曾击败了邓亚萍领衔的中国队夺冠。</p><p>　　不过，这一次在比赛中的突然组队还是引发了各界舆论的激烈讨论。</p><p>　　国际乒联和朝韩两队在当天下午3点（北京时间晚上9点）共同召开了新闻发布会，对于这些外界关注的问题进行了说明和解释。</p><p>　　“在短短的24小时内，我们创造了历史，大家进行了坦诚布公的讨论和商议。”国际乒联主席托马斯透露，在做出这个决定后，他们同样得到了中国、日本等其他协会的同意。</p><p>　　<strong>“我们得到了其他协会的同意”</strong></p><p>　　在比赛中宣布放弃竞争，并与对手握手、拥抱、组队。。。。。。这一幕在竞技体育的历史上，足以载入史册。</p><p>　　虽然朝韩联手的消息从前一天晚上就开始不胫而走，但双方在比赛中突然做出的决定还是令在场的所有人感到错愕。</p><p>　　在记者不停地追问中，国际乒联发表了一份声明。文中强调，“朝韩联队”的组成是国际乒联、朝鲜乒乓球队和韩国乒乓球队三方决定。</p><p>　　随后，国际乒联与朝韩双方一起举行了简短的新闻发布会。在发布会现场，他们的会议桌前还特意摆放着曾在平昌奥运会使用过的“朝鲜半岛旗”。</p><p>　　国际乒联主席托马斯介绍了整个决议的准备过程，“（瑞典时间5月3日）早晨，我们通知了IOC国际奥组委（国际奥组委），这一切都是为了世界和平，因为在平昌奥运会上，朝韩就曾携手参加了女子冰球比赛。”</p><p>　　托马斯透露，三方在做出决定后也通知了其他协会，并征得了他们的同意，“我们也通知了中国、中国香港、罗马尼亚、澳大利亚、日本和乌克兰等协会。”</p><p>　　“我可以肯定地说，他们对这个促进世界和平的决议表示赞成。”</p><p>　　此次朝韩联合组队的形式大致与平昌冬奥会一致。联队的队名是“KOREA（COR）”，比赛穿着统一队服，现场将升起朝鲜半岛旗，如果最终夺冠将在现场奏响《阿里郎》。</p><p>　　韩朝联队，这样的“剧本”并不新鲜。</p><p>　　在27年前的日本千叶世乒赛上，朝韩就曾组成联队并最终击败中国夺冠，她们的事迹还被拍成了电影《朝韩梦之队》。</p><p>　　“我坚信体育可以影响政治。”作为那一届世乒赛的主力成员，韩国名将玄静和在今年2月接受ESPN采访时，依然显露着坚定的信念，“我现在依然坚信。”</p><p>　　27年后，从韩国女队主教练位置退下来的玄静和不再是朝韩“乒乓外交”的亲历者，但她的后辈们正在踏着前辈们走过路，期待继续创造当时的辉煌。</p><p>　　两队合并后，朝韩联队将由10人组成，这与其他协会只能选派5人参赛不同。在比赛中，每场比赛只能从中选3名队员上场，双方教练员将共同坐在现场指导队员。</p><p>　　作为混双世界冠军，韩国名将梁夏银对于此次联合组队充满期待。在她看来，自己更有动力去准备接下来和日本的比赛，“团结会让我们更强大，争取打进决赛。”</p><p>　　朝鲜选手金宋依是朝韩联队中实力最为强劲的一位，她曾在里约奥运会上击败过石川佳纯而夺得铜牌，“（组队后）感觉力量变强大，离目标更近了。”</p><p>　　国乒女队教练李隼在此前接受采访时也坦言，朝韩组队虽然突然，但并不会对中国队造成实际上的影响，他认为朝韩联队想要赢下日本都很困难。</p><p>　　“虽然是联合组队整体实力有提升，但是（如何）出人还是有很大的问题，我认为我们主要对手还是日本队，联队想要赢日本队还是非常难的事情。”</p><p>　　不过，联手进入4强就意味着，朝韩联队已经至少确保一枚铜牌入账。5月4日，朝韩联队将在的半决赛中，向实力更为强大的日本队发起冲击。</p><p>　　<strong>修改规则，为了世界和平</strong></p><p>　　虽然朝韩两国在历史上曾多次携手参加比赛，但与之前不同的是，这次的组队并不是在世乒赛开始之前就决定好的，而是在1/4决赛前一天“临时起意”。</p><p>　　一位在世乒赛现场的工作人员告诉澎湃新闻记者，国际乒联和朝韩双方是在凌晨进行的磋商，“队员们是早上才知道的，而且表情都很惊讶。”</p><p>　　央视记者李武军就在现场质疑国际乒联是否做到了“沟通到位”，“他们说通知到了每一个协会，但我采访日本队的队员时，她们看上去并不知道情况，还特意叫来了翻译。”</p><p>　　国际乒联临时修改规则也在网络上引起了巨大的争议。</p><p>　　一些网友认为，这样的决定有助于推动半岛局势的缓和；而另一些则认为，此举不尊重竞赛规则和公平公正的原则。</p><p>　　“朝韩联队在世乒赛的这一幕，我认为是极其荒诞的。不要把它和亚运会朝韩组队联系到一起，这是已经打了一半了，8强比赛，你说突然两个体育协会合并为一个，共同晋级！这是践踏体育规则！”一位网友提出了自己的质疑。</p><p>　　而在当天新闻发布会的现场，有媒体也质问了国际乒联修改规则的原因，国际乒联主席托马斯解释道：</p><p>　　“是的，我们尊重规则；是的，我们改变了规则；是的，这比规则更重要，这就是答案，为了和平的决定。”</p>', 1525425780, 0, 1),
(45, '烦!詹姆斯又双叒超越乔丹了 还顺便超了个帕克', NULL, '<p>　　北京时间5月4日，勒布朗-詹姆斯不愧是“活在纪录中的男人”。在今天克利夫兰骑士客场以128-110大胜多伦多猛龙，取得总比分2-0的领先的比赛中，詹姆斯砍下43分14次助攻，生涯季后赛得分30+助攻10+次数超越迈克尔-乔丹，升至历史第一。</p><p>　　本场前三节战罢，詹姆斯已达成了里程碑。他拿下31分13次助攻，骑士也反超11分进入末节。</p><p>　　根据统计，这是詹姆斯季后赛生涯第16次单场得分30+助攻10+，由此超越乔丹（15次），高居NBA历史首位。杰里-韦斯特和奥斯卡-罗伯特森分别以14次和12次位列第三、四名。</p><p>　　此外，本场也是詹姆斯生涯第226场季后赛，由此追平托尼-帕克，并列NBA历史第5位，下一场就将完成对帕克的超越。目前排在前4位的分别是德里克-费舍尔（259场）、蒂姆-邓肯（251场）、罗伯特-霍里（244场）和卡里姆-阿卜杜-贾巴尔（237场）。</p><p>　　恭喜詹皇！（魑魅）</p>', 1525424880, 0, 1),
(46, '马斯克：共享模式电动无人车是“显而易见”的未来', NULL, '<p>　　新浪科技讯 北京时间5月4日早间消息，特斯拉CEO埃隆·马斯克（Elon musk）在周三的电话会议上阐述了他所谓汽车行业“显而易见的”未来。</p><p>　　马斯克表示，有朝一日，人们将会共享电动无人驾驶汽车。他在电话会议上说：“共享电动无人驾驶模式显然是未来的方向。”</p><p>　　人们会“分享自己的汽车，还能有效地提供自己的汽车——有点像机器人Lyft或机器人Uber，有点像Uber、Lyft和Airbnb的合体。”马斯克说。</p><p>　　他还表示，无人驾驶汽车的车主可以根据自己的需要和接受程度对外出租自己的汽车。</p><p>　　“你可以拥有自己的汽车，然后100%使用自动驾驶电动车。也可以供他人使用，在你不用的时候租给别人用。你可以按照自己的意愿召唤汽车，还可以只允许朋友和家人使用，或者仅限5星用户使用。”他说。</p><p>　　要让这些变成现实，汽车需要具备全自动驾驶功能，也就是四级或五级自动驾驶。根据美国国家高速公路交通安全管理局（NHTSA）的定义，四级自动驾驶可以在特定环境中完成所有驾驶功能，而作为最高等级的五级自动驾驶则能够在所有环境中完成所有驾驶功能。</p><p>　　马斯克认为，此外还需要一套“软件基础设施”方便汽车和驾驶员相互交流。但具体何时能实现这番愿景目前还很难判断，因为马斯克也不知道配套的行业监管何时能够出台。</p><p>　　“棘手之处在于，无人驾驶不能把事故率或死亡率降到零。它只能大幅改善现状。”马斯克表示，当人们非常关注无人驾驶的时候，这种技术仍然会发生事故。</p><p>　　“如果是无人驾驶技术出事故，那就会成为新闻头条，媒体不会提到他们其实不应该写这样的报道，而是应该写报道阐述无人驾驶汽车其实很安全。但这样的报道并不吸引人。”马斯克说。</p><p>　　监管者也需要考虑公众情绪，因此放慢了审批速度。(书聿)</p>', 1525421820, 0, 1),
(47, '小白加盟中超曝光惊人条款', NULL, '<p>　　虽然伊涅斯塔加盟重庆斯威的转会还没有正式官方宣布，但是据西班牙媒体的最新报道，伊涅斯塔的转会已经敲定。巴萨队长除了获得丰厚的年薪之外，他还有其他收入。</p><p>　　据《世界报》的爆料，伊涅斯塔的年新闻2700万欧元，与重庆斯威签约三年，换而言之他能从重庆斯威获得8100万欧元的收入。除此以外，重庆斯威将购买伊涅斯塔酒庄600万瓶红酒，平均每瓶红酒6欧元，即共支付3600万欧元。</p><p>　　此前，多家西班牙媒体爆料，伊涅斯塔很大程度选择前往中国踢球，正是为了自己的红酒生意。（雷欧）</p>', 1525420440, 0, 1),
(48, '中国舰载通用气垫登陆艇数量终超日本 但却不载坦克', 'uploads/BCDz-fzyqqiq647637386684.jpg', '<p>　　关心人民海军发展的军迷，应该都经历过拿我军现役“中华神盾”驱逐舰的数量和海自“宙斯盾”战舰数量比较的阶段。最近，又有一型海军装备可以进行这样的比较了。</p><p>　　2007年11月，人民海军首艘大型综合登陆舰071型首舰“昆仑山”号入列南海舰队某登陆舰支队，这是我军首次拥有世界先进水平的多功能大型两栖登陆舰艇，拥有宽大的飞行甲板可操作数架直-8舰载直升机，坞舱可容纳数艘大型气垫登陆艇，当然此时我军还并没有可用的这类装备。</p><p>　　<strong>而早在1998年，日本海上自卫队就为其“大隅”级运输舰采购了6艘美制LCAC-1型气垫登陆艇。</strong>隶属于驻扎吴港的第1输送队下的第1气垫船队，舷号分别为2101-2106。相比之下，这就反映了当时我军两栖登陆装备当时依然较为落后的状况，而跨海登岛维护祖国主权统一的作战目标，也使得我军需要一种能够配合大型两栖船坞舰艇使用的快速登陆载具。</p><p>　　2009年底，726型气垫登陆艇首艇3320号艇下水，这是一款与美制LCAC-1类似的舰载通用气垫登陆艇，两台燃气轮机分列两侧，左侧前方为驾驶室（LCAC-1则在右侧），右侧为一挺54式12.7毫米高平两用机枪，艇体中央布置有全长车辆甲板，理论上可运载一辆主战坦克或多辆装甲车等军用车辆进行快速突击登陆。</p><p>　　具有试验性质的首批艇建造速度并不快，长期以来，3320艇一直作为726型气垫登陆艇的“独苗”存在。在这段时间内，首批3艘071型综合登陆舰陆续入列海军南海舰队某登陆舰支队。而缓慢建造的首批次726型登陆艇显然无法满足日常训练值班需要，长期只有3320这一艘“独苗”，况且作为首艇其还承担着大量的试验任务，于是071宽大的坞舱就出现了各式各样、却又不大正常的装载配置。</p><p>　　时光飞逝，到了2013年，第二艘3321号艇才曝光，而第三艘3322号艇则要到2016年才出现在官方图片中，足见首批艇建造缓慢程度。</p><p>　　直到2017年初，二批次首艇3330号公开亮相，同样装备海军南海舰队某登陆舰支队。本批次艇的动力舱外观与一批次三艘有着明显的外观变化，这是由于从二批开始，艇载主机由乌克兰的GTD-6000更换为中航工业黎明生产的QC-70型燃气轮机，主要区别在动力舱布局、排气口外观等等。</p><p>　　<strong>由于QC-70是一台由FWS-10核心机发展而来的航改燃气轮机，二批次726型也被戏称为一架贴海飞行的歼-11B</strong>。以燃气轮机为主机的登陆载具，其维护难度与使用方式确实不同于我军之前所拥有的任何类型登陆船艇，以一架飞机打比方倒也不是完全的戏谑，它确实需要一定的时间，以让部队建立起完整的使用维护体系。</p><p>　　二批次以及之后的726型气垫登陆艇的生产建造，进入了一个相对速度较快的节奏，同年年内，3331与3332两艇也相继亮相。</p><p>　　而就在前不久，隶属于东海舰队某登陆舰支队的首艘071型“沂蒙山”舰与气垫登陆艇合练的图片中，首次出现了隶属于该支队的726型气垫登陆艇，舷号为3239。<strong>这是已知的最新批入列的726系列气垫登陆艇，在经历了9年的时间之后，我军终于拥有了亚洲最大规模的舰载通用气垫登陆艇队伍。</strong></p><p>　　在726系列生产建造逐渐走上正轨的同时，它的训练作战使用方面同样值得关注。尽管首艇下水已经9年，但对于南海舰队与东海舰队下属的两支登陆舰支队来说，它的批量服役入列，乃至参加实战化演训任务却仍然是近两年来的事情。</p><p>　　作为快速突击登陆载具，LCAC这类气垫登陆艇经典的使用方式是用高航速冲上滩头，卸下高价值作战单元比如主战坦克等，以期快速瓦解岸滩抵抗，为后续部队进一步巩固登陆场。</p><p>　　然而我军的该系列艇在此之前的历次演习当中，出现了许多非常不“LCAC”的使用方式。尽管确实有长期以来这类装备数量不多、部队使用机会较少的因素，但依然出现了很多令人觉得怪异的画面。</p><p>　　特别是自从当初那张搭载96式坦克进行装载试验的图曝光后至今，也再未见726系列在演习中运载主战坦克进行突击登陆的场景。相比之下，更晚引进装备的“野牛”级大型气垫登陆舰却并不缺乏类似画面，这就更显得奇怪了。</p><p>　　当然从装备上来说，曾经与海军登陆舰部队配合过的陆军两栖机械化合成旅们，也确实是全05车族配置（或者还有使用63A的未完成换装部队），并没有主战坦克，而东南方向的海军陆战队合成旅，自己也已经完成05车族的换装，同样没有主战坦克。</p><p>　　对于05系车族来说，其优异的航渡能力本身就足以自行航渡冲滩，完全没必要动用726；而若要进一步巩固登陆场，形成具备优势防护与火力的战场桥头堡，则需要及时将主战坦克送上滩头，这活儿现在看又有被生存能力更强的“野牛”承包之势。这可能是使得726们长期处于较为尴尬的使用方式的原因。</p><p>　　而接下来想要发挥726系列的真正作用，究竟是与两栖机械化合成旅之外的其他陆军部队扩大联训范围，亦或者是为海军陆战队配备真正的铁甲矛头——主战坦克？在部队逐渐调整编制体制、新型装备不断入列的今天，无论怎样的方向，都是值得期待的。（作者署名：扬基帧察站）</p><p>　　《出鞘》完整内容请关注新浪军事官方微信抢先查看（查看详情请搜索微信公众号：sinamilnews），《出鞘》每天在新浪军事官方微信完整首发。</p>', 1525420080, 0, 1),
(49, '连续3年季后赛效率飙升!15季詹皇秒杀所有超巨', NULL, '<p>　　北京时间5月4日，NBA数据专家汤姆-哈伯斯特罗在最新一期的《大数据》栏目里指出，勒布朗-詹姆斯已经连续第3年季后赛的PER效率值超过常规赛的PER效率值，他也是当今NBA里唯一一位做到这点的超级巨星。</p><p>　　这期《大数据》的关键数字是33.2，这是詹姆斯在今年季后赛的PER效率值，联盟排名第一，排在他之后的依次是：哈登（30.8）、卡培拉（28.3）、戴维斯（28.1）和字母哥（26.6）。</p><p>　　最关键的是，这是詹姆斯连续第3年季后赛的PER效率值超过常规赛的PER效率值。2015-16赛季，他的季后赛PER值比常规赛提升了2.5；2016-17赛季提升了3.1；今年季后赛提升了4.6！</p><p>　　就这点来说，当今NBA的其他超级巨星都没有做到。举例来说：哈登在2015-16赛季和2016-17赛季的季后赛PER值比常规赛分别下降了3.5和4.4，今年他只是比常规赛提升了1.0；库里在2014-15赛季和2015-16赛季的季后赛PER值比常规赛分别下降了3.5和9.2，去年季后赛，库里的PER值才比常规赛提升了2.5。今年季后赛，库里只打了1场比赛，所以没有计算；威斯布鲁克，过去3年季后赛的PER值分别比常规赛下降了0.7、2.9和4.3；杜兰特，过去5年季后赛的PER值分别比常规赛下降了2.3、7.2、7.9、0.1和2.8。</p><p>　　“这是勒布朗的第15个赛季，这太疯狂了！”汤姆-哈伯斯特罗忍不住在节目里大喊，“他真的太夸张了！”</p><p>　　（罗森）</p>', 1525416120, 0, 1),
(50, '西安千架无人机尬舞背后：创始人称年入过亿', NULL, '<p>　　5月1日，1374架无人机在西安城墙南门编队飞行表演，摆出西安城墙、丝绸之路、四十周年等图案，创下“数量最多无人机表演吉尼斯世界纪录”。不过，一些网友从现场拍到的视频显示，当天演出现场出现意外，图案的左半部分变成了“乱码”。</p><p>　　演出现场图案的左半部分变成了“乱码“ 图 视觉中国</p><p>　　一份来自采购招标网的《关于春舞大西安城墙千架无人机光影盛典活动无人机表演项目成交公告》显示，此次成交供应商为广东亿航白鹭传媒科技有限公司（简称：亿航白鹭），表演项目的成交金额为1050万元。上千万的“出场费”也让不少网友咂舌。</p><p>　　前所未有的大规模无人机编队表演与高额演出费用背后，是消费级无人机的转型探索。在大疆一家独大的无人机领域，受场景适应能力、售后保障、续航时间等的掣肘，无人机发展一直受限。各家公司纷纷在消防、监控、植保、电力等工业领域探索，但盈利一直是难题。</p><p>　　这次出现失误的表演和高额的“出场费”，把无人机编队表演从幕后推到了台前，这并非第一次文艺演出中的无人机表演，但一定是它最出名的一次。不少人惊叹演出费用之高，编队表演，也似乎成了一些无人机企业消耗库存，寻找新的盈利点的利器。</p><p>　　<strong>表演报价一台无人机一万</strong></p><p>　　1050万的费用是一次演出还是常态化的多场演出？西安城墙管理委员工作人员向媒体证实，这一款项包括5月1日当天的演出费用，以及4月29日的彩排和申请吉尼斯纪录等的费用。</p><p>　　寻找中国创客（ID：xjbmaker）致电亿航白鹭客服得知，无人机编队表演报价为一台无人机一万元，超过200台可打八折。但具体需要的台数根据表演的元素以及甲方的预算来设定。具体流程包括，甲方提供表演的内容，亿航白鹭出设计方案，数字、字母、简单图案基本能实现，太复杂的图案则不太好呈现，后续还要看场地，并需甲方报批空域申请。</p><p>　　提供无人机编队飞行表演的团队不止亿航白鹭一家，寻找中国创客（ID：xjbmaker）在另一家演出提供商，也是某无人机生产商处了解到，500架无人机编队的价格，随着造型和图案难度的不同，报价大概在200万到300万之间。有业内人士称，编队越庞大，演出的难度和成本也会不断增大。</p><p>　　<strong>彩排时的完美演出</strong></p><p>　　<strong>上千架无人机在空中编队表演</strong></p><p>　　<strong>技术难度有多大呢？</strong></p><p>　　无人机技术领域专业人士刘飞（化名）介绍，编队表演是在无人机控制技术日趋成熟的情况下发展出来的一个新的应用方向，需要涉及飞控系统、飞行平台硬件、地面站三个部分的技术支撑。</p><p>　　“要实现编队飞行还需要射频通信技术使每个独立飞行的无人机实现相互配合的飞行动作，这次不理想的效果有可能就是由于这个环节出现了问题，成因可能有现场电磁阀环境的因素，也不排除有流程操作因素。”刘飞分析到。</p><p>　　一位不愿具名的无人机公司创始人分析，无人机编队需要解决授时和同步、导航和定位、数据、通讯和抗干扰、路径协调等多个难题；多架无人机协同运动需要精确的定位也需要精确同步的时间，编队还需要规划合理的路线同时要应对相互间的干扰；如果是做特技飞行表演，对控制要求很高，如果只是静态或准静态的LED灯光秀控制难度相对较低。</p><p>　　在星瀚资本运营总监看来，无人机编队表演为主要技术难点在于多机的引导和协同，队形设计、气动耦合、队形的动态调整、航迹规划、信息互换以及编队飞行控制策略等问题都非常关键，成本会伴随机体数量增加而大幅提高。</p><p>　　千架无人机亮相西安城墙 图 视觉中国</p><p>　　<strong>收入超出预期的编队表演</strong></p><p>　　<strong>“去年做了几十场，今年收入会过亿”</strong></p><p>　　承接此次西安无人机表演的亿航白鹭的官网介绍，公司专注于一键式操作多台无人飞机编队在指定空域呈现及变换信息，以空中媒体的形式，将科技与媒体、广告融合，利用无人机、VR、AR、裸眼3D、激光投影和全息技术等科技元素，对传统媒体进行创新和补充。</p><p>　　有报道称，去年以来，亿航白鹭的母公司亿航智能花了很长时间做各种业务的转变，从to C转到包括编队表演、行业应用以及跟政府的合作等业务。</p><p>　　亿航智能创始人兼CEO胡华智在接受采访时表示，今年第一季度，亿航已经接到大量订单，包括来自184（其研发的载人机）的、政府合作的、还有行业应用以及大量编队表演的订单。</p><p>　　2017年以来，亿航白鹭已经承接了多场大型活动的无人机编队表演，包括电影首映礼、香港回归20周年、灯光节等活动，上述业内人士刘飞也表示，亿航在编队表演领域有一定的地位，之前并未出过问题。</p><p>　　千架无人机亮相西安城墙 图 视觉中国</p><p>　　显然，编队飞行表演已经是亿航智能的招牌之一。其CEO胡华智在4月接受采访时表示，飞行编队的发展已经完全超出了公司的预期，本来只是打算拿无人机表演给大家一点空中造景的喜气感觉，同时做一些资金上的回收，没想到甚至扭转了亿航消费级无人机不盈利的颓势。“我们去年做了几十场，今年表演的收入甚至会过亿，这是我们当初没有预料到的。”</p><p>　　此前就有报道称，今年五一开始，无人机编队就将在西安实现常态化表演。</p><p>　　而对于无人机编队表演这一业务的拓展，易瓦特无人机创始人赵国成觉得，这是顺应无人机行业发展趋的“明智选择”。因为在他看来，这条路上需要新的点来刺激市场，无人机原来大家知道的只有航拍，现在大部分人知道还可以做编队，这样的转型或者说新盈利点对行业发展也是一种贡献。</p><p>　　<strong>消费级无人机“去库存”</strong></p><p>　　亿航白鹭所属母公司亿航智能的宣传文案介绍，其由来自中国和美国硅谷的飞行器爱好者创立的智能无人机公司，成立于2014年4月。创立之初，亿航智能推出了智能无人机GHOSTDRONE 2.0，特点在于可直接用手机APP操控。但由于国内对于消费级无人机的管控和限制，加上强大的竞争对手，上述消费级航拍无人机并没有占到多大的市场份额。</p><p>　　此后，亿航智能以低空中短途自动驾驶载人飞行器“亿航184”引起了国内外的关注。不过，这款产品运营的可行性、安全性以法律等问题上让亿航184饱受质疑。</p><p>　　亿航184</p><p>　　快速发展的同时，关于亿航的负面消息时常见诸媒体。2016年末，亿航智能被曝大规模裁员，2017年10月左右，一张亿航被追债的照片现身网络：其办公楼前的横幅上写着“广州亿航欠贷款19个月未还。”2018年3月，据雷锋网报道，亿航美国分公司已于当地时间2017年12月29日在加州申请破产，原因包括为“消费类无人机由于产品质量的问题，基本上销售不出去；公司欠了美国房东（办公室）一百五十万美金，被告了。”</p><p>　　对此亿航方面发布了名为《关于“雷锋网”针对亿航不实报道的郑重声明》的声明，表示由于其正常公司的业务需要调整架构，“将面向美国市场销售和服务等职能收回国内总部统一管理”，于是亿航决定关闭美国分公司。</p><p>　　第一财经在4月18日的一篇报道中称，胡华智坦承，在目前竞争环境下，消费级无人机确实难有较好盈利。他告诉记者，“我们之前有几千台的库存，但是我们把这部分库存利用起来，来从事编队业务，产生了很可观的收益。”</p><p>　　在4月末接受另一家网站的采访时他表示，某种程度上，编队飞行扭转了亿航消费级无人机不盈利的颓势。“我们原来大概有几千台的库存，这些库存占用了我们很大的资金，2015、2016年，我们相当于把很多的钱变成了货，在财务认定上是亏损的，编队表演是我们去库存很好的办法。”</p><p>　　图片来源网络</p><p>　　“我们已经实现了收支平衡”，胡华智在上述第一财经的报道中称，亿航已经完成了C轮融资，但未公布融资细节。此前，亿航在2014年12月完成了由GGV纪源资本领投的1000万美元A轮融资，在2015年8月完成了由金浦投资领投的4200万美元B轮融资。此后便一直没有新的融资消息放出。</p><p>　　经历了一场寒冬，这家“有故事”的无人机厂商，是否可以依靠飞行编队表演和其宣称的无人机物流华丽转身尚未可知，不过能够预见的是，在今后的各类庆典中无人机编队表演会越来越多。相比于其他类型的表演，其自带高科技基因，但却受场地、天气、空域申请等等因素的影响。</p><p>　　在星瀚资本运营总监张昊看来，无人机行业发展至今，常规性应用场景基本已经被头部企业所瓜分，长尾场景的规模和需求迫切程度不足以支撑其他企业的长期发展，作为转型发展的路径之一，无人机编队表演短期看是个不错的选择，但如何发展为长期的业务，如何创造可持续价值，这都是转型者们要考虑的问题。</p><p>　　编辑：赵力</p>', 1525412760, 0, 1),
(51, '婚后购房父母出资咋认定？法官:对夫妻双方的赠与', NULL, '<p class="art_p">北京晨报讯 （记者 黄晓宇）刘女士与王先生婚后共同购买了一套房屋，房屋首付款160万元由男方父母支付，后续贷款由男方偿还，房屋则登记在了二人名下。后刘女士起诉要求离婚，分割该房屋。王先生虽同意离婚，但不同意分割房屋。日前，海淀法院审结了此案，法院判决该房屋归王先生所有，由王先生给付刘女士房屋折价款185万元。</p><p class="art_p">原告刘女士在法庭上表示，该房屋登记在她与王先生名下，且房屋贷款一直由两人共同偿还，王先生父母出资应视为对双方的赠与，故要求依法分割该房屋。而被告王先生不同意该说法，他认为房屋是父母出资专门为他购买的。“我和父母签了一份赠与协议，这份协议写明，他们出的房款只赠与我一人所有，且贷款也是我一人还，我不同意分割房屋。”</p><p class="art_p">法院经审理后认为，该房屋系双方于婚姻关系存续期间购买，且登记在双方名下，故该房屋属于夫妻共同财产。</p><p class="art_p">对于首付款160万元，法院认为，王先生虽主张系其父母赠与其个人，且以协议书为据，但该协议书是其父母与王先生单独签署，王先生未提交证据证明该协议约定之事实有第三方知晓，故该证据对第三方不发生效力。另外，该协议书签订于房屋登记之前，事后该房屋登记在王先生和刘女士两人名下，视为变更了上述约定，故法院对王先生主张该首付款为其父母赠与其个人之主张不予采信，该房屋应当作为夫妻共同财产依法分割。</p><p class="art_p">最后，法院判决该房屋归王先生所有，由王先生给付刘女士房屋折价款185万元。</p><p class="art_p"><strong>■法官释法</strong></p><p class="art_p">根据《婚姻法司法解释二》规定，当事人结婚后，父母为双方购置房屋出资的，该出资应当认定为对夫妻双方的赠与，但父母明确表示赠与一方的除外。根据该条法律规定可知，条文中的“应当认定”是在父母实际出资时，其具体意思表示不明的情况下，从社会常理出发，推定为赠与。若当事人有证据证明其与出资人之间形成的是借贷关系的，则不能适用该条规定，该部分出资应认定为借款，将按照夫妻共同债务或个人债务处理。当然，该证据应当是在当事人离婚诉讼前形成的。</p><p class="art_p">关于父母出资是赠与一方还是双方，法官表示，一方父母在婚后为子女购置不动产出资的，一般应认定为对夫妻双方的赠与，除非父母明确表示赠与给一方。特别是当出现不动产所有权登记在双方子女名下这一事实时，除非当事人能够提供其他证据证明该出资仅为父母对其子女一方的赠与，否则依据产权登记这一事实，一方父母出资的真实意思表示更合理的解释为一方父母对双方子女的赠与而不是父母赠与给一方子女。法官特别提示，这里所称的父母的意思表示应以赠与发生的时间为准。</p><p class="art_p">本案中，王先生提交的其父母与其签署的赠与协议书，内容虽写明系其父母的出资，且赠与王先生一人，但该协议内容未经刘女士确认并同意，且双方争议之房屋登记在王先生和刘女士名下，故本案综合上述事实认定王先生与其父母单方签署的赠与协议不能对抗第三人刘女士，不能视为“父母明确表示赠与一方”的情形，故王先生父母出资的部分，应认定为王先生和刘女士夫妻共同所有。</p><p class="art_p">来源：北京晨报</p>', 1525408984, 0, 1),
(52, '辽宁:渤海海峡黄海北部有军事任务 4日至11日禁航', 'uploads/fwbO-fzyqqiq848411131640.jpg', '<p class="art_p"><strong>辽航警0094</strong></p><p class="art_p">渤海海峡黄海北部，5月4日1600时至11日1600时，在</p><p class="art_p">1。 38°51′41″N 121°38′12″E</p><p class="art_p">2。 38°34′12″N 121°38′12″E</p><p class="art_p">3。 38°33′55″N 121°07′51″E</p><p class="art_p">4。 38°48′13″N 121°14′03″E</p><p class="art_p">诸点连线范围内执行军事任务。禁止驶入。</p><p class="art_p">此前在同一海域，辽宁海事局曾发布两则航行警告：</p><p class="art_p">　<strong>　辽航警0090</strong></p><p class="art_p">渤海海峡黄海北部，4月27日1600时至5月4日1600时，在1。 38°51′41″N　121°38′12″E2。 38°34′12″N　121°38′12″E3。 38°33′55″N　121°07′51″E4。 38°48′13″N　121°14′03″E诸点连线范围内执行军事任务。禁止驶入</p><p class="art_p">辽航警0081：渤海海峡黄海北部，4月20日1600时至27日1600时，在1。 38°51′41″N　121°38′12″E 38.86 121.632。 38°34′12″N　121°38′12″E 38.57 121.633。 38°33′55″N　121°07′51″E 38.56 121.134。 38°48′13″N　121°14′03″E 38.72 121.23诸点连线范围内执行军事任务。禁止驶入。</p><p class="art_p">来源：辽宁海事局</p>', 1525444971, 0, 2),
(53, '公安提醒：加这种微信群 你的个人信息或正被套取', 'uploads/jXJN-fzyqqiq819950623386.jpg', '<p class="art_p">厦门警方提醒，来路不明的微信群会给个人信息甚至钱财带来安全隐患。</p><p class="art_p">近日，厦门某小区楼道内、电梯里突然张贴了“加入业主群”的布告通知，通知的标题为“关于加入XX小区业主群的倡议书”，倡议书末尾有群二维码，并要求业主进群后必须修改昵称“X楼+X室+姓名”。</p><p class="art_p">小区最近正好要成立业委会，也有热心的业主在张罗着组建业主微信群。所以，看到这样用词颇具“正规”的倡议书，大家也没多想，陆续通过扫群二维码、互相推荐的方式进群。</p><p class="art_p">但进群后，群里根本没有涉及组建业委会的事宜，而是不断发送各种推销广告等信息，随后小区党支部和物业发布紧急声明，宣布这个所谓的“XX生活交流群”是假冒业主群。</p><p class="art_p">谁建的“李鬼”业主群？图什么？</p><p class="art_p">厦门市反诈骗中心民警分析，不法分子组建该假冒“业主群”，可能有以下三个目的：</p><p class="art_p">1、在群内推销广告：这可比挨家挨户去发放传单、推销广告方便得多，成本也小得多。</p><p class="art_p">2、获取小区业主信息：根据“入群要求”，一旦业主将昵称修改为“房号+姓名”，对方就轻松获取业主的个人信息，之后可能从事如诈骗等非法活动。</p><p class="art_p">3、群内发送附带病毒的链接、红包：在取得业主信任之后，不法分子会在群内发送附带病毒的链接、红包等，业主一旦点击，可能引起手机中毒，进而手机内信息被窃取，甚至导致手机绑定的银行卡内钱款损失。</p><p class="art_p">民警提醒广大群众，不要随意扫描不明二维码，不要添加陌生微信好友、加入陌生微信群，更不能随意点击不明链接等，以免造成财产损失。</p><p class="art_p">提高警惕！</p>', 1525441176, 0, 2),
(54, '财政部：已拨付1060.95亿元补助地方专项扶贫资金', NULL, '<p class="art_p">财政部最新消息，2018年，中央财政预算安排补助地方财政专项扶贫资金1060.95亿元，比2017年同口径增加200亿元。</p><p class="art_p">为便于各地及早安排资金，加快脱贫攻坚步伐，近日，财政部安排拨付中央财政专项扶贫资金322.64亿元。加上2017年10月提前下达的738.31亿元，截至4月底，2018年中央财政补助地方专项扶贫资金1060.95亿元已全部拨付完成，比上年提早了近两个月，为各地加快预算执行进度、打好精准脱贫攻坚战创造了有利条件。</p><p class="art_p">2018年，财政部将新增安排的200亿元资金全部用于支持深度贫困地区脱贫攻坚。其中，安排120亿元新增资金用于支持“三区三州”脱贫攻坚，并明确除中央财政安排给“三区三州”的新增资金外，“三区三州”所在省份要按照不低于2017年的资金规模，保障“三区三州”投入力度；其他省份也要统筹安排资金，加大对深度贫困地区脱贫攻坚的支持力度。（央视记者 王楠）</p>', 1525434888, 0, 2),
(55, '“金特会”前夕 特朗普要国防部考虑削减驻韩美军', 'uploads/djEO-fzyqqiq683810731124.jpg', '<p class="art_p">海外网5月4日消息，在美国总统特朗普或将同朝鲜最高领导人金正恩会面还有数周之际，消息人士透露，特朗普已经命令美国国防部准备好削减驻韩美军人数。</p><p class="art_p">据《纽约时报》报道，这些官员表示，朝韩间在近日达成的和平协定，或许意味着朝鲜半岛不再需要超过2名美国士兵。此外，特朗普一直考虑撤出驻韩美军部队，他认为美国并没有从中得到足够的资金来维持驻军。根据一项美韩两国一份2018年底到期的协议，韩国会支付维持驻韩美军的近一半费用，每年的总额超过8亿美元，但特朗普政府则要求韩方承担所有的费用。</p><p class="art_p">自1953年以来，美国一直在韩国派驻军队。而截至目前，韩军的战时作战指挥权仍掌握在美方手中。目前，匿名人士拒绝透露特朗普是要寻求全面撤军还是部分撤军，不过完全撤走似乎不太可能。</p><p class="art_p">美国国防部长马蒂斯早前也表示，美国将与盟友讨论美军从朝鲜半岛撤出一事。马蒂斯4月27日发表的声明中提到，对于美军撤出朝鲜半岛，“首先，这是我们与盟友谈判中将会讨论的问题之一，当然，我们也会与朝鲜讨论。”</p><p class="art_p">4月27日，朝韩首脑会晤在板门店韩方一侧“和平之家”举行。韩国总统文在寅与朝鲜最高领导人金正恩签署《板门店宣言》并共同发表。宣言内容包括：朝韩领导人同意将通过彻底无核化实现无核半岛作为共同目标；宣布半岛不再有战争，停止一切敌对行为；争取在年内宣布结束战争状态、力促韩朝美或中美韩朝会谈等。</p><p class="art_p">5月2日，青瓦台核心负责人也就驻韩美军一事表示，青瓦台方面认为，与朝鲜签署和平协定后，美军仍需继续驻扎在韩国。</p>', 1525425840, 0, 2);
INSERT INTO `pre_news` (`news_id`, `news_title`, `news_pic`, `news_content`, `news_time`, `news_like`, `news_cate_id`) VALUES
(56, '中央的这项重要任务 山西新任副省长披挂上阵', 'uploads/QQZl-fzyqqiq671141772142.jpg', '<p class="art_p">来源：长安街知事</p><p class="art_p">撰文|子豪</p><p class="art_p">履新副省长3个月后，刘新云再出战绩！</p><p class="art_p">5月3日，山西省公安厅公布了一批黑恶典型案件，截至目前，山西省公安厅已经打掉包括操纵黄赌毒、暴力讨债、强揽工程等在内的黑恶势力团伙超过180个，战果磊磊。</p><p class="art_p">长安街知事（微信ID：Capitalnews）注意到，这一系列行动正是由山西省副省长、公安厅长刘新云亲自组织指挥、督导斗争的。</p><p class="art_p">今年1月24日，中央正式发布《关于开展扫黑除恶专项斗争的通知》，要求各级党委和政府要将扫黑除恶专项斗争作为一项重大政治任务，摆到工作全局突出位置，列入重要议事日程。</p><p class="art_p">1月底，被任命为省公安厅厅长一个星期后，刘新云就投身部署针对枪爆违法犯罪的专项行动，直击作为民爆物品使用大省的痛点。此后，有关扫黑除恶的大大小小的推进会、调度会，刘新民也开了不下10余场，还划定了重点扫描打击的十二个区域，覆盖了从文物到宗族涉恶涉黑的方方面面。</p><p class="art_p">山西今年扫黑除恶的最高“战绩”，也是由刘新云亲自下令。春节前，刘新云对全省各级公安机关亲自下发“收网令”，对在侦在办的涉黑涉恶案件实施了集中收网，打掉涉黑涉恶犯罪组织和团伙54个。</p><p class="art_p">工作高效、想法全面，与他的经历也有关系。刘新云履新山西之前，主要任职于山东，堪称是一位“全能”警察，在刑警、交警、巡警等多个警种担任过有关职务，而且经常亲自上阵。2011年，时任菏泽副市长、市公安局局长的他，曾在暴雨下指挥交通数小时。</p><p class="art_p">2015年1月4日，济南市一办公楼后仓库起火，时任济南市公安局局长的刘新云亲赴现场指挥。8天后，刘新云奉调进京，出任公安部网络安全保卫局局长。</p><p class="art_p">事实上，对扫黑除恶这件大事，各地都格外重视。与刘新云几乎同时调任地方的浙江省副省长、省公安厅厅长王双全履新不久后，便在浙江省公安机关开展代号为“2018钱潮一号”的集中统一收网行动担任总指挥。行动出动了5000余人次的警力，抓获1200余名犯罪嫌疑人。</p><p class="art_p">全方位、拉网式集中排查是常规操作，辽宁通过监狱罪犯挖掘线索、深挖余罪漏罪，开辟扫黑除恶专项斗争“第二战场”；天津市委常委、政法委书记赵飞还曾暗访被举报较的汽车市场，以普通购车者的身份，询问汽车交易情况，观察存在的疑点。</p><p class="art_p">扫黑除恶斗争能否“斗得好”，对公检法人员的素质也一种考验。3月底，甘肃举办了为期三天的全省扫黑除恶专项斗争培训班，甘肃省副省长、省公安厅厅长余建亲自开班授课。</p><p class="art_p">面对涉黑涉恶的党员干部，要更加严惩。湖北省纪委监委成立了监督执纪问责工作专班，近日通报了荆门市公安局原党委委员以及刑侦支队党委原支队长被“双开”的消息，彰显重拳出击的决心和力度。</p><p class="art_p">此外，扫黑除恶更需要公安机关、检察院法院等机构的相互配合。四川南充市阆中市何某等16人涉黑社会组织犯罪案，是全国扫黑除恶专项斗争开展以来全国集中宣判的第一案，为之后的案件审理留下宝贵经验。</p><p class="art_p">今年是扫黑除恶三年攻坚战的第一年，开局即决战，起步就冲刺，给予了犯罪分子极大的震慑！</p>', 1525424607, 0, 2),
(57, '日自民党干事长访俄优先经济 引发搁置领土谈判担忧', NULL, '<p class="art_p"> <p class="art_p">【环球网报道 记者 王欢】日本自民党干事长二阶俊博5月1日结束对俄罗斯与土耳其的访问回国。在俄罗斯，二阶与其率领出访的经济界人士等约200人共同呼吁经济合作。此外，二阶还利用访问团带来的特大金枪鱼，与俄总理梅德韦杰夫展开了融洽的会谈。二阶采取了在对华外交惯用的的“二阶风格”，但鉴于本月下旬即将举行日俄首脑会谈，也有人担忧搁置日俄南千岛群岛(日称：北方四岛)领土问题谈判“优先经济”的姿态。</p><p class="art_p">“您吃过金枪鱼吗？喜欢吗？”日本共同社5月3日报道称，二阶4月27日在位于莫斯科郊外的总理公邸突然问起梅德韦杰夫。陪同在座的人员也邀请称：“希望您来日本尝尝正宗的金枪鱼。”梅德韦杰夫对此流露出积极意向，现场气氛和谐。</p><p class="art_p">会谈后，二阶还不忘为梅德韦杰夫送去了用现场演示分割的金枪鱼所制作的寿司和老家和歌山的地方酒“罗生门”。</p><p class="art_p">二阶周边人士透露，二阶想与担任执政党“统一俄罗斯党”主席的梅德韦杰夫建立起仅次于(日本首相安倍晋三与俄总统普京)首脑级别的关系。</p><p class="art_p">除商社、旅游、运输领域的企业最高层外，此次大规模访问团成员还包括山形、茨城和广岛各县知事，主推扩大观光交流。同时，俄方也对据称“被安倍建议访俄”的二阶的影响力和政治手腕抱有期待。</p><p class="art_p">报道回顾称，2017年5月，二阶代表日本方面参加了中国“一带一路”高峰论坛，并与中方领导人举行会谈。包括提倡日本应尽快加入中国主导的亚洲基础设施投资银行(AIIB)等在内，二阶阐述独自主张，积极承担起中日关系改善的任务。</p><p class="art_p">据随行人员透露，此次的一系列会谈上，二阶也最大限度地顾及了俄方的愿望。双方围绕经济合作集中交换了意见，但未提及日俄领土以及和平条约谈判的话题。</p><p class="art_p">对于二阶的做法，日方外交消息人士警惕称：“不捆绑领土谈判，则只会被俄罗斯‘吃白食’。”</p><p class="art_p">对此，二阶周边人士强调，二阶的努力让中日关系出现了积极的变化。此次访俄也是一样。“对于二阶，安倍首相将无法忘记其功勋”。   </p>', 1525424580, 0, 2),
(58, '韩方:未就“金特会”是否选址板门店接到美方通知', 'uploads/VT1O-fzyqqiq669154994134.jpg', '<p class="art_p">[环球网报道 记者 李小飞]朝美首脑会谈的具体地点，一直“众说纷纭”。此前有消息称，朝美首脑可能会在朝韩边境的板门店举行会谈。对此，韩国总统府青瓦台官员5月4日表示，尚未从美方接到相关通知。</p><p class="art_p">韩联社报道称，在被问及“若朝美首脑会谈选址板门店，美方须寻求韩方合作，青瓦台是否接到相关通知”时，该官员回答说“没有接到”。</p><p class="art_p">该官员还表示，朝美首脑会谈的具体日期和地点是否会在本周内出炉的问题，韩方不得而知，这个要问白宫。</p><p class="art_p">对于韩方是否计划在朝美首脑会谈前举行韩美首脑会谈的提问，该官员给出了肯定的答复。他称，这两个会谈相互联动。但就韩美首脑会谈告吹的可能性，他表示，根据情况，也有可能。</p>', 1525424100, 0, 2),
(59, '诺奖得主获中国永久居留身份证 这些名人也已拿到', 'uploads/erIh-fzyqqiq663586177014.jpg', '<p class="art_p">5月2日，诺奖得主伯纳德·费林加等7名外籍人士在上海正式领取到永久居留身份证——也就是民间俗称的“中国绿卡”。此前，获得“中国绿卡”的知名外国人士还包括CBA球星马布里、小说《牛虻》作者伏尼契的孙女、核物理专家Joan Hinton（中文名为寒春）等。</p><p class="art_p">随着中国经济的发展，中国对各国的吸引力不断增强。庞大的消费市场、日新月异的科技创新、包容和谐的社会文化。。。。。。越来越多的外国人选择来中国求学以及寻找工作机会。拥有一张“中国绿卡”，将大大减少外籍人士在中国出行、购房、医疗的种种不便。</p><p class="art_p">另一方面，中国一些大城市陆续推出相关政策，为外国人在华创办企业、投资、提供便利。例如，上海浦东推出吸引“外脑”参与自贸区建设的新举措，明确规定持“中国绿卡”外籍高层次人才创办科技企业，与中国公民享受同等待遇。</p><p class="art_p">曾经，中国“绿卡”所扮演的角色似乎有些尴尬。外籍人士要申请，除需遵守中国法律，身体健康，无犯罪记录外，还要符合一些特殊条件才可以。而这些“特殊条件”颇为严格，例如，在中国有较高数额直接投资的外国籍投资者、对中国有重大突出贡献或者国家特别需要的人员等等，这令不少外籍人士对中国绿卡“虽心向往之，然不能至”。“中国绿卡”“史上最难”的称号也随之而来。</p><p class="art_p">近年来，关于“绿卡”的改革与新规不断，包括放宽申请条件、简化申请流程、缩短审批时限、允许随行未成年子女申领、扩大持“绿卡”享有的福利等。据媒体报道，2016年中国公安部发出中国“绿卡”1576张，比上年度大涨163%。</p><p class="art_p">去年，公安部印发《外国人永久居留证件便利化改革方案》。改革措施包括将“外国人永久居留证”更名为“外国人永久居留身份证”、在证件中嵌入芯片存储证件签发管理信息、实现永久居留外国人信息部门间共享、提高“外国人永久居留身份证”的知晓度等。</p><p class="art_p">随着政策的改革和调整，“中国绿卡”的含金量也在不断提高，在金融、教育、医疗等各方面给外国公民带来切实便利。据了解，新版“中国绿卡”从2017年6月起开始签发，拥有此卡的外籍人才可以在购房、银行金融业务办理、驾照申领、住宿登记时，依法享受中国公民同等待遇，此外，在中国境内工作时，持卡人还可依法享有社保和公积金等。</p><p class="art_p">可以说，中国在“绿卡”政策方面日趋开放包容的态度，显示了中国在国际社会更加积极开放的心态，也预示着越来越多的国际人才将在中国这块梦想的热土上大展宏图，实现他们的中国梦。</p>', 1525423314, 0, 2),
(60, '进口抗癌药零关税患者能省多少钱？一张图算明白', 'uploads/usak-fzyqqiq661798977880.jpg', '<p class="art_p">5月份开始，关于抗癌药品的政策利好不断。</p><p class="art_p">据国家卫生健康委员会消息，从5月起，实行三措并举降低抗癌药价——进口药品实行零关税；对已纳入医保的抗癌药实施政府集中谈价、采购；对未纳入医保的抗癌药实行医保准入谈判。</p><p class="art_p">同时，财政部联合海关总署、税务总局、国家药品监督管理局也发布《关于抗癌药品增值税政策的通知》，自5月1日起，对进口抗癌药品（包括103种抗癌药品制剂以及51种抗癌药品原料药），减按3%征收进口环节增值税。</p><p class="art_p">减税+谈价谈判的“组合拳”会让抗癌药价格出现明显下降，缓解人们对抗癌用药费用过高的担心吗？一张图算明白↓↓</p><p class="art_p">点击进入专题：<br/> 降低抗癌药品费用 卫健委近期将采取三大措施</p>', 1525423203, 0, 2),
(61, '安徽怎么一下子来了这么多挂职副市长？', NULL, '<p class="art_p">加里·奥德曼在《至暗时刻》中炸裂般的演技，自然无可挑剔。不过看完电影半年之后，仍然深刻在我印象中的，却是编剧虚构的一个场景。丘吉尔去议会演讲时，第一次搭乘了伦敦地铁，坐在各阶层平民之中，这位出身贵族的首相，感受到了人民抵抗法西斯的决心。</p><p class="art_p">有人说这个情节不合逻辑，一个车厢的市民怎么能代表全体人民？对此，我只能说：这位观众，温斯顿先生不是去查人头数选票的，他此时只是需要一种叫“地气”的力量。艺术应该深入生活，政治必须深入群众，这就像树木万不能与大地切割。</p><p class="art_p">这几天很多人注意到，安徽、贵州等地纷纷迎来一批挂职副市长。挂职，通俗地说就是干部不改变原有行政关系，到其他地方担任职务、培养锻炼。而这批挂职干部中，绝大多数是中央机关干部，到基层地市去“接地气”。</p><p class="art_p">这次大规模的干部挂职，其实是中央一次统一行动。就在上个月，中央组织部为了聚焦脱贫攻坚，选派了283名干部到西部地区、老工业基地和革命老区挂职锻炼。其中比较典型的有贵州毕节市，这个位于黔西北的扶贫重点地区，在日前召开的人大第十次会议上一口气任命了4名挂职副市长。这几位副市长来自全国工商联、上海市卫计委、证券公司等部门。<strong>这无疑将改善当地的干部结构、调动更多资源，有助于扶贫攻坚任务的早日完成。</strong></p><p class="art_p">而安徽在这次干部选派中，显得尤为引人注目。到昨天为止，安徽16个地市中已经有合肥、蚌埠、亳州、宣城、铜陵、阜阳、池州、滁州、安庆、芜湖、马鞍石等11个地市都迎来了挂职副市长。<strong>除马鞍山市尚未公布外，这批副市长都任市委常委，在副市长中排名都在第三或以上。</strong></p><p class="art_p">如前所述，<strong>扶贫是这些副市长最主要的任务。</strong><strong>据已公开的分管工作安排，合肥副市长赵兵让还兼任安徽省扶贫办副主任。</strong>亳州副市长李胜辉，负责扶贫工作，分管扶贫局。中国证监会戴钦公挂职的安庆市宿松、太湖两县，本来就是证监会的重点定点帮扶县。此外，公开报道中阜阳、宣城、铜陵、滁州等地的挂职副市长，下车伊始就已开始出席扶贫工作会议或走访贫困群众。</p><p class="art_p">虽然也属革命老区，但相比其他“西部地区”、“老工业基地”，安徽此次挂职副市长的选派，在规模和节奏上，都显得更为突出。<strong>而在这十一个地市中，合肥、芜湖两地尤为不同。在赵兵让、郭丽君此次到任之前，这两地其实已经有中央机关挂职干部。</strong></p><p class="art_p">中国气象局科技与气候变化司司长罗云峰，去年底挂职合肥市委常委、市政府副市长。排名仅次于市长凌云。中国医学科学院北京协和医院副院长韩丁，与罗云峰同时挂职合肥，排名罗云峰之后。此外中央台办、国台办经济局副局长彭庆恩，去年8月挂职合肥副市长。也就是说，合肥市政府领导班子中已经有4位挂职副市长。其中三位为市委常委，排名依次在市长之后。挂职干部几乎占了半壁江山。</p><p class="art_p">中国东方航空股份有限公司总经济师郭丽君挂职芜湖市委常委、副市长之前，则有中央国家机关工委培训中心主任陈韶光，于去年底挂职芜湖。目前陈韶光、郭丽君在市政府领导班子中排名，依次在市长贺懋燮之后。<strong>这样的人事安排，除了让更多中央干部在基层得到锻炼之外，还有什么深层含义呢？</strong></p><p class="art_p">相比起挂职副市长上任，昨天的舆论场中，大家分明更关心另一个头条。安徽、山东两地同日召开省委常委会议，按照中央文件要求“对鲁炜严重违纪案件进行深刻剖析”。其中安徽省委书记李锦斌在会议中要求，安徽不仅要以鲁炜案件为反面教材，还要“联系我省发生的陈树隆、杨振超、周春雨等案件”，做好专题警示教育的系列动作。</p><p class="art_p">单是这几个名字，就足够让人回想起许多“安徽往事”。已落马的安徽原常务副省长陈树隆、原省政府秘书长杨敬农曾在芜湖搭档担任市委书记、市长五年之久。省会合肥则有原市长张庆军等落马。<strong>在这样的地市，安排中央机关挂职干部担任政府重要领导工作，有利于确保脱贫攻坚战略的坚定执行、有利于确保中央大政方针在地方不打折扣、也有利于当地政治生态的早日恢复。</strong></p><p class="art_p">巧合的是，同日召开会议以鲁炜案件进行反面教育的山东、安徽两省，也同是十八届中央首轮回头看瞄准的地方。两地后来查出的问题，不可谓不严重。这必然会对政治生态带来某种程度的影响。中央挂职干部的到来，肯定会为安徽完成脱贫任务带来助益。但归因到底，安徽的发展，还要建立在自身政治生态的清明之上。</p><p class="art_p">（文/于永杰)</p>', 1525421357, 0, 2),
(62, '央行：人民币合格投资者不得投资境外非人民币产品', NULL, '<p class="art_p"><span style="font-family: KaiTi_GB2312, KaiTi;">构建宏观审慎管理框架 推动RQDII业务健康发展</span></p><p class="art_p">为进一步规范和促进人民币合格境内机构投资者（以下简称人民币合格投资者）境外证券投资业务的常态化开展，中国人民银行发布了《中国人民银行办公厅关于进一步明确人民币合格境内机构投资者境外证券投资管理有关事项的通知》（银办发〔2018〕81号，以下简称《通知》）。经总行同意，人民银行上海总部配套发布了《人民币合格境内机构投资者境外证券投资信息报送流程》（以下简称《信息报送流程》）。</p><p class="art_p">《通知》在2014年推出的人民币合格投资者境外证券投资政策的基础上，对人民币合格投资者和托管银行开展境外证券投资的信息报送进行了明确。人民币合格投资者和托管银行应按照《信息报送流程》分别向人民银行上海总部或通过RCPMIS系统报送相关信息，具体包括账户信息、投资建仓计划、持仓信息以及相应的跨境资金流动信息等节点信息。同时，《通知》还完善了宏观审慎监管框架，明确不得投资境外非人民币产品，不得境外购汇，人民银行可根据跨境资金流动等宏观形势进行审慎管理等。</p><p class="art_p">推动人民币合格投资者开展境外证券投资活动（RQDII），并加强审慎管理，是积极防范风险前提下，服务实体经济多元资产配置需求的有力举措，有利于推动人民币走出去，促进离岸人民币市场健康发展，推动资本项目开放和人民币国际化。</p><p class="art_p">首先，有利于培育境外人民币市场，推动人民币国际化进程。RQDII制度将带动人民币资金流入境外市场，推动人民币金融产品研发，吸引更多国际投资者，形成良性循环。其次，可一定程度上满足境内投资者资产多元化配置需求，推动境内金融机构提升跨境金融服务能力和水平。RQDII制度将促进金融机构熟悉境外金融市场，延伸跨境金融服务链，建立适应国际市场投资的风险管理机制和内部运行架构。最后，有利于积极防范涉外金融风险，持续深化金融改革。加强对RQDII业务规范以及事中事后监管，有利于防范违法违规行为，确保业务健康有序发展。</p>', 1525451413, 0, 3),
(63, '商务部：中方就中兴公司案与美方进行了严正交涉', NULL, '<p class="art_p"><span style="font-family: KaiTi_GB2312, KaiTi;">商务部新闻发言人应询答记者问</span></p><p class="art_p">商务部新闻发言人于4日应询答记者问时表示，在3日至4日举行的中美经贸磋商中，中方就中兴公司案与美方进行了严正交涉。美方表示，重视中方交涉，将向美总统报告中方立场。（记者于佳欣）</p>', 1525449615, 0, 3),
(64, '中美经贸磋商部分问题达成共识 将建立机制保持沟通', NULL, '<p class="art_p">　<span style="font-family: KaiTi_GB2312, KaiTi;">　中美经贸磋商就部分问题达成共识 双方同意建立工作机制保持密切沟通</span></p><p class="art_p">新华社北京5月4日电（记者于佳欣）5月3日至4日，中共中央政治局委员、国务院副总理刘鹤与美国总统特使、财政部长姆努钦率领的美方代表团就共同关心的中美经贸问题进行了坦诚、高效、富有建设性的讨论。&nbsp;</p><p class="art_p">双方均认为发展健康稳定的中美经贸关系对两国十分重要，致力于通过对话磋商解决有关经贸问题。&nbsp;</p><p class="art_p">双方就扩大美对华出口、双边服务贸易、双向投资、保护知识产权、解决关税和非关税措施等问题充分交换了意见，在有些领域达成了一些共识。&nbsp;</p><p class="art_p">双方认识到，在一些问题上还存在较大分歧，需要继续加紧工作，取得更多进展。&nbsp;</p><p class="art_p">双方同意继续就有关问题保持密切沟通，并建立相应工作机制。（完）&nbsp;</p>', 1525449438, 0, 3),
(65, '银保监会：规范民间借贷行为 维护经济金融秩序', NULL, '<p class="art_p">新浪财经讯 5月4日消息，中国银保监会等发布规范民间借贷行为、维护经济金融秩序的通知，通知严禁银行业金融机构从业人员作为主要成员或实际控制人，开展有组织的民间借贷。中国银保监会要求小额贷款公司加大对实体经济的支持力度，未经有权机关依法批准，任何单位和个人不得设立从事或者主要从事发放贷款业务的机构或以发放贷款为日常业务活动。</p><p class="art_p">《通知》严厉打击以下非法金融活动：利用非法吸收公众存款、变相吸收公众存款等非法集资资金发放民间贷款；以故意伤害、非法拘禁、侮辱、恐吓、威胁、骚扰等非法手段催收贷款；套取金融机构信贷资金，再高利转贷；面向在校学生非法发放贷款，发放无指定用途贷款，或以提供服务、销售商品为名，实际收取高额利息（费用）变相发放贷款行为。</p><p class="art_p">同时，《通知》要求，严禁银行业金融机构从业人员作为主要成员或实际控制人，开展有组织的民间借贷。</p><p class="art_p"><strong>以下是《关于规范民间借贷行为 维护经济金融秩序有关事项的通知》原文：</strong></p><p class="art_p"><strong><span style="font-family: 微软雅黑;">中国银行保险监督管理委员会 中华人民共和国公安部 国家市场监督管理总局 中国人民银行</span></strong></p><p class="art_p"><strong><span style="font-family: 微软雅黑;">关于规范民间借贷行为&nbsp;维护经济金融秩序有关事项的通知</span></strong></p><p class="art_p"><span style="font-family: 微软雅黑;">银保监发〔2018〕10号</span></p><p class="art_p">各银监局；各省、自治区、直辖市公安厅（局）、工商局（市场监管部门）、新疆生产建设兵团公安局；中国人民银行上海总部，各分行、营业管理部，各省会（省府）城市中心支行，各副省级城市中心支行；各政策性银行、大型银行、股份制银行，邮储银行，外资银行，金融资产管理公司：</p><p class="art_p">为规范民间借贷行为，维护经济金融秩序，防范金融风险，切实保障人民群众合法权益，打击金融违法犯罪活动，根据《中华人民共和国银行业监督管理法》《中华人民共和国商业银行法》《中华人民共和国刑法》及《非法金融机构和非法金融业务活动取缔办法》等法律法规，现就有关事项通知如下：</p><p class="art_p"><strong>一、切实提高认识</strong></p><p class="art_p">近年来，民间借贷发展迅速，以暴力催收为主要表现特征的非法活动愈演愈烈，严重扰乱了经济金融秩序和社会秩序。各有关方面要充分认识规范民间借贷行为的必要性和暴力催收的社会危害性，从贯彻落实全面依法治国基本方略、维护经济金融秩序、保持经济和社会稳定的高度出发，认真抓好相关工作。</p><p class="art_p"><strong>二、把握工作原则</strong></p><p class="art_p">坚持依法治理、标本兼治、多方施策、疏堵结合的原则，进一步规范民间借贷行为，引导民间资金健康有序流动，对相关非法行为进行严厉打击，净化社会环境，维护经济金融秩序和社会稳定。</p><p class="art_p"><strong>三、明确信贷规则</strong></p><p class="art_p">严格执行《中华人民共和国银行业监督管理法》《中华人民共和国商业银行法》及《非法金融机构和非法金融业务活动取缔办法》等法律规范，未经有权机关依法批准，任何单位和个人不得设立从事或者主要从事发放贷款业务的机构或以发放贷款为日常业务活动。</p><p class="art_p"><strong>四、规范民间借贷</strong></p><p class="art_p">民间借贷活动必须严格遵守国家法律法规的有关规定，遵循自愿互助、诚实信用的原则。民间借贷中，出借人的资金必须是其合法收入的自有资金，禁止吸收或变相吸收他人资金用于借贷。民间借贷发生纠纷，应当按照《最高人民法院关于审理民间借贷案件适用法律若干问题的规定》（法释〔2015〕18号）处理。</p><p class="art_p"><strong>五、严禁非法活动</strong></p><p class="art_p">严厉打击利用非法吸收公众存款、变相吸收公众存款等非法集资资金发放民间贷款。严厉打击以故意伤害、非法拘禁、侮辱、恐吓、威胁、骚扰等非法手段催收贷款。严厉打击套取金融机构信贷资金，再高利转贷。严厉打击面向在校学生非法发放贷款，发放无指定用途贷款，或以提供服务、销售商品为名，实际收取高额利息（费用）变相发放贷款行为。严禁银行业金融机构从业人员作为主要成员或实际控制人，开展有组织的民间借贷。</p><p class="art_p"><strong>六、改进金融服务</strong></p><p class="art_p">各银行业金融机构以及经有权部门批设的小额贷款公司等发放贷款或融资性质机构应依法合规经营，强化服务意识，采取切实措施，开发面向不同群体的信贷产品。改进金融服务，加大对实体经济的资金支持力度，为实体经济发展创造良好的金融环境，有效疏通金融服务实体经济渠道，服务供给侧结构性改革。</p><p class="art_p"><strong>七、加强协调配合</strong></p><p class="art_p">民间借贷活动情况复杂、涉及方面多，按照《中华人民共和国银行业监督管理法》《中华人民共和国商业银行法》《非法金融机构和非法金融业务活动取缔办法》的规定，地方人民政府以及有关部门要加强协调配合，依法履行职责。</p><p class="art_p"><strong>八、依法调查处理</strong></p><p class="art_p">（一）对利用非法吸收公众存款、变相吸收公众存款等非法集资资金发放民间贷款，以故意伤害、非法拘禁、侮辱、恐吓、威胁、骚扰等非法手段催收民间贷款，以及套取银行业金融机构信贷资金，再高利转贷等违反治安管理规定的行为或涉嫌犯罪的行为，公安机关应依法进行调查处理，并将非法发放民间贷款活动的相关材料移送银行业监督管理机构。</p><p class="art_p">（二）对银行业金融机构从业人员参与非法金融活动的，银行业金融机构应当予以纪律处分，构成犯罪的，依法严厉追究刑事责任。</p><p class="art_p">（三）对从事民间借贷咨询等业务的中介机构，工商和市场监管部门应依法加强监管。</p><p class="art_p"><strong>九、加强宣传引导</strong></p><p class="art_p">银行业监督管理机构、公安机关、工商和市场监管部门、人民银行等有关单位采取各种有效方式向广大人民群众宣传国家金融法律法规和信贷规则。及时向社会公布典型案例，加大宣传教育力度，强化风险警示，增强广大人民群众的风险防范意识，引导自觉抵制非法民间借贷活动。</p><p class="art_p">2018年4月16日</p><p class="art_p"><strong><span style="font-family: 微软雅黑;">中国银行保险监督管理委员会有关部门负责人就</span></strong></p><p class="art_p"><strong><span style="font-family: 微软雅黑;">《关于规范民间借贷行为&nbsp;维护经济金融秩序有关事项的通知》答记者问</span></strong></p><p class="art_p">为进一步规范民间借贷行为，维护经济金融秩序，防范金融风险，切实保障人民群众合法权益，打击金融违法犯罪活动，中国银行保险监督管理委员会会同公安部、国家市场监督管理总局、中国人民银行，联合印发了《关于规范民间借贷行为&nbsp;维护经济金融秩序有关事项的通知》（以下简称《通知》）。中国银行保险监督管理委员会有关部门负责人回答了记者提问。</p><p class="art_p">　<strong>　1．问：发布《通知》的背景是什么？</strong></p><p class="art_p">答：近年来，民间借贷发展迅速，但以暴力催收为主要表现特征的非法活动愈演愈烈，严重扰乱了经济金融秩序和社会秩序，妨碍了正常金融活动的健康发展。为进一步规范民间借贷行为，引导民间资金健康有序流动，防范金融风险，打击金融违法犯罪活动，净化社会环境，维护经济金融秩序和社会稳定，中国银行保险监督管理委员会等部门联合印发了《通知》。</p><p class="art_p"><strong>2．问：出台《通知》的法律依据是什么？</strong></p><p class="art_p">答：根据《中华人民共和国银行业监督管理法》《中华人民共和国商业银行法》《中华人民共和国刑法》《非法金融机构和非法金融业务活动取缔办法》等法律规范，中国银行保险监督管理委员会等部门联合印发了《通知》，进一步明确相关要求。</p><p class="art_p"><strong>3．&nbsp;问：《通知》明确的信贷规则是什么？</strong></p><p class="art_p">答：《通知》明确，未经有权机关依法批准，任何单位和个人不得设立从事或者主要从事发放贷款业务的机构或以发放贷款为日常业务活动。</p><p class="art_p">　<strong>　4．问：《通知》严禁了哪些非法活动？</strong></p><p class="art_p">答：《通知》指出，严厉打击以下非法金融活动：利用非法吸收公众存款、变相吸收公众存款等非法集资资金发放民间贷款；以故意伤害、非法拘禁、侮辱、恐吓、威胁、骚扰等非法手段催收贷款；套取金融机构信贷资金，再高利转贷；面向在校学生非法发放贷款，发放无指定用途贷款，或以提供服务、销售商品为名，实际收取高额利息（费用）变相发放贷款行为。同时，《通知》要求，严禁银行业金融机构从业人员作为主要成员或实际控制人，开展有组织的民间借贷。</p><p class="art_p">　<strong>　5．问：《通知》要求如何开展规范民间借贷工作？</strong></p><p class="art_p">答：一是各银行业金融机构以及经有权部门批设的小额贷款公司等发放贷款或融资性质机构应依法合规经营，强化服务意识，开发面向不同群体的信贷产品，加大对实体经济的支持力度。二是地方人民政府以及有关部门要加强协调配合，依法履行职责。三是银行业监督管理机构、公安机关、工商和市场监管部门、人民银行等有关单位将及时向社会公布典型案例，加大宣传教育力度，强化风险警示，增强广大人民群众的风险防范意识。</p>', 1525448535, 0, 3),
(66, '任志强：为什么坚持说中国房价永远上涨？', 'uploads/MtMR-fzyqqiq859821682983.jpg', '<p class="art_p"><span style="font-family: KaiTi_GB2312, KaiTi;">任志强：为什么坚持说中国房价永远上涨？</span></p><p class="art_p">来源： 直面传媒</p><p class="art_p"><strong>任志强的战争</strong></p><p class="art_p">在房地产界，任志强是最火爆的热点，没有之一。</p><p class="art_p">作为多军的鼻祖，任志强曾挂帅东南，一夫当关，战绩赫赫，一直坚持“中国房价永远上涨”的铁打理念。</p><p class="art_p">其中最著名的战役就是和空军鼻祖房地产“三剑客”、资深经济专家谢国忠等人之间的世纪赌约。</p><p class="art_p">为此，谢国忠在12年的时候还把自己在上海的豪宅变卖。以身立誓，“中国房价一定会崩盘”！</p><p class="art_p">然而，上帝站在了任志强这边，上海房价没降反升，而且紧接着全国房价就迎来了新一轮的上涨周期，三剑客和谢国忠等空军主力惨败沙场，无颜于众，此后匿名于江湖。</p><p class="art_p">而任志强也因此一战成名，成了中国房地产的神话人物，行走的房价走势图。</p><p class="art_p">其实在这场战役上是没有输家的。只是谢国忠等人太过年轻，低估了政府干预经济的能力和人性的贪婪。</p><p class="art_p">在预测房价趋势上，有的专家在分析经济形式，有的在分析人口结构，甚至还有用算法给中国房价做精密数据模型。</p><p class="art_p">但只有任志强在潜心研究‘中国特色’，这就是不同之处。</p><p class="art_p">在非完全交易市场中，只有‘中国特色’才是房价最根本的天大变量！其他因素在它们面前又有什么用呢？</p><p class="art_p">一道政令下来，其他变量立刻归零，之前做的盘算和分析马上土崩瓦解。</p><p class="art_p">譬如海南这次，本来房价已经处于高位，属于锁盘下调的走势，结果直接涉入封死了楼市，然后还天降了个大礼包。换句话讲就是“给了你一巴掌后，又掏出了一颗糖豆豆”，问你甜不甜？</p><p class="art_p">你却只能捂着嘴巴苦笑着说，这糖真好吃，嗡嗡作响的脑袋里只剩下了对未来的幻想。</p><p class="art_p">最后大家也看到了，海南房价卡在那里动弹不得，不让你买也不让你卖，交易消失了，没了交易的市场还存在涨跌吗？这就是‘中国特色’。</p><p class="art_p">所以很多人坚信的‘中国房价崩盘论’是完全错误的，假以时日，真有那么一天中国楼市在高房价中风雨飘摇，岌岌可危，这时候肯定会有人站出来给它打一针高效镇静剂，睡它个一年半载。</p><p class="art_p">所以，在研究楼市的时候，不要单纯的只研究人口、土地、金融等等指标，更重要的是研究中国特色，看懂其背后的意图。</p><p class="art_p">任志强是见证了中国改革开放的人，懂中国特色，了解它的逻辑，所以他在预测房价方面他人难以企及。但一个敢说真话的人，一生注定很坎坷。这也是我最佩服他的一点。</p><p class="art_p"><strong>敢讲真话的任志强</strong></p><p class="art_p">其实，比说不说真话更严重一万倍的是能不能说真话，现在这个时代已经找不到像鲁迅那样敢说真话的人了。</p><p class="art_p">“看懂了真相但没人敢说出来”，这是一个民族的悲哀。</p><p class="art_p">就像历史上晚清闭关锁国一样，所有人都活在一片安逸之中，认为天朝是宇宙老大，无坚不摧。结果当洋枪洋炮轰开中国大门的时候，所有人都吓傻了。</p><p class="art_p">同样的今天，芯片贸易战也给安逸的中国人深刻地上了一课。手机市场再大、销量再好，并不重要，重要的是自己能不能做出手机。</p><p class="art_p">只可惜一直以来企业家中只有任正非看清了这个事实，但当年说出来这个真话时又有几个人听呢？大家只不过都是各忙各的，看谁赚的钱多，谁的份额市场第一而已。</p><p class="art_p">任志强把中国房地产的真话讲出来了，这和学识认知没有任何关系，而是一段段艰苦岁月勾画的亲身经历。</p><p class="art_p">他在《野心优雅》回忆录里，用五十万字记录了他的六十个春秋。从出身高干家庭的家世，到十一年的军旅生活。</p><p class="art_p">从复员后贩卖兔皮起家，到进驻华远地产，并把华远做到上市。状告银行，被穷人扔鞋，被上面暗查，官与商，红与黑，坎坷六十载。</p><p class="art_p">其实我对任老的评价就是，一直在体制中挣扎的“年轻人”！</p><p class="art_p">通读《野心优雅》你会发现任老人生中三个片段特别重要。一是参军表现非常突出，但因不听领导话，被称为“鸡肋”；二是在华远任职经理时，为了激励员工发了一大笔奖金，结果被以“贪污罪”下狱一年多；三是在把华远发展如日中天的时候，华润为了控股万科，活生生把他打下来的基业牺牲了。</p><p class="art_p">他一生都在挑战体制和束缚，最后倒在了‘虽败犹荣’的血泊中。最后他用一生明白了一个道理：</p><p class="art_p">永远不要轻视政府对市场的干预能力，基于基础国情和体制看市场发展才是最正确的道理，所以那些海归派高级经济学家能看懂中国市场吗？</p><p class="art_p">中国需要的是针灸拔罐，不是胶囊药片。</p><p class="art_p">但即使如此，爱说真话的任志强早期却被万众诟病。而且还荣获过一项大奖，就是“中国人最想骂的人”。为什么？</p><p class="art_p"><strong>为什么大家都爱骂任志强？</strong></p><p class="art_p">真话难听，良言逆耳。</p><p class="art_p">当他真正参透中国房地产市场后，提出了主要言论“房价永远涨！”</p><p class="art_p">此言一出，直接刺痛了劳苦大众的神经。现在都买不起房了，还要涨？房价都是被你唱起来的！一呼百应，群而攻之。</p><p class="art_p">所以一直以来，楼市几乎都被空军占领着，支持任志强的人微乎其微，当年房地产三剑客号令千万大军讨伐任志强时，称之为“狂妄至极，罪恶不赦，满嘴放炮”，这也是他“任大炮”绰号的来源。</p><p class="art_p">但是研究空军特征的时候你会发现，70%的人是买不起房的，20%是舍不得买房，最后10%的人认为“房子是用来住的，不具备投资属性”，只能说这群人的思维超前了好些年，毕竟这个口号现在才喊出来。</p><p class="art_p">所以空军在此思维导向下，不满于房价上涨，不满于靠房子发财的人和所有说房价要涨的人。这时候有背景的专家站出来了，大家欢呼雀跃，感觉专家终于把自己想表达的事情说明白了。</p><p class="art_p">于是把精神寄托全部都转移到了他们身上，而对于和自己唱反调的人，恨之入骨。</p><p class="art_p">所以楼市多空军之战其实就是中国阶级的斗争，底层老百姓对有高房价的不满其实就是对有钱人的不满。</p><p class="art_p">这样看，任志强成为被炮轰的对象就很正常了。因为他不仅代表了买不起房的老百姓对房价上涨的恐惧，还代表了资本有钱人的利益。</p><p class="art_p">当任志强对房价的预言几乎全部被应验，空军派主力悉数败下阵来之后，人们的精神寄托土崩瓦解，认清了买房是件遥不可及的事，明白了当年高喊无产阶级万岁本来就是一个弥天大谎。世界永远是有钱人的世界，有房有产才是王道，改不了的。</p><p class="art_p">当年听了任志强的，搭上了顺风车，赚到盆满钵满，听了空军派的永远也没搭上车，到现在还是骂天骂地骂房价。</p><p class="art_p">而且最重要的是，世界上有永远的多军，而没有永远的空军。买了房的空军，立马180度转为多军，比多军更期望房价上涨，人性如此。</p><p class="art_p">其实说这些事情的，并不是让大家去看涨房价，而是要明白一个道理。</p><p class="art_p">‘如果想在楼市中寻找机会投资赚钱，就要尊重房价可以涨也可以跌的事实，而不是凭一己私利站在绝对的一面。’</p><p class="art_p"><strong>我们务必看懂的真言</strong></p><p class="art_p">其实在2015年的时候，我每天都过得非常恐慌，因为低迷的市场、历史低谷的利率、超大的库存都是每次房价上涨周期来临的征兆。</p><p class="art_p">于是疯狂的让身边的人买房成为了我的日常工作。不幸的是，只有少部分买了，大部分嫌贵没买。我亲眼见到朋友原本可以买北京二环的房，却一直在等房价下跌，从二环到三环，从三环到四环，结果最后买到了燕郊。</p><p class="art_p">之后任老在发布“2015年不买房的人，就永远买不起房”的言论之后，又迎来了一波骂潮。可见，“买涨不买跌”的大众心态让多少人迷了心智。</p><p class="art_p">现在还有人对任老的一些言论不太理解，所以针对未来中国房价走势，子木着重挑几点来解释说明。</p><p class="art_p"><strong>1．政策越出，房价越涨</strong></p><p class="art_p">其实在出台的调控政策中，有些是好的，有些是不好的。例如限购和限价是恶性的，而限售和限贷是则是良性的。</p><p class="art_p">限购一出，房子不让买了，大家一想，越不让买的地方肯定越好。这和谈恋爱一样，得不到的永远在骚动，得到的反而不珍惜了。</p><p class="art_p">所以你看，所有人不会考虑未来是否会有人接盘，房子的增值属性如何。哪儿限购往哪儿买，找关系的，送茶水费的络绎不绝。有房子的还想买第二套、第三套，东西越少越要抢。</p><p class="art_p">限价政策才是房价上涨的助燃剂，而且我认为限价政策一定会被写入史册的，用来警示后人。</p><p class="art_p">限价的直接结果是导致新房二手房价格倒挂。一线城市因为该开发的都开发完了，市场发展饱和，二手房比新房多，倒挂是很正常的，但是你三四线城市倒挂是什么鬼？</p><p class="art_p">倒挂是什么意思？二手房卖3万块钱，政府给你批的一手房卖2万块钱。这说明什么？说明有人告诉你，你买了就有1万块钱的利润，那你还不去买？</p><p class="art_p">房价涨到现在其实很容易看出来，上面决心不够，下面贪图享受。有些城市炒房客早就脱身走了，劳苦大众却还在里面玩击鼓传花。</p><p class="art_p"><strong>2、房地产未来还有二十年的红利期</strong></p><p class="art_p">价格来自于需求。如果没有需求就没有价格。而未来20年中国要把城市化完成率达到70%。意味着什么？</p><p class="art_p">意味着，城市化建设还在加剧进行。农村人口往城市迁移，小城市人口往大城市迁移，弱城市人口往强城市人口迁移。中国人口快速流动还有20年。</p><p class="art_p">流动产生需求，需求产生价值。但流动的同时也会让市场分化，城市分化。分化后，强的城市会一直需求叠加，弱的城市需求减退，直至衰亡。</p><p class="art_p">所以，中国未来的房价会一直涨，且还会存在大涨小跌的规律。但是弱的城市一定不会涨，需求没了还怎么涨？如果涨也是通货膨胀的字面意思，例如20年后小县城工资都2万出头了，房价6万有何不可？</p><p class="art_p">但是，作为买房的人一定要踩到这条大涨小跌曲线的谷底，这也就是房产半投资属性的重要之处。因为一个涨幅透支的可能就是你10多年的努力。</p><p class="art_p">最后再说几句。</p><p class="art_p">睿智的人会包容万物，而一个人格局的大小在于他所能接受事物发展的两个极端点之间撑起的空间。</p><p class="art_p">换句话说，你可以接受房价涨到10万，也可以接受到房价跌到1000。最重要的是你可以理性看待房价，能听进去最难听的话。</p><p class="art_p">任志强是中国房地产界的鼻祖，在这里尊称为一声，任老。他用自己的一生践行了他对房价的判断理论，这一点难能可贵，值得学习。但并不意味着其所有理论都是正确的，例如他之前总在为有钱人和开发商说话，对穷人有一种蔑视之情。</p><p class="art_p">不过他后来也明白了，社会发展是以穷人为基础的。</p><p class="art_p">从洛克菲勒的说法来说，当他们富起来的时候发现，穷人没有同时富起来的时候，会破坏他们富有的社会条件，比如说革命，比如说战争。因此他们一定要帮助穷人也富起来，才能让这个社会平和，至少让穷人有机会。</p>', 1525446379, 0, 3),
(67, '媒体人爆料：有人因涉嫌损害伊利的商业信誉被抓了！', 'uploads/p3un-fzyqqiq858187237178.jpg', '<p class="art_p">媒体人爆料：牛逼伊利，又抓人了！</p><p class="art_p">作者：柳絮</p><p class="art_p">来源：直面传媒</p><p class="art_p">5月3日，微博曝出，又有人因涉嫌损害伊利的商业信誉，被抓了！</p><p class="art_p">《直面》注意到，爆料人认证为记者、时评员、金口娱言传媒创始人的@金口娱言。他在其微博爆料说：伊利牛逼，又抓人了！</p><p class="art_p">金口娱言传媒创始人的@金口娱言在微博中贴出了两张截图，其中一张为疑似呼和浩特市GAJ开出的拘留通知书，被拘留者名叫郭玉珍。</p><p class="art_p">这份拘留通知书写到：我局已于2018年4月17日15时将涉嫌损害商业信誉的郭玉珍刑事拘留，现羁押在呼和浩特市第一看守所。落款时间为2018年4月17日。</p><p class="art_p"><strong>图片来自金口娱言微博</strong></p><p class="art_p">而在之前，媒体披露，因传播伊利董事长潘某失联，已有数人被拘留，其中包括两名前媒体人。</p><p class="art_p">中国青年报的报道显示：因在个人公众号上连载小说《出乌兰记》被指影射伊利集团领导层，媒体人刘成昆被内蒙古自治区呼和浩特市J方以“寻衅滋事、诽谤罪”跨省刑拘，目前被羁押在呼和浩特市第一看守所。</p><p class="art_p">报道称：虽涉嫌用小说诽谤伊利，但刘成昆称自己无罪。至此，伊利已成为网络热点，随着另一家企业鸿茅药酒事件的爆发，伊利事件也吸引了众多的目光。</p><p class="art_p">为证实郭玉珍被抓与伊利有关，金口娱言祭出的第二张截图显示：一名叫郭玉珍女士，通过网络实名举报并发出一则《内蒙伊利公司如此欺压奶农 &nbsp;谁来保护弱势奶农利益诉求》的文章。</p><p class="art_p">也就是说，郭玉珍的被抓疑似与这起实名举报有关。但截至目前，未发现官方对此的回应。</p><p class="art_p">《直面》注意到，@金口娱言的微博发出日期为2018年5月3日 21：22&nbsp;分。截止5月4日下午14点11分，共被转发了26579次，点赞23306条，留言近6000条。</p><p class="art_p">梳理发现，网友留言中除了表示很震惊外，更有多名网友留言呼吁抵制伊利。</p><p class="art_p">5月4日早上8点，@金口娱言微博留言说：昨天晚上发的，没想到这么受关注，谢谢大家。如果大家今天不关注现在的他们，很可能明天就变成“后来的我们”。我相信法律是保护好人的，而不是帮助坏人的；我相信人民警察为人民，而不是抓人民。我更相信有关部门会公正处理此事。同时提醒大家客观判断、理性评论，不要地域歧视，不要人身攻击。</p>', 1525446195, 0, 3),
(68, '传中国批准高通与国有大唐电信的子公司组建合资公司', NULL, '<p class="art_p">据华尔街日报消息，知情人士称，中国批准高通与国有大唐电信的子公司组建合资公司，新的高通合资公司将与紫光集团旗下Spreadtrum进行竞争。</p>', 1525446128, 0, 3);
INSERT INTO `pre_news` (`news_id`, `news_title`, `news_pic`, `news_content`, `news_time`, `news_like`, `news_cate_id`) VALUES
(69, '中美贸易谈判进入第二天 美国财长:双方进行友好对话', 'uploads/poDf-fzyqqiq815784963116.jpg', '<p class="art_p">来源：环球外汇网</p><p class="art_p">环球外汇5月4日讯--美国财长努钦周五表示，美国贸易代表团与中方进行了友好的对话。今天谈判将进入第二天，也可能是最后一天。</p><p class="art_p">“我们正在进行友好对话，”他离开入住的酒店时对记者表示，未给出其他评论。</p><p class="art_p">中国目前未对谈判的进展作出评论。此前中国扬言将采取对等的报复措施，包括对美国大豆和飞机加征关税等。</p><p class="art_p">中国日报周五在社论中表示，“两国之间紧张关系的发酵突显出，分歧已经变得非常琐碎，双方握手言欢将非常困难”。社论还称，全球都将祈祷双方达成协议，因为如果谈判失败，将预示着双方互征重税，这可能使全球贸易受创。</p><p class="art_p">在中美两国官员在北京展开贸易谈判之际，美国总统特朗普周四赞扬了自己与中国国家主席习近平的关系。谈判在京西的一个国宾馆进行。</p><p class="art_p">美国的贸易专家称，他们预计中国当局将给美国代表团拿出一系列政策调整方案，其中或许包括之前就宣布过的部分举措，比如逐步取消部分行业的合资企业限制、下调汽车关税以及增加采购美国商品。</p><p class="art_p">安可顾问大中华区主席麦健陆（James McGregor）表示，谈判”极有可能”不会改变加征关税的情况。但美国或许能够使中国相信，美国已从根本上转变了贸易关系方面的方针。</p><p class="art_p">“我认为美国对于让中国做出改变不抱太大幻想，”麦健陆说道，他指出，旨在提升国内制造业的”中国制造2025”战略，是习近平推出的一项重要战略，海外人士则猜测这是一套进口替代计划。“我们想的是如何调整与中国的交流，从而保护美国、美国的创新以及美国的企业。如果采取保护己方的行动能促使中国改变做法，那就是胜利，”他说。</p><p class="art_p">美国贸易代表莱特希泽周二表示，他的目标并非要改变中国的经济体制，而是设法限制其对美国的伤害，并促使中国进一步开放外资竞争。但美国贸易代表团的人员构成多种多样，其成员在考量中国愿意达成的协议时，会有不同的看法。</p><p class="art_p">外界认为，努钦和白宫国家经济委员会主任库德洛（Larry Kudlow）可能青睐那种能够保持金融市场平稳、不会干扰经济强劲增长的方案。</p><p class="art_p">代表团成员还包括美国商务部长罗斯（Wilbur Ross）、莱特希泽以及特朗普贸易及制造业事务顾问纳瓦罗（Peter Navarro）。莱特希泽是强硬派人物，也是一位经验丰富的贸易谈判代表。纳瓦罗在对华贸易问题上也是有名的强硬派。</p>', 1525440955, 0, 3),
(70, '股神永远是股神 巴菲特一季度大幅增持苹果', 'uploads/7hTa-fzyqqiq736943227960.jpg', '<p class="art_p">\n<p class="art_p">【2018年巴菲特股东大会专题报道】</p><p class="art_p">新浪美股讯 北京时间4日消息，据CNBC报道，在苹果公布第二财季业绩之前，华尔街对该公司充满了质疑之声，但这家科技巨头的最大股东之一、“股神”巴菲特却从未动摇过。</p><p class="art_p">今年一季度，巴菲特旗下的伯克希尔哈撒伟公司增持了7500万股苹果股票，使其持股总数达到2.403亿股。</p><p class="art_p">巴菲特在伯克希尔哈撒伟年度股东大会之前披露了这一数字。他表示：“这是一家不可思议的公司。如果你看看苹果，我认为其盈利几乎是美国第二大盈利公司的两倍。”</p><p class="art_p">巴菲特表示，伯克希尔将于周六（5月5日）发布季度盈利报告，其中将包括该公司十大重仓股的信息，精明的读者从中能够发现伯克希尔对苹果的更大兴趣。</p><p class="art_p">苹果周二盘后发布了第二财季业绩报告，净盈利飙升25%，超过市场预期，营收增长16%，iPhone销量达5220万部。该公司还宣布，将股票回购计划的规模扩大1000亿美元。</p><p class="art_p">点击进入专题：<br/> 2018年巴菲特股东大会</p>', 1525431557, 0, 3),
(71, '鸿茅药酒冰火两重天:河南有药房下架 北京药店斥造谣', 'uploads/BSp3-fzyqqiq698019037023.png', '<p class="art_p"><span style="font-family: KaiTi_GB2312, KaiTi;">冰火两重天的鸿茅药酒：河南有药房下架，北京药店店员斥网友造谣</span></p><p class="art_p">文|AI财经社 唐煜</p><p class="art_p">编|祝同</p><p class="art_p">13围绕鸿茅药酒的舆论风暴，这次渗透到了市场层面。</p><p class="art_p">5月2日晚间，媒体人“一个有点理想的记者”发布微博称，鸿茅药酒出现首个大代理商退货，河南张仲景连锁大药房于4月30日将旗下八百多个门店的鸿茅药酒下架，退回配送中心。同时，要求门店严禁私自销售鸿茅药酒，接受顾客之前购买的实货退货。</p><p class="art_p">据“公司深读”报道，目前郑州市、洛阳市、平顶山市、驻马店市、安阳市等地的张仲景药房确实已经不再销售鸿茅药酒。至于何时能再到货，各分店均表示尚不知情，需要等上级通知。郑州多家张仲景分店已经约一个月没有销售鸿茅药酒了。</p><p class="art_p">5月3日，AI财经社走访北京多家药店发现，鸿茅药酒仍作为货架上的热销保健品，鲜少显露出受舆论影响的痕迹。与河南张仲景药房的反应几乎是冰火两重天。</p><p class="art_p">走进国贸附近一家药店，门口货架的正中央就摆放着一排鸿茅药酒。AI财经社以消费者身份询问是否有保健类药酒售卖，店员立即推荐：“鸿茅药酒啊，这是大品牌！”说到最近的网络传闻，一位店员表示自己才刚买了一箱，随后立即辟谣，“东西是没有问题的，现在也一直卖得挺好，都已经有固定顾客了。”店内一位年纪约60多岁的老店员忍不住插话：“鸿茅药酒，这可是非物质文化遗产，网上都是胡说八道。”<span class="bjh-br"></span></p><p class="art_p">这样的说辞在多家药店里屡见不鲜。许多店员将谭秦东对鸿茅药酒的质疑解释为”诋毁造谣“”恶意中伤“”爱出风头“，强调鸿茅药酒是有几百年的历史的老品牌，“要是出现问题，全国各地12小时内必须下架，不然药店就关门，现在没有接到任何通知。“但也有部分店员坦承，鸿茅药酒可能存在某些虚假宣传的问题。</p><p class="art_p">对于鸿茅药酒的功效，店员通常解释为舒筋活血、祛风除湿等，为了打消顾客的顾虑，他们在销售时乐于现身说法。永安里一家药店的老板说，过年回家就给自己老公带了6件鸿茅药酒，治腰腿疼痛效果很好，并提醒AI财经社，单价298元一瓶的不够体面，999元四瓶的套装是送礼的首选，“最近销售完全没受到什么影响，前天还有两老头一人拿了一件呢。”她表示，鸿茅药酒节假日卖得最好，平时一个月可以卖掉6、7件。</p><p class="art_p">但并非所有药店都报以乐观的态度。在世贸天阶一家药店，仅有一瓶鸿茅药酒孤独躺在货架的最底层，表层已经积了一层薄灰。当AI财经社询问疗效时 ，一旁的男店员语气有点不屑：“效果还行吧，不就是做广告吗？说得挺好的。”另一位店员则表示，之前鸿茅药酒卖得挺好的，最近就不行了，“大家也得避开风头，怎么也得有个结果再说。“随后开始热情推荐起放在顶层的益肾养肾丸、熊胆粉等其它保健品。</p><p class="art_p">据报道，在北京同仁堂、白马寺两家知名连锁药店里，目前也并未出现禁售鸿茅药酒的情况。</p><p class="art_p">此前4月16日，国家药监局在官网称，要求内蒙古药监局落实属地监管责任，严格药品广告审批，加大监督检查。并称已组织有关专家，对鸿茅药酒由非处方药转化为处方药进行论证。鸿茅药酒一直走的是撒金铺广告带销售的营销路线，如果转化成功，意味着这款神药不能再靠广告营销，只能凭医生处方销售。</p><p class="art_p">13一位经销商在接受媒体采访时曾表示，现在经销商们最关心的是鸿茅药酒未来的命运。“谭秦东有罪无罪已经不重要，别让药监部门禁销，最终别转成处方药，（鸿茅药酒）就能活下来。“</p><p class="art_p">虽然鸿茅药酒目前在北京药店仍正常销售，但河南张仲景药房的退货事件是偶然还是地震的前兆？这款”神药“的焦虑仍不会停止。</p>', 1525427672, 0, 3),
(72, '年轻有为!冯潇霆获第二十届广东青年五四奖章', 'uploads/vxyQ-fzyqqiq872971766289.jpg', '<p class="art_p">稿件来源：新快报 记者唐星 通讯员岳青</p><p class="art_p">昨日，第二十届广东青年五四奖章评选结果出炉。广东青年五四奖章是团省委、省青联授予广东优秀青年的最高荣誉。马学杰等11名同志、广东省公安消防总队特勤大队等8个集体获第二十届广东青年五四奖章。</p><p class="art_p">获奖个人名单（按姓氏笔画排序）</p><p class="art_p">马学杰 广东省汕头市顺杰农机种养合作社理事长</p><p class="art_p"><strong>冯潇霆中国国家男子足球队队长、广州恒大淘宝足球俱乐部运动员</strong></p><p class="art_p">江晓兵广州中医药大学第一附属医院脊柱骨科副教授、硕士生导师</p><p class="art_p">杨飞飞富士康科技集团C次集团金机虎事业群模具机械加工工程师</p><p class="art_p">沈 丹（女） 腾讯公司党委副书记、公共政策部总经理</p><p class="art_p">林浩添中山大学中山眼科中心研究员、博士生导师</p><p class="art_p">袁浩然中国科学院广州能源研究所城乡矿山集成技术研究室副主任</p><p class="art_p">黄德智广州启源数控技术有限公司技术部主管、高级技师</p><p class="art_p">黄建邦广东省公安厅网络警察总队案件侦查科副主任科员</p><p class="art_p">章秀银华南理工大学电子与信息学院教授、博士生导师、副院长</p><p class="art_p">潘开广广东合地集团股份有限公司董事长</p><p class="art_p">获奖集体名单（排名不分先后）</p><p class="art_p">广东省公安消防总队特勤大队</p><p class="art_p">广州市少年宫雨后彩虹融合艺术团</p><p class="art_p">广州海洋地质调查局天然气水合物试采团队</p><p class="art_p">中山大学肿瘤防治中心鼻咽科</p><p class="art_p">中远海运特种运输股份有限公司永盛轮</p><p class="art_p">汕头市龙湖区呼援通社工服务中心</p><p class="art_p">华南农业大学水稻育性发育科研团队</p><p class="art_p">陆丰市禁毒委员会办公室</p><p class="art_p">点击进入专题：<br/> 中超联赛</p>', 1525447821, 0, 4),
(73, '皇马欧冠三连冠？克式利物浦这次不怕皇马！', 'uploads/5XBR-fzyqqiq197388641707.jpg', '<p class="art_p">欧冠决赛最终的对阵确定，皇马将和利物浦上演巅峰对决，争夺最终的冠军奖杯。在这组对阵出炉之后，舆论显然更看好皇马夺冠。问题来了，利物浦真就惧怕皇马吗？</p><p class="art_p">赔率不会说谎，目前皇马的夺冠赔率是1赔1.72，而利物浦的夺冠赔率为1赔2.10。你很容易列出多条皇马更接近冠军的理由，比如皇马经验丰富，近四赛季三次夺得欧冠，比如皇马有无所不能的C罗，比如皇马有强大且平衡的阵容等等。但是，利物浦面对皇马真就没机会吗？答案显然是否定的。克洛普带领的利物浦，也有足够不用惧怕皇马的理由。</p><p class="art_p">　<strong>　皇马的防守未必能防住利物浦三叉戟</strong></p><p class="art_p">利物浦的身上一直笼罩着一个鲜明的标签——劫富济贫。意思是利物浦踢强队战绩好，踢弱队战绩一般，这其中的深层次原因，自然在于战术方面，克洛普的球队攻防奇快，讲究用快速推进抓住对手的空间。面对强队时，因为对方往往不会放下身段死守，这会给利物浦进攻空间。面对弱队时，对手通常铁桶阵死守，利物浦的进攻也就不再能摧枯拉朽。</p><p class="art_p">那问题来了，皇马会给利物浦进攻空间吗？答案显然是肯定的。《squawka》给本赛季的欧冠球队进行了传控评分，皇马以1269分排在第一名。而场均控球率方面，皇马达到了55.1%，也就是说，皇马是一支喜欢把皮球控制在自己脚下的球队。这也意味着，皇马将会给利物浦快速反击留下发挥空间。看看利物浦1/4淘汰赛的对手曼城，他们就酷爱控球，结果却惨遭利物浦双杀，被淘汰出局。</p><p class="art_p">具体到站位，皇马在控球或者进攻时，通常只留4人防守，即1名中场+3名后卫。这一名中场，指的是克罗斯或者卡塞米罗，他们两人中一人支援进攻时，另一人就会拖后防守。防线方面，马塞洛通常插上助攻，提高皇马的进攻推进能力，其余3人则会留后防守。这种进攻时的防守体系，会使得这名单后腰的责任更为重大。但《squawka》评论指出，卡塞米罗对空间的理解不够，这会导致皇马的两肋有较大空当，这就意味着，萨拉赫和马内届时将会有很大的发挥空间。</p><p class="art_p"><strong>皇马整体实力在滑落</strong></p><p class="art_p">过去两个赛季，皇马无疑是欧洲头号大boss，他们连续两年赢得欧冠冠军，而且在上赛季他们同时加冕了西甲冠军。一时间，人们都惊呼皇马王朝诞生了。但到了本赛季，这支皇马却在快速滑落，西甲中皇马已落后巴萨多达15个积分，欧冠中，皇马从1/4淘汰赛开始，也踢的无比艰难。面对尤文图斯，皇马次回合险些被尤文图斯翻盘。踢拜仁也是，两场比赛下来，拜仁踢的并不逊于他们，甚至创造了更多的进球机会。</p><p class="art_p">很难说皇马实力下降的真正原因，这或许关乎精神欲望，或许关乎于某些关键球星的下滑。但至少皇马这两方面的下滑是有目共睹的，第一，皇马的中场实力有不小下滑，莫德里奇随着年龄增长状态起伏，克罗斯不再既能组织又能兼顾防守，这让皇马的中场不再是碾压对手的存在。第二，皇马太依靠C罗进球，本赛季欧冠中的皇马，只有C罗一个稳定的进球点，其他人无法为皇马提供稳定的火力输出，在这种情况下，对手也就相对好防守皇马。</p><p class="art_p">种种因素叠加之下的结果是，皇马的统治力和实力，确实比之上赛季有下降，至少，其他球队击败皇马，已经不再是不可能完成的任务。</p><p class="art_p"><strong>克洛普擅长踢强队</strong></p><p class="art_p">克洛普入主利物浦后，让球队有了一种神奇的特质——专杀强队。看看克洛普上任至今，在英超中面对曼联、曼城、阿森纳、切尔西的战绩：9胜9平3负。正是有了这种特点，利物浦本赛季才能在欧冠中大发神威。如今面对欧洲最强boss皇马，利物浦能爆发出怎样的恐怖战力，这无疑也是人们津津乐道的事，至少利物浦已经给了人们无限的想象空间。</p><p class="art_p">值得一提的是，2016年夏天，克洛普还曾带领利物浦，以4-0的比分战胜巴塞罗那，虽然那是一场热身赛，但利物浦的面对欧陆豪强的战力，由此也能窥得一二。本赛季利物浦在欧冠中踢了12场比赛，他们的进球数达到了惊人的40球，高居榜首。由此可见，利物浦这种球风，在强队云集的欧冠赛场上，才更加的如鱼得水。接下来到底是专杀强队的利物浦终结皇马霸业，还是银河战舰碾压 一切对手勇夺三连冠，我们拭目以待。</p><p class="art_p">（小九）</p><p class="art_p">点击进入专题：<br/> 体育睡前一读<br/> 国际足球原创专栏</p>', 1525447668, 0, 4),
(74, '知情人:肇俊哲进国家队可能性很大 具体职务待沟通', 'uploads/6DkL-fzyqqiq852977343079.jpg', '<p class="art_p">昨天，中国男足意大利籍主帅里皮、领队刘殿秋分别从广州、北京飞赴阿联酋迪拜，准备参加于今晚在迪拜阿玛尼酒店举行的2019年亚洲杯分组抽签仪式。据了解，中国足协代表将在抽签结果出台后第一时间在现场助国足“锁定”今年下半年的热身对手。而中国足协特派工作组已于近日赴卡塔尔，帮助国足落实亚洲杯前备战训练营安排事宜。</p><p class="art_p"><strong>抽签现场开始物色热身对手</strong></p><p class="art_p">昨天下午，里皮离开广州奔赴阿联酋迪拜，而中国足协国管部主任、国足领队刘殿秋也乘坐下午5点多的班机由北京前往迪拜。两人一同参加抽签仪式，显然不是代表国足“刷存在感”，而是要利用此次仪式举办之机了解竞争对手最新情况，同时为国足争取一些有利的备战条件。</p><p class="art_p">根据亚足联推出的最新分档原则，参加世预赛12强赛的球队按成绩及东道主因素分别被纳入亚洲杯第一、二档之内。中国队与叙利亚队、乌兹别克斯坦队、伊拉克队、卡塔尔队、泰国队同处在第二档，这意味着中国队落入“死亡之组”的几率降低。由于中国队并没有世界杯正赛参赛任务，因此今年全年球队都将围绕着亚洲杯任务进行备战。在此之前，中国队已经确定分别于5月26日、6月2日与约旦队、泰国队进行热身。至于本年度余下6场热身赛，国足也将尽可能与亚足联球队进行争夺。刘殿秋与来自协会国管部、竞赛部的两位业务骨干同赴迪拜的一个重要目的正是为帮助国足锁定下半年的热身对手。</p><p class="art_p">按照事先设计的方案，国足将于9月初赴多哈与卡塔尔队进行一场热身赛，随后再回到主场与来自A档的非同组球队热身，目前主要目标锁定在澳大利亚队身上。不过一旦中国队与澳大利亚队同组，那么热身对手就将出现变数。据悉，中国足协的代表将在抽签结果出台后第一时间联系热身对手。而作为亮相本次仪式的最大牌教练，里皮的现身无疑也有益于吸引潜在的热身对手。</p><p class="art_p"><strong>备战亚洲杯不逊于12强赛</strong></p><p class="art_p">就在刘殿秋、里皮前往迪拜前几天，中国足协特派“先遣组”已飞赴卡塔尔多哈。据了解，该小组此行主要目的就是为国足亚洲杯前的备战冲刺物色合适的集训大本营。虽然亚洲杯赛到明年1月5日才揭幕，但考虑到国足面临新老接替，需要为下一届世界杯冲击作储备，中国足协对球队此次亚洲杯之行也格外重视。按计划，国足将于今年12月15日开始进行亚洲杯前最后一个阶段的集训。考虑到西亚气候条件的特殊性，中国足协同意球队提前赴西亚备战。卡塔尔与阿联酋气候相近，且由多哈至迪拜等阿联酋城市间的飞行时间大多只有一个多小时，因此“先遣组”径直赶赴卡塔尔进行考察。</p><p class="art_p">而此次考察实际也是为9月的热身做准备。除了训练条件外，当地的饮食、居住条件也是“先遣组”考量的重要目标。中国足协将尽可能为国足找到一处气氛安逸、条件优越的训练大本营，同时在赛前为球队找到部分热身对手，以确保球员的竞技状态。</p><p class="art_p">“先遣组”成员当地时间昨晚也已由多哈赶到了迪拜，这是因为他们需要将考察的信息及时回馈给里皮，同时利用人脉，协助教练、领队物色热身对手。在距开赛还有半年多的时间里，围绕国足亚洲杯备战的各项细节工作就已经展开。中国足协虽然没有明确国足本次出征的具体任务，但从备战规格和重视程度来看，协会还是非常期待国足在亚洲杯上有所斩获。</p><p class="art_p"><strong>“中国杯”惨败后国足求变</strong></p><p class="art_p">虽然距离“中国杯”落幕已经过去一个多月时间，但“0比6”似乎并不能迅速从外界的记忆中消退。中国足协在过去一段时间里，实际也围绕国足在“中国杯”中暴露的问题进行了深入总结，包括球队教练团队、管理层成员都根据分工参与了总结。在这个意义上，5月26日主场与约旦队的热身对国足而言意义非同寻常，中国足协也格外期待球队能够展现出更为积极的姿态。据悉，虽然国足新一期集训名单尚未公布，但据了解，即将于5月22日在南京江宁集中的国足很可能会出现相当幅度的阵容变化。除了能力、状态之外，进取心、态度将成为里皮考量国脚综合素质的重要依据，国足的血液更新或许也将借此次热身之机进一步提速。需要说明的是，已经为国出战98次的老队长郑智虽然能力、品质俱佳，但因为身体原因，很难参加新一期国家集训队。</p><p class="art_p">除了队员外，国足管理团队也存在变数的可能性。近期，前国脚肇俊哲与另一位前国脚邵佳一的身影频频出现在中国足协办公处及有关足球工作的重要会议现场，这让人不由联想到有关传闻。而据知情人透露，肇俊哲进入国家队的可能性非常大，但其具体角色如何，恐怕还需要依据足协与教练团队沟通的最终结果。</p><p class="art_p">文/本报记者 肖赧</p><p class="art_p">点击进入专题：<br/> 聚焦中国国足<br/> 男足国家队</p>', 1525445530, 0, 4),
(75, '小丁团队与多支NBA球队接洽 有望再战夏季联赛', 'uploads/tCk3-fzyqqiq799644153480.jpg', '<p class="art_p">丁彦雨航已经登上了奔赴美国的飞机，ESPN记者袁方对此进行了详细报道。</p><p class="art_p">袁方在微博上写道：”刚刚登上赴美飞机的丁彦雨航将首先在洛杉矶对伤病进行康复，然后将前往纽约，在曼哈顿和NBA著名私教克里斯-布里克力进行特训。“</p><p class="art_p">袁方对这位训练师进行了介绍，他写道：”如果各位还记得卡梅罗-安东尼去年夏天流传到网上的一系列带着帽衫在纽约的夜晚和球馆中训练的视频的话，他背后的教练就是这位克里斯，克里斯同时还是杜兰特、利拉德、CJ等多名球员的首选私教。能获得克里斯的训练，相信小丁的技术和身体将再上一个台阶。“</p><p class="art_p">另外，袁方还提及了小丁的NBA合同问题。他写道：”至于大家最关心的NBA合同，现在说什么都还为时过早，小丁新聘请的美方经纪团队正在和包括达拉斯在内的多支球队进行接洽。小丁最期待的还是达拉斯人可以兑现承诺，给他一纸体面的合约，但是在成人社会，不能听别人怎么说，而是要看别人怎么做，达拉斯绝不是小丁今夏的唯一出路。“</p><p class="art_p">袁方认为小丁今年有望继续出战夏季联赛，他写道：”不出意外，我们会继续在拉斯维加斯的NBA夏季联赛上看到他的身影。去年没讲完的故事今年继续聊，去年没抢到的饭碗今年接着拼，千里之行始于足下！反正我天天报道外国人打球真的是够了，赛后采访他们还都不会说中文！“</p><p class="art_p">本赛季效力山东队期间，丁彦雨航场均得到26分5.3篮板3.2助攻。在去年的奥兰多夏季联赛中，丁彦雨航在决赛中得到13分3篮板2助攻2抢断，帮助独行侠夺冠。</p><p class="art_p">（风之子）</p>', 1525439070, 0, 4),
(76, '名嘴给格林道歉杜兰特库里却不干！还暗讽他软', 'uploads/C39Y-fzyqqiq784897398954.jpg', '<p class="art_p"></p><p class="art_p">北京时间5月4日消息，据《thescore》报道，此前德雷蒙德-格林和查尔斯-巴克利打嘴仗，事后，针对“我想揍格林”的言论，TNT评论员查尔斯-巴克利公开向格林做出道歉。但凯文-杜兰特和斯蒂芬-库里都表示他们不接受巴克利的道歉。</p><p class="art_p">“我说了一些我本不应该说的话。”巴克利在道歉时说道，“我希望像一个男人那样站出来道歉，我100%错了。”</p><p class="art_p">格林曾表示“如果你不打我，那你就闭嘴”，而巴克利则表示“我并不会真的和一名NBA球员打架”，但同时也表示“罗德曼说过同样的话，而我真的揍了他”。</p><p class="art_p">格林的队友杜兰特和库里认为，巴克利并不是真诚地想对格林道歉。</p><p class="art_p">“他说了他所说的，巴克利不会打任何人，就像德雷蒙德所说的那样，我也不认为在他打球的年代他会那么做，”杜兰特说，“我不知道为什么他看到新一代篮球选手打球就总是生气，也许他就是这样吧。”</p><p class="art_p">“我不知道为什么，我认为他可能是认真的，但是这都过去了……他需要呆在演播室里看比赛说球，而我们则要出场去打比赛，这是我们所喜欢的。我敢肯定，在他那个时代，他有机会打很多人，但是他也并没有出手啊。”杜兰特说。</p><p class="art_p">库里则表示，如果自己像巴克利这样道歉的话，他的妈妈不会放过他。</p><p class="art_p">“小时候，如果我某件事做错了，然后老妈让我道歉，而我说，‘我错了，但是我依然是那么认为’，那我肯定会被老妈一拳打脸。”库里说。</p><p class="art_p">（咕哒）</p>', 1525436960, 0, 4),
(77, '双状元+探花被冰时最强！ 寸草不生变垃圾兄帝', 'uploads/RWUO-fzyqqiq771705070154.jpg', '<p class="art_p">北京时间5月4日，费城76人队客场挑战波士顿凯尔特人，此役两队激战四节，最终76人以103比108被对手逆转。回溯比赛最后一节，主教练布伦特-布朗的用人安排还是值得商榷。</p><p class="art_p">本赛季季后赛期间，状元秀马克-富尔茨一共只打了3场比赛，场均出战时间仅有7.7分钟。主教练布朗的用人安排，逐渐被外界有所质疑。在东部半决赛首战中，杰森-塔图姆的出色表现让人们再次将焦点对准富尔茨，他何时才能得到表现机会，与探花郎一决高下呢？主教练布朗在赛前表示，本场比赛可能会安排状元秀出战。</p><p class="art_p">然而全场比赛富尔茨没有得到任何机会，更让76人队感到遗憾的是，他们第四节还剩6分钟时，一度用一套小阵容收到了奇效。在被对手逆转反超后，这套由科温顿、萨里奇、麦康奈尔、雷迪克和伊尔亚索瓦组成的小阵容，打出一波11比2的攻击波，又一次将比分优势夺回。</p><p class="art_p">今天本-西蒙斯发挥不佳，运动战一分未得，全场-23的正负值能够表现出他的低迷。但关键时刻布朗主教练仍旧亲信他和恩比德，执意将伊尔亚索瓦和麦康奈尔替换下场。</p><p class="art_p">结果造成了凯尔特人又一轮的疯狂反扑，在西蒙斯和恩比德最后5分30秒上场后，76人队被对手打出17比10的攻击波，最终葬送胜局。领先21分被反超不可怕，最后时段手握优势，但关键的轮换出现崩盘，恐怕布朗主帅赛后需要谨慎思考的问题。</p><p class="art_p">（珅葳）</p>', 1525435256, 0, 4),
(78, '36-0！他们进东决螺旋稳 又是詹皇最熟悉敌人', 'uploads/FbBq-fzyqqiq769225848980.jpg', '<p class="art_p">北京时间5月4日，波士顿凯尔特人队与费城76人的第二场东部半决赛，两队激战四节，最终坐镇主场的绿衫军最多落后21分的情况下，以108比103逆战取胜。总比分2比0领先的情况下，凯尔特人基本上一只脚已经踏进东部决赛的大门。</p><p class="art_p">今年1月12日两队常规赛交战期间，凯尔特人队曾一度落后76人多达22分，但最终以114比103完成大逆转。而今天76人再次领先21分的情况下，又一次被“克星”绿衫军逆转。</p><p class="art_p">历史数据显示，在凯尔特人征战季后赛的历史上，他们前两个主场全部拿下时，36次全部拿下整个系列赛的胜利，至今无一失手。另外在对手76人队身上，同样保有着一项极为被动的数据统计。</p><p class="art_p">当76人队征战季后赛输掉前两场后，0比2落后的情况下，他们有11次全部输掉了系列赛。</p><p class="art_p">在缺少凯里-欧文和戈登-海沃德两元大将的情况下，凯尔特人本赛季季后赛已经取得六连胜。历史证明他们晋级下一轮东部决赛，仅是时间问题。不过年轻的76人能否继续制造悬念，打破定律，值得期待。</p><p class="art_p">（珅葳）</p>', 1525434846, 1, 4),
(79, 'NBA都是录播又1铁证！骑士连去年的带子都没换', 'uploads/1lbf-fzrwiaz349684470358.jpg', '<p class="art_p"></p><p class="art_p">北京时间5月4日，本届季后赛第二轮4组对决前2场皆战罢，有3组战成2-0。而今天2场比赛结果，又使得克利夫兰骑士“神复制”去年本队季后赛征程的可能性大增。</p><p class="art_p">今天进行了2场东区第二轮G2，结果骑士和凯尔特人分别取得总比分2-0的领先，唯一的不同是骑士在客场连下2城，对猛龙的逼迫更为强烈。</p><p class="art_p">但从历史几率来看，东区第二轮分别仅进行了2场，东区决赛的对阵双方貌似就快出炉了。骑士和猛龙这组，勒布朗-詹姆斯所在球队共21次在系列赛取得2-0领先，全部晋级；猛龙6次0-2落后，全部出局；凯尔特人和76人这组同样如此：绿军36次2-0领先，全部晋级；76人11次0-2落后，全部出局。</p><p class="art_p">倘若骑士和绿军真的相遇于东区决赛，骑士拥趸可能要感慨往日重现了。去年季后赛，骑士也是接连淘汰了步行者、猛龙和凯尔特人，从而问鼎东区的，今年至少前2轮和去年完全一致。</p><p class="art_p">对此，有媒体调侃称：“原来NBA真的是录播，骑士连去年的录播带都没换！”</p><p class="art_p">也有球迷调侃说道：“难道骑士今年又要在总决赛被勇士虐一遍？年年如此，要不要这么无聊？”</p><p class="art_p">季后赛的奇妙正在于此，正如首轮鹈鹕“下克上”，以4-0横扫开拓者也出乎大多数人预料一样。待4组对决G3、G4战罢回头再看，或许又会有不一样的风景。（魑魅）</p>', 1525434403, 0, 4),
(80, '20+9+7！哥教你啥叫相信过程 欧文复出也替补?', 'uploads/ST44-fzyqqiq763378188864.jpg', '<p class="art_p">北京时间5月4日，东部半决赛第2场，凯尔特人在主场以108-103战胜76人。凯尔特人首发控卫特里-罗齐尔出场38分钟，15投6中，三分球9投4中，得到20分，外加9次助攻和7个篮板。</p><p class="art_p">今年季后赛，罗齐尔的表现成为了凯尔特人的一大亮点。首轮对阵雄鹿，罗齐尔的表现完胜埃里克-布雷德索，而且在系列赛结束之后得到了布雷德索的认可。</p><p class="art_p">半决赛第1场比赛，罗齐尔在进攻端彻底爆发，他18投11中，三分球9投7中，得到29分，以至于一些美国媒体调侃称等到凯里-欧文伤愈复出之后可能要打替补了。</p><p class="art_p">今天这场比赛，罗齐尔依然是凯尔特人队里表现最出色的之一，特别是他能够在球队最需要的时候站出来。</p><p class="art_p">本场第二节，76人一度领先22分。但是本节后半段，凯尔特人展开反击，还剩3分多钟，罗齐尔连着助攻门罗和布朗得到5分，最后还剩1分多钟，罗齐尔先是命中一个三分，然后助攻斯玛特命中三分。最后34秒，罗齐尔又命中一个中距离跳投。这样一来，凯尔特人在半场结束时追到只差5分。</p><p class="art_p">第四节，76人曾经重新获得领先，而带领凯尔特人反超的又是罗齐尔。本节后半段，罗齐尔连中2个三分，帮助凯尔特人反超比分。</p><p class="art_p">最后2分23秒，罗齐尔抢到后场篮板之后发动快攻，并助攻塔图姆扣篮得分，凯尔特人领先6分。至此，76人大势已去。最后还剩8秒，罗齐尔助攻霍福德上篮得分，这是一个杀死比赛的上篮。</p><p class="art_p">全场比赛，罗齐尔15投6中，三分球9投4中，得到20分，外加9次助攻和7个篮板。此外，+17的正负值全队第二，仅次于霍福德（+21）。罗齐尔如果继续这么出色的发挥，球迷们估计都快忘记欧文了。</p><p class="art_p">（罗森）</p>', 1525434112, 0, 4),
(81, '唏嘘!走访武里南其配置让人羡慕 为何我们做不到?', 'uploads/GxkR-fzyqqiq766262350611.jpg', '<p class="art_p">稿件来源：大佬鸣 大佬鸣（丛硕鸣）的体育天地</p><p class="art_p">近期，一则足协“限薪令”的消息在坊间传开，说是到2021赛季所有俱乐部的球员薪酬不能超过收入的75%。由此话题结合之前恒大俱乐部公布的2017年年报，关于中超俱乐部盈利的话题再度被热议起来……实际上诸如此类的深度解读之前就出现过不少，且不谈其是否正确、是否符合实情，其意义、价值到底有多大，就值得先探讨一番。有时候，我们总爱将自己放诸于一个很高的层面、平台去进行类比，却不知自己的现状还远远未到可以与之相比的境地！在笔者看来，在眼下中超存在诸多仍属于瓶颈的问题且无从解决的基础上，去谈论所谓的中超俱乐部谈盈利的问题，为时过早，甚至可以说是庸人自扰。</p><p class="art_p"><img class="" src="https://n.sinaimg.cn/sports/crawl/116/w550h366/20180504/0djB-fzyqqiq7621825.jpg" alt="" data-copyright="0" data-ratio="0.66625" data-s="300,640" data-type="jpeg" data-w="800"><span class="img_descr"></span></p><p class="art_p">首先，说清一个道理就得追根溯源。此问题的引出，源于新三板挂牌的中超俱乐部恒大淘宝发布的2017财报——在2017年，恒大淘宝实现营业收入5.28亿元，同比减少5.91%；亏损扩大至-9.87亿元，而上一年这一数据为-8.12亿元。球迷朋友们看到了“亏损表现”感觉特别扎眼，甚至开始质疑号称国内最职业的恒大俱乐部，在运营上也要走下坡路了？非也！作为一家主营业务为足球俱乐部运营的公司，恒大淘宝的业务模式和盈利模式与其他足球俱乐部并无二致。之所以如此，完全是因为恒大淘宝是国内唯一一家挂牌新三板的足球俱乐部股份有限公司——需要面向公众如实公布财务报表。而中超联赛中其他同样呈现巨亏的足球俱乐部，则不必向球迷和外界露出账面垮塌的深坑。然而他们倘若用相同的标准去计算，相信亏损程度不会比恒大淘宝低。特别是一些成绩与投入相距甚远的球队，相信他们绝对不敢公布年度财报。</p><p class="art_p">记得有人说过，“如果说谁告诉你他做足球赚钱了，那肯定是做假账。要想让俱乐部不亏损，这就是个笑话，要盈利的话就不要做足球了……”尽管说做假账有些夸张，但在中国“上有政策，下有对策”的情况比比皆是。很多俱乐部负责人也开玩笑般承认，想要俱乐部实现盈利，只要请一个好的财务总监便能搞定。事实上大家都没有过多在意这个问题，原因很简单——对中超大俱乐部的母体来说，足球只是他们产业布局中很小的一部分。所以，只要母体持续有丰沛的盈利能力，就不怕俱乐部赔钱。对于母体来说，足球一是公益，二是母公司形象宣传的一个最好的载体。这才是一切的初衷，因此拿着恒大俱乐部亏损话题不放的人，根本就没搞清楚大环境是怎样。</p><p class="art_p"><img class="" src="https://n.sinaimg.cn/sports/crawl/116/w550h366/20180504/kYxI-fzyqqiq7621887.jpg" alt="" data-copyright="0" data-ratio="0.66625" data-s="300,640" data-type="jpeg" data-w="800"><span class="img_descr"></span></p><p class="art_p">在清晰源头问题真相后，再来分析一下中超俱乐部能否盈利，自然就简单了不少。众所周知，眼下的中超联赛是一个基本由中国足协以及中超公司把控的联赛，试问对于一家联赛旗下俱乐部而言，在最基本的运营自主权都近乎丧失的前提下，谈何盈利？拿什么盈利？如何才能做到盈利？毕竟可以运作的能够盈利的空间都被尽数剥夺了，再去谈什么俱乐部盈利，简直就是痴人说梦！</p><p class="art_p">而在这个层面咱就不存在和世界比较的余地；欧洲俱乐部尤其是豪门俱乐部的收入主要来源于三部分：转播权分成、商业收入和票房。而在中超，票房收入需要亚冠淘汰赛这样重量级比赛带动才可能有飞跃，但这个层级的比赛是想进就能进的？一般俱乐部票房收入也仅仅是几千万级别；商业收入方面，很明显眼下中国球迷消费习惯以及足球文化基础还比较薄弱，俱乐部即便在周边产品开发上很用心，收益也是微乎其微。球衣广告是仅剩的中国俱乐部收入来源的大头，而这恰恰是中国俱乐部营收比例与欧洲俱乐部最大的区别。</p><p class="art_p">而所有俱乐部最想改善的是，电视转播权分成等收入，却难有起色。中超商业价值最近两年有明显提升，2015年10月，中超签下了一份5年80亿元的转播合同，2017年5月22日，平安五年10亿冠名中超。但现在中超俱乐部能够得到多少转播分成？还是以恒大为例，2016赛季恒大仅仅得到6000万元分红，2017年这一数字也才达到7000万元。按照5.28亿的营收计算，电视转播的分成所占比例不到14%，这和欧洲有天壤之别。要知道，曼联在转播方面的收入达到了1.941亿英镑，占据5.812亿英镑营收的33.4%。现实很残酷，最应该辅助俱乐部盈利的项目，却一直在“拖后腿”。</p><p class="art_p">综上所述，企业的高投入推动了中超价值，而中超价值的提升则是未来所有俱乐部从中获利的前提，高投入是中超孵化盈利的必由之路，而任何对这一路径的阻断，则会令中超重回刀耕火种的时代，丧失活力，小本经营，勉力维系。因此在各自俱乐部严重丧失运营自主权，在许多层面只能遵循上方制定的所谓条例，只能根据已由别人代为决定的相关部门进行合作，没有自主去挖掘盈利的空间，谈何盈利？</p><p class="art_p"><img class="" src="https://n.sinaimg.cn/sports/crawl/162/w550h412/20180504/3e_O-fzyqqiq7621945.jpg" alt="" data-copyright="0" data-ratio="0.75" data-s="300,640" data-type="jpeg" data-w="1280"><span class="img_descr"></span></p><p class="art_p">然而就在本赛季，笔者由于跟随恒大队远征来到泰超武里南联队所属主场区域进行走访，对此有了更深的感受。当时除了对比赛的期待，更多朋友是来到武里南联队的主场区域后，被眼下所看到的一切所震撼，被随后自己所经历的一切所震撼！是的，尽管武里南这个地方经济不发达，但当进入武里南联队主场区域后，却是呈现出另一番景象。特别对于很多第一次到这里的球迷而言，感受是更深的。</p><p class="art_p">一个巨大的钢架型专业足球场出现在所有人眼前，体育场从内到外充盈着与武里南联队相关的各种元素，非常壮观！这样的主场，极有主人翁架势，一看就让人感觉是自家人的地盘，当然也有点英超的感觉，毕竟泰超在很多方面都模仿了英超的发展模式。然而给笔者更大震撼的，也是距离球场仅五分钟步行距离的一家武里南联队足球主题五星级AIMARI酒店！这样一来就跟英超更像了！然而有幸入住这家酒店，完全被他的制式所深深吸引。毕竟在专业球场附近配备具有主队特色的五星级酒店的情况，英超球队有过，武里南联队可以说移植了这套模式，然而酒店内设计从大到小，甚至是很多细节处，完全带有该球队的特色，感觉特别充满球迷属性，真是个不错的设计。好比你想买武里南联队的球衣，除了可以去周边的2家官方商店购买外，也可以在这个酒店大堂买到，完全满足了顾客的便利性。同样让笔者震撼的，自然还有位于球场西南侧的官方产品直营店。一进入其中，最先的感觉就是一个字：大！偌大的店面，却没有重复的摆设，每一个区域都有自己的职能，售卖着各种各样的官方纪念品。这足以证明武里南联足球俱乐部对于此方面的开发很重视也很用心，而满屋正在挑选的球迷、付账处排起长龙的球迷，都可以印证当地球迷文化水准之高，大家对所有的产品均有认可度！更让人难以置信的是，第二天准备离开酒店之时，笔者又去走了一趟这家官方球迷产品店，看到竟然一开门就有不少球迷涌入争相挑选产品，这天可不是比赛日啊，付账处居然还是排起了队伍！不可否认，当地的球迷文化确实有一定的水准，大家对于这支球队的感情非常深啊！</p><p class="art_p"><img class="" src="https://n.sinaimg.cn/sports/crawl/162/w550h412/20180504/ofXA-fzyqqiq7621999.jpg" alt="" data-copyright="0" data-ratio="0.75" data-s="300,640" data-type="jpeg" data-w="1280"><span class="img_descr"></span></p><p class="art_p">没有对比就没有伤害，一个严肃、严峻，甚至有些无奈的问题油然而生——为啥我们做不到这一切？我们难道就不能做得比现在更好吗？武里南这样一个地方出了这样一支球队，而综合实力更具优势的广州，同样有一支冠军球队、常胜将军，为啥我们不能也在这些层面做的跟他们一样好，甚至更好呢？事实上目前咱连专业球场都没有着落，要实现这一切，真是任重道远啊！专业球场附近配备主队属性及特色的五星级酒店，甚至配上整个商圈又怎样？我们没有实力去完成吗？更多的阻力与难关，大家心知肚明，并不源于财力，就是因为咱没有运营的自主权这一条！无奈，球迷朋友们对比后，更多的是唏嘘无奈。</p><p class="art_p"><img class="" src="https://n.sinaimg.cn/sports/crawl/162/w550h412/20180504/_KuO-fzyqqiq7622063.jpg" alt="" data-copyright="0" data-ratio="0.75" data-s="300,640" data-type="jpeg" data-w="1280"><span class="img_descr"></span></p><p class="art_p">虽然不知道泰超霸主球队的运营时候能够盈利，但对他们还是心生羡慕的，毕竟人家有那么高的运营自主权，的确拥有盈利的空间。而相较之下，恒大产业布局日益宏大科学，企业实力不断飞升，在足球上的这点亏损是计划内的，更是不会去算计。况且中超现在的环境，近乎没有俱乐部可以做到真正盈利，再去过度解读这些问题，真的没有意义。</p><p class="art_p">点击进入专题：<br/> 中超联赛</p>', 1525433950, 0, 4),
(82, '别瞎猜了！设计师：皮卡丘的原型是松鼠', 'uploads/dzau-fzyqqiq902617762005.jpg', '<p class="art_p">[环球网报道 记者 赵会芳]游戏《精灵宝可梦》和根据它改编的动画中的虚构角色皮卡丘一直备受大众喜爱。关于皮卡丘原型是什么的问题存在很大争议，大众比较广泛认同的可能原型为老鼠、龙猫以及花栗鼠，还有人认为其原型是短尾矮袋鼠和兔子。近日，皮卡丘开发公司——日本GAME FREAK公司首次公布称，皮卡丘的原型是松鼠。</p><p class="art_p">俄塔社5月3日援引日本《读卖新闻》报道称，皮卡丘主设计师西田敦子最初在创作该卡通形象时，曾收到公司请她设计出一个可爱的小怪物，并且这个小怪物要会放电袭击对手、有两个进化阶段的要求。</p><p class="art_p">西田敦子称，开始创作皮卡丘时，她曾受到日本甜点大福饼（夹心糯米团，类似放大版的汤圆）的启发。她给这个圆形的甜品加上富有表现力的耳朵和其它一些小怪物的特征，并将其外形塑造成更加纤长的形状。最后，为了使皮卡丘的形象更加可爱，西田敦子又给这个小怪物加上了松鼠的特征，因为那时她正好想养只小松鼠作为宠物。</p><p class="art_p">报道称，根据《宝可梦图鉴》中的描述，皮卡丘身高40厘米，体重6公斤。它全身的皮毛都是亮黄色的，尾巴呈闪电状，脸颊上的红色圆点是储存电能的地方。</p><p class="art_p">皮卡丘自出世以来就成为了经典的卡通形象，并在世界范围内享有声誉。2016年智能手机游戏《精灵宝可梦Go》（《Pokemon Go》）的出现又掀起了人们追捧皮卡丘以及所有口袋妖怪的热潮。日本横滨市每年8月份甚至还会举办皮卡丘文化节。</p>', 1525451327, 1, 5),
(83, '谢娜庆青年节发劲歌热舞片段 与观众互动超热情', 'uploads/40sN-fzyqqiq863227422296.gif&size=328_218', '<p class="art_p">新浪娱乐讯&nbsp;5月4日，谢娜[微博]在微博晒出一段视频，并配文称：“接下来我为大家带来一首歌曲，祝大家永远青春年少！”</p><p class="art_p">视频中，她在节目录制现场唱歌，并走下舞台，与观众互动，引起阵阵尖叫。网友纷纷表示：“谢谢娜姐，青春永驻！”“哈哈哈哈，娜姐永远最活力！”</p>', 1525446726, 0, 5),
(84, '捕获野生发哥+1！周润发带队爬山长发飘飘', 'uploads/NoSh-fzyqqiq854759740393.jpg', '<p class="art_p">新浪娱乐讯 据香港媒体报道，周润发近年最爱的运动就是爬山，几乎风雨不改，他清晨7点左右就集齐爬山之友一齐跑各大山头，今天就有爬山之友撞见发哥，一个名叫大帽山茶水亭的网友在Facebook放出同发哥的合照，还留言：“发哥又带队爬山啦！大帽山被发哥照亮了！”</p><p class="art_p">从照片中发现有一大堆人跟着发哥去爬山，当中有黄德斌、美指文念中、方中信等，一班人清早运动心情特别好。发哥的头发已经留了很长，据说是为了下半年一部新戏而留。</p><p class="art_p">另外，昨晚发哥同老婆原来约了杨受成晚饭，事后杨受成在自己Facebook发布照片留言：“今天和天皇巨星周润发，发嫂，超级靓佬香港Richard Gere（方中信）晚餐聚会，巧遇我的囡囡Cindy和新晋金像导演彭秀慧。”据说英皇有部戏想找发哥演出，所以杨受成约了发哥谈下合作。</p>', 1525445852, 0, 5);
INSERT INTO `pre_news` (`news_id`, `news_title`, `news_pic`, `news_content`, `news_time`, `news_like`, `news_cate_id`) VALUES
(85, '特朗普首次承认曾向艳星付封口费:在富人中很普遍', 'uploads/yUpY-fzyqqiq816411781175.jpg', '<p class="art_p">当地时间3日早上，美国总统特朗普在社交媒体发文，首次承认了曾向艳星丹尼尔斯支付了130，000美元的款项，称这是为了阻止她提出虚假的和勒索性指控的一份“不对外披露”的协议。</p><p class="art_p">与先前否认知道任何与“封口费”事情相矛盾的是，特朗普称，这样的协议“在名人和富有的人士中非常普遍”。他还强调，竞选资金“在这次交易中没有发挥任何作用”。</p><p class="art_p">此前，特朗普的新律师，纽约市前市长朱利安尼周三（2日）透露，特朗普的私人律师科恩代他处理与艳星“暴风女丹尼尔斯”（Stormy Daniels）的事情时，曾向丹尼尔斯支付了一笔13万美元的封口费。不过，特朗普过后曾分几个月把这笔钱还给了科恩。</p><p class="art_p">美国有线电视新闻网（CNN）3日报道称，刚刚加入特朗普律师团队的前纽约市长朱利安尼告诉福克斯新闻（FOX），“这笔钱是其律师（科恩）从律师行或什么基金支付的，是我的话我也会这么处理，这没关系。总统在几个月内还了这笔钱（给科恩）。”</p><p class="art_p">朱利安尼还说，“这笔钱是非常合法的。这笔资金不出自特朗普的竞选团队，没有违反竞选融资条例。”</p><p class="art_p">本名克利福德的丹尼尔斯宣称2006年曾与特朗普有染，并说科恩为此付给她13万美元封口费。此前，特朗普曾否认对科恩支付封口费一事知情。直到上周，他才承认科恩代他同丹尼尔斯达成了“交易”。而据朱利安尼此次的表述，特朗普其实知道这回事，还在科恩付给艳星封口费后给科恩“报销”。</p>', 1525441336, 0, 5),
(86, '周杰伦演唱会成都站承办方被约谈 将总结经验教训', 'uploads/CeH4-fzyqqiq794184955665.jpg', '<p class="art_p">5月3日上午，成都市文广新局有关负责人就“2018周杰伦‘地表最强2’世界巡回演唱会（成都站）”举办单位在组织、管理、服务上存在不完善和不周到等问题，约谈了此次演出承办单位成都启唐文化传播有限公司有关负责人。针对文化主管部门指出的问题，该负责人表示接受批评，总结经验教训，不断提高公司举办大型活动的能力和水平。</p><p class="art_p">下一步，成都市文广新局将进一步加强演出市场管理，规范演出市场秩序，指导各区（市）县抓好演出市场的规范和监督，督促演出单位提高服务质量，为繁荣成都演出市场作出努力。</p><p class="art_p">成都商报客户端记者 任宏伟</p>', 1525438333, 0, 5),
(87, '熊黛林出月子患“妈妈手” 外出就医老公接送', NULL, '<p class="art_p">    <p class="art_p"> <div class="img_wrapper"><img  src="https://n.sinaimg.cn/translate/426/w706h520/20180504/Q3om-fzyqqiq7578593.jpg">共10张</p><p class="art_p"><br></p><p class="art_p">1905电影网讯 据香港媒体报道，出月子的熊黛林被拍外出，却双手缠满绷带。</p><p class="art_p">当日，熊黛林一身牛仔私服，在妈妈陪同下步出位于北角半山寓所，身材已恢复的和产前差别不大，但双腕却戴有黑色护腕，连零钱都拿不稳。据悉，熊黛林生产后虽然有妈妈帮忙照顾两个女儿，但熊黛林依旧事事亲力亲为，最近双手又因抱女儿用错力，患上“妈妈手”。</p><p class="art_p">熊黛林就医出来，双腕缠着厚厚的白色绷带。宠妻的郭可颂也在熊黛林致电后，驾驶白色房车接她回家。熊黛林虽然手腕有伤，但仍不忘为母亲开车门。被记者询问是否不习惯抱孩子，身为新手妈妈的她坦言：“系呀！”再问双手可有大碍？她说：“妈妈手！”，她还不忘对镜头打招呼。（图片出处：新浪娱乐）</p>', 1525433487, 0, 5),
(88, 'Ninepercent将录制跑男 除蔡徐坤外其余成员参与', 'uploads/3xkT-fzyqqiq741843730321.jpg', '<p class="art_p">新浪娱乐讯 5月4日，《奔跑吧》在官方微博宣布下期录制将迎来在《偶像练习生》中出道的新男团Nine percent，此次录制也是该组合成员出道后的综艺首秀。从公布的阵容来看Ninepercent9人中除了蔡徐坤，其他8人都参加。据消息蔡徐坤是由于合约问题没能参加此次跑男录制。</p>', 1525432050, 0, 5),
(89, '吴卓林加国寻父 吴绮莉寄情种菜：天父的考验', 'uploads/zjhM-fzyqqiq703068945669.jpg', '<p class="art_p">新浪娱乐讯 据香港媒体报道，艺人吴绮莉（Elaine）的女儿卓林和女友Andi早前在加拿大拍片寻父，引起大众关注！作为妈妈的吴绮莉当然非常担心女儿，继早前在网上发文意有所指卓林为了爱情不顾一切。</p><p class="art_p">最近，吴绮莉再晒自己种植茄子的视频短片，片中见她晒出自己种的茄子和其他植物，留言：“又有新的开始长大。”而网民纷纷留言问她：“准备做什么野菜？”吴绮莉回复：“素菜包，豆腐、加豆角，加所有素菜，包生菜。”另有网民大赞吴绮莉厉害，她即回复：“厉害就不是这样了，天父的考验。”</p><p class="art_p">吴绮莉寄情种植，好多网友为她打气，希望卓林平安归来。</p>', 1525428524, 0, 5),
(90, '张杰不惧非议称动力足够强大 写信表白妻子谢娜', 'uploads/w640slw97766.jpg', '<p class="art_p"><strong><span style="color: #ff0000;">&gt;&gt;&gt;点击观看现场视频</span></strong></p><p class="art_p">新浪娱乐讯 5月4日消息，张杰[微博]2018“ 未·LIVE”巡演发布会在鸟巢举行。发布会上，张杰感慨做歌手以来招致过很多“不一样的声音（非议）” ，但自己一路向前的动力、信念足够强大。除了现场透露此轮巡演将献上多首还未曝光的新专辑曲目外，张杰亦表示会邀请“创造101”女团担任表演嘉宾。另在群访环节，有媒体提及本周六是谢娜[微博]怀孕生子后首度复出《快本》的日子，张杰温柔表白妻子：“我觉得所有人会想念她（在《快本》的样子），特别是我。在这次舞台上我也给她写了一封信（鼓励她）。”</p><p class="art_p">5月3日，张杰“ 未·LIVE”巡演发布会在鸟巢举行。之所以选择在鸟巢办发布会，是因为张杰之后巡演的北京站正是要在此地进行。在可容纳十万人的鸟巢场馆开唱，是很多歌手的梦想，但同时对大多歌手来说也绝非易事。张杰在发布会现场就感慨道：“我的一个目标就是希有华人的地方就有我的音乐，现在我也是一步步在往前做。虽然路上会遇到很多不一样的声音（非议）把你往后拽，但我觉得他们的力量太小了。因为我往前的动力很强，我不会在意那些声音，我只会往前冲。所以现在我冲到了鸟巢。”他另表示，一路以来“做真实的自己”这件事让他收获了很多，亦希望可以藉此带给歌迷正能量信念，“变得越来越好”。</p><p class="art_p">据悉，“ 未·LIVE”这轮巡演共包括6站7场演出。除早前公布的8月11日北京鸟巢场外，张杰另会在太原、郑州、上海、深圳、成都开唱。张杰透露，歌迷们在演唱会上不止可以听到自己以往那些耳熟能详的代表作，还可以第一时间听到多首未曝光的新专辑歌曲。尽管很多歌迷半开玩笑地“私心”提议不要设演唱会嘉宾，全程只听张杰一人唱歌，但张杰坦言，因为自己最近正在选秀节目《创造101》里做音乐导师，所以也希望最终选出的11人“101女团”姑娘们可以来到鸟巢场现场登台表演，“这对新人来说是一次机会”。提携之心溢于言表。</p><p class="art_p">早前，在一对双胞胎女儿出生时，张杰特意为她们写了一首歌曲《Precious》。群访环节，有媒体问及在之后的新专辑中是否还有为家庭创作的作品？张杰表示，除了之前那首歌，接下来在5月11日，宝宝们“百天”之时，自己也希望可以有个礼物送给她们。至于是一首新创作？还是其它形式？张杰在现场并未明说。</p><p class="art_p">此外，在本周六即将播出的《快乐大本营》中，因怀孕生子而久未在荧屏上露面的谢娜也将回归主持台。有媒体问张杰是否为老婆送上过“复工”鼓励？张杰面露温柔表示：“我觉得所有人会想念她（在《快本》的样子），特别是我。我很想看到她回到《快乐大本营》舞台。在这次舞台上我也给她写了一封信（特意鼓励她）。”（XLZY/文 王博/摄影 刘嘉奇/摄像）</p>', 1525426900, 1, 5),
(91, '李小璐嘻哈装扮深夜出行被拍 与友一路说笑心情好', 'uploads/9xJu-fzyqqiq672385098967.jpg', '<p class="art_p">新浪娱乐讯 近日，李小璐[微博]一身嘻哈装扮深夜出街与友聚餐被拍。当晚她头戴白色棒球帽，一身红色卫衣搭配牛仔外套，嘻哈风范十足。期间李小璐与友人一路说笑，在酒店门口与人通电话也是神情放松，看似心情不错。</p>', 1525424739, 0, 5),
(92, '美智库妄称解放军武统几乎不可能成功 会陷死亡埋伏', 'uploads/2Goa-fzyqqiq796275489051.jpg', '<p class="art_p">日前，美国智库研究员又声称，“中国大陆武力攻台，几乎不可能成功。”他还叫嚣，“如果台军快速集结部队并在台海布雷，解放军等于盲目走入一个‘死亡埋伏’。”</p><p class="art_p">海外网5月4日电大陆多次的军演，让台当局很慌，“武统”声浪也应声高涨。美国部分人士总爱对台海问题指手画脚，日前，美国智库研究员又插嘴声称，“中国大陆武力攻台，几乎不可能成功。”他还叫嚣，“如果台军快速集结部队并在台海布雷，解放军等于盲目走入一个‘死亡埋伏’。”然而，如人饮水冷暖自知，岛内舆论却感叹台军实力堪忧，“台湾没有打仗的本钱”。</p><p class="art_p">大陆“武统”不了？台湾网友：3天都有点多</p><p class="art_p">4月以来，解放军的军演频频，5日至11日在南海举行军演，有历来最具规模的海上阅兵；18日在泉州沿海的实弹军演，在台湾海峡海域最受瞩目；24、25两日将在浙江省舟山群岛黄大洋海域进行实弹演。此外，大陆军机一连3天绕台及辽宁舰在台湾东部海域巡航等活动。</p><p class="art_p">美国智库2049研究所研究员易思安又在《外交家》网站上大放厥词，5月2日他声称，中国大陆对台湾发动闪电突袭的终极噩梦，几乎不可能发生。如果台北预先知道将有敌军来袭，可以快速集结部队并在台海布雷，解放军等于盲目走入一个“死亡埋伏”。文章声称，“台湾情报部门早在2008年即发现解放军秘密拟定‘偷袭’攻台蓝图，已促使台湾军方加强相关防备。”</p><p class="art_p">这位美国专家叫嚣大陆无法“武统”，然而岛内舆论关于“武统”可是紧张的不行。根据台湾岛内不同民调显示，岛内过半数民众认为台湾宣布“独立”，大陆就会“武统”，所以大家都主张不要战争。还有国民党官员直言，“台湾没有打仗的本钱”，因为“射击时数严重不足，导致射不到靶，先进武器全靠采购外国，各军种只剩下18万6千人，且募兵失败，总兵力逐年下降。”</p><p class="art_p">日前，解放军中将称3天可攻下台湾，有台湾网友甚至认为“武统”台湾3天都有点多，称“解放军可以轮番24小时不停歇攻击，反正武器自己制造，反观台湾，导弹能打多久？向美国买来得及吗？”“以台湾的现状我也笑了，台湾军力现状军人士气我笑了，100小时有点多。”</p><p class="art_p">解放军军演 敲打美国和“台独”的勾结</p><p class="art_p">其实，关于解放军军演，外界舆论早已直指这是在警告“台独”，敲打美国。早前，台南大学行政管理学系教授马群杰称，这是向美国传达信息，表达掌握台海情势的能力与决心。当有的“台独”叫嚣着“台湾人民不是吓大的”的时候，就有美国媒体直泼冷水。美国有线电视新闻网曾明确分析指出，关于台海军演，解放军敲打的就是美国和“台独”的勾结。台湾《中国时报》则评论，这次“4·18 军演”，大陆挑明着说，也是第一次针对性的军演，台当局绝不能低估大陆政治宣示意涵。</p><p class="art_p">有人关注美国的台海底牌到底是什么？据环球时报援引台湾资深媒体人唐湘龙的观点指出，过去美国是在打“台湾牌”，他们明白台湾对于中国13亿人民来讲那种“无法割舍的心头肉”的感觉，所以美国对台湾做任何小小的动作，都会让全中国紧张。但是现在中国崛起已成事实，美国清楚地知道，靠“战略性模糊”去压制中国已不牢靠。所以，对台湾的政策，美国从过去打台湾牌，到现在亮出底牌——“台独”牌。</p><p class="art_p">关于台海军演，是否有警告“台独势力”的意味？国台办主任刘结一明确表示，“我们举行军演，是为了捍卫我们祖国主权和领土完整的一次行动。”刘结一表示，大陆的立场是一贯的，非常明确，大陆有最大诚意，愿意尽最大的努力争取祖国和平统一的前景；“但我们有足够的意志信心和能力，阻止任何企图把祖国任何一块领土从祖国分裂出去的言论和行动。 ”</p><p class="art_p">驻美大使崔天凯则表示，“我们当然最终会统一，我们仍希望和平统一，但如果有人试图把台湾从中国分离出去，那我们将尽一切可能保存领土和主权完整。”崔天凯强调，关键在于“一个中国”原则，如果有人拒绝“一个中国”原则，那就没有希望找出和平解决的方案。此外，对于所谓“美台”保持“非官方关系”，崔天凯指出，中方已经明确表明了一贯立场，这是美方在中美三个联合公报中的承诺。（海外网 庞晟）</p>', 1525438663, 0, 6),
(93, '美媒：解放军可能闪电解放台湾 已演习在台北港登陆', 'uploads/EmhR-fzyqqiq766286584074.jpg', '<p class="art_p">[来源：观察者网&nbsp;席亚洲]</p><p class="art_p">5月2日美国《外交》杂志网站刊发美国“2049项目学院”智库分析员伊安·伊斯顿的文章，称大陆可能通过突然袭击，迅速解决台湾问题，并称这是“终极噩梦情况”。</p><p class="art_p">文章称：这是终极的噩梦情况。大陆成功实施战略欺骗，趁台湾当局不备之机，发起军事行动。台湾当局和台军被震撼和压制，并被迅速压垮。几天内，幸存的台湾当局重要官员被解放军俘获，五星红旗飘扬在台湾“总统府”上空。</p><p class="art_p">自从解放军装备“野牛”气垫船以后，台军就非常担心这种装备被用于“突袭淡水河口”</p><p class="art_p">2016、17年，台军还认认真真的进行了“淡水河口反登陆”演习……</p><p class="art_p">然而说实话你这以为是玩“抢滩登陆”吗？</p><p class="art_p">……现在解放军登陆的都是两栖战车好吧？你以为还有步兵涉水登陆这样的二战戏码？</p><p class="art_p">作者称，2008年，台湾军情局报告称，发现海峡对岸的对手正在秘密制定这一新的计划。更令他们担心的是，<strong>解放军已经开始进行演习，以验证这一计划。这些演习模拟了在台湾西北海岸的突然登陆，准确的说，就是在台北港的登陆。</strong></p><p class="art_p">据称，这一计划的关键在于突然性，解放军部队首先将从福建沿海直接登船横渡海峡。中央政府将会宣布举行实弹演习和封锁台湾海峡。而且北京方面可能通过国家媒体发布信息，让外界相信这次演习不过是政治姿态。</p><p class="art_p">登陆部队将会达成快速的民用滚装船，这些船只已经在大陆和台湾之间例行航行。在“演习”当天，这些船只将全速开到台湾，直到最后一刻前不会暴露自己的真面目。</p><p class="art_p">当它们抵达台北港，大批机械化步兵和坦克将迅速下船，其实力足以压倒台湾守军，并为后续蜂拥而至的直升机和船只占领安全的登录区域。同时，特种部队将乘坐气垫船顺淡水河而上，伞兵也将在桃园机场降落。几分钟内，整个台湾西北海岸将变成激战的战场。</p><p class="art_p">同时，解放军将会对台湾实施电子攻击、导弹打击、精确暗杀和潜艇袭击，以及猛烈的轰炸。此外，这一计划还涉及到对澎湖列岛的突击，这迫使台湾中部和南部的军队就地防守，无法支援台北。</p><p class="art_p">当然，这篇文章的作者后面又自问自答的表示，台军对此已有应对计划，加强了台北的防御，并建造了“隐身导弹艇”，而且还可以“在海峡内布雷”，阻止解放军的攻势。乃至自相矛盾的认为，台湾在改为“募兵制”后，其军队规模虽然从27万人缩小到18万，但仍“足够庞大”。</p><p class="art_p"><strong>顺便还发表了一系列对于中国大陆政治形势的猜测，称大陆在2020年前不可能腾出手来解决台湾问题云云。并得出结论，称“这种情况不大可能出现”。</strong></p><p class="art_p">为了加快投送兵力，解放军也确实进行过民用滚装船快速装卸的演习</p><p class="art_p">然而正经的解放军集团军跨海登陆作战想定的主要内容是两栖突击，占领探头阵地，建立人工港迅速上陆……什么进行偷袭占领港口什么的……只是小说而已</p><p class="art_p">顺便，还不忘了补充说，美国是台湾唯一能获得高级军事训练和先进武器装备的来源，要增强台湾抵抗大陆“闪电突袭”的能力，只有靠美国提供军售和军事合作。</p><p class="art_p">观察者网军事评论员认为，这位作者就是在台湾被翻译成为“易思安”的美国反华专家，此君在大陆留学6个月，回去摇身一变就成了“中国通”……让人怀疑他在大陆期间到底在学什么……</p><p class="art_p">有意思的是，这位仁兄的2017年发表的《台湾防御与美国亚洲战略手册》（台湾翻译为《中共攻台大解密》）却是一本充满了臆想、猜测、过时的观点，对大陆公开资料的错误解读和造假“机密文件”的大杂烩，其内容更是充斥着同时宣扬“中国威胁论”和“中国无能论”的奇妙自相矛盾。</p><p class="art_p">事实上，解放军也不需要直接在台北登陆，从新竹附近的滩头上陆，然后机械化开进到台北，台军瓦解不过是时间问题，而且这个推进的速度正在不断加快</p><p class="art_p">然而即使是这样，该书仍然得到美国军方和外交界部分人的吹捧，而台军居然也是颇为重视，甚至按照他书中预言的“共军在淡水河口闪电登陆”的说法，认认真真开始演练“淡水河口防御作战”……真是让大陆认真的军事观察者们感到大跌眼镜，甚至颇为滑稽。</p><p class="art_p">针对易思安的这篇大杂烩，连台湾方面的一些政治人物也看不下去了，国民党文传会副主委胡文琦在2017年12月发表的文章专门抨击这位老兄，其中部分内容读来有趣，在此分享给各位：</p><p class="art_p">帮帮忙，真的不要像台谚的“发誓给别人死”或是存有“别人的孩子死不完”，好吗？媒体报导，针对最近中共军机绕台频繁，中国大陆驻美公使李克新呛声“美国军舰抵达高雄之日，就是武力统一台湾之时”。对此，美国智库“2049计划室”研究员易思安（IanEaston）分析，中共确实有攻台计划，但他相信台湾人“是很能忍耐，台湾能够撑很久”云云。</p><p class="art_p">“台湾能够撑很久……”？</p><p class="art_p">台湾募兵制之后战斗力更强了？！</p><p class="art_p">姑且不论易思安的解放军攻台三阶段的“想定”，从“封锁跟轰炸”、“两栖登陆”，再到“岛上作战”最后清理台湾的说法，基本上没有军事战略与战术上的逻辑先后顺序错误。但凭良心说，也没太多令人眼睛为之一亮的高见、理论或大发现。诸如从台湾有14个适合登陆的海滩，解放军最可能从桃园海滩登陆，因为此区既有海滩亦有机场与港口，地理位置适合解放军的作战计划。</p><p class="art_p">然而，易思安一席对台湾很有信心地指出，台湾的领导人和军人都“很有决心”，且通常民主国家被攻击后，民众都会像二战时的英国人，变得很有决心，乃至他认为“台湾人很耐打”，因为台湾每年都被平均6个台风侵袭，又有很多地震，台湾人也经常在国际社会上被侮辱，“所以我觉得台湾人是很能忍耐、很厉害的”，坦白说，易思安不说还好，一说就立马露馅他根本“不接地气”。因为，如果说台风地震就可以很厉害的话，那日本老早就应该是地球王、宇宙神了。</p><p class="art_p">如果说台风地震就可以很厉害的话，那日本老早就应该是地球王、宇宙神了</p><p class="art_p">暂且不论易思安一厢情愿且无法验证的所谓“中国的内部档和宣传攻台的说法完全不一样”，或是中国大陆宣传能在2到3天内可以打下台湾，“台湾是很弱的”说法正确与否，那些都是台湾自己所不能控制的变量，拿来“说嘴”，甚或像台湾王禄仙地呓语“发誓给别人死”，试问，这样的学者难道值得台湾民众额手称庆说好棒棒吗？讲白了，战争又不是发生在美国本土的类似911攻击，易思安当然像个“旁观者”可以心安且了无牵挂。</p><p class="art_p">再讲更直接一点，易思安很显然“没接地气”的不了解台湾，试想，现在蓝绿两极对抗严重的台湾，蔡“总统”及民进党有无决心都很难说了，遑论全台湾民众都会“很有决心”，这是在说笑话吗？只要问问类如金美龄“嘴巴爱台湾”等人，是否愿意“举家回台”落籍定居以应“圣战”需求，即可知悉很多话“听听就好”，真要检验大家愿意一起共赴“国”难的话，“真相”可能会让易思安更为难堪与惊讶。只需检视蔡“总统”过往竞选口号“台湾共识”，就可知悉易思安根本在状况外。</p><p class="art_p">或许，易思安压根没注意到的是，真正敢战、能战且真正付诸行动的，“绝大多数”是那些真正台湾原始的主人，“原住民朋友们”，从那些过往可歌可泣的抗日战斗，诸如雾社事件等等，那才叫做“很耐打、很能忍耐、很厉害的”，讲直白一点，易思安所自认的“国军的训练、武器、设备都比解放军还要好”的说法，都值得台湾好好思量。（转载注：……真的是得好好思量一下）就算真是如此的话，难道台湾就要“自我实现预言”，2018我们是全世界第二高发生战争机率的区域吗？</p><p class="art_p">准此，笔者要严正告诉易思安，帮帮忙，作为一个“智库”的战略研究学者，请先把基本功的数据及民情研究“做好、做满、做彻底”，不要一副事不关己、大言不惭的发表一些既不负责任，又是“发誓给别人死、别人的孩子死不完”的煽情研究，好吗？</p>', 1525434919, 0, 6),
(94, '中国舰载固定翼预警机将首飞 配国产航发功率增30%', 'uploads/4oI8-fzyqqiq758637040213.jpg', '<p class="art_p">据说是国产舰载固定翼预警机地面测试模型，可以看到它与美国鹰眼预警机相似</p><p class="art_p">近日有消息说中国海军舰载固定翼预警机即将首飞，它的研制成功，将会有力提高中国海军航空母舰信息获取能力，中国海军航母编队作战能力将会发生又一个飞跃。</p><p class="art_p">在许多人印象之中，舰载固定翼预警机只能配备在弹射器航母之中，根据海外资料，美国海军曾经利用E-2C鹰眼预警机进行滑跃试飞，结果表明鹰眼预警机具备滑跃起降能力，当年英国皇家海军CVF项目，美国就曾经向英国推荐过鹰眼2000滑跃型，按照这些资料说法，在甲板风为25节情况下，鹰眼2000从12度斜角甲板起飞，起飞距离大约为165米，而辽宁号航空母舰第3起飞点距离舰艏大约为195米，从这个角度来讲，辽宁号航空母舰起降E-2C这个级别预警机没有太大问题。</p><p class="art_p">中国相关单位已经研制成功多型预警机，图为空警500预警机</p><p class="art_p">以中国现在技术实力，研制E-2D这样预警机没有太大技术障碍</p><p class="art_p">苏联YAK-44预警机就是因为设备问题而功败垂成</p><p class="art_p">进入新世纪之后，中国相关单位在预警机领域可谓一时千里，研制成功多型预警机，并且出口到国外，其中新型空警500预警机采用了国产第二代数字式有源相控阵预警雷达，它以隐身目标作为设计基准，为此采用全数字器件，系统灵敏度更高、探测能力更强，尤其是系统体积和重量更小，更适合舰载预警机配备，另外中国还研制新一代宽带数据链，它传递速率更高、信息容量更大，不但可以为战斗机进行指挥引导，还可以为舰空导弹提供火控支持，形成协同交战系统，增强航母编队抗击饱和攻击能力，实际上当年苏联研制YAK-44预警机，就是因为机载雷达、任务设备迟迟没有取得突破，结果功败垂成。</p><p class="art_p">发动机是舰载固定翼预警机另外一个关键，对于滑跃甲板航母来说更加重要，滑跃起飞对于飞机推（功）重比要求较高，推（功）重比越大，飞机加速度性能越好，也就可以更快起飞，E-2C预警机采用美国艾利逊T56涡桨发动机，功率为5250马力，两部发动机功率大约为10500马力，E-2C最大起飞重量大约为26吨，起飞功重比达到0.4马力/公斤，相比较之下一般运输机大约在0.25-0.3马力/公斤左右，正是如此强劲功重比，才让E-2C预警机具备较好滑跃起飞能力，中国相关单位已经研制成功与T-56相近的涡桨发动机发动机，这就是中国空警200/500系列预警机配备的涡桨6C功率与之相当，它的重量、体积和功率与T56涡桨发动机相当，这样利用国产第二代有源相控阵雷达、任务系统、涡桨6C涡桨发动机，中国完全可以研制国产舰载固定翼预警机。</p><p class="art_p">国产5000KW涡轴发动机，有消息说相关单位还在它基础上研制国产5000KW涡桨发动机</p><p class="art_p">不过从海外资料来看，国产舰载固定翼预警机可能还会选择功率更大发动机，有消息说<strong>中国相关单位已经开展新一代涡桨发动机研制，这种发动机功率达到5000KW（约合6800马力），功率比涡桨6C和T56涡桨发动机还要大30%左右，国产舰载固定翼预警机配备这种新型发动机之后，起飞功重比将会达到惊人的0.52马力/公斤，差不多是普通运输机一倍，如此大起飞功重比可以有效提高国产舰载固定翼预警机起飞能力，也就是说在同样条件下起飞距离更短，甚至可能从辽宁舰第1、2号起飞点短点起飞，降低对远点起飞要求，这样就可以简化航母甲板布列和调运难度，这也是限制滑跃甲板航母使用固定翼预警机因素之一。</strong></p><p class="art_p">从这里我们可以看出，中国相关单位通过研制大功率涡桨发动机，成功解决了滑跃甲板航母使用舰载固定翼预警机这个难题，让辽宁号航空母舰和第1艘国产航母作战能力显著增强，也为未来弹射器航母早日形成作战能力打下了坚实基础。（作者署名：小飞猪观察）</p><p class="art_p">《出鞘》完整内容请关注新浪军事官方微信抢先查看（查看详情请搜索微信公众号：sinamilnews），《出鞘》每天在新浪军事官方微信完整首发。</p>', 1525433827, 0, 6),
(95, '美媒：中国驻吉布提基地激光照射致美军两飞行员受伤', 'uploads/MFr6-fzyqqiq687076247861.png', '<p class="art_p">[来源：观察者网/张晨静]</p><p class="art_p">从今年4月中旬开始，美国方面就不断渲染“中国驻吉布提基地使用激光照射美飞行员”一事。</p><p class="art_p">继美军方发布一份“对飞行员警告”后，《华盛顿邮报》报道称，当地时间5月3日，五角大楼发言人戴娜·怀特（Dana W White）在记者会上声称，中国在该基地使用军用级别激光，致使两名美飞行员眼睛受到轻伤。</p><p class="art_p">《华盛顿邮报》报道截图</p><p class="art_p">“这是非常严重的事件，已经有人受到轻伤，这对我们飞行员构成了威胁”，怀特对记者表示，美国官员已向中国提出交涉，要求中方对这些事情进行调查。</p><p class="art_p">《南华早报》报道称，怀特十分确信就是中国使用了高功率激光武器。五角大楼之所以对中国提出交涉，是因为最近几周里发生了近10起案件。</p><p class="art_p">对于有两名美飞行员受到轻伤一事，怀特没有提供更多的细节。但另一名五角大楼发言人科林可（Sheryll Klinkel）则表示，在发生的一起案件中，有两名美飞行员驾驶着一架C-130飞机，因遭到军用级激光照射，使得眼睛受到轻度伤害。</p><p class="art_p">五角大楼发言人怀特</p><p class="art_p">对此有军事观察员在接受《南华早报》采访时表示，这种激光可能是用来吓走机场附近的鸟类，或用来阻挠可能出现的间谍无人机，而不是用来针对外国飞行员。</p><p class="art_p">此外他们还指出，中国是激光致盲武器协议的签字国，禁止使用会导致永久失明的激光。</p><p class="art_p">中国驻吉布提的军事分析员周晨明也指出，中国早已在航空展会上，公开展示过用来反无人机的激光武器。</p><p class="art_p">据了解，此前在4月14日，美方就曾援引消息来源称，中国人民解放军海军（PLAN）涉嫌在基地或离岸海域使用高功率激光武器。美军还“煞有介事”地发布了一份“对飞行员警告”（NOTAM），警告他们注意吉布提的“未经授权的激光活动”。</p><p class="art_p">对此观察者网当时就指出，美英经常在冷战末期宣传咄咄逼人的苏联船员用激光照射飞行员，此次美方所渲染中国使用激光武器，其用心大概也是把中国宣传成苏联吧。</p><p class="art_p">保利展出过是用来反无人机的激光武器“沉默猎手”，可以在800米外烧穿10毫米铁板</p>', 1525426579, 0, 6),
(96, '韩媒：特朗普下令做好裁减驻韩美军准备', 'uploads/2K-6-fyarrcf438871591628.jpg', '<p class="art_p">[环球网报道 记者 刘昆]韩联社5月4日援引美国《纽约时报》报道称，美国总统特朗普下令做好减少驻韩美军数量的准备。</p><p class="art_p">韩联社援引消息人士的话称，特朗普在美朝首脑会谈前夕掏出“驻韩美军牌”，意在与朝鲜最高领导人金正恩就朝核问题进行谈判。目前驻韩美军数量约为23500人。</p>', 1525426026, 0, 6),
(97, '印度空军司令否认不要俄苏57战机 称对美F35不感兴趣', 'uploads/-2W--fzyqqiq678730544017.jpg', '<p class="art_p">俄印的五代机计划，基本上是以俄罗斯苏-57为蓝本</p><p class="art_p">最近，有媒体报道印度将放弃与了是联合研制第5代战斗机（即FGFA计划），并盛传印度将转向美国采购F-35。</p><p class="art_p">不过现在看来，这个报道并不确实。据印度媒体报道，印度空军司令上月底在参加一个分析中心的会议时表示“没有任何F-35的谈判在进行，印度空军对F-35不感兴趣，也没有跟任何人谈。说我们对F-35感兴趣是不确实的。”而关于印度要退出FGFA计划一事，印度空军司令则表示这是政府层级的决策，细节要问国防部 。</p><p class="art_p">印度空军司令说对F-35没有兴趣倒挺有意思</p><p class="art_p">4月27日，在土耳其举办的欧亚航空展上，俄罗斯科技集团的官员则表示，俄方并没有收到印度这方面（取消FGFA计划）的消息。这位官员称，“我无法就媒体的猜测表达意见，我只能就印度政府的决策做评论，我们并没有得到这样的官方讯息。”他进一步补充到，“现在印度高层在空军的整体发展策略上有争论，可能自制战机，也可能整机进口。我们尊重他们的自主决策。”</p><p class="art_p">可见，俄印联合研制五代机的这场“肥皂剧”仍将持续……</p><p class="art_p">那么假如印度如果真的取消俄印联合研制五代机的计划，俄会遭受严重损失么？应该十分有限，印度与俄罗斯联合研制五代机并非俄印一起研发一种飞机然后一起用，而是俄罗斯研制自己的苏-57，然后在苏-57的基础上依据印度的需要、并且让印度方面参与研发，做出印度版本。因此这个案子与EF-2000、F-35差异很大。印度退出与否，对俄罗斯自身的苏-57计划而言影响的只有资金，就技术发展方面可说是完全没有影响，所以印度如果退出，苏-57计划或许会受阻，但不会有灾难性的困境。</p><p class="art_p">俄军自己的苏-57距离服役已经越来越近，目前正忙着参加过几天的红场阅兵</p><p class="art_p">至于FGFA之所以充满争议，是印度方面的要求很难用原定的金额去执行，甚至印度方面的要求能否办到就是个问号。在俄罗斯的观点来看，FGFA大概是比照苏-30MKI的模式，由俄国提供飞机主体以及最根本的控制、导航、火控系统，确保整个架构无虞，再用上印度自己的选配。</p><p class="art_p">不过，印度观点则不是这样，印度想要基本的程序代码自己改，想要把结构材料换成自己的，想要用自己的计算机等等，也就是印度想要更大程度的涉入研发过程，而不只是简单的拉皮改装。</p><p class="art_p">假如真要照着印度的想法走的话，一方面是有保密问题，毕竟现代战机最关键的就是程序，贡献原始程序给印度，难保不会外泄，毕竟印度左右逢源，很难说后面就到了谁手上。而就算美国跟英国这种铁杆子兄弟，英国也是没能拿到F-35的源代码。</p><p class="art_p">印度空军的苏-30MKI很大程度上是“定制”的，使用了大量印度国产电子设备</p><p class="art_p">另一方面，就算不考虑泄密问题，现代战机最关键的是程序，测试周期通常都很长，甚至要边服役边改，如果提供给印度，印度自己改，出了问题算谁的？拖到进度又要算谁的？更改结构更可怕，飞机的结构、动力是最根本的，所有测试都是要结构与动力先测完再说，如果照印度要求更换结构材料，很多测试都要重来。</p><p class="art_p">因此很显然的，印度所要求的事情，不是原定金额能办到的事，要追加金额非常正常，并不能说是俄罗斯在坑印度人。至于又要双座又要隐身又要超音速巡航，这本身就有冲突，美国F-22、F-35也都是单座，那印度用什么依据来认定不能兼顾双座、隐身、超音速巡航为“不好”？总而言之，在印度身上总能看到一些比较神奇的事儿。（作者署名：北国防务）</p><p class="art_p">《出鞘》完整内容请关注新浪军事官方微信抢先查看（查看详情请搜索微信公众号：sinamilnews），《出鞘》每天在新浪军事官方微信完整首发。</p>', 1525425689, 0, 6),
(98, '中国歼11D或借助苏35重生 2架新机现身试飞基地(图)', 'uploads/M8EG-fzyqqiq671112740487.jpg', '<p class="art_p">近日，国内军事论坛发布4月份新照显示，2架由沈飞最新生产的歼-11D战机原型机进驻西部某试飞基地，至此，歼-11D原型机已有近10架，“下马”传闻似乎已消散。在成飞专心推进歼-20隐身战机后续改进工程的同时，沈飞新型战机项目似乎也迎来“爆发”。歼-11D战机在约两年前已完成高原试飞之后，再次有原型机出现在正常海拔地区的试飞基地，很可能是又进行了重大改进。结合我国引进的苏-35战机已服役1年多，空军对其性能比较满意来看，3.5代重型空中优势战机似乎仍有较大用武之地。</p><p class="art_p">图片来自南海研究论坛，非常感谢制作者。</p><p class="art_p">国内军事论坛发布的是4月份刚拍摄的我国西部某试飞基地照片，左下角的两架歼-11D黄皮原型机此前未曾出现过，应该是新到飞机。梳理此前相关卫星照片会发现，歼-11D战机虽然近年来没有什么公开消息，但原型机数量却在逐渐增加，到现在已有将近10架。如果像传闻中那样，歼-11D项目已经“下马”，是不可能还有新机不断下线的。</p><p class="art_p">不过，歼-11D战机当初是与歼-16一起到高原进行的测试，结果歼-16多用途战机已批量装备了约80架，歼-11D却迟迟未能定型。由此可以推测军方对歼-11D战机早期批次性能并不够满意。那么现在新出现的歼-11D原型机应该是Block 2批次，而且很可能根据军方意见进行了针对性重大改进。</p><p class="art_p">歼-11D战机Block 2批次进行的改进可能有几个方面：第一，换装与歼-16战机量产型同一系列的有源相控阵雷达。此前，歼-16战机在试点装备阶段经历过一次雷达换装，因为初始装备相控阵雷达性能未达到军方要求，后换装中电科14所研发的雷达解决问题。从目前空军对歼-16战机的相关消息来看，换装后的雷达应该得到了军方肯定。歼-11D原型机可能也在进行类似换装和测试。</p><p class="art_p">不过鉴于歼-11D战机的定位偏重夺取空中优势，所以其换装的有源相控阵雷达可能加强了对空探测跟踪性能，多用途模式则没有歼-16战机雷达那么高的要求。</p><p class="art_p">第二，一个很可能进行的重大改进，会是换装国产矢量喷管。此前消息显示，成飞正在歼-10B验证飞机、歼-20国发版原型机上测试这一技术。</p><p class="art_p">新的歼-20原型机。图片来自网络，感谢作者。</p><p class="art_p">国产矢量喷管虽然被曝于成飞系战机上测试，但其完全可能应用到歼-11D战机上。我国引进的3.5代战机苏-35，一个重要技术优势就是应用了发动机矢量技术。从空军接装部队的使用情况来看，苏-35由矢量技术带来的机动性是很让部队满意的。所以，空军很有可能据此对歼-11D战机提出应用同样技术的要求。</p><p class="art_p">如果说国产矢量技术从理念上对苏-35战机进行了借力，那么苏-35的“飞火推”一体化技术则可能对歼-11D战机改进的影响更大。“飞火推”一体化可在近距离格斗中占据较大的优势。应用这一技术后，火控数据和飞行员的操纵杆发出的飞控数据和发动机矢量喷管的状态数据三者合一，经过机载电脑的比对，可给出最佳决策。飞行员只需要专心于眼前目标作出正确的战术判断即可，不必再单独控制矢量喷管的偏转。</p><p class="art_p">对歼-11D战机来说，换装国产矢量喷管，采用“飞火推”一体化技术，既会是最重要的改进，也将是最耗时的升级。如果这些测试完成，歼-11D战机相对苏-35的部分优势才得以显现。由于是我国自行研制的3.5代重型空中优势战机，歼-11D可以使用包括PL-10、PL-15、PL-XX等在内的所有国产先进空空导弹，这一点是使用俄式导弹的苏-35战机不能比的。</p><p class="art_p">在重新设计了机体结构，并改善了包括机动性在内的作战飞行性能后，可以使用更先进空空导弹的歼-11D战机，应该能满足空军未来一定时期的作战需求。如果歼-11D战机能在今明两年内完成试飞定型，那么批量装备部队的可能性仍然较大。</p><p class="art_p">歼-11D原型机效果图。感谢作者（见水印）。</p><p class="art_p">不过，歼-11D战机能否真正浴火重生，除了要看沈飞的试飞进度，还要看成飞对歼-20国发版型号的测试进度。如果歼-20换装国产发动机和矢量喷管后，进展非常顺利，在较短时间内就定型，那么空军也有可能压缩所有国产3.5代战机的订货量，转而装备更多的歼-20战机。（作者署名：百战刀）</p><p class="art_p">《出鞘》完整内容请关注新浪军事官方微信抢先查看（查看详情请搜索微信公众号：sinamilnews），《出鞘》每天在新浪军事官方微信完整首发。</p>', 1525424665, 0, 6),
(99, '中国红旗9与鹰击62导弹现身南沙 防御范围达400公里', 'uploads/Qeux-fzyqqiq669946978728.jpg', '<p class="art_p">作者署名：百战刀</p><p class="art_p">5月3日，国内数家媒体援引美媒报道称，中国首次在南沙群岛部署了反舰导弹和地对空导弹系统，可能为鹰击-62岸舰导弹和红旗-9B防空导弹。对此，我国外交部发言人表示，“有关部署不针对任何国家，无心侵犯者无需担忧害怕。”公开报道显示，2016年7月，上述两款导弹曾出现在我国西沙群岛永兴岛，并参加了海军三大舰队联合举行的南海军演。鹰击-62为岸基远程重型反舰导弹，最大有效射程约为400公里，1枚足以令一艘5000吨级以上驱逐舰丧失战斗力。</p><p class="art_p">南部战区海军鹰击-62岸舰导弹。图片来源见水印。</p><p class="art_p">从岛礁防御角度来说，岸舰导弹、防空导弹、岸炮、高射炮（机枪）等都属于必要的防御设施。虽然在岛礁机场可以部署歼-11B、飞豹等战机，而且岛上也建有一定的地勤保障设施，但是远海多变的气象条件，高盐分、高湿度、高腐蚀性的自然环境，却使得战机部署的时长和便利性仍不及大陆。所以除了战机不时部署并进行战备巡航之外，只有部署必要的国土防御设施，才能保证岛礁的常备防御力量。</p><p class="art_p">鹰击-62岸舰导弹与052C型神盾驱逐舰装备的反舰导弹属同一系列。不过鹰击-62舰射型在装备了6艘052C型驱逐舰后，就迅速被鹰击-18“亚超结合”双速制反舰导弹取代，只有岸舰型号继续批量装备。鹰击-62与此前研制的鹰击-83反舰导弹一样，也为高亚音速导弹，最大飞行速度在0.8-0.9马赫之间。不过它重点突出的是大威力，战斗部重量超过300公斤，外贸型号也有480公斤级战斗部。</p><p class="art_p">052C型驱逐舰发射鹰击-62反舰导弹。</p><p class="art_p">而且，鹰击-62最远达400公里的有效射程，使其可以有效覆盖我国200海里专属经济区。抗干扰能力强、可规划航路、可在山峰后隐蔽发射、陆路机动性强等特点，使得鹰击-62导弹已成海军三大舰队岸舰导弹部队的主要武器装备。比如在北海舰队防区，鹰击-62导弹如果分别部署在山东半岛和辽东半岛的话，可轻易对渤海湾实施有效封锁。</p><p class="art_p">鹰击-62岸舰导弹如果被部署到岛礁，虽然由于可迂回机动范围小，隐蔽性和生存性会有所下降，但其射程差不多正好可以覆盖岛礁东西两面的我国拥有管辖权海域，并可对执行战备巡航任务的海军战舰给予远程火力掩护，作战效能比较高。而且相对于歼-11B等战机，岸舰导弹在岛礁的部署时间更长。</p><p class="art_p">海军歼-11B战机在西沙群岛永兴岛机场降落。</p><p class="art_p">至于红旗-9B防空导弹，南部战区海军某防空兵旅确有装备。相对于射程为120公里的红旗-9导弹，红旗-9B的射程延长至200公里以上，而且抗干扰能力更出色，拦截巡航导弹、低空飞行战术导弹的能力也更强。以远程地空导弹与歼击机协同作战，是解放军进行要地防空的常用战术。就算没有战机配合，红旗-9B导弹在配套相控阵雷达的支持下，抗击多批次空中来袭目标的能力也很出色。</p><p class="art_p">近年来，南部战区海军052C/D等型号导弹驱逐舰多次组成舰艇编队，以蓝军身份对岛礁驻守部队进行了压力强大的实兵对抗演习。同时，空军、海军歼-11B、飞豹等战机也从空中模拟对岛礁进行封控和攻击，岛礁驻守部队主要依靠的就是岸舰导弹和防空导弹等武器系统。在进行完红蓝对抗之后，海军舰艇编队、海空军战机机群还会与岛礁部队联合进行立体防御实战化训练。</p><p class="art_p">南部战区海军某防空旅实射红旗-9防空导弹。</p><p class="art_p">南部战区海军神盾舰编队在南海进行训练。</p><p class="art_p">以岛礁上的岸舰、防空导弹为主，辅以海军舰艇编队、海空军战机机群，可以大大增强岛礁国土防御能力。其中红旗-9B导弹可以有效抗击非隐身目标，比如像F/A-18E/F舰载机、空射/舰射“鱼叉”导弹这些类型的目标。鹰击-62导弹虽然并非超音速导弹，但其威力大、射程远，也可有效防御试图抵近的驱逐舰、巡洋舰等舰艇，令其心有忌惮，不敢轻易靠近。</p><p class="art_p">《出鞘》完整内容请关注新浪军事官方微信抢先查看（查看详情请搜索微信公众号：sinamilnews），《出鞘》每天在新浪军事官方微信完整首发。</p>', 1525424506, 0, 6);
INSERT INTO `pre_news` (`news_id`, `news_title`, `news_pic`, `news_content`, `news_time`, `news_like`, `news_cate_id`) VALUES
(100, '美媒：高超音速武器领域中国第一俄第二美国算第三', 'uploads/Ji9v-fzyqqiq656903723929.jpg', '<p class="art_p">俄罗斯和中国可能利用高超音速武器，加强针对美国的进攻型武器的装备，这导致中国已经开始服役东风-17导弹，而俄罗斯的“匕首”空地导弹也开始装备俄空天军。据美国nextbigfuture网站2日报道，美国可能在研究高超音速武器方面比俄罗斯和中国晚几年时间，而这两个国家原本开始研发的时间比美国晚几年，但现在正以前所未有的高速度超越美国。为了不被甩的更远，尽管美国已经将研究高超音速武器的经费增加2倍，但美军可能不早于2023年才能接装这种武器，所以现在美国落在了第三位。</p><p class="art_p">美国是高超音速武器的始作俑者，其研发的X-51A、HTV-2、AHW等高超音速武器项目几乎尽人皆知，并刺激了中国和俄罗斯的同类项目的诞生。但美国又是研发高超音速武器的失败者，像X-51A本来在新世纪初就开始研制，但是因为技术定位过高和资金支持动力不足，导致4次试验中有3次失败，如今发展前景并不明朗。这大大挫伤了美国发展此类武器的雄心，并在研发上进入“迟滞”阶段，因此导致认识到这类武器必将带来新的战争革命的中国和俄罗斯奋起直追，现在已经有实战型武器开始服役。</p><p class="art_p">在高超音速武器发展中竞赛中走在前列的是中国，根据美国国防部的说法，中国在过去10年所进行的高超音速武器试验次数是美国的20倍，现在已掌握全球领先的高超音速武器技术。基本可以判定，中国可能已经拥有可用于实战的高超音速武器——比如东风-17弹道导弹。而且最新的一次试验，是在4月底进行的，这次试验所形成夜间气象云，在大半个中国都被观测到，间接证明了中国此类武器的发展进度。如果美国无法追赶上中国的研发速度，美国在未来战场上将被“拒绝接近”能力增强的中国军队逼入苦战。</p><p class="art_p">而另一个世界大国俄罗斯也毫不落后，携带最新型导弹综合体“匕首”的米格-31K歼击机将参加5月9日莫斯科胜利阅兵——这表明此款武器也已形成战斗力。根据俄罗斯专家的说法，Kh-47M2“匕首”导弹是一种“精确制导武器，具备多用途弹头，能够攻击固定和移动目标”，可以躲避任何现有的反导系统，并能攻击移动的大型水面目标，比如航空母舰、驱逐舰和巡洋舰。Kh-47M2“匕首”导弹有三大特点，一是速度为10马赫，二是射程为2000公里，三是由米格-31K歼击机携带，这保证了其基本没法被拦截。</p><p class="art_p">分析认为，在国际研发高超音速的第一集团中，中国虽然是后来者但已开始领跑，紧跟其后的俄罗斯也进展神速，当然美国距离也不远，现在中俄美几乎已进入发展高超音速武器的三国时代。从发展高超音速武器的需求来看，中俄美三国肯定不会安于现状，所以未来关于高超音速武器发展的报道一定围绕着这三国来进行。当然，中俄美均不甘心落后，因为这关系着未来战争的胜败，失败即意味着被出局。因此，美国现在担心中俄高超音速武器的进步，也意味着其在今后必将全力追赶中俄，所以军备竞赛局面已经成型。（作者署名：军评陈光文）</p><p class="art_p">《出鞘》完整内容请关注新浪军事官方微信抢先查看（查看详情请搜索微信公众号：sinamilnews），《出鞘》每天在新浪军事官方微信完整首发。</p>', 1525422434, 0, 6),
(101, '疑似中国隐身战轰曝光造型前卫 或让歼20成过气网红', 'uploads/w640slw69484.jpg', '<p class="art_p">在中国武器装备现代化的推动下，新型装备层出不穷，导致一些刚服役、曾被广大军迷关心的新装备，“名气”迅速被更先进装备取代，成为过气网红。不过歼-20作为我国最先进五代战机，可以说牢牢占据“当红明星”的地位，即便是后期出现的“鹘鹰”FC-31，也难以撼动其地位。<strong>不过最近网络上出现一本有关FC-31的画册，其中暴露的信息，甚至可能会让歼-20也成“过气网红”。</strong></p><p class="art_p">这是某网友在贴吧发出的一本有关歼-31的名叫《鹘鹰神话》的画册，其内容是有关沈飞在研发、建造鹘鹰战机背后的可歌可泣的事迹，展示了很多该型战机应用的先进设计制造技术，某种程度上体现了中国“五代机”的生产水平。<strong>但更重要的是，该画册后面出现的一张照片背景中，却出现了一种从来没有见过的战机。</strong></p><p class="art_p">在沈飞工作人员向上级领导展示歼-15战机的时候，<strong>在后面出现了一架黑色涂装飞机，看前方尖锐的设计应该是某种隐身飞行器的头部。而其上部有非常巨大的开口</strong>，形状像是飞行员的座舱。这一外形和中国现在已知的歼-20、歼-31、歼-16、歼-10C等国产战机都不相同。</p><p class="art_p">有网友猜测这一黑色涂装飞机为早就曝光的“利箭”无人机，上方的开口为无人机的进气道。<strong>但通过和公开的“利箭”无人机对比可以发现，明显不可能有如此巨大的“进气道”。</strong>真正和这一神秘“黑色机头”相似的，是刚刚过去的五一劳动节期间，中国航空工业沈阳所（601所）微信号文章曝光的一种形似苏-34“鸭嘴兽”机头的隐形战机头部全比例模型。而这被认为是601研发的“隐身中型轰炸机”或“隐身战斗轰炸机”。</p><p class="art_p">只有这种大型战机，才符合图中的巨大飞机尺寸。而《鹘鹰神话》这张图的出现，可以看出“黑色机头”旁边有专用的飞机建造装配平台。<strong>这证明中国研制中远程“隐身攻击空中平台”，已经进入全尺寸样机部件生产阶段。参考首架歼-20原型机曾经使用“718工程验证机”的称呼</strong>，因此在中远程“隐身攻击空中平台”，中国军队同样可能采用先造验证机的手段，而且轰炸机、战斗轰炸机飞机尺寸更大、技术挑战大、研制费用高、装备数量少，更需要验证机来“探探路”。</p><p class="art_p"><strong>这一“黑色机头”的曝光，代表中国空军高层领导曾公开的“正在研制新一代远程轰战机”的说法，绝不是说说而已。</strong>然而中国下一代作战飞机的全面“隐身化”，意味着在技术上将实现全面跨越。特别是从战斗轰炸机和中型轰炸机领域，中国将直接从第二代的轰-6跨越到第四代的隐身轰炸机，和美军B-2同代。这样战机一旦出现，歼-20很可能就要成为“过气网红”。此外这种曝光的中远程“隐身攻击空中平台”，<strong>是意味着中国已经放弃研制类似B-2的大型隐身飞翼轰炸机，亦或是证明此路不同，还是两条腿同时走路，仍然需要观察。</strong>（作者署名：诤闻军事）</p><p class="art_p">《出鞘》完整内容请关注新浪军事官方微信抢先查看（查看详情请搜索微信公众号：sinamilnews），《出鞘》每天在新浪军事官方微信完整首发。</p>', 1525421683, 0, 6),
(102, '老师所出化学试卷成“网红”:考试范围是星辰大海', 'uploads/CCcm-fzrwiaz282887515651.jpg', '<p class="art_p">澎湃新闻记者 李思文 实习生 万璇 习钰</p><p class="art_p">“题目太长，有点彷徨，套路太深，不必当真……”</p><p class="art_p">在全国紧张备战2018年高考之际，江西省南昌市南昌县莲塘第一中学（下文简称“莲塘一中”）的一份周考化学试卷，在网络走红，被称为“最解压试卷”。试卷上每一道试题都是“鸡汤”加“知识点”的组合，用段子来引出提问，在试卷的结尾还不忘加一句：这次考试的范围是星辰大海。</p><p class="art_p">试卷被学生们上传至网络后，立刻引起了广泛关注，有网友评价出卷人：“考试都不忘给学生关注正确的价值观。”也有网友表示：“这样的卷子把恐怖的化学考试也变成了乐趣。”</p><p class="art_p">4月30日，澎湃新闻（www.thepaper.cn）专访出卷老师之一徐振宇，他表示，在紧张的高考氛围下，只是希望通过这份试卷让学生稍微放松一下。“这套试卷里的段子和‘鸡汤’其实也是我们平时课堂的积累，在上课的时候我就喜欢穿插一些段子来吸引学生注意力，不想教学过于死板。”</p><p class="art_p"><strong>一份“让学生开心的试卷”</strong></p><p class="art_p">“没想到这份卷子会引起那么多人的关注。”作为莲塘一中高二年级的数学老师，早在这份试卷出来之前，徐振宇就已在学生中“扬名”，“我经常给学校出卷子，而我出的卷子又比较难，所以学生都认识我，还会在贴吧里写下一些气话：希望下次考试不是徐振宇出卷。”</p><p class="art_p">在这份试卷之前，徐振宇刚为高一年级月考出了一份文科和理科数学试卷，“题目有些新颖，绕了一些弯，导致学生考的平均分都比较低，然后紧接着又开了一场家长会，估计学生也被家长批评了，情绪都比较低落。”</p><p class="art_p">因此，在随之而来的周练考试中，徐振宇决定和化学老师彭毅一起，出一份“让学生开心的试卷”。</p><p class="art_p">徐振宇说，学校领导也很支持他们的想法，觉得应该让学生偶尔放松一下，“学校给我们一周的时间准备试题，知识点的题目由化学老师彭毅负责，鸡汤改编的部分就是我们俩共同完成的。”</p><p class="art_p">在这份“让学生开心的试卷”里，每一道试题的前面，徐振宇都加上了一些段子和“鸡汤”：“有些歌第一句就喜欢上，有些人第一眼就爱上，有些试卷第一题就不想做。下列说法不正确的是。”“心是用来碎的，周期表是用来背的……下列叙述正确的是。”“问君能有几多愁，恰似一江春水向东流，没错，这道题目考水！”</p><p class="art_p">在试卷的开头，徐振宇还别出心裁的写上：“出卷人：反正不是徐振宇。”他告诉澎湃新闻，学生都比较怕他出的试卷，“这些如果看到出题人不是我，不知道是不是会开心点。”</p><p class="art_p">这些对仗押韵的网络段子和歌词，让拿到试卷的学生们苦笑不得，该校高二理科班一名学生向澎湃新闻表示，从来没有做过那么搞笑的试卷，“觉得很有意思，题目也不是很难，做起来是挺开心的。”他说，一定会把这份试卷好好保存起来。</p><p class="art_p">这份“段子试卷”被学生们上传至网络后，出卷老师彭毅和徐振宇就变成了“网红教师”，“连照片都被发到了网络上。”对于突如其来的“火”，徐振宇表示并不想过分的宣传，“其实就是想出一份让学生开心的卷子而已，权当让他们放松了。”</p><p class="art_p">他告诉澎湃新闻，卷子里的段子和“鸡汤”都是平时的积累，“我们两个平时看到好玩的段子就会互相交流，也比较喜欢把这些段子融入到教学里，让学生觉得课堂比较好玩。”</p><p class="art_p"><strong>尝试“幽默教学”</strong></p><p class="art_p">“高中生活比较乏味，学生也比较疲惫，我就想通过一些有趣的内容，偶尔调侃一下，来带动学生的学习兴趣，学生如果对这个老师比较感兴趣，就愿意上他的课，也就更愿意学这门学科了。”徐振宇介绍说，自己在读高中的时候，授课老师偶尔会把方言给融入到教学之中，熟悉幽默的方言往往能吸引他的注意力，“有时候用方言讲题，我们的印象会更加深刻。”</p><p class="art_p">基于这个体验，在自己的教学过程中，徐振宇也会有意识的通过一些有趣的段子或者俚语，来加深学生的印象，“我有时改编一些成语，比如‘口是笔非’，就是说老师和学生常常都会犯笔误的问题，希望能让学生和老师产生共鸣。”</p><p class="art_p">作为一个教数学的老师，徐振宇更加明白有些学生对数学的“恐惧”，“所以我就会在讲数学的时候，讲一些段子，一是加深印象，二是让学生觉得数学并不枯燥，是很有趣的，他们对数学有兴趣了，提高成绩就是理所当然的事情了。”</p><p class="art_p">徐振宇说，他所带的班级，常常列学校同列班级第一名，辅导竞赛也取得了不错的成绩，“正因为教学质量有保障，所以我们才敢更大胆地去做一些尝试。”</p><p class="art_p">出生于1993年的彭毅，也是一名青年教师，因为和徐振宇年纪相仿，两人平时关系很好，“我和彭老师一起负责学校竞赛，平时交集很多，而我们俩又都是名校毕业，年纪比较小，教书就不太会拘于形式。”</p><p class="art_p">徐振宇告诉澎湃新闻，平时在备课时，彭毅也会想尽办法让自己的课堂生动起来，“他也是认为，有趣的课堂氛围更吸引学生，偶尔让学生们笑一笑，精神反而会更好。”</p><p class="art_p">但徐振宇表示，调侃、讲段子只是在学生们疲惫的时候，平时考试、作业要求还是比较严肃的，“毕竟对于高中生来说，高考和成绩还是最重要的。”</p><p class="art_p">因为上课风格幽默有趣，年纪又比较年轻，徐振宇在学校里也很受学生的欢迎，“我和学生相差不到十岁，年龄代沟没有那么大，和学生的亲近感就更强一些，很多学生遇到了困难，和父母沟通不好，反而和我们沟通起来比较方便，像是知心哥哥姐姐的感觉。”</p><p class="art_p">在学校里，学生常常称他为“宇哥”或者“宇神”，随着这份“网红试卷”的走红，徐振宇也受到了更多的关注，但他并不想过多的宣传或者炒作自己，“只是希望这种教学模式能够给大家一些启发，传播教育的正能量。”</p><p class="art_p">责任编辑：王颖</p>', 1525418839, 0, 7),
(103, '被曝虚假宣传遭工商约谈 尚德机构:深刻自省', NULL, '<p class="art_p"><span style="font-family: KaiTi_GB2312, KaiTi;">被曝虚假宣传等问题 工商约谈尚德机构</span></p><p class="art_p">新京报讯 新京报5月2日刊发调查报道，曝光从事教育培训的尚德机构涉虚假宣传、退费难等问题，以及多重身份、虚假承诺、话术截杀等招生“套路”。尚德机构昨日回应称，部分问题属实，公司对此负责。北京市工商局朝阳分局相关负责人表示，已经约谈尚德机构相关负责人。</p><p class="art_p"><strong>尚德机构发声明：深刻自省</strong></p><p class="art_p">北京工商石景山分局相关负责人称，关注到媒体对尚德机构涉嫌虚假宣传等问题的曝光后，工商执法人员迅速开展行动，5月2日，执法人员到尚德机构总部固定证据，5月3日，北京工商石景山分局约谈了尚德机构的相关负责人，将进一步调查。</p><p class="art_p">5月3日尚德机构发布“关于尚德机构虚假宣传事件的声明”称，在调查结果出来之后，该业务部门对应的集团副总裁及事业部总经理主动要求自我扣薪，并深刻思考自省；公司对任何影响用户体验的行为作出了明确的列示和更加严格的罚则。</p><p class="art_p">昨日，新京报此前报道中化名“赵宇”的尚德机构学员告诉记者，尚德机构工作人员前天联系了他，为他解除了分期贷款，并承诺这一两天就退还学费。此前，赵宇听信尚德机构“补录学籍”的说法，分期支付7980元报名了自考辅导班，临近考试却没有学籍无法参加考试。</p><p class="art_p"><strong>将推出人工智能质检等措施</strong></p><p class="art_p">尚德机构声明称，会推出三项改善措施：售前咨询过程100%人工智能质检、无意愿零打扰策略、公共课免费学。</p><p class="art_p">声明称，对于“学习规划师”通过电话、微信、QQ等任何官方渠道沟通的所有内容，全部通过人工智能技术进行语音和语义质检，加上专门质检团队的双重人工检查，一经发现任何违规承诺，不实销售话术，不当沟通内容，对该部门各层级涉及人员都将严惩不贷，绝不姑息。</p><p class="art_p">对于电话骚扰问题，尚德机构称，如用户明确表示暂无进一步的学习需求，系统将自动识别，将该用户的联系方式从系统中屏蔽。</p><p class="art_p">声明还称，尚德还将推出“自考公共课免费学”的产品，让用户提前体验在线学习产品，了解考试，消除用户付费前后对该考试和在线备考学习产品的理解落差。</p>', 1525403718, 0, 7),
(104, '湘潭大学两名女生校内被流浪狗追咬受伤 校方回应', 'uploads/Yxe6-fzyqqiq204284977594.jpg', '<p class="art_p">5月3日，湘潭大学在其微信公众号上，发布《关于校园内学生被流浪狗咬伤事件的情况通报》，全文如下：</p><p class="art_p">5月1日21时左右，我校化工学院一女生在校园内被多只流浪狗追咬，右手背和腰部两处被咬伤。5月2日16时左右，我校法学院一名女生，再次遭到多只流浪狗追咬，右腿后侧被咬伤。目前，两名女生在学院老师的陪同下，已及时到医院进行狂犬疫苗注射和伤口处理，学生目前身体情况稳定，已恢复正常学习生活。</p><p class="art_p">流浪狗追咬学生事件发生后，学校保卫部门立即做了紧急布置，加强保安巡逻力度，并在校内学生管理群发布了安全提示。5月2日保卫处干部带领所有保安队员分组携捕狗器具，在校园内24小时值班巡逻，对流浪猫狗进行驱赶、捕捉。同时报告公安派出所安排专业人员协助彻底清理流浪狗。5月2日晚，学校召开校园环境治理会议，要求相关学院进一步跟踪关心受伤学生的身体健康，保卫部门进一步加大校园流浪动物的清理力度，确保不再发生类似事件。</p><p class="art_p">湘潭大学保卫处</p><p class="art_p">2018年5月2日</p><p class="art_p">来源：“湘潭大学”微信公众号</p>', 1525341360, 0, 7),
(105, '日媒：中国研究论文数量取得胜利 质量未追上美国', 'uploads/wBgi-fzyqqiq155696783522.jpg', '<p class="art_p">参考消息网5月3日报道 日本《读卖新闻》4月30日刊登了题为《中国大步迈向科学大国：资金人才丰富 课题是“质”》的文章。文章称，今年是中国实行改革开放40周年。如今中国不仅在经济领域走在前列，在科学技术领域也正引领世界。本文在回顾中国科学技术发展历程的同时，就现状和问题进行了分析。</p><p class="art_p">中国科学技术持续发展的动力来自丰富的资金和人才。据日本文部科学省统计，2015年中国的大学、研究机构和企业的研发经费总额达42万亿日元（100日元约合5.79元人民币——本网注），达到1987年总额（不足1万亿日元）的约40倍。</p><p class="art_p">文章称，1987年日本的研发经费是中国的近十倍，但在2009年被中国赶超；至2015年，中国的研发经费比日本多出25万亿日元。中国研究人员人数达162万（2015年数据），远超过日本（2015年约为68万人）。</p><p class="art_p">文章认为，中国此前在科学技术领域明显落后于日美欧。但进入21世纪以来，中国的科学技术与经济同步取得了飞跃发展。中国超级计算机的运算速度在2010年跃居世界第一，2013年中国的嫦娥三号无人探测器成功登月，中国的科技发展备受世界关注。</p><p class="art_p">据日本文部科学省统计，中国的论文数在1981年时约为1800篇，到2015年增至原来的160倍。美国国家科学基金会报告显示，中国论文数在2016年首次超过美国跃居世界第一。</p><p class="art_p">自2005年起与中国联合推进传染病研究的东京大学名誉教授岩本爱吉表示：“起初中国缺乏实验设备，研究水平较低。但后来在海外学有所成的研究人员回国，中国逐步引进高性能设备，中国研究人员的论文开始刊登在一流的科学杂志上。”</p><p class="art_p">不过，不少人认为，中国的论文虽然在数量上取得了胜利，但在质量上尚未追上美国。日本科学技术振兴机构2016年发表的报告也指出，中国缺乏创造性研究。一位中国的研究人员表示：“中国对研究人员进行评价之际过度重视论文数量。比起质量更重视数量，结果就是产生了许多内容欠缺的论文。”</p><p class="art_p">2011年中国政府启动引进海外高层次人才的“千人计划”，很多日本研究人员也被中国政府聘用。</p><p class="art_p">文章提及，中国政府2017年提出了建设世界一流大学和一流学科的“双一流”战略。《泰晤士报高等教育》杂志2017年9月公布的世界大学排名中，北京大学（27名）和清华大学（30名）两所中国高校进入前100名。</p><p class="art_p">与之相比，日本的东京大学和京都大学进入前100名，但排名较高的东京大学位居第46，与中国高校的差距逐渐拉大。中国政府人士意气风发地表示：“到本世纪中叶，希望进入世界前100名的中国大学达十所以上。”</p><p class="art_p">中国科学技术今后将继续实现飞跃发展吗？日本应该如何应对呢？日本《读卖新闻》采访了日本政策研究大学院大学副校长角南笃。他认为，中国认为科学技术不仅是经济增长所必不可少的，对于安全也是不可或缺的，因此发展科学技术作为大国而言是理所当然的。在这一思想的指引下，中国切实推进科技进步，取得了切实成就。</p><p class="art_p">中国还在有效利用全球化的时机，一些中国的海外留学生也在当地成立研究团队，之后再回中国从事研究工作，这种世界级别的头脑循环正不断发生。在美国的硅谷，中国比日本的影响力更大。</p><p class="art_p">有人认为，如果经济发展达到极限，则科学技术也停止发展。但是，创意引发的革新可以克服预算的约束，这正是科学技术的魅力所在。中国拥有丰富的人才和国际网络，即使预算有限也可以保持科技发展。</p><p class="art_p">文章认为，对于日本而言，与其把中国视为威胁，不如将中国视为伙伴，互相交流和学习。</p><p class="art_p">在古代，中国花了很多年时间，才终于建成了万里长城。如今，中国也会把确定的国家工程坚持到底。</p>', 1525335360, 0, 7),
(106, '摔跤吧少年！这所农村中学的励志故事堪比电影', 'uploads/KTQ0-fzyqqiq145205584423.jpg', '<p class="art_p">在简陋的环境中刻苦训练，通过摔跤改变命运，开启不一样的人生……</p><p class="art_p">这可不是在描述印度热门电影《摔跤吧！爸爸》，而是三峡库区云阳县红狮中学校长和学生们上演的中国版摔跤励志故事。</p><p class="art_p"><strong>山里中学“逼”上摔跤办学路</strong></p><p class="art_p">红狮中学位于三峡库区腹地的重庆市云阳县红狮镇中坪村，面朝浩浩长江，背靠连绵大山。走出大山实现自己的人生梦想，是这所中学一千多名孩子的梦想。</p><p class="art_p">然而，通过考大学走出大山，对这里的孩子来说并不容易。回想2015年1月调到这所学校时的情景，校长王平仲感慨地告诉记者，全校近1500名学生，其中70%来自留守家庭，建卡贫困户学生470多人，学生成绩普遍较差，当时学校各项指标在全县排名倒数。</p><p class="art_p">如何给这些成绩欠佳的孩子们找到出路？王平仲和老师们走访发现，由于长期爬坡上坎，学生们普遍上肢长、爆发力出色，这恰恰是摔跤运动员需要的特质。于是，王平仲和老师们决定，走摔跤运动特色办学之路。</p><p class="art_p">他们从重庆专业队淘来二手摔跤服、摔跤垫，把泡沫钉在墙上防撞，将学校唯一的会议室改成了训练馆。另外，王平仲托人四处打听，找到了退役摔跤运动员向乙烜到学校当教练。</p><p class="art_p">2015年当年，红狮中学摔跤队组建起来，最初有100多名孩子，每天训练5小时。因为训练强度大，不少孩子受不了放弃了，最终有16名队员坚持了下来。向乙烜说，这16名山里孩子能吃苦，手脚磨破皮是常事，但从来不叫疼。有一个小队员在训练中手肘骨裂，还坚持训练，后来伤势加重就医才知道受了伤。</p><p class="art_p">训练不到一年，这批队员在2016年重庆市第五届运动会摔跤项目比赛中便迎来“大丰收”，获得4金2银6铜，其中2人被输送到重庆市运动技术学院。</p><p class="art_p"><strong>摔跤开启孩子成才大门</strong></p><p class="art_p">摔跤办学初见成效，坚定了王平仲的办学思路，“让一部分有天赋、有条件的孩子成为专业运动员，让一部分孩子通过体育特长考入高校，为这些山里孩子开启一扇新的成才大门”。</p><p class="art_p">想培养运动员，先要找到好苗子。在县城北部的农坝镇，王平仲发现12岁的女孩付坤宁身体条件出色，可付坤宁父母认为女孩子不应该摔摔打打，不同意孩子加入学校摔跤队。王平仲多次上门将摔跤视频放给付坤宁和她父母看，介绍摔跤运动。付坤宁看了视频后对摔跤产生了兴趣，而王平仲的诚意也打动了付坤宁父母，他们同意让孩子先去学校试训三个月。</p><p class="art_p">经过几个月的训练，在2017年4月重庆市的摔跤比赛中，付坤宁仅用10秒时间就击败了专业队的运动员夺冠。摔跤国家队教练潘教利看到付坤宁的比赛视频后十分兴奋，决定亲自培养这个好苗子。付坤宁从此跟随教练走出大山，走上了专业摔跤运动员的道路。</p><p class="art_p">三年来，红狮中学共获得全国摔跤锦标赛6枚奖牌、重庆市冠军33个，培养国家健将级运动员2人、一级和二级运动员23名。在县里的支持下，红狮中学扩建了食堂、运动员宿舍，新建了一座专业摔跤场馆，聘请了从国家队退役的专业教练，目前学校摔跤队达到了126人。</p><p class="art_p"><strong>摔出自强自信、精彩人生</strong></p><p class="art_p">“摔跤不仅仅是一项运动，我们将摔跤运动中顽强拼搏、永不言败的精神作为学校办学理念，希望更多学生通过摔跤收获抗挫折能力，强健体魄，找到自信，走出自己的精彩人生。”王平仲说，2017年30多名学生因摔跤特长升入本科院校，其中3名学生考入重点院校。</p><p class="art_p">高三学生吴本银家境贫寒，初中毕业后他本想外出务工补贴家用。通过老师做工作，吴本银接触了摔跤并喜爱上了这项运动，经刻苦训练，2016年被授予国家一级运动员称号。为了早日缓解家庭负担，吴本银放弃了成为专业运动员的想法。谈到未来的打算，他对记者说：“通过自主招生考取体育院校，将来当体育老师。”</p><p class="art_p">曾获得市级比赛四连冠的女学生黄丹，被输送到湖北体育职业学院进行了一年的专业训练，去年却出人意料地返回学校准备高考。当学院教练最后一次打电话来劝她时，黄丹没有多说，只是挂掉电话就哭了。</p><p class="art_p">王平仲介绍，黄丹家里四姐弟都由年过七旬的爷爷奶奶抚养，她说要尽早工作挣钱养家，不能离爷爷奶奶太远，“说到这些我也就没办法再劝了，这些孩子懂事”。</p><p class="art_p">为让吴本银、黄丹等13名摔跤队员备战高考，学校单独将他们组建成一个班，有针对性地辅导。高三数学教师胡波深有感触地说，摔跤运动让同学们精神面貌焕然一新，“这些山里孩子从小就缺乏自信，摔跤运动让他们知道自己能行”。</p>', 1525335334, 0, 7),
(107, '百名盲人参加"指尖上的高考" 愿努力的人被善待', 'uploads/i1T9-fzyqqiq037107499350.jpg', '<p class="art_p">近日，近百名来自全国各地的盲人在贵州盛华职业学院参加2018年“指尖上的高考”。他们中有按摩店老板、先天性视力障碍者…有人说毕业了想当音乐老师，有人说想创业，也有人说想过上普通人的生活…为了能够取得好的成绩，他们在考试前做了充足的准备。祝福他们！</p>', 1525332566, 0, 7),
(108, '高校要求学生微信运动1万步 有人买摇步器刷步数', 'uploads/0p-r-fzyqqiq106883789743.jpg', '<p class="art_p">“微信步数每天过万”成了广东外语外贸大学学生这学期的难题。</p><p class="art_p">这源于广外为鼓励学生课外锻炼提出的新要求：每天锻炼步数达10000步（微信运动）以上，或当天在装有蓝牙发射器的体育场馆锻炼不少于40分钟；每周锻炼需达标五次，至少有一次在体育场馆练习专项内容不少于40分钟。未完成者扣除体育成绩的10%。</p><p class="art_p">为进行有效管理，该校体育部4月2日启用了基于小程序的“课外锻炼管理系统”记录锻炼情况。一个月来，不少学生反映每天10000步有点难，为完成“任务”有同学买“摇步器”刷步数，运动步数排行榜上甚至出现了七八万步的作弊高分。</p><p class="art_p"><strong>校方</strong></p><p class="art_p"><strong>为促进课外锻炼量身定做</strong></p><p class="art_p">据广外介绍，2015-2016学年起，广外就开始将课外锻炼纳入体育课课程评价，占体育课成绩10%。当时要求学生每周锻炼三次，每次半小时以上，由各体育课班级将学生分成几个小组，设小组长和微信群。学生将课外锻炼截图、视频上传到微信群，小组长进行考勤。</p><p class="art_p">“我们发现这个方式存在明显漏洞，没有硬性要求的话学生不重视，但当时没有更好的办法去监管。”广外一名体育老师表示。</p><p class="art_p">为对学生课外体育锻炼情况进行有效管理，今年4月2日，广外正式启用“学生课外体育锻炼管理系统”。据介绍，该系统是国内首个针对教育部有关大学生课外体育锻炼要求指标量身定做的应用系统。</p><p class="art_p">“我们希望学生通过2年体育课掌握1-2项技能，养成终身锻炼的习惯。”上述体育老师说，“但调研发现，大学体育课每周1次，一次120分钟，中间还有考试、放假、天气情况、体质测试，真正锻炼的时间不到10周，想让学生掌握一门技能，提高体质太难了，所以把重点放在了课外。”</p><p class="art_p"><strong>学生</strong></p><p class="art_p"><strong>新系统刺激了摇步器消费</strong></p><p class="art_p"><strong>系统启用一个月来收效如何？</strong></p><p class="art_p">“喜欢穿带口袋的衣服了”“起码会走走”“把手机绑腿上，抖腿一万步很简单的”“刺激消费，学校给摇步器做贡献了”……记者采访了8名广外学生，多数学生对新规并不乐观。</p><p class="art_p">首先，要达到每天步数上万并非易事。“广外本身不大，如果当天满课来回宿舍大概有五千步，下雨天操场也不能去”“步数每天顶多就8000多”，一些学生表示每天走一万步有点困难。也有一名学生表示，“课多的话，来回几次教学区还是有一万步。”</p><p class="art_p">因每天一万步的较难达到，随之出现了“消极”的应对措施。有学生表示，这一系统刺激了摇步器的销售，微信步数排行榜甚至出现了七八万步的“选手”；还有学生选择比较原始的手摇，或绑在腿上抖腿；再不然就去操场“待”够40分钟。“晚上七点到九点去操场，你会发现很多人坐在那里玩手机。”一名学生告诉记者。</p><p class="art_p"><strong>回应</strong></p><p class="art_p"><strong>不会影响学业 没必要作弊</strong></p><p class="art_p">针对这些问题，上述体育老师介绍，一万步是根据教育部每天锻炼一小时的要求来量化的，之前做过实验，并找学生分两批做了调研。“第一批是调取学校各学院共30名学生过去一个月的微信步数，发现学生平均步数在每天8300步左右；第二批找了20名学生做一周的步数监控，观察学生每天具体活动与步数的关系。”</p><p class="art_p">这名老师表示，一万步和40分钟的课外锻炼本来就不难达到，而且课外锻炼本来就只占课内成绩的10%，不存在说直接影响学业的问题，我们也教育学生没必要去作弊。</p><p class="art_p"><strong>■ 观点</strong></p><p class="art_p"><strong>“中小学体育教育缺失，大学补课困难”</strong></p><p class="art_p">清华大学体育部主任刘波认为，鼓励学生锻炼，单纯采取强制的方式学生会存在逆反心理，需在强制基础之上用新的方式吸引他们。清华大学除用App督促学生跑步，还通过动漫、体育社团等各种方式给学生提供更好的锻炼条件。</p><p class="art_p">“通过强制和引导结合，能争取更多学生去锻炼。”刘波说。</p><p class="art_p">此外，刘波表示，大学体育理应在中小学基础上，通过社团、体育课等方式加以提高，但问题在于中小学体育教育缺失，补课困难。</p><p class="art_p">“全国范围来看都是如此，大学上的体育课和中小学没有区别，就是补课来学习中小学没有学到的东西。中小学基础没打好，学生运动技能缺乏，没有自己擅长的项目，锻炼习惯和健康常识也缺乏，大学只能去做补课的工作。”刘波说，跟以前相比，现在对学生的锻炼越来越重视，教育部也提出每个学生学会至少两项终身受益的体育锻炼项目，最后还需要政府、中小学学校和家长一起联动来落实。</p><p class="art_p"><strong>■ 综述</strong></p><p class="art_p"><strong>多所高校“出大招”激励学生锻炼</strong></p><p class="art_p">2017年《中国学生体质监测发展历程》显示，我国大学生体质依然呈下降趋势，只是下降速度趋缓。与此同时，肥胖率持续上升，每5年提高2%到3%。2016年，国务院办公厅印发《关于强化学校体育促进学生身心健康全面发展的意见》，要求切实保证学生每天一小时校园体育活动落到实处，高等学校要通过多种形式组织学生参加课外体育锻炼。</p><p class="art_p">记者梳理发现，为鼓励大学生进行体育锻炼，广东外语外贸大学的“新招”并非个例。近两年，不少高校都出台了相应激励措施。</p><p class="art_p">今年3月，广州大学要求大一大二学生从3月19日到6月22日参加课外长跑，每人必须完成25次以上，跑速要求在每千米3分钟至10分钟，这些数据通过一款跑步APP上传。课外长跑占大学体育总评成绩20%，如学生未按要求完成25次长跑，体育成绩将不给予评定。</p><p class="art_p">去年8月，华中农业大学要求选修体育俱乐部的所有本科生（一、二年级学生，还包括三、四年级未修满体育学分或需重修的学生）参加环湖长跑锻炼，锻炼情况纳入每学期体育成绩总评，占30%。环湖长跑路线全程约1500米，路线中间需刷卡，学生须以2米/秒以上的速度跑进，每半程有效时间为6分15秒以内，否则计为无效练习。路线沿途设监控设备，监控信息直接在体育馆监视器可见。</p><p class="art_p">此外，早在2016年，清华大学、北京师范大学、北京建筑大学、武汉大学等多所高校就曾要求学生“运动打卡”。清华要求学生参加跑步锻炼，并且要“刷脸”3次完成打卡，为配合学生更好地完成锻炼还推出了专门的App。武汉大学也通过一款App对本科生环跑进行核算，考核计入体育成绩20%。</p><p class="art_p">值得一提的是，清华大学去年重启老“校规”，从2017级开始，清华学生必须通过入学后游泳测试，或参加游泳课学习并达到要求，否则不能获得毕业证书。</p>', 1525318484, 0, 7),
(109, '日本高中积极招揽中国籍学生 中国国歌“进入”日本校园', NULL, '<p class="art_p"> <p class="art_p">参考消息网5月3日报道 台媒称，日本严重的少子化及老龄化问题，造成当地学校面临学生不足的窘境，为避免破产，许多学校将目光转向中国大陆，大举招募大陆学生。九州宫崎县就有私立高中今年度的入学新生九成以上为中国大陆籍，校长在入学典礼上，不但要用中文致辞，还率领师生高唱《义勇军进行曲》。</p><p class="art_p">据台湾联合新闻网5月2日报道，日前引发网友热议的《义勇军进行曲》“挺进”日本校园，甚至日本九州宫崎县的高校校长还带领全校师生一同齐唱，引人注目。日本私立高中校长马笼胜典表示，“对中国人民深表敬意”，因为这所私立高中今年入学的183名新生当中，只有16名日本人，剩下的167名全都是来自中国大陆的留学生，足足占了总人数的九成。由于人数比例太过悬殊，入学典礼时，校长不但要用中文致辞，还需要演唱《义勇军进行曲》，马笼胜典直言，“中国是离我们最近的国家之一，只要他们的学生能来，学校就有办法经营下去。”</p><p class="art_p">他解释，受到少子化及老龄化的影响，日本地方县市的就学人口不断减少，私立高中在日本境内很难招到学生，因此才会把招生目光转向中国大陆，“日本学生不断减少，虽然我们不是公司也可能会破产”。</p><p class="art_p">报道称，由于招募外国学生福利多，每招募一名学生，就能从中央得到优厚的税款，不但能提供学生一个月8000日元（1元人民币约合17.26日元——本网注）的补助金，还能充实当地老人及幼儿的福利设施，因此，除了日本私立高中可以藉由招募中国大陆学生避免学校倒闭外，地方乡镇也能够利用设立日语学校招募外国留学生，促进地方区域的经济发展。</p><p class="art_p">该报道在网上立刻引发网友热议，纷纷留言表示，“出国读书结果九成都是自己人，搞得出国跟没出国一样”、“很好奇他们的日文是否能进步”、“留日三年不会日文也可以”。</p>', 1525307520, 0, 7),
(110, '国办:坚决防止因学校布局不合理致上学难甚至辍学', 'uploads/MTqA-fzyqqip956298840437.jpg', '<p class="art_p">中新网5月2日电 据中国政府网消息，国务院办公厅近日发布《关于全面加强乡村小规模学校和乡镇寄宿制学校建设的指导意见》。《意见》提出，各地要通过满足就近入学需求、解决上下学交通服务、加大家庭经济困难学生资助力度等措施，坚决防止因为学校布局不合理导致学生上学困难甚至辍学。</p><p class="art_p">《意见》要求，妥善处理小规模学校撤并问题。布局规划中涉及到小规模学校撤并的，由县级人民政府因地制宜确定，但要按照“科学评估、应留必留、先建后撤、积极稳妥”的原则从严掌握。学校撤并原则上只针对生源极少的小规模学校，并应有适当的过渡期，视生源情况再作必要的调整。</p><p class="art_p">《意见》提出，要严格履行撤并方案制订、论证、公示等程序，并切实做好学生和家长思想工作。撤并后的闲置校舍应主要用于发展乡村学前教育、校外教育、留守儿童关爱保护等。对已经撤并的小规模学校，由于当地生源增加等原因确有必要恢复办学的，要按程序恢复。</p>', 1525283100, 0, 7);
INSERT INTO `pre_news` (`news_id`, `news_title`, `news_pic`, `news_content`, `news_time`, `news_like`, `news_cate_id`) VALUES
(111, '谢宇：美国科学在衰退吗？', 'uploads/-npW-fzyqqip781814671829.png', '<p class="art_p">《财经》记者 马国川/文 苏琦/编辑</p><p class="art_p">“美国科学没有衰退，”美国两院院士、普林斯顿大学终身教授谢宇在接受《财经》记者专访时说，“美国科学的最大财富和最大优势不是钱，也不是人，而是美国文化。”</p><p class="art_p">自21世纪以来，关于“美国科学正在走向衰落”的议论就在美国流行。随着中国科技的快速进步，围绕“美国科学衰落”的争论更趋激烈。作为一个致力于实证研究的社会学家，谢宇和他的合作者一起撰写了专著《美国科学在衰退吗？》，对这场争论进行了回应。</p><p class="art_p">谢宇是高考恢复后的第一批大学生，后来留学美国，并曾在美国大学任教。2009年，50岁的谢宇当选为美国国家科学院院士，成为改革开放以来赴美的百万留学生中唯一一位在社会科学领域入选美国国家科学院院士的华人。</p><p class="art_p">著名科学家施一公在为《美国科学在衰退吗？》中文版撰写的序言中说，“我的直觉与谢宇的论证不谋而合”，“我看到书中大量的科学采集的数据佐证其观点，常常感到既充分翔实又痛快淋漓”。</p><p class="art_p">随着中国经济实力的增强和科学研究的发展，“美国科学衰落论”同样在国内流行。有些人甚至乐观地说，“中国已经超过了美国”。</p><p class="art_p">作为北京大学千人计划讲座教授，日常往返于中美两国的谢宇也注意到了国内的议论。他提醒说，中国科学与美国的差距确实在缩小，但是原创性的、前沿性的科学创新太少了，“中国现在有钱、有人才，但是缺乏创新的文化土壤”。</p><p class="art_p">谢宇说，“未来中国科学要有大发展，要在多中心的科学生态中占有优势地位，就要从改善文化、改善体制入手”。</p><p class="art_p"><strong>美国科学的三大困境</strong></p><p class="art_p">《财经》：近年来中国科学技术发展很快，有一个说法越来越盛行，就是美国科学在衰落。在美国是否也有类似的议论？</p><p class="art_p">谢宇：很久以来，许多研究者、政策制定者和民众就对美国科学的发展前景信心不足，各方面的言论不断地为美国科学界敲响警钟。</p><p class="art_p">2007年美国国家科学院、国家工程院和医学研究所联合发布的一份报告说，美国在不久的将来可能失去科学研究领域的领导地位。报告发布后受到政策制定者的广泛关注，一年里国会就收到了几十份议案。不过，这种“警钟观点”也受到了一些来自权威机构的挑战：著名智库兰德公司发表报告认为，美国的科学现状并没有那么令人担忧。</p><p class="art_p">在美国，有这些争论是非常正常的，也是非常有意义的。我在2012年出版了《美国科学在衰退吗？》一书，就是对这些争论的回应。</p><p class="art_p">《财经》：作为一个社会学家，你为什么会关心科学发展问题呢？</p><p class="art_p">谢宇：我本科读的是冶金工程专业，毕业后到美国留学，读的是科学史，后来又加了一个社会学专业。博士阶段放弃了科学史，但是我一直对科学史感兴趣。1989年我完成的博士论文《成为科学家的过程》，研究的就是什么样的人能够成为科学家。</p><p class="art_p">博士毕业后，我去密歇根大学教书，专注于社会学研究。我的博士论文在当年没有出版，多年来一些好友经常问这篇论文的最新进展，于是我就邀请一个从前的学生、现在是哈佛大学社会学教授的亚丽珊德拉·齐沃德作为合作者，在我的博士论文基础上完成了这本书。</p><p class="art_p">《财经》：从题目上看，这本书与当初的博士论文有很大不同。</p><p class="art_p">谢宇：是的，不仅仅更新了数据，几乎是重写。因为在合作的过程中我们的研究兴趣有所转移，主要围绕关于美国科学发展现状的争论展开讨论，最终是想要回答美国科学是不是在衰落。因为我们研究发现，美国科学确实存在现实困境。</p><p class="art_p">《财经》：主要是哪些困境？</p><p class="art_p">谢宇：第一，从美国科学家的人口结构来看，科学家中移民的比例增长迅速：从1960年的7.2%增长到2007年的27.5%。与此同时，美国本土出生的科学家比例在下降。</p><p class="art_p">《财经》：在第二次世界大战之前，就有大批科学家移民美国。</p><p class="art_p">谢宇：但差别在于，起初主要是欧洲移民，从上世纪70年代以来，亚洲人占了大多数，种族有明显的变化，数量上的变化也很大。亚洲移民在美国的融入性比欧洲移民差些，自我认识较强，与输出国的联系较紧密，回国服务的可能性也要大些。现在有超过四分之一的科学家来自外国。美国担心，如果过度依赖移民科学家，一旦移民科学家供给下降，美国科学将会陷入困境。</p><p class="art_p">第二，从20世纪60年代起，相对于同等教育水平的其他职业来说，美国科学家的收入是相对下降的。特别是基础科学，工资比从事社会科学、经济学、医生和法律工作都低，对年轻人的吸引力也会随之降低。另外，美国大学教育没有继续扩大，所以教职有限，造成了一些专业人才拿不到教职，例如生命科学就是这样。</p><p class="art_p">第三，美国的科学研究经费也有所下降，不像以前那样宽裕。中国的研究经费的增长是很可怕的，每年增长20%多，所以现在有很多年轻的科学家愿意回来，因为他们回来有比较可观的工资。</p><p class="art_p">《财经》：中国科学家的工资比美国还要高吗？</p><p class="art_p">谢宇：和美国的工资差距并不大，但是在中国获得的研究经费会比在美国高很多，而且国内的设备也很好，所以很多人愿意从海外回国工作，这种选择也是出于理性考虑的。</p><p class="art_p">《财经》：美国科学面临这三大困境，未来发展前景似乎确实不乐观。</p><p class="art_p">谢宇：但是我们并不能由此得出“美国科学正在衰退”的结论。</p><p class="art_p">美国科学没有衰退</p><p class="art_p">《财经》：从哪些方面可以看出美国科学仍然保持优势？</p><p class="art_p">谢宇：美国科学本身还是很好的，比如美国科学劳动力的规模在增长，美国公众对科学的兴趣和支持度依然很高，美国高中生选修的数学和科学课程更多且成绩在提高，美国科学专业毕业生人数在持续增加，获得科学学位的大多数毕业生都找到了相关工作……。这些方面的数据否定了悲观主义者的看法。</p><p class="art_p">《财经》：不过，其他国家的科学在发展，包括亚洲地区，特别是中国科学的发展迅速，你怎么看这些国家对美国的科学地位的影响？</p><p class="art_p">谢宇：这是一个好问题。科学从一开始就是一个全球化行业。近百年来，许多前沿的科学成果是美国最先发现的，但国际化的教育使得这些基础知识变成了世界的共同产品。世界各地的科学家都有机会接触和学习美国的研究成果，尤其是在基础科学领域。同时美国人也能够从国外研究者的成果中持续受益。</p><p class="art_p">来自国外的激烈竞争并不意味着美国科学的衰退，它可能仅仅说明了当今的美国科学在不断地全球化。在一个其他国家都在努力赶超美国的时代，即使与自身的不同时期相比，美国科学也没有衰退，当然，它也必然不会长期居于统治地位。不同国家的科学家联系越来越紧密，合作也越来越多。在新的世界秩序下，美国科学要一如既往地保持无可挑战的地位无疑会面临巨大困难，但这并不意味着美国科学就会因此而退步。</p><p class="art_p">《财经》：不是美国的科学衰落了，而是整个国际环境变了，其他国家的科学在崛起。</p><p class="art_p">谢宇：是的。相对来讲，美国科学的领导地位不像以前那样完全是起决定作用的。在过去90年里，美国是世界科学的唯一中心，现在这个中心的地位受到了挑战。</p><p class="art_p">从科学发展史看，世界科学中心的转移路线很清晰：最早的科学中心是意大利，然后转移到英国，再从法国到德国，最后在20世纪20年代转移到美国。</p><p class="art_p">《财经》：依照科学中心不断转移的历史发展模式，很容易得出“在不久的将来，世界科学中心将从美国转移到其他国家”的结论。</p><p class="art_p">谢宇：得出这样的结论为时尚早。和其他国家不同，美国成功改变了科学，使科学实现了完全的职业化。只有在美国，科学才做到了真正和教学相结合、和实用相结合。在此之前的几个科学中心都没做到这一点。现在各国都仿照美国模式来发展科学，以国家力量来支持科学发展（比如通过金钱投入和发展教育）。随着科学的普及，我认为，未来的世界科学可能不像以前那样只有一个中心，而是会形成一个多中心的生态局面。不同的国家以自己的比较优势来发展科学的某些方面，从而对科学做出不同的贡献。鉴于网络交流的便捷性和低价航空的大范围普及，与一流科学中心的地理距离已经越来越不重要了。</p><p class="art_p">《财经》：这种科学的世界性分工和经济的分工很相像。</p><p class="art_p">谢宇：这种生态雏形已经出现。例如，现在美国人有一些想法，但是不一定能够做很多事情，他们把这些想法带到中国来做，中国人出钱出力，在实际上把美国人的想法做进一步扩展。换句话说，中国有些科学研究就是跟风、验证，是一种劳动密集度比较高的工作。</p><p class="art_p">美国科学为什么会继续保持优势？</p><p class="art_p">《财经》：在未来的多中心的科学生态中，美国科学还会保持优势吗？</p><p class="art_p">谢宇：答案是肯定的。因为美国科学的最大财富和最大优势不是钱，也不是人，而是美国社会的大环境，也就是文化。科学作为一种独特的社会制度，有三个特征：普遍主义、重视创新、致力于增进公众福利。美国文化与科学的三个特征相契合。</p><p class="art_p">普遍主义是美国一直坚守的原则，它是“人人机会平等”观念的基石。与曾经有长久的贵族阶级特权统治历史的欧洲不同，美国推崇“个人主义”文化，非常重视个人的独立思考，而非一味尊重权威。不断接受新移民就是美国个人主义的一个体现。一个美国人的成就大小或贡献多少只取决于他做了什么，这就是普遍主义的原则，也是科学一直强调的文化规范。</p><p class="art_p">《财经》：问题在于，普遍主义的原则虽然被推崇，但并没有被完全践行。不断有人批评说，美国社会阶层流动不再像以前那样畅通。</p><p class="art_p">谢宇：尽管没有完全被遵守，但它在美国社会是根深蒂固的信念，它的存在会鼓励美国人去追求科学。这种普遍的社会信念会影响那些面临多种职业选择的年轻人的观念，进而影响他们最终的职业选择。</p><p class="art_p">再者，美国文化强调创新。欧洲文化也欣赏创新，美国的独特之处在于它对普通民众创造力的鼓励。用托克维尔的话说，美国人“扫除了挡道的特权，而为平等竞争敞开了大门”。科学因创新而发展，科学的繁荣反过来又会促进创新。美国历史就是一部技术创新史。</p><p class="art_p">《财经》：确实，美国是一个特别推崇创新的国家。在经济学上，创新理论的创立者就是美国经济学家熊彼特。</p><p class="art_p">谢宇：除了普遍主义原则和强调创新的观念，美国文化还认为，每个个体的成功都能改善整个社会，高水平的成就会增进社会福利。因此美国社会少数取得辉煌成就的人能获得大众的赏识和认可，人们也对社会不平等有较高的容忍度。美国人对那一小部分拥有极高声望和财富的群体很少表现出愤怒、不满或嫉妒。</p><p class="art_p">所以，普遍主义、重视创新、致力于增进公众福利，这些科学所具备的特征，也是美国人的价值观。从世界看，两者的结合在美国是最好的，因此孕育了强大的科学创造力。由此我们就可以理解，为什么美国在近一个世纪的时间里能够保持世界科学中心的地位。</p><p class="art_p">《财经》：文化是有持久力的，所以你对未来美国科学继续保持优势仍然有信心？</p><p class="art_p">谢宇：美国文化是多元的、开放的、自由的、推崇个人创造力的，对科学来说这是最重要的文化，是科学发展的“软实力”。换句话说，科学的软实力就是一种支持个人创造、反权威的创造性工作。这种“软实力”很难复制。</p><p class="art_p">从长远来看，在激烈的国际竞争环境下，有利于科学发展的文化环境才是美国科学所拥有的最宝贵的财富。尽管美国科学面临一些困境，但是美国科学发展的软实力没有下降。在竞争激烈却又高度开放、人人能够受益的社会环境中，美国科学会像过去一样持续繁荣下去。如果某一天这样的环境消失了，那才意味着美国科学的真正衰退。</p><p class="art_p">要加强科学“软实力”</p><p class="art_p">《财经》：改革开放以来，随着中国经济实力的不断增强，对科学的投入也越来越大，中国已经成为世界科学研究的重要参与者。</p><p class="art_p">谢宇：这是人们公认的事实。因为科学进步对经济增长有明显的推动作用，所以世界各国都将科技投入作为一项国家战略。中国的研发经费增速惊人，高等教育也在普及，科技人员的规模迅速扩大。设备完善的科学实验室越来越多，中美两国科学家的收入差距在缩小。中国一流的研究机构和大学已经能提供总体上相当于美国较好的研究型大学的待遇和科研条件。此外，中国还积极争取引进国外优秀人才，特别是海外华人科学家，尤其那些在美国的科学工作者。因此中国科学与美国的差距确实在缩小。</p><p class="art_p">《财经》：很多人认为，中国科学赶超很明显，将来是有可能超过美国的。有些人就预言，如果更多地砸钱，二三十年中国也可能出现诺贝尔奖井喷现象。</p><p class="art_p">谢宇：这是可能的，出了诺贝尔奖并不能说明我们的科学赶上美国了。因为有些诺贝尔奖可能是机遇造成的，有些诺贝尔奖是通过实验靠钱砸出来的。问题在于，来自中国的原创性的、前沿性的科学创新太少了。</p><p class="art_p">科学的精髓在于创新。中国现在有钱、有人才，但缺乏创新的文化土壤。中国文化强调下级服从上级、晚辈听从长辈、尊重权威。这样的文化更倾向于做重复劳动或者是扩大规模性质的工作，你做十，我做一百、一千，这不是创新。</p><p class="art_p">《财经》：经济学家钱颖一说过，创新就是从零到一，中国则擅长复制，因为人口基数大。</p><p class="art_p">谢宇：中国的技术性产品是很成功的，因为产品是标准化的、同质性的，是可以进行比较的。和技术性产品不同，科学的不可比性非常强。一所大学有几篇文章、几个院士、多少个学生，都可以计算出来，但是科学成果不能够用同样的方法完全量化。一个好的想法可能比500个想法都要好，一篇有创见的文章可能比500篇文章都要好。科学家与科学家之间不是等价的。</p><p class="art_p">《财经》：从这个意义上说，中国的谚语“三个臭皮匠顶一个诸葛亮”其实是错的，一千个普通科学家可能比不上一个有创新的科学家。</p><p class="art_p">谢宇：科学家之间是不可比的。完全是创造性的、依靠个人想象力的原创性成果，是不能用数字来衡量的。中国的问题是把科学家和科学研究成果以量化的方式来计量和评判。这就是在中国科学发展的困境。中国的管理模式对于产品的批量生产是有效的，因为它可以测量、可以复制，第一个和第一千个、第一万个都是一样的东西。但科学家不是这样，科学产品也不是这样。把商品生产的管理模式移植到科学产品的产出过程，把管理产业的模式放到管理科学家上，就大错特错了。</p><p class="art_p">《财经》：因为中国文化不鼓励创新，所以中国人的创新能力也受到了质疑。</p><p class="art_p">谢宇：到美国留学、就业的中国人也很有创造力，所以中国人不是没有创造力，而是文化环境压抑了创造力。年轻人从读书考试一路走来，一直在强调尊重权威，压抑个人创造力，压抑反权威的不同声音。即使读到了研究生，也是跟着导师做“跟班”。这种文化对科学发展是很不利的。</p><p class="art_p">《财经》：也就是说，科学发展的“软实力”是中国的一大短板？</p><p class="art_p">谢宇：中国在科学发展的“软实力”方面还有很大差距。这种“软实力”不是金钱能够买来的，也不是人多就可以堆积出来的。中国如果不改变文化土壤和社会环境，原创性的基础科学是很难有大突破的。未来中国科学要有大发展，要在多中心的科学生态中占有优势地位，就要从改善文化、改善体制入手。</p><p class="art_p">中国太强调客观指标了，喜欢GDP，喜欢SCI，因为这样使考核和排名更加简单和易于操作。但是，科学成就不是简单的行政认可，而是要获得科学共同体的认可。假如有一天，最有名最活跃的科学家都认为，中国是世界的科学中心，是源源不断的科学原创思想的诞生地，那么中国科学就在多中心的科学生态中真正占据了令人景仰的优势地位。</p>', 1525264005, 0, 7),
(112, '媒体：适格主体最快一个多月后可申请发行CDR', NULL, '<p class="art_p">《存托凭证发行与交易管理办法》（下称《CDR办法》）今起正式向社会公开征求意见。根据工作程序，《CDR办法》征求意见期为一个月，征求意见完成后，将在进行必要修改且完成相应程序后发布实施。</p><p class="art_p">上证报记者从权威渠道获悉，除《CDR办法》以外，与此相关的一系列规范性文件也正在制定过程当中，这些规范性文件作为《CDR办法》的配套细则，将在履行行业内部征求意见后同步或者稍晚于《CDR办法》发布实施。</p><p class="art_p">待《CDR办法》和配套规则正式发布实施之后，企业即可以按照相应的要求进行发行CDR的申报，证监会也将启动受理相关申请的工作。</p><p class="art_p">按照正常程序的时间推算，最快将在一个多月以后成行。</p>', 1525453012, 0, 8),
(113, '特斯拉产能瓶颈原因查明 二季度有望走出产能地狱', 'uploads/LiGZ-fzyqqiq910834650694.jpg', '<p class="art_p">作者：杨海艳</p><p class="art_p">成立15年后，特斯拉终于明白大规模量产的边界在哪里。</p><p class="art_p">特斯拉在一季度创下最大额度的亏损，但好消息是，CEO马斯克表示困扰Modle3产能瓶颈的问题终于水落石出，并有被逐渐解决的迹象。</p><p class="art_p">特斯拉昨日发布了一季度财报，虽然公司收入有所增加，总营收达到34.09亿美元，高于去年同期的26.96亿美元，但却创下了7.85亿美元的季度亏损纪录。此外，2018年第一季度特斯拉的自由现金流为负10亿美元，去年第四季度该数值则为负2.27亿美元，业内认为，这可能会造成支付延迟和消费者押金的风险。</p><p class="art_p">虽然投行和机构进一步看空特斯拉，但马斯克却表示，特斯拉可以在2018年下半年达到正向现金流，在第三和第四季度实现盈利。与此同时，特斯拉计划在2018年削减4亿美元开支。马斯克称，“2018年的开支预计会稍微低于30亿美元，低于2017年的34亿美元。”</p><p class="art_p">让马斯克自信特斯拉能在今年实现正向现金流的最关键点在于，他认为特斯拉已经在逐渐走出“产能地狱”。虽然特斯拉在此前不久宣布大规模召回，与此同时，自动驾驶系统也经常出现事故，但这些都不足以影响特斯拉在市场层面的表现，导致亏损最主要的原因一直以来就在于产能不足。</p><p class="art_p">“由于产能暂时不足，本季度的Model 3毛利率仍为负，但这在我们的预期之内。”马斯克说。但在给员工的内部信以及给股东的函件中，他表示，这一状况将很快得到解决，“截止至4月我们按计划暂停生产线以进一步提升产能前，Model 3的产量已连续三周超过2000辆，并且在其中的最后一周达到2270辆”。</p><p class="art_p">产能改善的主要原因包括以下几个方面：首先电池模块的产能瓶颈得到改善，这曾是特斯拉的主要生产瓶颈。“在安装了我们在德国设计、制造的全新自动化生产线之前，我们就可以满足周产5000辆车辆的所需电池模组。而全新自动化生产线将大幅降低生产成本。”马斯克说。</p><p class="art_p">其次是工厂的自动化程度应用过快。马斯克反思说：“在早期，我们犯了一个错误：将大量的自动化过快地应用到生产过程中。因此，在感受到应用全自动化对某些部分进行装配带来的挑战后——如电池模块生产线、部分原料周转系统，以及总装的两个步骤——我们改善了生产流程，以一定程度的半自动化以及人工装配取代部分全自动化。”在整个生产流程中，最慢的环节其实是决定整个生产效率的关键点。特斯拉此前的错误在于，片面追求全自动化，而忽视了在某些特定的生产制造环节，人工装配或者说半自动化其实才能达到效率的最大化。</p><p class="art_p">明白这些问题后，特斯拉对工厂进行了全面的改造。马斯克重申，Model 3的产量会在2018年第二季度前，如期达到周产量5000台的目标。“一旦我们实现周产5000辆的里程碑，我们将会汲取经验并改善生产线，继续以更有效的方式增加产能，最终达到周产10000辆的水平。”他说。</p><p class="art_p">成立近15年，在向市场投放了4款车以后，从马斯克对外发声的情况看，特斯拉才逐渐理顺了供应链体系，领悟到生产制造的奥妙，并逐渐磨合。由此可见，从生产一款车到量产一款车再到大规模量产一系列车型，确实并不是一件易事。相反，这一路充满荆棘和挑战。不少报道以“每分钟烧掉6500美元”来形容特斯拉造车所耗费的巨资，在这一过程中，不断有机构和分析师认为特斯拉已经处于破产边缘。虽然我们并不知道此次特斯拉modle3是否真的能走出产能困境，但特斯拉的经历也给中国广大的造车新势力们提了不少醒：“造车并非易事”。资金只是一个很小的方面，最难的地方在于对供应链的掌握和把控、生产制造流程的理解，以及生产节奏的掌控和把握。</p><p class="art_p">即便是能够完美改善生产制造瓶颈，马斯克依然还需要解决诸多挑战。在致股东函中，他表示，特斯拉的需求旺盛，“尽管全球范围内仅有不到20家特斯拉展厅展示了Model 3，但该车型的预订量（包括尚未交付的配置订单）超过了45万辆”。今年第二季度，特斯拉计划在更多的特斯拉展厅展示Model 3。</p><p class="art_p">但大规模交付也会遭遇别的挑战。比如说，一季度，交付量扩大后的基础设施增加，使得特斯拉加大对服务基础设施的持续投入，服务及相关业务的亏损进一步增长至1.18亿美元。另外，相较上一季度，2018年第一季度末，由于有更多的Model S和Model X尚在运输途中等待交付，这对特斯拉的营运资金产生了约1.2亿美元的负面影响，经营行为现金支出达到3.98亿美元。由于一季度最后几天车辆交付量的显著增加，第一季度应收账款对运营现金流产生了1.69亿美元的负面影响。</p><p class="art_p">同时也有好消息，比如二手车收益的增加，新能源积分交易的收益增加以及来自汽车租赁等服务品类的资金收益。</p>', 1525452628, 0, 8),
(114, '今日头条母公司字节跳动内部通报黄子峰等员工受贿案', NULL, '<p class="art_p">来源：界面</p><p class="art_p">界面新闻今日获悉，字节跳动近期对内部邮件通报了一起内部员工受贿案件。</p><p class="art_p">相关通报邮件显示，原火山小视频员工黄子峰等3人，收受贿赂，引入亲属入股的合作方，向公司隐瞒他与该合作方的关系，并且本人也与该合作方股东有利益往来。</p><p class="art_p">据了解，2017年4月至12月期间，黄子峰利用职务便利，多次收取合作方大额行贿款，以及购物卡、iPad等礼品。并利用职务之便将平台政策漏洞和一些活动规则透露给有利益相关的合作方。</p><p class="art_p">目前，字节跳动对黄子峰等三人做开除处理，并且罚没年终绩效奖金，扣除全部期权，并加入阳光诚信联盟的共享信息系统。黄子峰已被海淀警方依法刑事拘留。</p><p class="art_p">阳光诚信联盟由京东、腾讯、百度、美团点评、字节跳动、360、新浪微博等数十家知名企业组成。联盟曾发表联合宣言，将合力加强企业内部腐败治理，承诺拒绝录用违背职业道德的失信人员。</p><p class="art_p">公开信息显示，阳光诚信联盟已经上线共享信息系统，实现失信名单的共享。如果有联盟某家成员企业的员工有违背职业道德行为，录入失信名单后再去其它企业中的任何一家求职，都会被拒绝。截止2018年3月，整个联盟成员单位已达124家，员工总数百余万人。</p>', 1525451838, 0, 8),
(115, '多链钱包Kcash获千万级融资 为1亿人次用户免转账费', NULL, '<p class="art_p">新浪科技讯 5月4日消息，多链和跨链钱包Kcash宣布近获得来自币圈和传统VC的千万级融资，投资方未透露。同时，Kcash宣布两个重要动作，一是，Kcash将给1亿人次用户免转账手续费。二是，将和数据交易所公信宝达成战略合作。即日起，双方将在产品、社区、生态多个层面进行协作与发展，Kcash多币种钱包将入驻布洛克城，公信宝GXS将于今日正式登陆Kcash钱包。</p><p class="art_p">根据介绍，Kcash定位是像蚂蚁金服一样的数字金融平台，用户已经突破70万，管理的数字资产折换成人民币已经超过了30亿人民币，主流币种包括BTC、ETH、ETC、LTC还有EOS。</p><p class="art_p">Kcash团队指出，今年Kcash将聚焦三个业务重点：Kchain公链研发、交易功能拓展、与N个千万级用户数量公司合作，最终打造一个包容开放的Kchain生态平台。</p><p class="art_p">Kcash团队自2013年底，2014年初正式从事区块链创业，最初的产品是一款比特币数字钱包Yardwallet，2017年9月份上线第一版Kcash，10月份全面支持主流链条的多条资产。</p><p class="art_p">根据Kcash资料，Kcash于去年9月份上线APP，目前用户已突破70万，支持的数字资产代币超1000种。此次为1亿人次免手续费，Kcash的补贴将达千万级人民币。</p><p class="art_p">Kcash创始人祝雪娇毕业于清华大学，连续创业者，有8年技术研发经验，全栈工程师，曾创办微团、Payyard pte、奇点未来等多个互联网创业项目。2013年开始研究数字货币和区块链。</p>', 1525451443, 0, 8),
(116, '三星李在镕在华会见商界领袖：包括华为小米高管', 'uploads/PRsH-fzyqqiq901696788524.jpg', '<p class="art_p">新浪科技讯 北京时间5月4日下午消息，据韩国媒体The Investor援引知情人士消息称，三星电子副会长李在镕在中国会见了多名商界领袖。</p><p class="art_p">消息人士透露，本周初抵达中国后，李在镕会见了多位主要中国IT企业的高层决策者，包括比亚迪汽车、华为技术和小米等企业的高管。</p><p class="art_p">李在镕在中国会见多名高管，显然是希望与当地合作伙伴建立更深厚的关系。与李在镕同行的还有三星设备解决方案部门（主要负责集团最为关键的芯片业务）负责人金基南（Kim Ki-nam）和三星显示器负责人李东勋（Lee Dong-hoon）。</p><p class="art_p">今年初，李在镕访问了欧洲和加拿大，此次行程标志着李在镕被监禁约一年后全面回归集团事务。2017年，因涉嫌韩国前总统朴槿惠的贿赂与腐败丑闻一事，李在镕被判刑5年，上诉高等高等法院后于2018年2月改判2年6个月并缓刑4年，当庭获释。(小白)</p>', 1525451200, 0, 8),
(117, '今日头条员工受贿案:火山开除三人 运营负责人被刑拘', 'uploads/ip2k-fzyqqiq898773375094.jpg', '<p class="art_p">今日头条员工受贿案：火山小视频开除三人，运营负责人被刑拘</p><p class="art_p">澎湃新闻记者 杨鑫倢&nbsp;</p><p class="art_p">北京字节跳动科技有限公司（今日头条）近期通过内部邮件，通报了一起内部员工受贿案件。</p><p class="art_p">5月4日，澎湃新闻记者了解到，字节跳动公司的相关内部通报邮件显示，近日，该公司查处了3名火山小视频运营员工违法违规，破坏公司廉洁文化损害公司利益的行为。其中，原火山小视频运营负责人黄子峰，收受贿赂，引入亲属入股的合作方，向公司隐瞒他与该合作方的关系，并且本人也与该合作方股东有利益往来。</p><p class="art_p">根据通报，2017年4月至12月期间，黄子峰利用职务便利，多次收取合作方大额行贿款，以及购物卡、iPad等礼品。并利用职务之便将平台政策漏洞和一些活动规则透露给有利益相关的合作方，使其利用政策漏洞降低自身的运营成本，或在活动中扩大额外收益。</p><p class="art_p">另外，通报还提及，两名火山运营员工孙国新、刘冬霞，违反公司利益冲突申报制度，并违规操作使自己或亲属成立的合作方获利。</p><p class="art_p">据悉，字节跳动对黄子峰等三人做开除处理，并且罚没年终绩效奖金，扣除全部期权。近日，黄子峰已被海淀警方依法刑事拘留。通报还称将把上述员工加入阳光诚信联盟的共享信息系统。</p><p class="art_p">字节跳动方面向澎湃新闻表示，公司对于此类贪腐行为零容忍。坚决维护公司“正直向善”的价值，要求全体员工诚信经营。</p><p class="art_p">此前，腾讯、阿里、京东等互联网公司也都曾查处过内部员工的腐败案件。</p><p class="art_p">阳光诚信联盟由京东、腾讯、百度、美团点评、字节跳动、360、新浪微博等数十家知名企业组成。联盟曾发表联合宣言，将合力加强企业内部腐败治理，承诺拒绝录用违背职业道德的失信人员。</p><p class="art_p">公开信息显示，阳光诚信联盟已经上线共享信息系统，实现失信名单的共享。如果有联盟某家成员企业的员工有违背职业道德行为，录入失信名单后再去其它企业中的任何一家求职，都会被拒绝。截止2018年3月，整个联盟成员单位已达124家，员工总数百余万人。</p>', 1525450740, 0, 8),
(118, '中国版《旅行青蛙》开启内测 将游遍国内著名景点', 'uploads/NASm-fzyqqiq892507978719.jpg', '<p class="art_p">阿里曾表示中文版《旅行青蛙》发行后将使国内玩家体验到更多本地化的内容，最大程度挖掘游戏本身所具有的IP价值。</p><p class="art_p">来源：界面</p><p class="art_p">作者：彭新</p><p class="art_p">4月2日，阿里巴巴集团宣布与日本游戏公司Hit-Point达成战略合作，获得Hit-Point授权，在中国大陆地区独家发行旗下手机游戏《旅行青蛙》。近日，中国版《旅行青蛙》已经开启了内测，小青蛙将可以在国内开启旅程。</p><p class="art_p">此前，从目前网上曝光的多张游戏截图来看，中国版《旅行青蛙》将命名为《旅行青蛙：中国之旅》，游戏本身除了进行汉化之外，还对整体内容做出了修改，原本的旅行地点从日本换成了中国，而旅行中带回的照片也变成了中国的古迹与景点。</p><p class="art_p">游戏内购商品改为中国特色的香葱烤包子、海苔煎豆腐、玉佩、围巾等等。</p><p class="art_p">这与阿里此前表述相符，其表示中文版《旅行青蛙》发行后将使国内玩家体验到更多本地化的内容，最大程度挖掘游戏本身所具有的IP价值，并通过打通阿里巴巴集团内部核心资源，为《旅行青蛙》中文版中加入更多娱乐元素。</p><p class="art_p">阿里游戏官微亦曾提及《旅行青蛙》中文版部分内容：“旅行青蛙即将出发游览中国的大好河山”。这样一来，众多牵挂蛙儿子命运的老母亲以后就将可能见到更多具有国人共鸣的景点，比如北京、杭州、西安、洛阳、张家界、西双版纳等。</p><p class="art_p">此外，阿里巴巴旗下版权交易平台阿里鱼将作为该游戏在国内的唯一授权商务合作方，负责IP内容的商业化合作。阿里鱼方面表示，双方将“围绕商品化授权、营销授权、空间授权，启动全渠道合作”，并将帮助旅行青蛙版权方在中国寻找合作机会，扩大市场。</p><p class="art_p">创立于2007年的Hit-Point，主要从事休闲手机游戏开发，例如他们的《猫咪后院》，这款游戏让玩家在庭院里摆放各种道具来吸引各式各样的猫咪。在十年中，Hit-Point共开发了约30款游戏。</p><p class="art_p">《旅行青蛙》是Hit-Point在2017年底上线的一款放置类游戏，这款主打轻度、休闲的手机游戏2018年1月在没有汉化版本的情况，经过朋友圈的分享传播获得激增的下载量，并一度登顶苹果应用商店付费榜榜首。据阿里官方透露，《旅行青蛙》iOS端在中国总下载量超过3000万。</p>', 1525450387, 0, 8),
(119, '小e微店称每日优鲜盗窃 每日优鲜称小e微店拒绝搬离', 'uploads/Cx3R-fzyqqiq884035047234.jpg', '<p class="art_p"><span style="font-family: KaiTi_GB2312, KaiTi;">无人货架领域近期动荡不安。一边是头部玩家果小美传出融资困难，甚至面临公司解散的消息。另一边，企业之间竞争加剧，争抢点位引发纠纷不断。</span></p><p class="art_p">原标题：无人货架再起纷争 每日优鲜就小e微店控诉其盗窃发布公告</p><p class="art_p">来源：界面&nbsp;&nbsp;</p><p class="art_p">作者：周伊雪</p><p class="art_p">无人货架领域近期动荡不安。一边是头部玩家果小美传出融资困难，甚至面临公司解散的消息。另一边，企业之间竞争加剧，争抢点位引发纠纷不断。</p><p class="art_p">5月3日下午6时左右，无人货架企业小e微店官方公众号发布，称4月25日每日优鲜天津地区员工假冒小e微店工作员工，将小e微店冰箱和货架等设备、商品运走。</p><p class="art_p">小e微店还称，在五一假期结束后，小e微店再次接到客户投诉，称每日优鲜便利购在客户不知情情况下，拆卸小e微店货架等设备，被客户相关负责人发现异样后制止。</p><p class="art_p">对此，5月3日晚，每日优鲜便利购发布声明称，此事系每日优鲜在正常市场拓展中，与企业达成入驻合作协议，客户多次联系小e微店人员要求处理原货架，但未果。此后，企业希望每日优鲜便利购人员协助将小e微店货架暂存别处，以便退还。</p><p class="art_p">每日优鲜便利购称，经与公安部门核实与协调，并与小e微店相关负责人沟通并还原事实后，此事和解，并未立案。</p><p class="art_p">为证明协助迁出小e微店货架经过企业许可，每日优鲜还出具了该写字楼物业加盖公章的《大件物品迁出申请单》。</p><p class="art_p"><strong>以下是每日优鲜的公告全文：</strong></p><p class="art_p">针对近日每日优鲜便利购天津二区与小e微店产生的摩擦，每日优鲜公关部经过严谨的内部核实，将事情经过做如下还原：</p><p class="art_p">事件源于我方正常市场拓展过程中，与天津某企业达成入驻合作协议后，客户多次联系小e微店人员要求撤离原货架，未果。</p><p class="art_p">4月23日，企业表示希望我方人员协助将小e微店货架移至企业仓库，因仓库存储空间不足，询问是否可以将其货架暂存别处，以便退还。</p><p class="art_p">4月24日，应客户要求，且在对方出具该写字楼物业加盖公章的《大件物品迁出申请单》的情况下，我方人员协助客户将小e微店货架搬离至我方办公室暂存，待对方回收。</p><p class="art_p">4月25日早上，我方人员接到小e微店方面人员电话，称已报警，经公安部门核实与协调，并与小e微店相关负责人沟通并还原事实后，此事和解，且并未立案。</p><p class="art_p">整个过程中，每日优鲜便利购员工是在获得客户企业授权的情况下协助执行，不存在违法违规行为。</p><p class="art_p">事情发生后，每日优鲜便利购出于维护行业健康发展地考虑，于情、于理、于法分别做了以下处理：</p><p class="art_p">1. 每日优鲜便利购CEO李漾、CMO刘澍第一时间与小e微店CEO荣光电话沟通，还原事件经过，消除误解，对此次事件造成的不良影响主动道歉。并表示希望登门拜访，及时解决问题，表达了希望共建行业的意愿。</p><p class="art_p">2. 每日优鲜便利购内部已经启动了自查，排除与公司“用户第一、诚信正直、赢得尊重”价值观不符的行为，并加强了企业价值观的宣导培训，对于任何违背公司价值观的行为都将严厉惩处。</p><p class="art_p">3. 每日优鲜便利购支持小e微店在尊重事实的前提下，通过正规渠道诉诸法律途径解决，同时也将对小e微店因恶意诽谤造成我方商誉损失事件，保留法律追究的权利。</p><p class="art_p">每日优鲜便利购自成立以来，对内，始终坚持“用户第一、赢得尊重、诚信正直”的企业价值观。在持续进化的过程中，我们不仅要保证狼性文化不动摇，更要在竞争中赢，且赢得尊重。对外，作为行业领导者，我们必须以身作则，树立行业标准，同时不断完善自身，每日优鲜便利购有责任带领无人货架行业走向共赢。</p><p class="art_p">在此，每日优鲜便利购呼吁业内企业共同制定行业行为公约，互联互通，维护公平竞争的市场秩序，营造更加公正、透明的市场环境。</p><p class="art_p">每日优鲜公关部</p><p class="art_p">2018年5月3日</p>', 1525449890, 0, 8),
(120, '商务部：就中兴通讯公司案与美方进行了严正交涉', NULL, '<p class="art_p">5月4日消息，商务部新闻发言人于4日应询答记者问时表示，在3日至4日举行的中美经贸磋商中，中方就中兴公司案与美方进行了严正交涉。美方表示，重视中方交涉，将向美总统报告中方立场。（记者于佳欣）</p>', 1525449540, 0, 8);
INSERT INTO `pre_news` (`news_id`, `news_title`, `news_pic`, `news_content`, `news_time`, `news_like`, `news_cate_id`) VALUES
(121, '柳传志撒腿就跑：与李泽楷掰手腕，在年轻人背上指挥', 'uploads/F2L9-fzyqqiq882074767701.jpg', '<p class="art_p"><span style="font-family: 楷体, 楷体_gb2312, simkai;">欢迎关注“创事记”的微信订阅号：sinachuangshiji</span></p><p class="art_p">编者按：本文为知名IT记者刘韧于2000年对柳传志的采访稿件。</p><p class="art_p">文/刘韧</p><p class="art_p">一步，两步，三步，踩实一脚，再踩实一脚，每踏出一步，都小心翼翼地抬头远望并回头四顾，感觉这一步迈大了，就再回头踩踩，终于看到了踏实的黄土路。1997年，联想与盈动第一次接触的时候，盈动还不叫盈动，但其想做机顶盒由来已久。盈动当时看中的是联想全国服务网络，想让联想负责其未来遍布全国的机顶盒维护工作。此刻，柳传志一方面认为机顶盒还远着呢；一方面认为盈动还早着呢，没特别将这件事当回事。1999年底，在Internet劲风中，柳传志与正春风得意的李泽楷在香港吃了两次饭，李泽楷到北京参观了联想一次。此时，李泽楷在柳传志心中的地位提高了，联想在李泽楷心中的地位也提高了，合作升级，盈动想将机顶盒与NOW PC的生产全交给联想，联想接受了盈动这家概念公司。2000年3月2日，柳传志在香港紧紧握了李泽楷的手，双方谈成的合作内容是：共同发展宽带互联网服务、制造；销售联想的NOW电脑以及通过NOW分享多媒体内容。消息一经传出，联想股价登时翻了一番，一度摸高至70港元，市值冲到800亿港元，跻身香港十大市值公司之列。看着联想股票一个劲地往上蹿，柳传志不是兴奋，而是奇怪，“心中当然有高兴的成分，但更多的是觉得这个长不了，联想股票怎么可能保持300多倍的市盈率？”“我们在内部说了说，觉得不太可能长久，没有对外大讲特讲，只图继续炒高。”李泽楷和柳传志的感觉不同，那时，他对柳传志说，联想股票还不够高，还可以更高，柳传志笑笑，没说话。柳传志是真没想到与盈动合作的消息能使股票翻一番，就在宣布与盈动合作的前3天，联想刚刚进行了配股，配股价格是33.75港元，而3天后，联想股票是70港元，前后3天时间，联想从股市上少拿了28亿港元。柳传志说，他没有想到，但他不后悔。“这是我们的一个方针，我们不想在最高处配股，我们希望买我们股票的人能得到这些好处。”“这里有一个长赚短赚的问题，如果股民买的真是70港元一股，过不久就回到30多港元，股民会怎么想？联想还长办不长办？”杨元庆相信柳传志不后悔，但他猜想：“柳总会有一点心疼，那么多钱。”</p><p class="art_p">柳传志希望，联想市盈率比别人高，但又是一个比较稳定的倍数，联想利润增长，股票就会随之增长，此时，“股民会比较信我们，管理团队也能从股市上得到鼓励。”“如果联想股票高得不得了，接着，说高就高，说低就低，大家会觉得挺玄，这会使管理团队失去方向。我不愿意这样。”</p><p class="art_p"><span data-bgopacity="40%"></span><strong> <span data-bgopacity="25%"></span> 与李泽楷掰手腕</strong></p><p class="art_p">新闻发布会开过，股票涨过，轮到实际合作，李泽楷的算盘是，联想做设备以及设备的维护，盈动做内容；已经看清楚宽带服务，并已经通过股市证明宽带服务前途的柳传志刚撒腿跑出一步，怎么可能就此改成散步，怎么可能甘心只做打下手的设备？柳传志要做内容；李泽楷不太想让柳传志介入到内容服务，柳传志也不逼他，直飞美国，找了一家更大的公司在谈；李泽楷又有点紧张，要和联想继续谈；柳传志此时想将与盈动合作的调子定得低一点，以便进退自如。柳传志非要和李泽楷抢着做，不是想挤压李泽楷，而是因为柳传志在2000年已经看得很清楚——联想发展速度要年年超过50%，仅靠PC不行。联想PC现在已经占到了中国市场的25%，“还能再大，但不可能永远大下去”。联想当然不能满足于业界的平均增长，要维持联想的高速增长，必须开新的领域。在中关村，柳传志看过太多公司在一个业务做到高端，由于没及时转移，后来走到没落的例子，所以，“联想一定要在往上走的时候就部署，不能等原有业务往下走的时候再部署，那样就来不及了”。进新领域，柳传志现在还不敢打到海外去，他怕失去了本土优势。“进到世界市场，不能以服务的方式，只能以产品的方式。联想要积累起足够的能量，在中国有一个坚实的利润基础，通过中国市场吸引投资人更大的投资，做出有竞争力的产品，才能闯荡世界市场，仓促开拓国际市场，会三下两下被人打回来。”“未来，联想要做到100亿美元以上，非要进到世界市场不行，但2005年之前，还不行。”</p><p class="art_p">因此，柳传志必须选择在中国横向发展的办法进入新领域，因此，柳传志不会让李泽楷挡住联想进入宽带内容服务的步伐。柳传志会对挡住自己去路的人怎么样？岂止宽带，柳传志觉得自己打实管理基础以后，可以从产品做到服务，从一个产品做到更多的产品，“已经到了我在Internet上撒开腿跑的时候了”。</p><p class="art_p"><span data-bgopacity="40%"></span><strong> <span data-bgopacity="25%"></span> 我不是老夫聊发少年狂</strong></p><p class="art_p">柳传志在军校读书的时候，教官教导他打仗要四快一慢，包抄、分隔、冲锋、突击都要快，但发动总攻要慢。1997年，柳传志到美国访问，Intel和柳传志关系最好的副总裁马洛力提醒柳传志要特别注意Internet的发展，柳传志只是在听，只是觉得他们都站在一个比较高的位置上看整个产业，但柳传志还没很能理解他们说的，“我予以注意吧，但我不是他们说了，我就要动的人”。1997年，柳传志和现在的联想研究院院长贺志强在香港爬山，一边爬山，贺志强一边给柳传志讲Java语言和互联网技术，讲了一天。之后，“贺志强也经常给我普及Internet知识，讲讲课什么的”。但是1998年之前，柳传志一直不相信Internet发展得会像后来实际的那么快，1998年之后，柳传志劝自己：“我也别太固执了，我还是要认真想想Internet到底是怎么回事。”想来想去，柳传志依然觉得，“张树新将接入做好了，没内容肯定不行”，“门户将内容做好了，不能赢利，也形成不了市场行为。”“后来，就开始吵吵电子商务，我仔细研究以后，发现B2C在中国根本行不通，付款方式、配送以及购物习惯等方面存在诸多问题。”</p><p class="art_p">联想内部，关于Internet问题意见也比较统一，因为“我的问题出来之后，谁也解答不了。”所以，谁也不嚷嚷大干快上Internet。1998年，杨元庆为抵御DELL，开始按照电子商务方式对自己的销售网进行改进，这个改进相当成功，大大提高了效率。这给了柳传志很大触动，虽然，“中国的B2C还早着呢，但B2B立即可行”。</p><p class="art_p"><strong><span data-bgopacity="40%"></span> <span data-bgopacity="25%"></span> 都是个死</strong></p><p class="art_p">这么多人都做。com，说白了还不是因为门槛儿低，嘴上说是高技术，其实大家心里都清楚，没什么技术。大批的融资来了，以为那就是前景，其实离成功还远着呢，还要经过无数条曲曲弯弯的羊肠小道，多数。com都要消失在这里面，最多落得烧把钱，风光一把。——柳传志到1999年和柳传志说，“Internet上一天等于六年”的人越来越多；到1999年和柳传志说，“Internet上不能等想好了再做”的人越来越多。“怎么可能不想好就做呢？”柳传志想不明白，直到后来，柳传志才弄明白，。com们说的和他自己想的不是一回事。“他们是在抢上市，在抢拿股市钱的时间。出水才见两脚泥，要得到市场的认可，能赚回利润，这才是真的，他们离成功还早着呢。”看清楚他们离成功还早着呢，柳传志就不急了。“靠概念炒股，即使取得了一笔，那么，还要不要对股市负责？为股市负责，时间就长着呢。”“新浪算是老大哥了，做得不错了，但是它将来还有向宽带走的问题、转型的问题，路还长得很，一时难见胜负。”新浪上市的时候，柳传志给王志东发了祝贺信。</p><p class="art_p">“大多数。com公司都没想那么多，仓促上马，上去以后，也许能烧一阶段钱，如果管理不行，连这段都烧不了。跟着，投资人就会要回报，过不了一两年，一定会要回报。投资人会在上市时自己炒一把，但是到后来，他一定会要回报，没有回报，得罪了投资人，就会形成一个很大的连锁反应，一下全打下来。”“做PC，除了联想、长城、方正、实达之外还允许几百家小企业同时做，但Internet上，强者更强，不允许几百个同类型的网站和平共处，共同生存。”“不是将这些事情想得特别清楚，早死，晚死，还是个死。”</p><p class="art_p"><strong><span data-bgopacity="40%"></span> <span data-bgopacity="25%"></span> 联想烧得起FM365</strong></p><p class="art_p">2000年7月14日，联想成为恒生指数成分股，截至2000年6月30日，联想2000/2001财年第一季度，营业额比去年同期增长72%，利润增长136%。利润增长136%是在FM365烧掉2000多万元的前提下取得的。2000年，FM365计划烧掉1个亿，刘晓林一个季度花掉2000多万元，节省了一点，柳传志挺满意，“这次CNNIC评选，FM365评了第15名，挺好，我们就值15名，只要有个名次就好，后面，我们会越做越好。”投资人也很满意，“联想一边花着这些钱，为投资人的未来着想，当前还能好好地给大家分红，这多好。”“钱能不能撑得住很重要，联想有足够的利润支撑FM365，仅用投资人的钱不可靠。亚马逊是最明显的例子。”FM365在联想本来是一个可进可退的项目，它起源于联想天禧电脑的一个功能，所以，在联想眼里，是FM365的服务支持了天禧电脑这个产品卖得更好，而天禧电脑挣的钱反过来又支持了获利遥远的信息服务，这是一个循环。柳传志和很多人一样，认为ICP会有好的前景，认为证券网站、旅游网站、远程教育今后都会有大量的收入，但是“要等，要等人的观念跟上，配套设施跟上”。</p><p class="art_p">坚决地在2000年4月重新发布FM365，并加大投入，是因为柳传志仔细研究过新浪等ICP后，认定网站技术和其他应用技术一样，没什么可神秘的，联想掌握起来没什么难度，而进入媒体、艺术等领域，对大家来说都是新的，大家都一样。“仗打到最后是组织能力的较量，不在乎一两个奇巧的点子。AOL的成功很明显就是管理得好。”</p><p class="art_p">坚决地在2000年4月重新发布FM365，并加大投入，是因为柳传志仔细研究过新浪等ICP后，认定网站技术和其他应用技术一样，没什么可神秘的，联想掌握起来没什么难度，而进入媒体、艺术等领域，对大家来说都是新的，大家都一样。“仗打到最后是组织能力的较量，不在乎一两个奇巧的点子。AOL的成功很明显就是管理得好。”</p><p class="art_p"><span data-bgopacity="40%"></span><strong> <span data-bgopacity="25%"></span> 杨元庆和郭为不矛盾</strong></p><p class="art_p">郭为的电子商务是B2B，杨元庆吆喝电子商务主要是为了卖他的产品，没有更多的矛盾。问题出在杨元庆应该让郭为发第一枪，杨元庆的名字也不要叫系统集成，叫应用集成不就完了。我们也有责任，没有及时调整，杨元庆那边也有责任，没有及时汇报，内部协调上出现了问题，但决不像外面说的协调不了。柳传志将郭为、杨元庆分别解释为“一个是电子商务，另一个也是电子商务”——未来的宽带、现实的FM365都部署好了。2000年5月，柳传志将原来的联想一分为二，郭为率领的联想科技变成神州数码，“用电子商务推动代理产品，顺理成章”。杨元庆“除了PC之外还要做更多的接入设备”。2000年5月12日下午，杨元庆从柳传志手中接过联想大旗时，向柳传志保证：2000年“会将联想PC总体市场占有率提升到30%以上，台式电脑做到188万台，笔记本电脑做到12万台，服务器4万台，掌上电脑30万台，MP3机3万台，打印机8万台，主板300万块”。联想2000年誓师大会，柳传志向杨元庆、郭为“交权”的时候叮嘱：“在互联网经济中，信息服务技术将在这个领域中占有很大比重，而联想以前却主要是一个提供硬件产品为主的厂家，互联网经济的信息服务技术是要靠产品技术作为支持平台的；产品将会包括接入端产品、局端产品两大类型，接入端产品又分台式电脑、笔记本电脑、掌上电脑、机顶盒设备、无线接收设备林林总总；局端产品也有服务器、网络产品等等品种繁多。在未来的社会里，它们的市场份额是平行的，还是某种产品居于主要位置？这在千变万化的技术发展面前只能说是未知数。但有一点却是我们面对的现实，就是在去年以前联想的自制产品中，挑大梁的就只是台式电脑一种。这种业务构架要迎接一场即将来临的暴风雨，要支撑起联想肩上民族振兴、自身发展的重任，肯定是非常危险的。”</p><p class="art_p">杨元庆表示，他将会在“软件、信息服务、系统集成、ASP、宽带和移动通信”方面向Internet全线挺进，这其中，软件（幸福Linux）和信息服务（FM365）是今年的重点。“FM365年底的目标是100万以上的接入客户，1000万以上的Pageview，进入中国门户网站的第一集团军，教育和财经两个垂直网站上要做好构架，为赢利做好充分准备。软件业务的目标是：营业额1.2亿元，但更重要的是以Linux功能软件和针对中小型企业的ASP为契机，抢占中国软件和ASP服务的制高点。”郭为领导的神州数码有三个最主要的业务方向：电子商务和Dlink合作的网络设备制造、系统集成。</p><p class="art_p">从这个阵势看，联想将目前在Internet上能做的全做了。柳传志说：“钱够了，人够了，面开宽一点，没关系。”“联想一直在培养领军人物，我有几个能独当一面的人，就能撑开几个面。”“刘晓林，算半个大将，但是，你看，他基本能撑起FM365。”会不会因此失去了专一的优势？“有些公司说自己专一，FM365能烧一个亿，他很专一，也烧不了一个亿，而且，在组织管理能力上，他们还不如我们这些人。”</p><p class="art_p"><strong><span data-bgopacity="40%"></span> <span data-bgopacity="25%"></span> 在年轻人背上指挥</strong></p><p class="art_p">柳传志和AOL总裁斯蒂夫·凯兹坐在小圈子里谈话，陪同的人在后圈，后圈有人坐到了桌子上，在联想肯定没人敢这样，但柳传志并不就此认为AOL乱。“很多记者只看表面，说网络公司很乱，AOL一点都不乱，他尽管坐在桌子上，但是从他的眼神，可以看出，他对斯蒂夫·凯兹绝对非常尊重，坐在桌子上可能是他的一种习惯，并不能说明什么。”既然是撒开腿跑，柳传志就不想单单用自己的力气。解决了杨元庆、郭为问题，2001年，柳传志将正式启动风险投资业务，以此分享年轻人在Internet上的创新成果。风险投资的钱从大股东那里来，“大股东占57%的股票，稍微卖一点就足够了，有的是钱”。柳传志借年轻人之力，也为年轻人输入管理之力。“奇巧的想法很重要，但再有创意的公司，发展到100个人，也需要激励的方式，如果激励方式一片混乱，今天想给这个钱，明天想给那个钱，能行吗？”“有管理潜资，又肯学，联想会输送人扶助他管理；没有管理潜资，又不肯学，愿意当股东，可以让他出来，让别人进去；有好的创意，但是，霸着位置一定要自己管，又管理不了，就太糟糕了。”</p><p class="art_p">另外，柳传志还要负责为这些公司联系“上家”，考虑谁来买他们以及他们自己有没有可能直接上市。“年底之前，肯定会有信息出来，联想肯定要收购、投资、兼并一批网站。”</p><p class="art_p"><span data-bgopacity="40%"></span><strong> <span data-bgopacity="25%"></span> 柳传志说：</strong></p><p class="art_p">网络不会低迷，真正想做的公司，还会继续做，想捞到钱就走的公司，以后捞不到钱了。这一层是我们都预料到的，没有什么。新浪的形势就不错，已经在前十名的公司都不错，但是，是不是能坚持得住，会是一个问题。网络股会不会再热成那样，投资人会不会再疯狂地投，。com自己会不会还狂得不得了？我想不太会了。挺奇怪，我亲眼见过两次这种情况，一次是日本和香港的那几年，一次是1997年前后拼命投红筹股，明明不可能……在Internet上，联想总体没有失误，学习了一个新的东西，并将这个新的东西实事求是地引到了联想。联想在Internet上打的不是遭遇战，整个战役里面，联想难免会遇到一些挫折，不必担惊受怕，既然是一场战役，总有个过程。</p><p class="art_p"><span style="font-family: KaiTi_GB2312, KaiTi;">作者简介：</span></p><p class="art_p"><span style="font-family: KaiTi_GB2312, KaiTi;">1998年，刘韧出版中国 IT 史上最著名的作品《知识英雄》，同年共同发起中国第一个互联网启蒙组织数字论坛，1999年成立被誉为中国互联网风向标的 DoNews社区，为互联网在中国的普及和商业化做出过令人难以忘怀的贡献。2005年，DoNews 出售给千橡后，刘韧先后投资或合伙了人人、凡客、传课、青萍、WeGene等20多家高科技公司，并在2012年发起成立了 K2创业互助网络。2018年发起成立新加坡区块链技术基金会和日本区块链技术协会。</span></p>', 1525449218, 0, 8),
(122, '38岁老妖试水自由市场！他去年为1队放弃骑勇', 'uploads/nLHD-fwnpcnt567343254552.jpg', '<p class="art_p"></p><p class="art_p">北京时间5月3日消息，据《hoopshype》报道，明尼苏达森林狼球星贾马尔-克劳福德日前决定跳出球员选项，这意味着他将成为一名完全自由球员。</p><p class="art_p">森林狼本赛季的征程已经结束，他们在季后赛首轮就输给了休斯顿火箭。而球队老将克劳福德也决定跳出合同，试水自由球员市场。</p><p class="art_p">克劳福德去年夏天与亚特兰大老鹰达成买断协议，然后加盟了森林狼，他与森林狼签下2年900万美元的合同，明年到期，而且合同是全额保障，明年的薪水是450万美元。</p><p class="art_p">但克劳福德跳出合同后，就成为完全自由球员了。显然，克劳福德认为自己的薪水有点低，想要一份大合同。目前森林狼的工资总额达到1.17亿美元，比奢侈税线低了600万美元。</p><p class="art_p">如果森林狼在6月30日之前放弃科尔-阿尔德里奇，那么他们还能创造490万美元的空间，阿尔德里奇的合同只有205万美元是带保障的。</p><p class="art_p">因为克劳福德没有伯德权，所以森林狼只能给他提供一份520万美元的起薪合同。此外，森林狼还有860万美元的中产特例，以及340万美元的双年特例，只可惜他们最多能给克劳福德使用529万美元。</p><p class="art_p">克劳福德今年已经年满38岁，在森林狼本赛季出战80场，场均10.3分1.2篮板2.3助攻，命中率41.5%，三分命中率33.1%。</p><p class="art_p">有意思的是，克劳福德对球队的选择耐人寻味。上赛季休赛期他被买断后，勇士和骑士本来十分希望他能加盟，但是克劳福德没有选择勇士和骑士，而是选择了森林狼，这一选择令人难以理解。</p><p class="art_p">（咕哒）</p>', 1525423549, 0, 9),
(123, '詹姆斯43+14+8乐福31+11 骑士大胜猛龙2-0', NULL, '<p class="art_p"><p class="art_p"><img  src="https://n.sinaimg.cn/translate/297/w640h457/20180504/BbXa-fzyqqiq6643324.jpg" img-size="640,457"></p><p class="art_p">北京时间5月4日，NBA季后赛继续进行，多伦多猛龙主场迎来与克利夫兰骑士的系列赛第二战，骑士在第三节突然发力，一举确立了两位数的领先优势，最终以128-110轻取猛龙，系列赛大比分2-0领先。</p><p class="art_p">此役，詹姆斯砍下43分14助攻8篮板，季后赛30+10次数超乔丹升至历史第一，乐福31分11篮板，JR15分，格林14分2封盖。</p><p class="art_p">猛龙方面，德罗赞24分，洛瑞21分8助攻，瓦兰修纳斯16分12篮板，范弗里特14分。</p><p class="art_p">现场高清 技术统计</p><p class="art_p">本场之星：勒布朗-詹姆斯&amp;凯文-乐福</p><p class="art_p">在骑士和猛龙的系列赛开打之前，猛龙对位詹姆斯的新秀阿奴诺比说，詹姆斯强壮他也强壮，言下之意是不会畏惧詹姆斯。然而无论是今天的比赛还是系列赛第1场，阿奴诺比完全限制不住詹姆斯，詹姆斯一次又一次的用行动告诉阿奴诺比，自己可不单单是NBA最强壮的球员之一，也是NBA技术最全面和最有统治力的球员。</p><p class="art_p">具体到今天的比赛中来说，詹姆斯半场策应助攻空切的科沃尔上篮得分，秒传篮下助攻乐福空接上篮得分，自己突破内线轻松得分，一次次在外线完成高难度的跳投……无论是个人进攻还是组织球队，詹姆斯都是无所不能，展现出联盟第一人的统治力和风采。或许对于东部球队或者整个NBA球队来说，防住詹姆斯仍然是最大的课题，虽然他已经33岁。</p><p class="art_p">乐福今天的表现也非常出色，终于打出全明星的统治力，展现出最好的一面。对于骑士和詹姆斯来说，他们只希望乐福保持这种水准。</p><p class="art_p">比赛回顾：乐福爆发骑士轻取猛龙</p><p class="art_p">比赛开始后，德罗赞很快出现回场违例，但骑士没有抓住机会，凯文-乐福内线勾手不中。回过头来，洛瑞手起刀落，在外线投中三分，帮助猛龙完成得分。但骑士很快还以颜色，詹姆斯左侧底线背打策应，助攻空切的凯尔-科沃尔上篮得分，骑士咬住比分。</p><p class="art_p">双方其后一度陷入缠斗，此前异常萎靡的乐福今天表现活跃，他在首节7分47秒突破扣篮得分，两队打成10平。在随后的比赛中，骑士的攻防都出现问题，猛龙猛攻内线，且效果不错，打出一个6-0攻击波，乐福关键时刻又站了出来，再次完成得分，帮助骑士止血。首节比赛结束，骑士26-29咬住比分。</p><p class="art_p">第二节比赛开始后，骑士打乔丹-克拉克森、凯尔-科沃尔、罗德尼-胡德、詹姆斯和杰夫-格林死亡五小，格林打到中锋位置，效果非常不理想——猛龙突破分球外线投中三分，雅各布-珀尔特尔在内线不断完成强吃。第二节进行到6分53秒，珀尔特尔突破上篮得分，猛龙48-40取得领先。</p><p class="art_p">在猛龙请求暂停过后，泰伦-卢换上凯文-乐福，场面得到改观，乐福上来不久就接到詹姆斯传球，稳稳地投中三分，帮助骑士止血。在其后的比赛中，乐福仿佛恢复了全明星水准，在他和詹姆斯相继扣篮得分后，骑士51-56咬住比分。猛龙这边洛瑞发挥出色，他罚球得分帮助猛龙61-53领先。</p><p class="art_p">在关键时刻，乔治-希尔在詹姆斯上篮被破坏过后，压哨抛投得分，还造成德罗赞投篮犯规，完成2+1，史密斯也快攻完成远投，骑士再度追回比分。第二节进行到1分26秒，场上出现意外事故，洛瑞突破传球失误过后，手肘打到特里斯坦-汤普森左眼角，导致汤普森流血下场。半场结束，骑士凭借詹姆斯罚球线的干拔跳投，以61-63咬住比分。乐福半场得到18分，詹姆斯得到16分。</p><p class="art_p">易边再战，骑士从防守端做起，先是科沃尔抢断塞尔吉-伊巴卡，接着是洛瑞被造了一个进攻犯规。骑士借机反击，詹姆斯助攻JR史密斯完成2+1，助攻凯尔-科沃尔投中三分，然后自己干拔跳投得分，率队打出一个8-0攻击波，骑士以69-63反超比分。</p><p class="art_p">猛龙请求暂停过后，瓦兰修纳斯内线强攻得手，但猛龙仍然没有打出应有的防守强度，史密斯投中三分，乐福也三分得手，骑士很快以79-68扩大比分。猛龙再度请求暂停，骑士这次站出来的是乐福，他在内线利用错位单打CJ迈尔斯的机会，连续完成得分，很快帮助骑士89-75扩大分差。</p><p class="art_p">猛龙其后试图追分，但是詹姆斯打出了极高的个人水平，完全统治比赛，他在右侧背打转身跳投得分，在外线投中超远三分，突破篮下轻松完成得分，帮助骑士牢牢控制场面。第三节比赛结束，骑士98-87领先，詹姆斯得到31分13助攻，乐福得到27分9篮板。猛龙这边，洛瑞得到21分，德罗赞得到15分。</p><p class="art_p">第四节比赛开始后，詹姆斯继续展现联盟第一人的统治力，在他连续得到6分过后，骑士打出一个9-2攻击波，以107-89扩大比分。在其后的比赛中，即便德罗赞率队疯狂追分，无奈他们落后太多，詹姆斯又打得像神一样，猛龙只得接受失败的结果。</p><p class="art_p">人物简评：最好的乐福终于回来</p><p class="art_p">乐福是骑士除勒布朗-詹姆斯之外最好的球员，但是在今天之前的季后赛，他打得非常糟糕——他内线错位单打推不开对方的小个子，在内线错失轻松的上篮机会，突破到内线过后轻松抛投不中，变成了一个只会投篮的大个子，变得和火箭的里安-安德森类似。但熟悉乐福的人都知道，他以前不是这个样子，他本来是一个能里能外能传球的大个子。</p><p class="art_p">很明显，在骑士对阵猛龙第2场比赛中，最好那个乐福回来了，上半场打得非常有灵性——他空切篮下扣篮得分，篮下假动作骗过对手轻松得分，高位佯装投射三分，骗开对手过后助攻詹姆斯中路空切轻松扣篮得分，在外线稳稳投中空位三分……上半场结束的时候，乐福已经得到18分，而他之前季后赛单场最高分是19分。</p><p class="art_p">骑士需要这样的乐福，詹姆斯也需要这样的乐福。詹姆斯之前在乐福受伤的时候有些落寞地说，我很久没有和全明星队友一起打球了。是的，骑士除了詹姆斯之外，只有乐福一个全明星，如果乐福发挥不出来的话，詹姆斯会陷入孤立无援的境地，只得单骑救主，累死累活。幸运的是，最好的乐福今天复活了，这也不算晚吧。</p><p class="art_p">伤病：汤普森意外被洛瑞撞伤</p><p class="art_p">第二节进行到1分26秒，场上出现意外事故，洛瑞突破传球失误过后，手肘打到特里斯坦-汤普森左眼角，导致汤普森流血下场。汤普森受伤之前打了8分钟，得到2分2篮板，在场期间骑士赢了3分。对于骑士来说，汤普森非常重要，他是骑士首轮淘汰步行者的功臣，在对阵步行者的第7场比赛中扮演了奇兵角色。</p><p class="art_p">在汤普森受伤过后，詹姆斯第一时间发现汤普森眼角流血，在关切汤普森的同时，招呼队医过来治疗，并让汤普森用毛巾捂住伤口。幸运的是，汤普森的伤不严重，他在接受简单的处理过后，第四节比赛重新替补出场，并且仍然在防守端发挥出色，帮助骑士完成大胜。</p><p class="art_p">双方先发：</p><p class="art_p">骑士：乔治-希尔、JR史密斯、凯尔-科沃尔、勒布朗-詹姆斯、凯文-乐福</p><p class="art_p">猛龙：凯尔-洛瑞、德玛-德罗赞、OG阿奴诺比、塞尔吉-伊巴卡、乔纳斯-瓦兰修纳斯</p><p class="art_p">（腾讯体育）</p>', 1525422960, 0, 9),
(124, '单场92分球弟遭前队友揭老底 才16岁就这么傲?', 'uploads/8xtx-fzyqqiq658714827822.jpg', '<p class="art_p"><span style="font-family: 微软雅黑;">北京时间5月4日，洛杉矶湖人队新星郎佐-鲍尔的三弟拉梅罗-鲍尔被推到了风口浪尖。</span></p><p class="art_p"><span style="font-family: 微软雅黑;">此前，球爹拉瓦尔-鲍尔让两个儿子利安吉洛和拉梅罗辍学，并带着他们加入立陶宛的一家篮球俱乐部积累经验。</span></p><p class="art_p"><span style="font-family: 微软雅黑;">但在一周之前，球爹声称这家俱乐部的教练“搞砸了一切”，随后带着两个儿子离开了立陶宛。</span></p><p class="art_p"><span style="font-family: 微软雅黑;">美国著名大学篮球记者杰夫-古德曼在一档节目中谈到了拉梅罗的立陶宛之旅，</span></p><p class="art_p"><span style="font-family: 微软雅黑;">“对拉梅罗来说这段经历并不好。有一个他在立陶宛的前队友告诉我拉梅罗又懒又傲慢。我跟很多那支球队的人聊过，他们都说拉梅洛从来就没有真正融入过。”</span></p><p class="art_p"><span style="font-family: 微软雅黑;">16岁的拉梅罗曾在高中比赛中狂砍92分，但他在立陶宛共打了8场正式比赛，场均仅有6.5分。</span></p><p class="art_p"><span style="font-family: 微软雅黑;">另外，二球利安吉洛已经宣布参加今年6月份的NBA选秀，他预计不会被选中。</span></p><p class="art_p"><span style="font-family: 微软雅黑;">（云彦）</span></p>', 1525422730, 0, 9),
(125, '换帅如换衣！自从小范甘迪离开后 尼克斯更换了11个主帅', NULL, '<p class="art_p"> <p class="art_p"><img  src="https://n.sinaimg.cn/translate/88/w585h303/20180504/OOW7-fzyqqiq6569406.jpg" img-size="585,303"></p><p class="art_p">直播吧5月4日讯 今天，据woj消息，尼克斯是和菲兹戴尔达成了协议，将在下赛季任职尼克斯。</p><p class="art_p">而菲兹戴尔也成为了自从2001年杰夫-范甘迪离开之后的第11任主帅了，尼克斯也是追平了这段时间内NBA更换主教练最多的球队。</p><p class="art_p">并且自从杰夫-范甘迪离开之后，尼克斯的总共胜率仅有40.3%，也是这段时间内最差的。</p>', 1525421292, 0, 9),
(126, '詹姆斯接球竟遭猛龙五人无视 结局你懂的-gif', 'uploads/SghB-fzyqqiq643868479249.gif', '<p class="art_p">北京时间5月4日，NBA常规赛继续进行，克利夫兰骑士队客场挑战多伦多猛龙队。比赛进行到第二节，詹姆斯杀入猛龙空无一人的内线贡献一记暴扣。动图如下：</p>', 1525418842, 0, 9),
(127, '投资英超球队收益7年翻5倍!詹姆斯真商业天才', 'uploads/m8OL-fzrwiaz336105366369.png', '<p class="art_p">北京时间5月4日，据美媒体报道，2011年，勒布朗-詹姆斯投资英超豪门利物浦队并成为小股东。时隔7年之后，詹姆斯的这笔投资收益翻了近5倍。</p><p class="art_p">据悉，早在2011年，詹姆斯买下了利物浦队2%的股份，当时他花了大约650万美元。</p><p class="art_p">去年6月，《福布斯》杂志列出了世界足球俱乐部的价值排行榜，利物浦排名第八，价值约为14.9亿美元。</p><p class="art_p">詹姆斯手里的利物浦队股份，现在的价格约为3200万美元。</p><p class="art_p">这就意味着，詹姆斯的这笔投资收益在7年里翻了近5倍。</p><p class="art_p">詹姆斯不仅是当今NBA最出色的球员，他在场外的投资成绩也非常了解，而他对利物浦的投资就是一个经典案例。</p><p class="art_p">（罗森）</p>', 1525418414, 0, 9),
(128, '正牌超6跳出合同 真核敦促管理层不惜代价留他', 'uploads/fHIv-fzyqqiq637626023261.jpg', '<p class="art_p">北京时间5月4日，据美媒体报道，森林狼队的第六人贾马尔-克劳福德将拒绝执行下赛季的合同选项并成为一名非受限自由球员。</p><p class="art_p">作为当今联盟里最好的板凳得分手之一，克劳福德将放弃下赛季价值450万美元的球员选项，并成为自由球员。</p><p class="art_p">去年夏天，在被老鹰队买断合同之后，克劳福德和森林狼队签了一份2年900万美元的合同。</p><p class="art_p">本赛季常规赛最后阶段，克劳福德发挥出色，并帮助森林狼队在时隔14年之后重返季后赛。</p><p class="art_p">值得一提的是，贾马尔-克劳福德是NBA历史上唯一一位曾经3次成为最佳第六人的球员。</p><p class="art_p">据悉，森林狼队核心吉米-巴特勒已经敦促森林狼队管理层不惜一切代价留下克劳福德。</p><p class="art_p">在18年的职业生涯里，克劳福德效力过多支球队，包括公牛、尼克斯、勇士、老鹰、开拓者和快船。</p><p class="art_p">本赛季，克劳福德场均得到10.3分，投篮命中率47%。</p><p class="art_p">（罗森）</p>', 1525416365, 0, 9),
(129, '爵士球迷嘲讽哈登惨遭“毒手” 他已不是初犯', 'uploads/xyJd-fzyqqiq635991495320.jpg', '<p class="art_p">北京时间5月4日，据TMZ网站报道，昨天火箭与爵士的半决赛第2场，当詹姆斯-哈登从球员通道里走出来的时候，一位爵士球迷拿着手机拍哈登并讽刺他是“NBA最糟糕的假摔者”，结果哈登一掌把那位爵士球迷的手机拍掉。</p><p class="art_p">当时，哈登穿着训练服和队友一起步入场内，而一位爵士球迷正在看台的栏杆后面守着。当哈登接近的时候，那位爵士球迷一边拿着手机拍哈登，一边对哈登说：“你依然是NBA最糟糕的假摔者。”</p><p class="art_p">结果哈登什么话也没说，而是一掌把那位球迷的手机拍倒。</p><p class="art_p">与此同时，周围的两名警察迅速走到那位爵士球迷旁边并要求他不能对NBA球员如此不敬。尽管那位球迷指出受到攻击的是他，但是警察依然告诉他不应该从一开始就离开自己的座位并守在栏杆后面（对球员不敬）。</p><p class="art_p">随后，那位爵士球迷没有再继续辩解，而是乖乖地回到自己的座位，这事也就到此为止了。</p><p class="art_p">事实上，这已经不是哈登第一次拍掉球迷的手机。早在2013年夏天，哈登在一家夜店门口遇到一名湖人球迷的嘲讽，当时那名球迷对哈登说：“科比能打爆你！”</p><p class="art_p">（罗森）</p>', 1525415716, 0, 9),
(130, '著名说唱歌手遭NBA联盟警告：好好看球别骂人', 'uploads/6bMR-fzrwiaz335441996938.jpg', '<p class="art_p"></p><p class="art_p">北京时间5月4日，据美媒体报道，骑士和猛龙的半决赛第1场，著名说唱歌手、猛龙队的全球推广大使Drake和骑士队中锋肯德里克-帕金斯发生口角。由于Drake“使用了对人不敬的语言”，他已经受到了NBA联盟的警告。</p><p class="art_p">据沃神透露，NBA篮球事务执行副总裁奇奇-范德维奇已经和猛龙队篮球事务总裁乌杰里讨论了这件事情。</p><p class="art_p">那场比赛的中场休息时间，当骑士队返回更衣室的路上，场边的Drake和骑士队中锋帕金斯发生了口角。赛后，他俩再次发生口角，直到被其他人分开。</p><p class="art_p">当得知Drake受到联盟的警告时，帕金斯表示他来这里是支持他的队友。</p><p class="art_p">“我在这里是代表克里夫兰骑士队。”帕金斯说道，“最终，这不是我和他（Drake）之间的事情，而是骑士对阵猛龙。如果他不来惹我，我不会去惹他。”</p><p class="art_p">（罗森）</p>', 1525414995, 0, 9),
(131, '洛瑞季后赛三分命中总数升至历史第75位', NULL, '<p class="art_p"> <p class="art_p"><img  src="https://n.sinaimg.cn/translate/20171113/xNgW-fynship2106696.jpg" img-size="658,450"></p><p class="art_p">直播吧5月4日讯 猛龙与骑士的比赛正在进行中，截止发稿前凯尔-洛瑞命中了1记三分。</p><p class="art_p">加上这记三分，洛瑞季后赛三分命中总数达到了102记，超越了希度-科特格鲁，追平布伦特-巴里，升至历史第75位。</p><p class="art_p">目前排在洛瑞前一位的是德维恩-韦德和泰肖恩-普林斯的103记三分。</p><p class="art_p">（Nirvana）</p>', 1525414730, 0, 9),
(132, '美股收盘涨跌不一 道指微涨0.02%', NULL, '<p class="art_p">证券时报</p><p class="art_p">周四，美国股市三大股指涨跌不一，标普500指数收盘下跌5.94点，跌幅0.23%，报2629.73点；纳斯达克指数收盘下跌12.75点，跌幅0.18%，报7088.15点；道琼斯指数收盘上涨5.17点，涨幅0.02%，报23930.15点。</p>', 1525424145, 0, 10),
(133, '去年7月自查现过亿差错 惠而浦被立案调查', NULL, '<p class="art_p"><span style="font-family: KaiTi_GB2312, KaiTi;">涉嫌虚假记载惠而浦被立案调查</span></p><p class="art_p"><span style="font-family: KaiTi_GB2312, KaiTi;">公司曾自查发现销售折扣计提方面在2015及2016年度存在会计差错</span></p><p class="art_p">5月3日，惠而浦（中国）股份有限公司（证券代码：600983&nbsp;股票简称：惠而浦）发布公告称，公司收到《中国证券监督管理委员会调查通知书》。因公司披露的2015年年度报告、2016年年度报告涉嫌虚假记载，根据《中华人民共和国证券法》的有关规定，中国证券监督管理委员会决定对公司实施立案调查。</p><p class="art_p">惠而浦表示，在调查期间，公司将积极配合监管部门的调查工作，严格按照相关法律法规和监管要求认真履行信息披露义务。</p><p class="art_p"><strong>公告</strong></p><p class="art_p"><strong>去年7月自查现过亿差错</strong></p><p class="art_p">新京报记者注意到，去年7月27日，惠而浦发布了《关于重大会计差错更正及复牌的提示性公告》，文中显示：经近期自查，公司发现主要在销售折扣计提方面在2015及2016年度存在会计差错，公司管理层初步估计该等差错金额约为人民币2.5亿-3亿元。随后的10月和12月，公司分别收到上海证券交易所《监管工作函》和证监会安徽监管局的《警示函》。</p><p class="art_p">随后，惠而浦表示，公司自查发现上述以前年度会计差错及内部控制重大缺陷后，及时采取措施，完善销售管理内部控制制度，严格要求销售、营销财务和物流部门执行公司会计政策及相关公司政策，并于2017年12月12日发布了整改公告。</p><p class="art_p">惠而浦前身为合肥荣事达三洋电器股份有限公司（合肥三洋），2014年与惠而浦集团战略合作而“变身”。据惠而浦2017年年报，公司旗下拥有惠而浦、三洋、帝度、荣事达四大品牌，涵盖冰箱、洗衣机等白色家电，以及厨房电器、生活电器等系列产品线。</p><p class="art_p">惠而浦的“本土化”之路并非一帆风顺。2014年，惠而浦入主合肥三洋时，其董事长兼总裁金友华提出要进入白电第一阵营的目标，但近年来表现疲软。据中怡康数据显示，惠而浦洗衣机在国内市场2017年累计市场份额为3.7%，低于上年同期，排名第九。其他产品市场份额也多徘徊在10名左右。</p><p class="art_p"><strong>业绩</strong></p><p class="art_p"><strong>2017年净利润由正转负</strong></p><p class="art_p">根据惠而浦近年年报，2015年，公司的营业收入约为54.69亿元，归属于上市公司股东的净利润约为3.67亿元，归属于上市公司股东的扣除非经常性损益的净利润约为2.44亿元；2016年公司的营业收入约为69.31亿元，归属于上市公司股东的净利润约为3.72亿元，归属于上市公司股东的扣除非经常性损益的净利润约为1.63亿元。</p><p class="art_p">记者注意到，在公司2016年的年度报告中，2015年的财务数据有所调整，在2017年的年度报告中，2016年的财务数据也发生了变化。</p><p class="art_p">根据数据计算，在2016年的财报中，2015年归属于上市公司股东的净利润增加了约0.74亿元；在2017年的财报中，2016年归属于上市公司股东的净利润减少了约0.89亿元。</p><p class="art_p">此外，从2015年到2017年，惠而浦还有两件事值得关注。其一是公司三年连换四任首席财务官。其二是在2015年4月24日，公司发布公告称更换会计师事务所，把为公司服务多年的华普天健会计师事务所（特殊普通合伙），更换为安永华明会计师事务所（特殊普通合伙）。</p><p class="art_p">经历了连续两年财报出现重大会计差错风波，惠而浦2017年的净利润由正转负。财报显示，2017年公司的营业收入约为63.64亿元，归属于上市公司股东的净利润约为-0.97亿元，归属于上市公司股东的扣除非经常性损益的净利润约为-4.88亿元。</p><p class="art_p"><strong>延伸</strong></p><p class="art_p"><strong>上市公司被立案调查后可能面临什么？</strong></p><p class="art_p">被证监会立案调查的消息刚出，惠而浦立刻成为舆论中心，业内人士评论“罚款60万”。</p><p class="art_p">根据《中华人民共和国证券法》规定，发行人、上市公司或者其他信息披露义务人未按照规定披露信息/报送有关报告，或者所披露的信息/报送的报告有虚假记载、误导性陈述或者重大遗漏的，责令改正，给予警告，并处以三十万元以上六十万元以下的罚款。对直接负责的主管人员和其他直接责任人员给予警告，并处以三万元以上三十万元以下的罚款。</p><p class="art_p">并且，罚款并非唯一处罚形式。时代九和律师事务所高级合伙人陆群威告诉新京报记者，“上市公司被证监会立案调查，如果调查结论认定公司和/或公司相关负责人、责任人违反行政法规或规章，公司可能被处以责令改正、整顿、没收违法所得、罚款等行政处罚，公司责任人员可能被处以撤销任职资格或从业资格、没收违法所得、罚款、市场禁入等行政处罚，触犯刑法则会移交公安机关立案调查。”</p><p class="art_p">此外，根据证监会于2014年发布的退市新规，“上市公司中如有被认定为欺诈发行或重大信息披露违法的，将按照重大违法公司强制退市要求启动退市机制。”欣泰电气（300372）是因欺诈发行而被强制退市的案例之一。</p><p class="art_p">新京报记者&nbsp;阎侠&nbsp;陈维城</p>', 1525423894, 0, 10),
(134, 'A股公司研发投入合计高达5700亿元', NULL, '<p class="art_p">中国证券网讯 据《证券日报》5月4日消息，近年来，各企业抓创新，促进发展的积极性高涨。《证券日报》记者整理同花顺统计数据发现，截至今年5月2日，A股2978家上市公司投入的研发费用合计高达5728.7亿元。</p><p class="art_p">在上述上市公司中，有923家公司的研发费用过亿元；其中，有79家公司的研发费用超过10亿元，计算机、通信和其他电子设备制造业的上市公司数量最多。京东方A因研发费用近70亿元而居首位；TCL集团以47.21亿元的研发费用位居行业第二；海康威视和紫光股份的研发费用则分别为31.94亿元和30.49亿元。</p><p class="art_p">另有10家研发费用超10亿元的上市公司属于土木工程建筑业。其中包括中国建筑、中国中铁、中国铁建、中国交建、中国电建、中国中冶等上市公司。值得注意的是，在上述公司中，中国石油、中兴通讯、中国建筑、中国中铁、上汽集团、中国中车、中国铁建7家上市公司2017年的研发费用超过百亿元。</p><p class="art_p">以中国中车为例，年报显示，2017年，公司实现营业收入2110.13亿元；实现归属于母公司所有者的净利润107.99亿元。</p>', 1525423815, 0, 10),
(135, '白马股遭遇估值杀 真成长料现新行情', NULL, '<p class="art_p">证券时报</p><p class="art_p">据中国证券报报道，多数基金经理表示，在没有增量资金入场的背景下，A股市场的“估值杀”将会成为常态，本质上并不区分白马股或成长股。而所谓的“估值杀”，其实不过是又一轮“高预期—资金扎堆—股票大涨—不及预期—资金逃离—股票下跌”周期的重复上演而已。有基金经理指出，今年市场风险偏好会更为均衡，去年一边倒倾向白马股的局面将有所改变，中小盘成长股会有更多的投资机会。</p>', 1525423798, 0, 10),
(136, '上海争取年内开通沪伦通 已有外资机构提相关申请', NULL, '<p class="art_p">证券时</p><p class="art_p">据中国证券报报道，上海市金融办副主任李军在3日举行的上海市政府例行新闻发布会上表示，已会同国家在沪金融监管部门积极争取金融业开放的相关举措在上海先行先试，已有欧洲和亚洲等地的大型金融机构在扩大外资金融持股比例、扩大经营范围方面提出相关申请。上海争取今年年内开通“沪伦通”。</p>', 1525423495, 0, 10),
(137, 'MSCI纳入A股在即 外资重估中国股市寻找投资机会', NULL, '<p class="art_p">证券时报</p><p class="art_p">据上海证券报报道，距离指数编制公司MSCI正式将A股纳入其国际指数还有不到1个月时间，外资机构对于中国市场的布局意图日益清晰。近期，有外资机构展开以中国A股投资机会为主题的系列路演，也有机构加大了针对A股公司的研究力度。记者还获悉，主要追踪QFII的EPFR离岸中国股票基金年内已吸引到超过80亿美元资金净流入。中国“对内强化资本规范、对外加大开放力度”的做法，令海外投资者重新开始评估中国股市，寻找可投资的机会。</p>', 1525423368, 0, 10);
INSERT INTO `pre_news` (`news_id`, `news_title`, `news_pic`, `news_content`, `news_time`, `news_like`, `news_cate_id`) VALUES
(138, '早间策略：短期反弹窗口临近 看好两类成长股', NULL, '<p class="art_p">早间机构策略 证券时报APP</p><p class="art_p"><strong>天风证券：</strong>白马抱团的终结意味着资金迁移仍在持续，在存量资金博弈的环境，这些撤离白马股的资金将继续在市场中寻找阿尔法的机会。考虑到当前宏观经济仍有一定的下行预期、成长股业绩改善的趋势更加明显、政府对加快培育新动能的持续推动和相当部分成长股仍处于估值和股价的低位，我们认为“成长出奇”是资金寻找阿尔法的重要方向。成长股将是今年获取超额收益的关键。看好两类成长股：一类是景气度改善的，包括医药、工业软件、军工、新能源车等，另一类是估值处于低位的，包括环保、传媒等。</p><p class="art_p"><strong>山西证券：</strong>中美贸易谈判的变数对短期市场情绪面的波动、以及对行情的不利冲击不容忽视，建议投资者对事态进展保持高度关注，控制仓位，以稳健配置为主，并做好应对市场短期非理性波动的准备；市场风格方面，我们延续前期判断，即市场风格将继续走向均衡化，因主板、创业板各有支撑，亦各有压制。我们建议具体配置上近期以三条主线为主，第一、低估值修复、基本面改善的银行券商保险，第二、预期反转的房地产、房地产相关的水泥、玻璃、钢铁，第三、大资金偏好、回调后有望重现估值优势的消费蓝筹（家电、食品饮料、医药）。</p><p class="art_p"><strong>中信建投：</strong>由于政策边际变化带来在流动性和实体经济运行层面本身都会带来较大的改善。我们从5月开始，结束2月以来谨慎的观点，重新看好市场，并预期市场存在一轮普涨的行情，因此建议投资者乐观看待市场，提升仓位水平。在行业配置方面，从反弹的节奏上来看，周期和金融地产的反弹速度最快，可以优先参与，从持续时间来看，成长和消费会在金融地产之后表现，上涨的幅度也相对占优。因此，我们建议投资者以成长&gt;消费&gt;金融地产&gt;周期的顺序展开配置，提升仓位水平。</p><p class="art_p"><strong>兴业证券：</strong>短期反弹窗口临近。1、美国财长和商务部长将领衔贸易代表团访华，贸易战这一影响短期市场的主要矛盾或将有所缓解。2、资管新规正式发布，整体基调不变，细则略超预期，在过渡期安排上也有宽限，靴子以较缓和的方式落地。并且，在贸易战的“外患”及经济下行压力增大的“内忧”中金融去杠杆短期内集中推进导致流动性风险的概率下降。3、MSCI纳入A股正式实施日益临近，半年度指数评审后，2.5%的纳入因子将于6月1日正式生效，近期北上资金也在加速流入。因此，判断在连续弱势调整后，短期反弹窗口正在到来。</p>', 1525423070, 0, 10),
(139, '房企整体负债率处于历史高位 绿地泰禾等负债率超85%', NULL, '<p class="art_p">房企整体负债率处于历史高位</p><p class="art_p">Wind数据显示，130家房地产企业一季报实现营收和净利润分别为2998.84亿元和334.57亿元，同比分别增长18.87%和39.93%。其中，87家企业实现业绩增长，龙头房企表现良好。不过，整体负债率达到79.42%，房企杠杆率已经处于历史高位。</p><p class="art_p"><strong>净利润率持续改善</strong></p><p class="art_p">2017年房企年报显示，受益于2015年-2016年房地产销售的大幅增长，行业上市公司普遍实现营收与净利润的双增长。Wind数据显示，板块净利润率达到12.4%。这是行业三年以来净利润率首次回归两位数。</p><p class="art_p">2018年一季报显示，行业整体销售毛利润率和净利润率持续改善，分别为30.97%和12.65%。2017年房地产企业平均销售毛利润率和净利润率分别为29.6%和12.4%。</p><p class="art_p">市场人士指出，销售净利润率持续改善，主因是2015年-2016年房地产一轮牛市后，多地房价上涨明显。相对于2017年至今的房价，房企前期土地成本相对较低，尤其是2015年前所拿土地价格，相对售价来看成本优势更为明显。</p><p class="art_p">阳光股份、华侨城A、苏宁环球等公司毛利润率居于前列。除去多主业经营和部分一季度存在大额非经常性损益后的公司后，深振业A净利润率位于前列。公司表示，业绩大幅增长主要系2018年1-3月公司合作开发项目结转面积大幅增加、相应确认投资收益大幅提高所致。</p><p class="art_p">东方证券认为，自2016年第三季度开启楼市严调控以来，全国房价增长受到严格限制，但土地成本却始终上涨，这将明显压缩房企的盈利空间，行业整体的毛利率和净利率水平将逐渐进入下行通道。</p><p class="art_p"><strong>负债率达历史高位</strong></p><p class="art_p">Wind数据显示，房地产行业的资产负债率不断上行，已经达到了2005年以来的最高点。截至一季度末，房企上市公司整体负债率小幅上升，达到79.42%，较2017年年报的79.08%继续上涨。房地产行业资产负债率位居申万28个一级行业中第三位，仅次于银行和非银金融。</p><p class="art_p">一季报显示，房地产开发企业中，中交地产、绿地控股、泰禾集团、*ST松江、阳光城、信达地产等近20家公司资产负债率超过85%，有近一半的企业资产负债率超过70%。分析人士指出，2015年-2016年地产去库存进程加快，开发商补库存意愿加强，使得2017年土地投资力度明显加大。此外，2016年以来大量房企通过发行交易所公司债进行融资，导致房地产企业有息负债大幅增长。</p><p class="art_p">东方证券指出，一季度上市房企销售回款总额为5222.2亿元，同比增长14.5%，增速同比下滑13个百分点，回款放缓的趋势仍然在延续。销售回款是房企资金流周转的重要渠道，回款放缓将给公司现金流带来较大压力，未来房企的土地扩张将受到限制。</p><p class="art_p">面对即将到来的偿债高峰，多家房企开启多渠道“找钱”模式。截至4月20日，今年房企海外发债的规模仍然呈现放量的趋势。天风证券研报显示，按存量规模计算，截至4年20日，房地产行业在境外发债的存续规模约7934亿元，处于所有行业中的第二名，其中2018年共发行1626.66亿元。</p><p class="art_p"><strong>存货同比增长近三成</strong></p><p class="art_p">一季报数据显示，130家上市房企存货达到4.79万亿元，同比增长26%，较2017年年底增长5.4%。</p><p class="art_p">其中，万科、绿地控股、保利地产、华夏幸福等10家公司存货达到2.72万亿元，占比超过六成。其中，万科一季度末存货达到6300亿元，同比增长28.57%。存货同比增长居前的还有京粮控股、广宇发展、新城控股、荣安地产、陆家嘴、华侨城A等公司。</p><p class="art_p">有60多家房企一季度存货周转率同比实现增长，显示这些公司运营能力明显提升。整体来看，龙头房企存货周转率表现较中小房企出色，这意味着高周转和高杠杆是大型房企实现高ROE的利器。</p>', 1525422884, 0, 10),
(140, '1年48亿炒股炒期货 温氏股份大手笔投资抵御"猪周期"', 'uploads/0tx--fzyqqiq659296644878.jpg', '<p class="art_p">来源：斑马消费</p><p class="art_p">正在持续的“猪周期”里，就连“养猪大王”温氏股份都毫无招架之力。2017年公司业绩同比几近腰斩至67亿元。</p><p class="art_p">这和市场供需关系有关。2016年生猪行情达到巅峰，养猪企业和个人都赚得盆满钵丰，国内生猪养殖规模陡增，从去年开始生猪出栏量剧增和叠加固有的“猪周期效应”，导致猪价跌至近十年最低位。</p><p class="art_p">最近几年来，温氏股份试图通过投资业务来平抑风险，布局旗下温氏投资来搭建自己的“金融体系”。不过，从日前发布的2017年年报来看，温氏股份股票收益仅1.83亿元，基金和期货业务处于亏损状态。</p><p class="art_p"><strong>业绩“掉膘”仍碾压同行</strong></p><p class="art_p">在“猪周期”趋势明显的2017年里，国内各大养猪企业业绩均较为低迷，但从营收规模和净利润两项指标来看，温氏股份仍处于龙头地位。</p><p class="art_p">2017年年报显示，温氏股份（300498.SZ）营收556.57亿元，归属净利润为67.5亿元，同比2016年分别下滑6.23%和42.74%。</p><p class="art_p">不仅温氏股份业绩处于低谷，去年国内养猪行业10多家上市公司均出现不同程度的下滑状况。</p><p class="art_p">雏鹰农牧（002477.SZ）去年实现营收56.98亿元，同比上年下滑6.44%，实现扣非净利润-3.05亿元，同比下滑154.81%。</p><p class="art_p">正邦科技（002157.SZ）去年营收206亿元，同比上年增长8.96%，扣非净利5.25亿元，同比下降46%。</p><p class="art_p">斑马消费梳理发现，在猪周期影响下，能养猪卖猪还正向增长的，恐怕只有位于河南南阳的牧原股份（002714.SZ）了。</p><p class="art_p">2017年，牧原股份实现营收100.42亿元，同比增加79.14%，实现扣非净利润23.66亿元，同比增加2.94%。</p><p class="art_p">为什么唯独牧原股份还能赚钱呢？这是由其经营模式决定的。</p><p class="art_p">与温氏股份对比，牧原股份从原料采购一路延伸到屠宰等环节，相当于持有重资产养猪，而温氏股份采用轻资产运营，利用“公司+农户”扩张规模，这使得二者在业绩上有了很大差异。</p><p class="art_p">去年，温氏股份销售商品肉猪1904.17万头，同比增长11.18%，位居行业第一；牧原股份出栏生猪723.74万头，同比增长132.42%。</p><p class="art_p">相比之下，温氏股份倒是可以轻松赚钱，轻资产模式获得的收益，即便在猪价下行时仍能碾压同行。</p><p class="art_p"><strong>投资业务布局已成雏形</strong></p><p class="art_p">除了最近几年来布局下游，温氏股份也将投资作为公司的重要业务来做，一方面便于应对企业扩张对资金的需求，另一方面可以平抑产业周期性波动。</p><p class="art_p">母公司温氏股份确实对投资业务比较倾心，在2016年末，温氏股份就拟以3.8亿元自有资金增资子公司温氏投资，温氏投资注册资本增至5亿元。</p><p class="art_p">背靠大树，温氏投资的资本运作却一贯谨慎，温氏投资投资总监罗月庭曾对外表示，公司投资的股份都不超过5%。</p><p class="art_p">如果要看投资收益，还是比较可观，兴业证券在2016年一份研报里预测，温氏投资的年回报率保守估计在30%以上。</p><p class="art_p">别看温氏投资是个投资操盘新手，其实手法已经相当老道，它也十分注重早期参投企业。</p><p class="art_p">比如，早期参股的起步股份（603557.SH），去年8月顺利上市，上市当天，温氏投资就赚了10倍收益。</p><p class="art_p">温氏投资早前参股的盈趣科技、华夏航空和宏川智慧，在今年前三个月内陆续挂牌上市，尚有参股的多家公司正在IPO排队。</p><p class="art_p">迄今为止，温氏投资投入资金最多、持股最多的项目应该是参与中粮资本混改，去年，温氏投资出资10亿元获得中粮资本5.144%股权，今年4月，公司拟将持有的股份转让给中原特钢（002423.SZ），以实现更好的退出通道。</p><p class="art_p">综合来看，温氏投资的标的涉及上市公司、拟上市公司、新三板企业以及地方农商行等，截至去年底，温氏投资的资管项目120个，资金规模87亿元，形成可媲美主业的投资布局。</p><p class="art_p">不过，温氏投资具体收益多少，温氏股份并未在年报中体现出来，唯一可知的是，起步股份曾披露温氏投资2015年净利润为2.61亿元。</p><p class="art_p">如果从纯财务投资方面来看，温氏股份投资收益并不太好，去年，温氏股份拿出48.19亿元炒股票、买卖基金和期货，这笔钱相当于2016年扣非净利的4成。</p><p class="art_p">2017年，温氏股份除了股票投资获得收益1.83亿元外，基金累计投资收益亏损4264万元，期货亏损568万元。</p><p class="art_p">当然，温氏投资也有百密一疏的时候，2012年，温氏投资参股福建东亚水产，后者深涉多起诉讼，估计此前雄心勃勃的IPO也搁浅了。</p><p class="art_p">温氏投资与东亚水产曾经签有回购款项，不过，斑马消费在去年年报里并没找到相关数据。</p><p class="art_p">数据显示，2017年末按成本计量的可供出售金融资产27.8亿。</p>', 1525422720, 0, 10),
(141, '巨亏“冠军”乐视网 人均薪酬超30万元、同比增30%', 'uploads/Wc0g-fzyqqiq658643041609.jpg', '<p class="art_p">来源：基本面力场</p><p class="art_p">伴随着A股上市公司2017年年报发布的正式收官，亏损额排名也新鲜出炉，乐视网以139亿元的亏损额度“荣登” 2017年巨亏榜榜首，较排名第二位的*ST油服的106亿元亏损，还多出了33亿元；同时，乐视网还被担任审计工作的立信会计师事所出具了“无法表示意见”的审计结论，被认定存在“持续经营能力产生重大疑虑的重大不确定性”，并被质疑多项财务数据核算依据不充分。</p><p class="art_p">在乐视网2017年的上百亿亏损中，人力成本也占到了一定份额。根据乐视网发布的年报数据显示，资产负债表中的“应付职工薪酬”科目的“本年增加额”为16.99亿元，这对应着该公司计入到当年成本、费用中的人力成本支出金额，较2016年的人力成本支出19.75亿元略有下降。</p><p class="art_p">但是乐视网2017年的人均薪酬却出现了较大幅度上涨，该公司2017年支出“工资、奖金、津贴和补贴”项目共计120445.99万元，对应到2017年度3867人的“领取薪酬员工总人数”，折算人均薪酬多达31.15万元、月均工资约为2.6万元。</p><p class="art_p">而在2016年，乐视网的“领取薪酬员工总人数”则为6348人，同年支出“工资、奖金、津贴和补贴”项目共计150646.63万元，折算人均薪酬为23.73万元、月均工资约为1.98万元。也即乐视网的人均薪酬水平在出现了上百亿巨亏的2017年，还同比增长了31.25%。</p><p class="art_p">再来看乐视网的高管薪酬，年报披露乐视网2017年中年薪过百万的高管共有3人，分别为现任副总经理谭殊116万元、原副总经理吴亚洲116万元和原副总经理高飞108万元。这3名年薪过百万的副总经理中，现任副总经理谭殊年薪较2016年增加了26万元，另外两名原副总经理年薪则有不同幅度的减少。</p><p class="art_p">从乐视网统计的全年“董事、监事和高级管理人员报酬情况”来看，2017年合计1005万元，较2016年的1202万元减少了两百万元左右。</p><p class="art_p">此外，根据乐视网年报披露的数据显示，2017年领取薪酬员工总人数多达3867人，显著超过了同年在职员工的数量合计2180人，且几乎相当于后者的两倍。可能的合理解释是，乐视网在2017年中进行了大量裁员，导致年中平均在职员工人数超过了年末在职员工人数。</p><p class="art_p">但是，乐视网2016年也同样存在相似的数据矛盾。根据2016年年报披露，乐视网当年领取薪酬员工总人数多达6348人，同样超过了当年在职员工的数量5389人，也超过了2015年末的在职员工的数量4885人，这样的数据表现就无法用“大量裁员”来解释。</p>', 1525422616, 0, 10),
(142, '生命灵数：如何留住爱人的心(组图)', NULL, '<p class="art_p">抓住一个男人的心是门艺术，把握了精髓才能够事半功倍。而有针对性地去套牢一个男人的心，当属明智之举。科技紫微星座网这就从生命灵数的角度，来为大家揭秘如何套牢你的他的心！</p><p class="art_p">他的生命灵数计算方法：</p><p class="art_p">将他阳历出生年月日各位数拆开相加，加到不能再拆，变成个位数为止。</p><p class="art_p">例如1998年12月8日，则为：1+9+9+8+1+2+8=38，然后3+8=11，1+1=2，生命灵数即为2。</p><p class="art_p"><strong>生命灵数1</strong></p><p class="art_p">想要套牢他的心，多变是百试不爽的秘诀。不要让他能猜测到你下一秒会变成什么风格出现在他面前，总之让他觉得眼花缭乱、新鲜感爆满，你就赢了。</p><p class="art_p"><strong>生命灵数2</strong></p><p class="art_p">想要套牢生命灵数为2的他的心，你要有足够的自信。自信的女人在他眼里才有着无限的魅力，不要时时刻刻都担心失去，唯独这样你才会永不失去他的喜欢。</p><p class="art_p"><strong>生命灵数3</strong></p><p class="art_p">生命灵数为3的他的心，要用体贴和温柔来禁锢住。这个人群的男人就是禁不起温柔的折腾，你对他好，他会觉得此生都无以为报，更别提是要另有所好。</p><p class="art_p"><strong>生命灵数4</strong></p><p class="art_p">如何套牢他的心？抓住他的胃就可以！这个人群的男人就是大家公认的吃货，对味蕾的满足分外在意，只要你可以源源不断做出好吃有花样的菜，他就心甘情愿一辈子陪在你身边。</p><p class="art_p"><strong>生命灵数5</strong></p><p class="art_p">生命灵数为5的他，敏感多疑，偶尔还会有自己的任性和坏脾气。如果你是唯一一个可以包容理解他，且给予他力量和温暖的女人，想必他是离不开你的。</p><p class="art_p"><strong>生命灵数6</strong></p><p class="art_p">想要套牢他的心，你或许需要再多多努力一把。他见过了太多优秀的女人，自身条件也不赖，而想要他一辈子对你忠诚，除了成为他眼里最闪亮的存在，别无他法。</p><p class="art_p"><strong>生命灵数7</strong></p><p class="art_p">套牢生命灵数为7的男人心，要步步为营，心急吃不了热豆腐。让他慢慢习惯你的存在，必要时刻充当他的左膀右臂，与他亦恋人亦搭档，久而久之会离不开你。</p><p class="art_p"><strong>生命灵数8</strong></p><p class="art_p">想要套牢灵数为8的他的心，请你学会拥有除了爱情之外的自己的生活。不仅仅是为了他而活，当你活出自己的灿烂色彩，他的心也早就不知不觉被你所俘获。</p><p class="art_p"><strong>生命灵数9</strong></p><p class="art_p">生命灵数为9的男人，抵抗力薄弱，有时候也并非你魅力不够，而是外界诱惑太多。想要套牢他的心，偶尔强势一些也是未尝不可以，拿出女王范，他的抖M特质自然呼之欲出，对你尊敬到爆。</p>', 1523892892, 0, 11),
(143, '婚恋心理：为啥关爱自己的人越多越累(图)', NULL, '<p class="art_p">文/飘雨桐</p><p class="art_p"><strong>Q：为什么关心爱自己的人越多，人也就越累，幸福感在降低？</strong></p><p class="art_p">A：有没有听过一个词，叫做情感绑架？就是，别人很关心你很照顾你。但对你来说那是极大的压力，极大的负担。你根本就不需要，甚至根本就不喜欢。但别人还这么对你好，你能怎样呢？即便不反感，也会特别的累。</p><p class="art_p">这哪有什么为什么呢？关键是，没有什么人会以你喜欢的方式关心你。而你，也只稀罕别人以你喜欢的方式关心你。对方的出发点是好的，但你不接受就是不接受。</p><p class="art_p">当情感处于这样的纠结状态，相信没有任何人是感觉幸福的。尤其，当别人越来越关心你；你就越来越痛苦。</p><p class="art_p">有一种伤，叫做别人对你很好很好。这也是交际的技巧能力，不是谁都有的。改变这种情况，你就最好直接告诉他、你要的是什么、你想怎样。</p><p class="art_p"><strong>Q：为什么说女孩子嘴甜才好？</strong></p><p class="art_p">A：什么叫嘴甜？就是会说话，会哄人。这不仅是女人，男人嘴甜的也会讨人喜欢啊。</p><p class="art_p">只是，怎样才能做到嘴甜呢？这个需要技巧，过了那个度就麻烦了。太甜了，别人觉得你在奉承。甜的不对，反而让人生气。</p><p class="art_p">哪些嘴不甜的女人怎样？没怎样，可能她将遇到嘴巴更不甜的男朋友。要么，就是遇到很爱很爱她的男朋友。否则，沦为剩女是走不掉的咯。</p><p class="art_p">而我是女人啊，我不找女朋友的。但我也希望自己的闺蜜嘴巴甜些，一张嘴就得罪人的、气得我火冒三丈的——谁喜欢这样的女人呢？而且，不得罪人的前提还是要说真话。虚情假意，敬谢不敏。</p><p class="art_p"><strong>Q：追女孩时，普通人遇到有钱的竞争对手怎么办？</strong></p><p class="art_p">A：首先呢，爱情是平等的。男未婚、女未嫁，谁都有追求谁的权利。而且，与这个人有没有钱、长得帅不帅气没有直接关系。</p><p class="art_p">其次呢，女生被多个男人追求是正常的事情。但她却不能同时与这么多个男生交往，所以她面临着做出选择的问题。这，你不能替她做主。</p><p class="art_p">最后呢，你强调自己是普通男生。对方则是各方面都比较出色的情敌，你怂了吗？还是觉得，很想放手、让这个女生得到更多的幸福？</p><p class="art_p">如果，你和那个条件好的男生都那么喜欢这个女生。那么，请让这个女生自己做出判断吧。那个被选上的，请好好对待这个女生。那个没被选上的，也要祝福这个女生一直快乐下去。</p><p class="art_p">你所能做的，就这些。让这个女生，自己考虑吧。你，多想无益。</p><p class="art_p"><strong>Q：姑娘因5块钱与闺蜜闹翻，你见过哪些塑料友谊？</strong></p><p class="art_p">A：哈，这不是钱多钱少的问题。而是骗财就骗吧，干嘛要利用别人的感情呢。尤其是女人，对这些更是在乎。对你好，可以掏心掏肺、全部付出。一旦遭到背叛，那她就会恨之入骨。翻脸就翻脸，还需要挑日子吗？</p><p class="art_p">有人觉得，闺蜜大过天。有人却觉得，闺蜜是最好骗的物种。你自己看着办咯，害人之心不可有、防人之心不可无嘛。</p><p class="art_p">我见过哪些塑料友谊？也只是见过，但幸好我也没啥这方面的经历。</p><p class="art_p">那就是大家都比较清楚的，抢闺蜜的男朋友。是的，好男人见少买少。刚好，他就是你闺蜜的男朋友。闺蜜得罪了也就得罪了，撕开了也就撕开了。男人，总该要抢过来的。那才是终生的幸福呢，见色忘友如是也。</p><p class="art_p">有些姐妹情真的很塑料，岂止不值几分钱。简直，就是用来当踏板的。这，不可谓不悲哀啊。</p>', 1523882526, 0, 11),
(144, '我哇哇哭!当12星座告白被拒绝(组图)', NULL, '<p class="art_p">文/摘星工厂-星吧</p><p class="art_p">当男生鼓起勇气，向心仪女生表明爱意，多多少少都在心底预先设想过深情告白却遭对方拒绝的画面，当然也会顺便设想一下被拒之后，该给自己怎样的台阶下，才不至于搞得双方都尴尬。今天摘星工厂—星吧要直击的便是，12星座的男生告白遭拒后，各自会有怎样的表现、怎样的心理活动以及怎样的方式来安慰无所遁形的失落感？（请参考太阳及上升星座）</p><p class="art_p">白羊男：愿意接受失败的事实</p><p class="art_p">白羊男不会因害怕遭拒，而丧失向心仪女生告白的勇气，也不会因为好胜，而不敢正视失败的事实。因此，当他们的满腔热情遭遇对方的婉言相拒，彼时白羊男不会死皮赖脸继续纠缠对方，苦苦追问自己被拒绝的原因，而是会以一颗乐观的心，还给对方一个温柔的微笑，表示自己愿接受这个事实，希望对方不要有负担。</p><p class="art_p">金牛男：郁郁寡欢负能量缠身</p><p class="art_p">要让性格内敛、习惯被动的金牛男，鼓起勇气开口去向喜欢的女生告白，本就是件不容易的事！若再惨遭拒绝，这会令彼时心灰意冷的金牛男郁闷好一阵子。多愁善感的金牛男，可能整日郁郁寡欢，闷闷不乐，做什么都提不起劲儿，甚至还会不断给自己挑错，觉得自己这儿也不好、那儿也不对，自卑心理会愈加明显。</p><p class="art_p">双子男：改变目标重头再来呗</p><p class="art_p">玩世不恭、游戏人间的恋爱态度，使得双子男对待感情之事不是很专注。当他们相中某个女孩，往往是一时冲动之下的喜欢，虽然告白的冲劲儿十足，可一旦被对方发出“好人牌”，彼时双子男虽有挫败感，可他们并不会表现得多么伤心难过，而是会潇洒地跟对方道再见，然后继续抱着玩世不恭的态度，追求新的目标。</p><p class="art_p">巨蟹男：通过搞自闭治愈心伤</p><p class="art_p">面对中意的女孩，巨蟹男那句到嘴边的告白就是说不出，若他们真的开口说爱了，那肯定是抱定了极大的勇气！若告白遭拒，蟹男原先五彩缤纷的期待必将都化作温暖成空的泡影，但注重内心隐私的他们不会把自己的苦闷讲于人知，而是会选择独处的方式，来安慰自己深深的失落，希望在静思冥想中，心痛会慢慢淡化。</p><p class="art_p">狮子男：找台阶下，面子要紧</p><p class="art_p">骄傲的狮子男对“向中意女生告白”这回事很有自信，在他们看来，自己完美无人敌，当女生被“屈身”告白，应赶快“接旨”并点头如捣蒜答应才是！可若不幸自己被泼冷水，狮子男会觉得颜面尽失！为了不让失败的自己显得尴尬，他们会赶快给自己找台阶下“我懂，你想跟我做好朋友，不必担心有一天会分手”。</p><p class="art_p">处女男：冷静，反思自己不足</p><p class="art_p">当处女男表白遭拒，理性远远胜过感性的他们，不会只是一味地沮丧不悦，而是会保持冷静，多多反思自己的不足。因为处女座的男生往往都比较强势，他们觉得男人因为告白失败而选择从此一蹶不振，实在会让被表白方更加看不起，与其浪费时间郁闷，倒不如抽空充实一下自我，内外兼修，期待下一次爱情的靠近！</p><p class="art_p">天秤男：潇洒离开投身众乐乐</p><p class="art_p">天秤男觉得自己无论何时何地，都不能丢掉一个男人该有的风采，在表白遭拒这件事上自然也不例外。当恋爱关系的确定最终成为幻想中的美景，天秤男不会选择恋恋不舍，而是会表现出毫不在乎的样子，潇洒转身。然后在热闹有趣的集体活动中，跟大家一起嘻嘻哈哈，畅快享乐，失意的心情就会自动调成晴朗模式啦。</p><p class="art_p">天蝎男：不服输酝酿再度告白</p><p class="art_p">若喜欢上某个女孩，对爱专注、为爱痴狂的天蝎男会真心想要与其彼此守护终老，不允许失败！所以第一次告白被拒对天蝎男来讲，与其说是一次失败的经历，倒不如说是一次经验的积累，因为他们坚信失败乃成功之母！随后天蝎男会甩掉灰心、沮丧，继续保持勇气，为自己身心注入满满正能量，酝酿筹划第二次告白。</p><p class="art_p">射手男：找兄弟求安慰求恭维</p><p class="art_p">面子，在射手男的字典里可是占据着相当重要的位置！倘若射手男告白失败，他们原本的满满自信以及不知从哪来的优越感，一定会被瞬间击个粉碎。彼时，射手男能想到的最佳疗伤方式，便是一通带着命令口吻的电话要求三五好友火速赶往某某场地，表示当下的自己急需安慰，急需朋友恭维的话来填补内心的委屈！<br><br></p><p class="art_p">魔羯男：借工作让郁闷远去吧</p><p class="art_p">魔羯男若告白遭拒，从此便颓废度日的可能性不大，因为时刻保持头脑清醒冷静的他们，不属于经不起风吹雨打、挫折失败的弱男。虽然魔羯男还是免不掉在被拒的瞬间心冷一下，但是随后，他们自有方法来让这种压抑、苦闷消散、远去--借助擅长的工作来释压，让自己根本无暇顾及自己刚刚经历了感情上的打击！</p><p class="art_p">水瓶男：静观其变，不欲其乱</p><p class="art_p">乐观、豁达的水瓶男若真诚表白却惨遭拒绝，他们不会过分伤心，因为他们坚信，大雨过后说不定有彩虹！所以他们不会受挫败情绪的影响，而是按兵不动、静观其变，若女孩遇到了什么麻烦，或处于人生某个低潮期，已准备充分的水瓶会瞅准时机再次向其示爱--在女孩最需要安慰最需要帮助的时候告白最易成功吧！</p><p class="art_p">双鱼男：失意情诗情歌齐上阵</p><p class="art_p">性格脆弱的双鱼男在告白被拒后，可能有很长一段时间都是沮丧、悲伤的心理状态，但与此同时，也很有可能会诗情才气大爆发！在自己营造的哀伤氛围里，双鱼男会将自己与心仪女孩失之交臂的苦闷、没人能懂自己心伤的孤独，统统寄托于信手拈来的几句情诗或自弹自唱的忧郁调调中，希望借此安慰受伤的玻璃心。</p>', 1523880480, 0, 11),
(145, '12生肖女最易在哪里邂逅爱情(组图)', NULL, '<p class="art_p">不经意的邂逅是很多女生期待遇见爱情的一种方式，这不仅是天时地利下的一种偶遇，接下来还容易出现两人合二为一的连续剧情。能够见到爱情的地方会是你经常出入的环境、地点吗？科技紫微星座网为你揭晓十二生肖女们最容易在哪里邂逅自己的爱情？</p><p class="art_p"><strong>属鼠女</strong></p><p class="art_p">一向在职场中埋头苦干的属鼠女们，一不小心就容易邂逅惊喜，因为她们期待带有被欣赏与信任的倾慕，所以让周围人们感到乏味而暗潮汹涌的同事之间，却可以遇到让她们一见钟情、再见倾心的人。</p><p class="art_p"><strong>属牛女</strong></p><p class="art_p">属牛女们在最单纯的岁月里最易留下终生记忆，所以即使校园青涩让他们擦肩而过，她们在同学聚会，把酒匆匆那年时，也最容易遇到想象中的再次遇见，让曾经的错过重新成为命运的惊喜。</p><p class="art_p"><strong>属虎女</strong></p><p class="art_p">风花雪月的场所最能激发属虎女对异性的想象力。所以，放下手中的一切，到酒吧、咖啡馆、旅游胜地放松度假时，她们的气场容貌都会给那里带来亮色，也更容易让她们发现惊喜的意外。</p><p class="art_p"><strong>属兔女</strong></p><p class="art_p">属兔女们一直都希望永远生活在呵护中，所以，家人与知心朋友热心安排下的相亲最容易给她们的命运带来转折。即便期待风花雪月、浪漫如歌，但是安全与保障才是催生她们荷尔蒙的基础。</p><p class="art_p"><strong>属龙女</strong></p><p class="art_p">对于属龙女来说，能够欣赏与能够掌控的才是自己想要的，所以腥风血雨的厮杀战场，有时也可以给她带来另一种心动--毕竟能与之并驾齐驱、默契竞争、坦然合作的对手才能够打动她们。</p><p class="art_p"><strong>属蛇女</strong></p><p class="art_p">属蛇女不乏桃花，只要她们有一颗寂寞的心，有足够燃烧的热情，不论是焦头烂额的职场，还是风花雪月的咖啡厅、电影院，甚至漫步街头，都有可能遇到让她们怦然心动的时刻。</p><p class="art_p"><strong>属马女</strong></p><p class="art_p">优秀的人，总是会希望自己的另一半也足够优秀。对属马女来说，只有足够高端、成功的人才最易打动自己的心。所以，去参加优秀人士们的婚宴、酒会就是值得起到的遇见。</p><p class="art_p"><strong>属羊女</strong></p><p class="art_p">属羊女们是很不容易对陌生人产生异样情感的信号，她们喜欢呆在熟悉人的圈子里，直到某天突然发现那个曾经熟悉之人的某个亮点，而有了心动的感觉。</p><p class="art_p"><strong>属猴女</strong></p><p class="art_p">属猴女很善于发现惊喜，只要发现别人身上有她们喜欢的标志性特点，不管是街头路边，或者聚会擦肩，她们都会勇敢地去撩，甚至一追到底。</p><p class="art_p"><strong>属鸡女</strong></p><p class="art_p">周一一大早的商场、书店、超市、咖啡馆，无人问津的电影放映厅……都是属鸡女邂邂逅一见钟情的地方，因为很可能那同时出现了另一个不安常理出没的异性，就进入了她的视野，挑起了她的兴趣。</p><p class="art_p"><strong>属狗女</strong></p><p class="art_p">属狗女的爱慕常有崇拜的成分，所以常常会对自己的老师、兄长、甚至上司领导们痴情不改。所以不论是校园、职场、或者某个行业内部高精尖人群的活动，都是让她们开始陷入相思的环境。</p><p class="art_p"><strong>属猪女</strong></p><p class="art_p">属猪女人缘相当好，是生日聚会、朋友下午茶甚至朋友圈婚宴的常客，这也让她们增加了很多艳遇的几率。不管是熟悉的、陌生的，只要志趣相投、相谈甚欢，就给她们保留了牵手概率的可能。</p>', 1523880455, 0, 11),
(146, '女朋友不断，全靠一张嘴的星座(图)', NULL, '<p class="art_p">有的男生笨嘴拙舌，不擅长讲甜言蜜语，但也有的男生恰恰相反，讲起情话来那可就功夫了得了！摘星工厂—星吧今天就来总结一下哪三大星座是情话高手？</p><p class="art_p"><strong>射手座</strong></p><p class="art_p">都说射手座男生有点花心，喜欢和人搞暧昧，但其实，射手座男生真心喜欢上一个女孩的时候，会一改浪子本色，全心全意守护心爱的另一半。而射手座男生由于早就习惯了对女孩360式花样赞美，所以总能找到最动听的情话来打动另一半的心。再加上射手座男生属于放浪不羁的坏坏型男生，所以他们总能把每句情话讲得火辣而滚烫，让心爱的另一半停不下来的脸红！</p><p class="art_p"><strong>天蝎座</strong></p><p class="art_p">平日一贯以高冷形象示人天蝎座男生，一旦陷入爱情后，会一改往日的冷傲，在心爱的女孩面前变了一个人似的，乐于表达、分享自己的内心感受。而由于和冷面冰山的一贯形象对比太强烈，所以天蝎座男生不讲情话则以，一开口，每句情话的杀伤力瞬间爆棚！那种“喷薄式”的情话，足以将天蝎座男生内心对女朋友的爱完全表达出来。</p><p class="art_p"><strong>天秤座</strong></p><p class="art_p">天秤座男生观察力敏锐，懂得取悦讨好他人之道，所以他们总能在最短的时间内，轻松捕捉到女孩的兴趣爱好等一些重要讯息，接下来投其所好，对对方身上的优点，进行有层次、有针对性的夸赞，往往能起到撩翻少女心的绝佳作用！况且天秤座男生嘴里说出来的情话可是为心爱的女孩独家定制的，当极具煽动力的情话铺天盖地进入自己的耳朵，女孩子的芳心势必瞬间被天秤座男生牢牢俘获！</p>', 1523879637, 0, 11),
(147, '爱情测试：你谈几次恋爱才修成正果(图)', NULL, '<p class="art_p">星座频道征稿通缉令</p><p class="art_p">很少有人能谈第一次恋爱就可以步入幸福的婚姻殿堂。恋爱是需要多次尝试的，你在从中也能成长，变得更成熟，更有责任心。那么你谈几次恋爱才能修成正果呢？</p><p class="art_p">开始测试</p><p class="art_p">(本测试20道题，5个答案，系统自动跳转。仅供娱乐，非专业心理指导。)</p>', 1523878101, 0, 11),
(148, '塔罗测试：TA的心里还想着你吗(组图)', 'uploads/SjVG-fyixtym375454637697.jpg', '<p class="art_p">爱情有时候能给人带来欢喜，但是有时也会给人带来忧伤！很多人都为爱痴狂为爱无法自拔，甚至有的人能为爱付出自己的一切！大多数恋人都会问对方的话就是你心里爱不爱自己？可能你们有很长时间没有了联系，你也许很想念对方，但是也很希望对方心里也会想念自己。那么你想知道对方到底想不想你吗？来用塔罗牌占卜为你预测这个问题吧。</p><p class="art_p">占卜选牌规则：先让自己的心平静下来，心里只想着要占卜的问题；ta的心里还想着我吗？然后凭第一感觉在下面的4张牌中选出1张最有感觉的牌，看结果如何吧！</p><p class="art_p"><strong>A、愚人</strong></p><p class="art_p">抽中这张牌的你，是一个喜欢自由自在的人，不喜欢受任何管控。看来你在对方眼中占有一定的位置和影响力，未来彼此的发展空间很大、潜力无限。他正在等你表明态度，两人关系是否能恢复，主导权在你身上。</p><p class="art_p"><strong>B、太阳</strong></p><p class="art_p">你是一个比较自我为中心的人，还有点傲慢，对他人还算是很热情。太阳是一张充满积极意义的牌，即使逆位仍具有正位的意义。此牌显示你给对方留下的印象不错，是分手了还可以继续做朋友的类型。</p><p class="art_p"><strong>C、月亮</strong></p><p class="art_p">此牌寓意一种逃避的心态，不是他不再爱你，不是他不奢望复合，只是无法看清楚你内心真正的想法，猜心游戏玩得过多因而变得疲累不堪。</p><p class="art_p"><strong>D、正义</strong></p><p class="art_p">你是一个对人比较刻薄，常会不顾别人的想法一意孤行的人。此牌的出现预示一段不安定的关系，暗示对方有漂泊的感受，对你的感觉他自己也说不清楚，很可能陷入彼此纠缠却又复合无望的境况中。</p>', 1523795537, 0, 11),
(149, '塔罗测试：你将会有哪种桃花运(组图)', 'uploads/SjVG-fyixtym375454640095.jpg', '<p class="art_p">新年已过，天气回暖，春天悄然开始</p><p class="art_p">恋爱的萌芽在4月这个浪漫温馨的白羊月悄悄生长</p><p class="art_p">让我们一起看看，</p><p class="art_p">今年，你会获得怎样的感情</p><p class="art_p">请看下面的牌面</p><p class="art_p">凭着你的第一直觉，</p><p class="art_p">选择一张塔罗牌，</p><p class="art_p">不要偷看答案哦！</p><p class="art_p"><strong>A、魔术师</strong></p><p class="art_p">今年，你很幸运，爱情上的发展，将会有无限的可能。从潜意识来看，你选择魔术师这张牌，象征了2018，你在感情上会有多种选择的可能性。首先，魔术师在塔罗中，序号是一。这预示一切的开始。牌面中的魔术师站在桌前，桌上有剑、杯、星和杖，象征物质构成四要素：权杖（火）、圣杯（水）、宝剑（风）和钱币（土）。四大元素的集合，象征着完美。他手持魔杖，天地会赐予他力量。这象征着你将会获得好运的加持。魔术师身披的大红色的披风，象征着你会展开一段激情热烈的情感，而白色的衣服又代表了纯洁美好的爱情。因此，在2018，你会获得一份很不错的恋情。</p><p class="art_p">不过，需要注意的是，因为你很幸运，所以可能同时遇见几个桃花，你需要识别其中，究竟谁才是真正适合你的人。遇到特别喜欢的对象，要多表现自己，幸运之神站在你这边，加油！</p><p class="art_p"><strong>B、女祭司</strong></p><p class="art_p">女祭司代表着拥有智慧、淡定和从容。你会选择这张牌，说明你渴望一份感情，却又非常理智清醒，担心有所失去，不愿意轻易开始。画面中的女祭司，坐在两根黑白廊柱之间，恰恰代表着你潜意识里的矛盾。你的心情时而平静，时而又彭拜。你拥有着良好的直觉，非常清楚自己需要什么，想要什么。但是却解不开自己心中的矛盾。你可能正在经历异常柏拉图式的恋情，你感觉到了精神的契合，你们可能是在朋友跟恋人之间的一种关系。你有些犹豫，不知道要不要跨出现实的那一步。</p><p class="art_p">你可能比较被动，认为只需要默默的等待，真正属于自己感情一定会到来。不过，现在社会的诱惑很大，如果真的遇到喜欢的人，不如尝试大胆争取一下。</p><p class="art_p"><strong>C、太阳</strong></p><p class="art_p">在新的一年，你身边可能环绕着一些追求者。但是你的内心渴望自由，你更看中的是自己的感受，也非常在乎工作事业上的成功。你不会把感情放在第一位。并且，从你选择这张牌来看，你不会屈从家人的安排，而是选择倾听自己的内心，选择自己想要的。你更渴望证明自己的能力，爱情对你来说，只是一部分。</p><p class="art_p">牌面中一个可爱俏皮的小孩开心的骑在白马上，背后是灰白的墙，这代表逃脱束缚、走向更自由自在的明天。孩子裸露的身体，代表着真实与纯真。他背后的向日葵和头顶的太阳，都说明了你心里已经非常明朗了。非常清楚自己未来该走的路。</p><p class="art_p">即便你此刻已经有了对象，在2018，你也会活出精彩，过上自己想要的生活。</p><p class="art_p"><strong>D、女皇</strong></p><p class="art_p">新的一年，你在感情上，会如愿得到关心与疼爱，获得一份温馨浪漫的感情。并且，除了甜蜜之外，你们的感情也会有丰富的物资基础，对方会把你宠成女皇。</p><p class="art_p">从牌面来看，女皇坐在舒适华贵的宝座上，手握着代表权利的保障。这象征这至高无上的地位与权势，说明你在感情中，会成为被偏爱的一方。对方被你深深的迷恋，甘愿成为你爱情的俘虏。在女皇的脚下，是生机勃勃的麦田，女皇的背后，是浓密的绿色森林。这一切，都代表着物质的丰润。座椅上有一个心形，里面刻着金星，爱之女神维纳斯的符号。这预示着女皇的母爱天性，和宽厚仁爱。</p><p class="art_p">新年一年，你不仅会收获一份令人羡慕的感情，还会获得家人的支持，可能会定下婚约或者走进婚姻的殿堂。</p>', 1523705003, 0, 11);
INSERT INTO `pre_news` (`news_id`, `news_title`, `news_pic`, `news_content`, `news_time`, `news_like`, `news_cate_id`) VALUES
(150, '12星座本周爱情吉日吉时(组图)', 'uploads/DqbQ-fyzeyqc515796422591.gif', '<p class="art_p">本年度第一次水逆，过去了！顿时感觉心头一松？犹如卸下一身压力？来看本周有什么“新菜”：4/17凯龙星换星座，4/18土星逆行。凯龙星运行在白羊座让人积极回复，水逆期间虽然会受伤，但却有自愈经验的积累，本周就是实践的开始！而土星逆行会带来规则的变化，经验的作用又减弱了……这么矛盾，还怎么恋爱嘛！摘星工厂-星吧（4.16-4.22）爱情运给你提示！</p><p class="art_p"><strong>本周爱情运势前五名：</strong>魔羯座、双鱼座、处女座、水瓶座、射手座。（请参考太阳及上升星座）</p><p class="art_p"><strong>*白羊座*</strong></p><p class="art_p">本周白羊座的爱情运势进展不大。单身的白羊座，有强烈的不安全感，在社交场合很多时候你都表现得过度紧张，面对有好感的人，面对颇有兴趣了解你的人，你更是各种不自在。有伴的白羊座，不想做的事情偏偏找上门，打乱你原本约会的计划，好心情也瞬间不见了踪影。</p><p class="art_p"><strong>约会吉日：4月16日</strong></p><p class="art_p"><strong>约会吉时：20：00</strong></p><p class="art_p"><strong>*金牛座*</strong></p><p class="art_p">本周金牛座的爱情运势不够稳，时起时伏的。单身的金牛座，明知错在自己，还不虚心接受周围人的批评、建议，小心坏了你的异性缘。有伴的金牛座，犯了错第一时间就应当勇于承认，别只顾给自己找借口、找理由辩解，以免给另一半留下硬拗的坏印象哦！</p><p class="art_p"><strong>约会吉日：4月17日</strong></p><p class="art_p"><strong>约会吉时：14：00</strong></p><p class="art_p"><strong>*双子座*</strong></p><p class="art_p">本周双子座将遇到不少爱情方面的挑战与困扰，还需沉住气应对。单身的双子座，实力强大的竞争对手（情敌）给了你不小的压力，让你很难有自信去应对接下来的挑战。恋爱中的双子座，希望另一半事事顺着你的意愿来，但现实难如愿，你很可能要忍不住发脾气了。</p><p class="art_p"><strong>约会吉日：4月16日</strong></p><p class="art_p"><strong>约会吉时：18：00</strong></p><p class="art_p"><strong>*巨蟹座*</strong></p><p class="art_p">本周巨蟹座人际方面有困扰，爱情运势多多少少也会受到影响。单身的巨蟹座，想一个人静静，不想被人问及关于相亲、恋爱、结婚方面的事情，但偏偏就是有人很不识相地对你的隐私刨根问底，惹得你很不爽。有伴的巨蟹座，心情不佳，硬脾气会来的很突兀，跟另一半斗气的可能性不小。</p><p class="art_p"><strong>约会吉日：4月16日</strong></p><p class="art_p"><strong>约会吉时：19：00</strong></p><p class="art_p"><strong>*狮子座*</strong></p><p class="art_p">本周狮子座的爱情运势还是有一些进步的。单身的狮子座，寻觅到合你眼缘、合你心意的人，几番接触、交谈之后，对对方的好感度也越来越高，感觉美妙的爱情就要降临。有伴的狮子座，对另一半极尽宠爱，而对方对你的温柔与体贴也使你尽情享受被爱的温暖，爱情升温也是情理之中咯。</p><p class="art_p"><strong>约会吉日：4月17日</strong></p><p class="art_p"><strong>约会吉时：10：00</strong></p><p class="art_p"><strong>*处女座*</strong></p><p class="art_p">本周处女座的爱情运势稳中有升。单身的处女座，放低身段，以平易近人的姿态出现在众人面前，更有利于缘分的靠近，本周你就有机会邂逅一位合你眼缘的人，彼此还挺聊得来。恋爱中的处女座，跟另一半的感情已然步入稳定阶段，即使是再平淡不过的生活也能被你们过得有滋有味！</p><p class="art_p"><strong>约会吉日：4月19日</strong></p><p class="art_p"><strong>约会吉时：18：00</strong></p><p class="art_p"><strong>*天秤座*</strong></p><p class="art_p">本周天秤座爱情运势大起大落，很不稳。单身的天秤座，锁定了恋爱目标，但追爱路上挫折多多，坎坎坷坷，克服了这个困难，还有下一波挑战在等待着你，一想到你就压力山大。有伴的天秤座，另一半说的某句话不够委婉、或者做的某件事情有失妥当，让你很是看不过去，可能会当面对其吐槽几句。</p><p class="art_p"><strong>约会吉日：4月21日</strong></p><p class="art_p"><strong>约会吉时：17：00</strong></p><p class="art_p"><strong>*天蝎座*</strong></p><p class="art_p">本周天蝎座爱情运势不够好，尤其是周四周五那两天。单身的天蝎座，对自己信心不足，害怕自己在某件事上表现不佳而给喜欢的人留下坏印象。有伴的天蝎座，有比较紧张的人际关系，跟另一半的互动方式会做些调整，对彼此间的相处而言，消极、负面的意义居多，需注意。</p><p class="art_p"><strong>约会吉日：4月18日</strong></p><p class="art_p"><strong>约会吉时：11：00</strong></p><p class="art_p"><strong>*射手座*</strong></p><p class="art_p">本周射手座爱情运势总体来看还是不错的。单身的射手座，即使没有爱情的滋润，你也照样能把单身的日子过得缤纷多彩，在你看来，真命天子/天女还没出现的时候，就应该努力充实自己，把自己变得更好。有伴的射手座，跟另一半的感情升华，甜度不减，恩爱程度让人羡慕。</p><p class="art_p"><strong>约会吉日：4月16日</strong></p><p class="art_p"><strong>约会吉时：18：00</strong></p><p class="art_p"><strong>*魔羯座*</strong></p><p class="art_p">本周魔羯座的爱情好运指数一路飙升，好到爆！单身的魔羯座，向人表白，成功的几率很高；被追求的魔羯座，你对对方的好感度明显上升了不少。有伴的魔羯座，和谐恩爱，是你们相处时的氛围，尽管偶有拌嘴，但那都只是你们枯燥生活的调剂，不会影响你们的感情的！</p><p class="art_p"><strong>约会吉日：4月18日</strong></p><p class="art_p"><strong>约会吉时：20：00</strong></p><p class="art_p"><strong>*水瓶座*</strong></p><p class="art_p">本周水瓶座的爱情世界还算明媚。单身的水瓶座，跟心仪人的互动不少，沟通的时候，行事上富有弹性，不会太死板，这给对方留下不错的印象。有伴的水瓶座，跟另一半尽情享受二人世界，不管是出门散心约会，还是宅在家一起做顿饭、看个电影，都是满满的恩爱。</p><p class="art_p"><strong>约会吉日：4月19日</strong></p><p class="art_p"><strong>约会吉时：20：00</strong></p><p class="art_p"><strong>*双鱼座*</strong></p><p class="art_p">本周双鱼座的爱情运势良好。单身的双鱼座，有人悄悄走进你的心，让你隐隐感觉，美妙的爱情就要来临，一想到这些，你就忍不住欣喜。有伴的双鱼座，另一半有怒火，你很善于平息，不管是温柔的安抚，还是轻声的劝慰，你总能找到方法让你们之间的相处气氛重归融洽、平和。</p><p class="art_p"><strong>约会吉日：4月21日</strong></p><p class="art_p"><strong>约会吉时：19：00</strong></p>', 1523621943, 0, 11),
(151, '婚恋心理：拒绝尬撩!恋爱的正确方法(图)', 'uploads/84lE-fyzeyqa568815786538.gif', '<p class="art_p">恋爱中的男女如何把握交往的度才能让对方更爱你呢？很多人都不知道究竟该如何正确谈恋爱，今天我们就来好好讨论一下。</p><p class="art_p"><strong>学会自我肯定</strong></p><p class="art_p">对于我们的灵魂来说，感情是一面巨大的镜子。我们总能够通过我们的亲密伴侣发现自己的“特质”。如果我们自己都无法接受自己的某些东西，那我们就不能指望我们的亲密伴侣接受它们，因为从感情上就说不通。自我肯定能够带来很多好处，其中之一就是在感情上会少些隔阂，这就意味着会给恋爱关系带来更多的良性循环。</p><p class="art_p"><strong>小心试探</strong></p><p class="art_p">对于想要调情的人来讲，记得，刚开始的时候，不能直接进入调情的节奏，而是需要用你们觉得，再正常不过的谈论来入题。例如，可以讲一下所在地的气氛，所在地的环境，所在地的气候等。这样，你可以判断一下这个人，是否值得你去调情。</p><p class="art_p"><strong>不停眨眼调情</strong></p><p class="art_p">其实调情没你想得那么复杂，单纯地眨眼也能达到有趣的调情作用哦！手机调 震动或静音吧情意正浓时，手机突然来了小伙伴的问候，这场面让你有些尴尬，继续亲热呢还是接电话呢？毋庸置疑的是，当你接完电话，恐怕那股热火朝天的亲热 劲儿也跟着没了，接下来，你们要说什么？看着彼此是不是有些不好意思？提前调成震动或静音，享受属于你们两个人的幸福瞬间吧。</p><p class="art_p"><strong>口气清新让你更自信</strong></p><p class="art_p">清洁口腔只需五分钟，但能换你一整天的自信。口气清新不仅仅让自己更自信，还能让对方更想要亲近你，即便她离你多近都不用怕污浊的口气破坏情绪。如果真的不方便刷牙，戴上漱口水和牙线，随时保持口气清新也是个好办法。</p><p class="art_p"><strong>多一些幽默感</strong></p><p class="art_p">家庭生活中的锅碗瓢勺等等琐事，有时确实需要多一些承受困难的勇气和 驾驭生活的能力。家庭中的幽默，可以减轻心理上的压力，幽默语言，常常能激起感情上的浪花。因为幽默是坚毅、冷静、智慧、能力的象征，是家庭矛盾的调和剂。</p><p class="art_p"><strong>适当照顾对方的喜好</strong></p><p class="art_p">男生和女生喜欢的东西有所差别，但是一定要尊重对方的喜好，适当的陪对方做一些他喜欢但自己却不喜欢去做的事情，这会让对方非常的感激你，也会做出同样的举动来让你开心，两个人在一起就是为了快乐，何乐而不为呢？</p><p class="art_p"><strong>不要试图控制对方</strong></p><p class="art_p">做好自己，恋爱不是生活的全部，不过度关注并干涉对方的生活，也不要一味的向下妥协，沉浸于感动自己。每个人都需要自由隐私的空间，不管两人是多么亲密的关系。给别人自由，也是一种对别人的尊重，所谓距离产生美说的就是这个道理。</p>', 1523465806, 0, 11),
(152, '生命灵数：如何留住爱人的心(组图)', NULL, '<p class="art_p">抓住一个男人的心是门艺术，把握了精髓才能够事半功倍。而有针对性地去套牢一个男人的心，当属明智之举。科技紫微星座网这就从生命灵数的角度，来为大家揭秘如何套牢你的他的心！</p><p class="art_p">他的生命灵数计算方法：</p><p class="art_p">将他阳历出生年月日各位数拆开相加，加到不能再拆，变成个位数为止。</p><p class="art_p">例如1998年12月8日，则为：1+9+9+8+1+2+8=38，然后3+8=11，1+1=2，生命灵数即为2。</p><p class="art_p"><strong>生命灵数1</strong></p><p class="art_p">想要套牢他的心，多变是百试不爽的秘诀。不要让他能猜测到你下一秒会变成什么风格出现在他面前，总之让他觉得眼花缭乱、新鲜感爆满，你就赢了。</p><p class="art_p"><strong>生命灵数2</strong></p><p class="art_p">想要套牢生命灵数为2的他的心，你要有足够的自信。自信的女人在他眼里才有着无限的魅力，不要时时刻刻都担心失去，唯独这样你才会永不失去他的喜欢。</p><p class="art_p"><strong>生命灵数3</strong></p><p class="art_p">生命灵数为3的他的心，要用体贴和温柔来禁锢住。这个人群的男人就是禁不起温柔的折腾，你对他好，他会觉得此生都无以为报，更别提是要另有所好。</p><p class="art_p"><strong>生命灵数4</strong></p><p class="art_p">如何套牢他的心？抓住他的胃就可以！这个人群的男人就是大家公认的吃货，对味蕾的满足分外在意，只要你可以源源不断做出好吃有花样的菜，他就心甘情愿一辈子陪在你身边。</p><p class="art_p"><strong>生命灵数5</strong></p><p class="art_p">生命灵数为5的他，敏感多疑，偶尔还会有自己的任性和坏脾气。如果你是唯一一个可以包容理解他，且给予他力量和温暖的女人，想必他是离不开你的。</p><p class="art_p"><strong>生命灵数6</strong></p><p class="art_p">想要套牢他的心，你或许需要再多多努力一把。他见过了太多优秀的女人，自身条件也不赖，而想要他一辈子对你忠诚，除了成为他眼里最闪亮的存在，别无他法。</p><p class="art_p"><strong>生命灵数7</strong></p><p class="art_p">套牢生命灵数为7的男人心，要步步为营，心急吃不了热豆腐。让他慢慢习惯你的存在，必要时刻充当他的左膀右臂，与他亦恋人亦搭档，久而久之会离不开你。</p><p class="art_p"><strong>生命灵数8</strong></p><p class="art_p">想要套牢灵数为8的他的心，请你学会拥有除了爱情之外的自己的生活。不仅仅是为了他而活，当你活出自己的灿烂色彩，他的心也早就不知不觉被你所俘获。</p><p class="art_p"><strong>生命灵数9</strong></p><p class="art_p">生命灵数为9的男人，抵抗力薄弱，有时候也并非你魅力不够，而是外界诱惑太多。想要套牢他的心，偶尔强势一些也是未尝不可以，拿出女王范，他的抖M特质自然呼之欲出，对你尊敬到爆。</p>', 1523892892, 0, 11),
(153, '婚恋心理：为啥关爱自己的人越多越累(图)', NULL, '<p class="art_p">文/飘雨桐</p><p class="art_p"><strong>Q：为什么关心爱自己的人越多，人也就越累，幸福感在降低？</strong></p><p class="art_p">A：有没有听过一个词，叫做情感绑架？就是，别人很关心你很照顾你。但对你来说那是极大的压力，极大的负担。你根本就不需要，甚至根本就不喜欢。但别人还这么对你好，你能怎样呢？即便不反感，也会特别的累。</p><p class="art_p">这哪有什么为什么呢？关键是，没有什么人会以你喜欢的方式关心你。而你，也只稀罕别人以你喜欢的方式关心你。对方的出发点是好的，但你不接受就是不接受。</p><p class="art_p">当情感处于这样的纠结状态，相信没有任何人是感觉幸福的。尤其，当别人越来越关心你；你就越来越痛苦。</p><p class="art_p">有一种伤，叫做别人对你很好很好。这也是交际的技巧能力，不是谁都有的。改变这种情况，你就最好直接告诉他、你要的是什么、你想怎样。</p><p class="art_p"><strong>Q：为什么说女孩子嘴甜才好？</strong></p><p class="art_p">A：什么叫嘴甜？就是会说话，会哄人。这不仅是女人，男人嘴甜的也会讨人喜欢啊。</p><p class="art_p">只是，怎样才能做到嘴甜呢？这个需要技巧，过了那个度就麻烦了。太甜了，别人觉得你在奉承。甜的不对，反而让人生气。</p><p class="art_p">哪些嘴不甜的女人怎样？没怎样，可能她将遇到嘴巴更不甜的男朋友。要么，就是遇到很爱很爱她的男朋友。否则，沦为剩女是走不掉的咯。</p><p class="art_p">而我是女人啊，我不找女朋友的。但我也希望自己的闺蜜嘴巴甜些，一张嘴就得罪人的、气得我火冒三丈的——谁喜欢这样的女人呢？而且，不得罪人的前提还是要说真话。虚情假意，敬谢不敏。</p><p class="art_p"><strong>Q：追女孩时，普通人遇到有钱的竞争对手怎么办？</strong></p><p class="art_p">A：首先呢，爱情是平等的。男未婚、女未嫁，谁都有追求谁的权利。而且，与这个人有没有钱、长得帅不帅气没有直接关系。</p><p class="art_p">其次呢，女生被多个男人追求是正常的事情。但她却不能同时与这么多个男生交往，所以她面临着做出选择的问题。这，你不能替她做主。</p><p class="art_p">最后呢，你强调自己是普通男生。对方则是各方面都比较出色的情敌，你怂了吗？还是觉得，很想放手、让这个女生得到更多的幸福？</p><p class="art_p">如果，你和那个条件好的男生都那么喜欢这个女生。那么，请让这个女生自己做出判断吧。那个被选上的，请好好对待这个女生。那个没被选上的，也要祝福这个女生一直快乐下去。</p><p class="art_p">你所能做的，就这些。让这个女生，自己考虑吧。你，多想无益。</p><p class="art_p"><strong>Q：姑娘因5块钱与闺蜜闹翻，你见过哪些塑料友谊？</strong></p><p class="art_p">A：哈，这不是钱多钱少的问题。而是骗财就骗吧，干嘛要利用别人的感情呢。尤其是女人，对这些更是在乎。对你好，可以掏心掏肺、全部付出。一旦遭到背叛，那她就会恨之入骨。翻脸就翻脸，还需要挑日子吗？</p><p class="art_p">有人觉得，闺蜜大过天。有人却觉得，闺蜜是最好骗的物种。你自己看着办咯，害人之心不可有、防人之心不可无嘛。</p><p class="art_p">我见过哪些塑料友谊？也只是见过，但幸好我也没啥这方面的经历。</p><p class="art_p">那就是大家都比较清楚的，抢闺蜜的男朋友。是的，好男人见少买少。刚好，他就是你闺蜜的男朋友。闺蜜得罪了也就得罪了，撕开了也就撕开了。男人，总该要抢过来的。那才是终生的幸福呢，见色忘友如是也。</p><p class="art_p">有些姐妹情真的很塑料，岂止不值几分钱。简直，就是用来当踏板的。这，不可谓不悲哀啊。</p>', 1523882526, 0, 11),
(154, '我哇哇哭!当12星座告白被拒绝(组图)', NULL, '<p class="art_p">文/摘星工厂-星吧</p><p class="art_p">当男生鼓起勇气，向心仪女生表明爱意，多多少少都在心底预先设想过深情告白却遭对方拒绝的画面，当然也会顺便设想一下被拒之后，该给自己怎样的台阶下，才不至于搞得双方都尴尬。今天摘星工厂—星吧要直击的便是，12星座的男生告白遭拒后，各自会有怎样的表现、怎样的心理活动以及怎样的方式来安慰无所遁形的失落感？（请参考太阳及上升星座）</p><p class="art_p">白羊男：愿意接受失败的事实</p><p class="art_p">白羊男不会因害怕遭拒，而丧失向心仪女生告白的勇气，也不会因为好胜，而不敢正视失败的事实。因此，当他们的满腔热情遭遇对方的婉言相拒，彼时白羊男不会死皮赖脸继续纠缠对方，苦苦追问自己被拒绝的原因，而是会以一颗乐观的心，还给对方一个温柔的微笑，表示自己愿接受这个事实，希望对方不要有负担。</p><p class="art_p">金牛男：郁郁寡欢负能量缠身</p><p class="art_p">要让性格内敛、习惯被动的金牛男，鼓起勇气开口去向喜欢的女生告白，本就是件不容易的事！若再惨遭拒绝，这会令彼时心灰意冷的金牛男郁闷好一阵子。多愁善感的金牛男，可能整日郁郁寡欢，闷闷不乐，做什么都提不起劲儿，甚至还会不断给自己挑错，觉得自己这儿也不好、那儿也不对，自卑心理会愈加明显。</p><p class="art_p">双子男：改变目标重头再来呗</p><p class="art_p">玩世不恭、游戏人间的恋爱态度，使得双子男对待感情之事不是很专注。当他们相中某个女孩，往往是一时冲动之下的喜欢，虽然告白的冲劲儿十足，可一旦被对方发出“好人牌”，彼时双子男虽有挫败感，可他们并不会表现得多么伤心难过，而是会潇洒地跟对方道再见，然后继续抱着玩世不恭的态度，追求新的目标。</p><p class="art_p">巨蟹男：通过搞自闭治愈心伤</p><p class="art_p">面对中意的女孩，巨蟹男那句到嘴边的告白就是说不出，若他们真的开口说爱了，那肯定是抱定了极大的勇气！若告白遭拒，蟹男原先五彩缤纷的期待必将都化作温暖成空的泡影，但注重内心隐私的他们不会把自己的苦闷讲于人知，而是会选择独处的方式，来安慰自己深深的失落，希望在静思冥想中，心痛会慢慢淡化。</p><p class="art_p">狮子男：找台阶下，面子要紧</p><p class="art_p">骄傲的狮子男对“向中意女生告白”这回事很有自信，在他们看来，自己完美无人敌，当女生被“屈身”告白，应赶快“接旨”并点头如捣蒜答应才是！可若不幸自己被泼冷水，狮子男会觉得颜面尽失！为了不让失败的自己显得尴尬，他们会赶快给自己找台阶下“我懂，你想跟我做好朋友，不必担心有一天会分手”。</p><p class="art_p">处女男：冷静，反思自己不足</p><p class="art_p">当处女男表白遭拒，理性远远胜过感性的他们，不会只是一味地沮丧不悦，而是会保持冷静，多多反思自己的不足。因为处女座的男生往往都比较强势，他们觉得男人因为告白失败而选择从此一蹶不振，实在会让被表白方更加看不起，与其浪费时间郁闷，倒不如抽空充实一下自我，内外兼修，期待下一次爱情的靠近！</p><p class="art_p">天秤男：潇洒离开投身众乐乐</p><p class="art_p">天秤男觉得自己无论何时何地，都不能丢掉一个男人该有的风采，在表白遭拒这件事上自然也不例外。当恋爱关系的确定最终成为幻想中的美景，天秤男不会选择恋恋不舍，而是会表现出毫不在乎的样子，潇洒转身。然后在热闹有趣的集体活动中，跟大家一起嘻嘻哈哈，畅快享乐，失意的心情就会自动调成晴朗模式啦。</p><p class="art_p">天蝎男：不服输酝酿再度告白</p><p class="art_p">若喜欢上某个女孩，对爱专注、为爱痴狂的天蝎男会真心想要与其彼此守护终老，不允许失败！所以第一次告白被拒对天蝎男来讲，与其说是一次失败的经历，倒不如说是一次经验的积累，因为他们坚信失败乃成功之母！随后天蝎男会甩掉灰心、沮丧，继续保持勇气，为自己身心注入满满正能量，酝酿筹划第二次告白。</p><p class="art_p">射手男：找兄弟求安慰求恭维</p><p class="art_p">面子，在射手男的字典里可是占据着相当重要的位置！倘若射手男告白失败，他们原本的满满自信以及不知从哪来的优越感，一定会被瞬间击个粉碎。彼时，射手男能想到的最佳疗伤方式，便是一通带着命令口吻的电话要求三五好友火速赶往某某场地，表示当下的自己急需安慰，急需朋友恭维的话来填补内心的委屈！<br><br></p><p class="art_p">魔羯男：借工作让郁闷远去吧</p><p class="art_p">魔羯男若告白遭拒，从此便颓废度日的可能性不大，因为时刻保持头脑清醒冷静的他们，不属于经不起风吹雨打、挫折失败的弱男。虽然魔羯男还是免不掉在被拒的瞬间心冷一下，但是随后，他们自有方法来让这种压抑、苦闷消散、远去--借助擅长的工作来释压，让自己根本无暇顾及自己刚刚经历了感情上的打击！</p><p class="art_p">水瓶男：静观其变，不欲其乱</p><p class="art_p">乐观、豁达的水瓶男若真诚表白却惨遭拒绝，他们不会过分伤心，因为他们坚信，大雨过后说不定有彩虹！所以他们不会受挫败情绪的影响，而是按兵不动、静观其变，若女孩遇到了什么麻烦，或处于人生某个低潮期，已准备充分的水瓶会瞅准时机再次向其示爱--在女孩最需要安慰最需要帮助的时候告白最易成功吧！</p><p class="art_p">双鱼男：失意情诗情歌齐上阵</p><p class="art_p">性格脆弱的双鱼男在告白被拒后，可能有很长一段时间都是沮丧、悲伤的心理状态，但与此同时，也很有可能会诗情才气大爆发！在自己营造的哀伤氛围里，双鱼男会将自己与心仪女孩失之交臂的苦闷、没人能懂自己心伤的孤独，统统寄托于信手拈来的几句情诗或自弹自唱的忧郁调调中，希望借此安慰受伤的玻璃心。</p>', 1523880480, 0, 11),
(155, '12生肖女最易在哪里邂逅爱情(组图)', NULL, '<p class="art_p">不经意的邂逅是很多女生期待遇见爱情的一种方式，这不仅是天时地利下的一种偶遇，接下来还容易出现两人合二为一的连续剧情。能够见到爱情的地方会是你经常出入的环境、地点吗？科技紫微星座网为你揭晓十二生肖女们最容易在哪里邂逅自己的爱情？</p><p class="art_p"><strong>属鼠女</strong></p><p class="art_p">一向在职场中埋头苦干的属鼠女们，一不小心就容易邂逅惊喜，因为她们期待带有被欣赏与信任的倾慕，所以让周围人们感到乏味而暗潮汹涌的同事之间，却可以遇到让她们一见钟情、再见倾心的人。</p><p class="art_p"><strong>属牛女</strong></p><p class="art_p">属牛女们在最单纯的岁月里最易留下终生记忆，所以即使校园青涩让他们擦肩而过，她们在同学聚会，把酒匆匆那年时，也最容易遇到想象中的再次遇见，让曾经的错过重新成为命运的惊喜。</p><p class="art_p"><strong>属虎女</strong></p><p class="art_p">风花雪月的场所最能激发属虎女对异性的想象力。所以，放下手中的一切，到酒吧、咖啡馆、旅游胜地放松度假时，她们的气场容貌都会给那里带来亮色，也更容易让她们发现惊喜的意外。</p><p class="art_p"><strong>属兔女</strong></p><p class="art_p">属兔女们一直都希望永远生活在呵护中，所以，家人与知心朋友热心安排下的相亲最容易给她们的命运带来转折。即便期待风花雪月、浪漫如歌，但是安全与保障才是催生她们荷尔蒙的基础。</p><p class="art_p"><strong>属龙女</strong></p><p class="art_p">对于属龙女来说，能够欣赏与能够掌控的才是自己想要的，所以腥风血雨的厮杀战场，有时也可以给她带来另一种心动--毕竟能与之并驾齐驱、默契竞争、坦然合作的对手才能够打动她们。</p><p class="art_p"><strong>属蛇女</strong></p><p class="art_p">属蛇女不乏桃花，只要她们有一颗寂寞的心，有足够燃烧的热情，不论是焦头烂额的职场，还是风花雪月的咖啡厅、电影院，甚至漫步街头，都有可能遇到让她们怦然心动的时刻。</p><p class="art_p"><strong>属马女</strong></p><p class="art_p">优秀的人，总是会希望自己的另一半也足够优秀。对属马女来说，只有足够高端、成功的人才最易打动自己的心。所以，去参加优秀人士们的婚宴、酒会就是值得起到的遇见。</p><p class="art_p"><strong>属羊女</strong></p><p class="art_p">属羊女们是很不容易对陌生人产生异样情感的信号，她们喜欢呆在熟悉人的圈子里，直到某天突然发现那个曾经熟悉之人的某个亮点，而有了心动的感觉。</p><p class="art_p"><strong>属猴女</strong></p><p class="art_p">属猴女很善于发现惊喜，只要发现别人身上有她们喜欢的标志性特点，不管是街头路边，或者聚会擦肩，她们都会勇敢地去撩，甚至一追到底。</p><p class="art_p"><strong>属鸡女</strong></p><p class="art_p">周一一大早的商场、书店、超市、咖啡馆，无人问津的电影放映厅……都是属鸡女邂邂逅一见钟情的地方，因为很可能那同时出现了另一个不安常理出没的异性，就进入了她的视野，挑起了她的兴趣。</p><p class="art_p"><strong>属狗女</strong></p><p class="art_p">属狗女的爱慕常有崇拜的成分，所以常常会对自己的老师、兄长、甚至上司领导们痴情不改。所以不论是校园、职场、或者某个行业内部高精尖人群的活动，都是让她们开始陷入相思的环境。</p><p class="art_p"><strong>属猪女</strong></p><p class="art_p">属猪女人缘相当好，是生日聚会、朋友下午茶甚至朋友圈婚宴的常客，这也让她们增加了很多艳遇的几率。不管是熟悉的、陌生的，只要志趣相投、相谈甚欢，就给她们保留了牵手概率的可能。</p>', 1523880455, 0, 11),
(156, '女朋友不断，全靠一张嘴的星座(图)', NULL, '<p class="art_p">有的男生笨嘴拙舌，不擅长讲甜言蜜语，但也有的男生恰恰相反，讲起情话来那可就功夫了得了！摘星工厂—星吧今天就来总结一下哪三大星座是情话高手？</p><p class="art_p"><strong>射手座</strong></p><p class="art_p">都说射手座男生有点花心，喜欢和人搞暧昧，但其实，射手座男生真心喜欢上一个女孩的时候，会一改浪子本色，全心全意守护心爱的另一半。而射手座男生由于早就习惯了对女孩360式花样赞美，所以总能找到最动听的情话来打动另一半的心。再加上射手座男生属于放浪不羁的坏坏型男生，所以他们总能把每句情话讲得火辣而滚烫，让心爱的另一半停不下来的脸红！</p><p class="art_p"><strong>天蝎座</strong></p><p class="art_p">平日一贯以高冷形象示人天蝎座男生，一旦陷入爱情后，会一改往日的冷傲，在心爱的女孩面前变了一个人似的，乐于表达、分享自己的内心感受。而由于和冷面冰山的一贯形象对比太强烈，所以天蝎座男生不讲情话则以，一开口，每句情话的杀伤力瞬间爆棚！那种“喷薄式”的情话，足以将天蝎座男生内心对女朋友的爱完全表达出来。</p><p class="art_p"><strong>天秤座</strong></p><p class="art_p">天秤座男生观察力敏锐，懂得取悦讨好他人之道，所以他们总能在最短的时间内，轻松捕捉到女孩的兴趣爱好等一些重要讯息，接下来投其所好，对对方身上的优点，进行有层次、有针对性的夸赞，往往能起到撩翻少女心的绝佳作用！况且天秤座男生嘴里说出来的情话可是为心爱的女孩独家定制的，当极具煽动力的情话铺天盖地进入自己的耳朵，女孩子的芳心势必瞬间被天秤座男生牢牢俘获！</p>', 1523879637, 0, 11),
(157, '爱情测试：你谈几次恋爱才修成正果(图)', NULL, '<p class="art_p">星座频道征稿通缉令</p><p class="art_p">很少有人能谈第一次恋爱就可以步入幸福的婚姻殿堂。恋爱是需要多次尝试的，你在从中也能成长，变得更成熟，更有责任心。那么你谈几次恋爱才能修成正果呢？</p><p class="art_p">开始测试</p><p class="art_p">(本测试20道题，5个答案，系统自动跳转。仅供娱乐，非专业心理指导。)</p>', 1523878101, 0, 11),
(158, '塔罗测试：TA的心里还想着你吗(组图)', 'uploads/SjVG-fyixtym375454682171.jpg', '<p class="art_p">爱情有时候能给人带来欢喜，但是有时也会给人带来忧伤！很多人都为爱痴狂为爱无法自拔，甚至有的人能为爱付出自己的一切！大多数恋人都会问对方的话就是你心里爱不爱自己？可能你们有很长时间没有了联系，你也许很想念对方，但是也很希望对方心里也会想念自己。那么你想知道对方到底想不想你吗？来用塔罗牌占卜为你预测这个问题吧。</p><p class="art_p">占卜选牌规则：先让自己的心平静下来，心里只想着要占卜的问题；ta的心里还想着我吗？然后凭第一感觉在下面的4张牌中选出1张最有感觉的牌，看结果如何吧！</p><p class="art_p"><strong>A、愚人</strong></p><p class="art_p">抽中这张牌的你，是一个喜欢自由自在的人，不喜欢受任何管控。看来你在对方眼中占有一定的位置和影响力，未来彼此的发展空间很大、潜力无限。他正在等你表明态度，两人关系是否能恢复，主导权在你身上。</p><p class="art_p"><strong>B、太阳</strong></p><p class="art_p">你是一个比较自我为中心的人，还有点傲慢，对他人还算是很热情。太阳是一张充满积极意义的牌，即使逆位仍具有正位的意义。此牌显示你给对方留下的印象不错，是分手了还可以继续做朋友的类型。</p><p class="art_p"><strong>C、月亮</strong></p><p class="art_p">此牌寓意一种逃避的心态，不是他不再爱你，不是他不奢望复合，只是无法看清楚你内心真正的想法，猜心游戏玩得过多因而变得疲累不堪。</p><p class="art_p"><strong>D、正义</strong></p><p class="art_p">你是一个对人比较刻薄，常会不顾别人的想法一意孤行的人。此牌的出现预示一段不安定的关系，暗示对方有漂泊的感受，对你的感觉他自己也说不清楚，很可能陷入彼此纠缠却又复合无望的境况中。</p>', 1523795537, 0, 11),
(159, '塔罗测试：你将会有哪种桃花运(组图)', 'uploads/SjVG-fyixtym375454661827.jpg', '<p class="art_p">新年已过，天气回暖，春天悄然开始</p><p class="art_p">恋爱的萌芽在4月这个浪漫温馨的白羊月悄悄生长</p><p class="art_p">让我们一起看看，</p><p class="art_p">今年，你会获得怎样的感情</p><p class="art_p">请看下面的牌面</p><p class="art_p">凭着你的第一直觉，</p><p class="art_p">选择一张塔罗牌，</p><p class="art_p">不要偷看答案哦！</p><p class="art_p"><strong>A、魔术师</strong></p><p class="art_p">今年，你很幸运，爱情上的发展，将会有无限的可能。从潜意识来看，你选择魔术师这张牌，象征了2018，你在感情上会有多种选择的可能性。首先，魔术师在塔罗中，序号是一。这预示一切的开始。牌面中的魔术师站在桌前，桌上有剑、杯、星和杖，象征物质构成四要素：权杖（火）、圣杯（水）、宝剑（风）和钱币（土）。四大元素的集合，象征着完美。他手持魔杖，天地会赐予他力量。这象征着你将会获得好运的加持。魔术师身披的大红色的披风，象征着你会展开一段激情热烈的情感，而白色的衣服又代表了纯洁美好的爱情。因此，在2018，你会获得一份很不错的恋情。</p><p class="art_p">不过，需要注意的是，因为你很幸运，所以可能同时遇见几个桃花，你需要识别其中，究竟谁才是真正适合你的人。遇到特别喜欢的对象，要多表现自己，幸运之神站在你这边，加油！</p><p class="art_p"><strong>B、女祭司</strong></p><p class="art_p">女祭司代表着拥有智慧、淡定和从容。你会选择这张牌，说明你渴望一份感情，却又非常理智清醒，担心有所失去，不愿意轻易开始。画面中的女祭司，坐在两根黑白廊柱之间，恰恰代表着你潜意识里的矛盾。你的心情时而平静，时而又彭拜。你拥有着良好的直觉，非常清楚自己需要什么，想要什么。但是却解不开自己心中的矛盾。你可能正在经历异常柏拉图式的恋情，你感觉到了精神的契合，你们可能是在朋友跟恋人之间的一种关系。你有些犹豫，不知道要不要跨出现实的那一步。</p><p class="art_p">你可能比较被动，认为只需要默默的等待，真正属于自己感情一定会到来。不过，现在社会的诱惑很大，如果真的遇到喜欢的人，不如尝试大胆争取一下。</p><p class="art_p"><strong>C、太阳</strong></p><p class="art_p">在新的一年，你身边可能环绕着一些追求者。但是你的内心渴望自由，你更看中的是自己的感受，也非常在乎工作事业上的成功。你不会把感情放在第一位。并且，从你选择这张牌来看，你不会屈从家人的安排，而是选择倾听自己的内心，选择自己想要的。你更渴望证明自己的能力，爱情对你来说，只是一部分。</p><p class="art_p">牌面中一个可爱俏皮的小孩开心的骑在白马上，背后是灰白的墙，这代表逃脱束缚、走向更自由自在的明天。孩子裸露的身体，代表着真实与纯真。他背后的向日葵和头顶的太阳，都说明了你心里已经非常明朗了。非常清楚自己未来该走的路。</p><p class="art_p">即便你此刻已经有了对象，在2018，你也会活出精彩，过上自己想要的生活。</p><p class="art_p"><strong>D、女皇</strong></p><p class="art_p">新的一年，你在感情上，会如愿得到关心与疼爱，获得一份温馨浪漫的感情。并且，除了甜蜜之外，你们的感情也会有丰富的物资基础，对方会把你宠成女皇。</p><p class="art_p">从牌面来看，女皇坐在舒适华贵的宝座上，手握着代表权利的保障。这象征这至高无上的地位与权势，说明你在感情中，会成为被偏爱的一方。对方被你深深的迷恋，甘愿成为你爱情的俘虏。在女皇的脚下，是生机勃勃的麦田，女皇的背后，是浓密的绿色森林。这一切，都代表着物质的丰润。座椅上有一个心形，里面刻着金星，爱之女神维纳斯的符号。这预示着女皇的母爱天性，和宽厚仁爱。</p><p class="art_p">新年一年，你不仅会收获一份令人羡慕的感情，还会获得家人的支持，可能会定下婚约或者走进婚姻的殿堂。</p>', 1523705003, 0, 11),
(160, '12星座本周爱情吉日吉时(组图)', 'uploads/DqbQ-fyzeyqc515796459755.gif', '<p class="art_p">本年度第一次水逆，过去了！顿时感觉心头一松？犹如卸下一身压力？来看本周有什么“新菜”：4/17凯龙星换星座，4/18土星逆行。凯龙星运行在白羊座让人积极回复，水逆期间虽然会受伤，但却有自愈经验的积累，本周就是实践的开始！而土星逆行会带来规则的变化，经验的作用又减弱了……这么矛盾，还怎么恋爱嘛！摘星工厂-星吧（4.16-4.22）爱情运给你提示！</p><p class="art_p"><strong>本周爱情运势前五名：</strong>魔羯座、双鱼座、处女座、水瓶座、射手座。（请参考太阳及上升星座）</p><p class="art_p"><strong>*白羊座*</strong></p><p class="art_p">本周白羊座的爱情运势进展不大。单身的白羊座，有强烈的不安全感，在社交场合很多时候你都表现得过度紧张，面对有好感的人，面对颇有兴趣了解你的人，你更是各种不自在。有伴的白羊座，不想做的事情偏偏找上门，打乱你原本约会的计划，好心情也瞬间不见了踪影。</p><p class="art_p"><strong>约会吉日：4月16日</strong></p><p class="art_p"><strong>约会吉时：20：00</strong></p><p class="art_p"><strong>*金牛座*</strong></p><p class="art_p">本周金牛座的爱情运势不够稳，时起时伏的。单身的金牛座，明知错在自己，还不虚心接受周围人的批评、建议，小心坏了你的异性缘。有伴的金牛座，犯了错第一时间就应当勇于承认，别只顾给自己找借口、找理由辩解，以免给另一半留下硬拗的坏印象哦！</p><p class="art_p"><strong>约会吉日：4月17日</strong></p><p class="art_p"><strong>约会吉时：14：00</strong></p><p class="art_p"><strong>*双子座*</strong></p><p class="art_p">本周双子座将遇到不少爱情方面的挑战与困扰，还需沉住气应对。单身的双子座，实力强大的竞争对手（情敌）给了你不小的压力，让你很难有自信去应对接下来的挑战。恋爱中的双子座，希望另一半事事顺着你的意愿来，但现实难如愿，你很可能要忍不住发脾气了。</p><p class="art_p"><strong>约会吉日：4月16日</strong></p><p class="art_p"><strong>约会吉时：18：00</strong></p><p class="art_p"><strong>*巨蟹座*</strong></p><p class="art_p">本周巨蟹座人际方面有困扰，爱情运势多多少少也会受到影响。单身的巨蟹座，想一个人静静，不想被人问及关于相亲、恋爱、结婚方面的事情，但偏偏就是有人很不识相地对你的隐私刨根问底，惹得你很不爽。有伴的巨蟹座，心情不佳，硬脾气会来的很突兀，跟另一半斗气的可能性不小。</p><p class="art_p"><strong>约会吉日：4月16日</strong></p><p class="art_p"><strong>约会吉时：19：00</strong></p><p class="art_p"><strong>*狮子座*</strong></p><p class="art_p">本周狮子座的爱情运势还是有一些进步的。单身的狮子座，寻觅到合你眼缘、合你心意的人，几番接触、交谈之后，对对方的好感度也越来越高，感觉美妙的爱情就要降临。有伴的狮子座，对另一半极尽宠爱，而对方对你的温柔与体贴也使你尽情享受被爱的温暖，爱情升温也是情理之中咯。</p><p class="art_p"><strong>约会吉日：4月17日</strong></p><p class="art_p"><strong>约会吉时：10：00</strong></p><p class="art_p"><strong>*处女座*</strong></p><p class="art_p">本周处女座的爱情运势稳中有升。单身的处女座，放低身段，以平易近人的姿态出现在众人面前，更有利于缘分的靠近，本周你就有机会邂逅一位合你眼缘的人，彼此还挺聊得来。恋爱中的处女座，跟另一半的感情已然步入稳定阶段，即使是再平淡不过的生活也能被你们过得有滋有味！</p><p class="art_p"><strong>约会吉日：4月19日</strong></p><p class="art_p"><strong>约会吉时：18：00</strong></p><p class="art_p"><strong>*天秤座*</strong></p><p class="art_p">本周天秤座爱情运势大起大落，很不稳。单身的天秤座，锁定了恋爱目标，但追爱路上挫折多多，坎坎坷坷，克服了这个困难，还有下一波挑战在等待着你，一想到你就压力山大。有伴的天秤座，另一半说的某句话不够委婉、或者做的某件事情有失妥当，让你很是看不过去，可能会当面对其吐槽几句。</p><p class="art_p"><strong>约会吉日：4月21日</strong></p><p class="art_p"><strong>约会吉时：17：00</strong></p><p class="art_p"><strong>*天蝎座*</strong></p><p class="art_p">本周天蝎座爱情运势不够好，尤其是周四周五那两天。单身的天蝎座，对自己信心不足，害怕自己在某件事上表现不佳而给喜欢的人留下坏印象。有伴的天蝎座，有比较紧张的人际关系，跟另一半的互动方式会做些调整，对彼此间的相处而言，消极、负面的意义居多，需注意。</p><p class="art_p"><strong>约会吉日：4月18日</strong></p><p class="art_p"><strong>约会吉时：11：00</strong></p><p class="art_p"><strong>*射手座*</strong></p><p class="art_p">本周射手座爱情运势总体来看还是不错的。单身的射手座，即使没有爱情的滋润，你也照样能把单身的日子过得缤纷多彩，在你看来，真命天子/天女还没出现的时候，就应该努力充实自己，把自己变得更好。有伴的射手座，跟另一半的感情升华，甜度不减，恩爱程度让人羡慕。</p><p class="art_p"><strong>约会吉日：4月16日</strong></p><p class="art_p"><strong>约会吉时：18：00</strong></p><p class="art_p"><strong>*魔羯座*</strong></p><p class="art_p">本周魔羯座的爱情好运指数一路飙升，好到爆！单身的魔羯座，向人表白，成功的几率很高；被追求的魔羯座，你对对方的好感度明显上升了不少。有伴的魔羯座，和谐恩爱，是你们相处时的氛围，尽管偶有拌嘴，但那都只是你们枯燥生活的调剂，不会影响你们的感情的！</p><p class="art_p"><strong>约会吉日：4月18日</strong></p><p class="art_p"><strong>约会吉时：20：00</strong></p><p class="art_p"><strong>*水瓶座*</strong></p><p class="art_p">本周水瓶座的爱情世界还算明媚。单身的水瓶座，跟心仪人的互动不少，沟通的时候，行事上富有弹性，不会太死板，这给对方留下不错的印象。有伴的水瓶座，跟另一半尽情享受二人世界，不管是出门散心约会，还是宅在家一起做顿饭、看个电影，都是满满的恩爱。</p><p class="art_p"><strong>约会吉日：4月19日</strong></p><p class="art_p"><strong>约会吉时：20：00</strong></p><p class="art_p"><strong>*双鱼座*</strong></p><p class="art_p">本周双鱼座的爱情运势良好。单身的双鱼座，有人悄悄走进你的心，让你隐隐感觉，美妙的爱情就要来临，一想到这些，你就忍不住欣喜。有伴的双鱼座，另一半有怒火，你很善于平息，不管是温柔的安抚，还是轻声的劝慰，你总能找到方法让你们之间的相处气氛重归融洽、平和。</p><p class="art_p"><strong>约会吉日：4月21日</strong></p><p class="art_p"><strong>约会吉时：19：00</strong></p>', 1523621943, 0, 11),
(161, '婚恋心理：拒绝尬撩!恋爱的正确方法(图)', 'uploads/84lE-fyzeyqa568815721150.gif', '<p class="art_p">恋爱中的男女如何把握交往的度才能让对方更爱你呢？很多人都不知道究竟该如何正确谈恋爱，今天我们就来好好讨论一下。</p><p class="art_p"><strong>学会自我肯定</strong></p><p class="art_p">对于我们的灵魂来说，感情是一面巨大的镜子。我们总能够通过我们的亲密伴侣发现自己的“特质”。如果我们自己都无法接受自己的某些东西，那我们就不能指望我们的亲密伴侣接受它们，因为从感情上就说不通。自我肯定能够带来很多好处，其中之一就是在感情上会少些隔阂，这就意味着会给恋爱关系带来更多的良性循环。</p><p class="art_p"><strong>小心试探</strong></p><p class="art_p">对于想要调情的人来讲，记得，刚开始的时候，不能直接进入调情的节奏，而是需要用你们觉得，再正常不过的谈论来入题。例如，可以讲一下所在地的气氛，所在地的环境，所在地的气候等。这样，你可以判断一下这个人，是否值得你去调情。</p><p class="art_p"><strong>不停眨眼调情</strong></p><p class="art_p">其实调情没你想得那么复杂，单纯地眨眼也能达到有趣的调情作用哦！手机调 震动或静音吧情意正浓时，手机突然来了小伙伴的问候，这场面让你有些尴尬，继续亲热呢还是接电话呢？毋庸置疑的是，当你接完电话，恐怕那股热火朝天的亲热 劲儿也跟着没了，接下来，你们要说什么？看着彼此是不是有些不好意思？提前调成震动或静音，享受属于你们两个人的幸福瞬间吧。</p><p class="art_p"><strong>口气清新让你更自信</strong></p><p class="art_p">清洁口腔只需五分钟，但能换你一整天的自信。口气清新不仅仅让自己更自信，还能让对方更想要亲近你，即便她离你多近都不用怕污浊的口气破坏情绪。如果真的不方便刷牙，戴上漱口水和牙线，随时保持口气清新也是个好办法。</p><p class="art_p"><strong>多一些幽默感</strong></p><p class="art_p">家庭生活中的锅碗瓢勺等等琐事，有时确实需要多一些承受困难的勇气和 驾驭生活的能力。家庭中的幽默，可以减轻心理上的压力，幽默语言，常常能激起感情上的浪花。因为幽默是坚毅、冷静、智慧、能力的象征，是家庭矛盾的调和剂。</p><p class="art_p"><strong>适当照顾对方的喜好</strong></p><p class="art_p">男生和女生喜欢的东西有所差别，但是一定要尊重对方的喜好，适当的陪对方做一些他喜欢但自己却不喜欢去做的事情，这会让对方非常的感激你，也会做出同样的举动来让你开心，两个人在一起就是为了快乐，何乐而不为呢？</p><p class="art_p"><strong>不要试图控制对方</strong></p><p class="art_p">做好自己，恋爱不是生活的全部，不过度关注并干涉对方的生活，也不要一味的向下妥协，沉浸于感动自己。每个人都需要自由隐私的空间，不管两人是多么亲密的关系。给别人自由，也是一种对别人的尊重，所谓距离产生美说的就是这个道理。</p>', 1523465806, 0, 11);
INSERT INTO `pre_news` (`news_id`, `news_title`, `news_pic`, `news_content`, `news_time`, `news_like`, `news_cate_id`) VALUES
(162, '郭敬明《爵迹2》王源王俊凯造型曝光 网友狂吐槽', 'uploads/bg0M-fzyqqiq339959928695.jpg', '<p class="art_p">2016年，郭敬明自编自导的《爵迹》问世，这部作品饱受诟病，至今以3.9分的成绩稳居豆瓣低分电影榜。最近，《爵迹2》进入审核阶段，郭敬明对新作也是相当自信。30日晚，他在个人微博曝光片中王源王俊凯剧照，随后秒删，引发网友热议。</p><p class="art_p">当晚，郭敬明显然十分激动，他晒出《爵迹2》剧照并写到：“在审核镜头文件的过程里实在忍不住要偷拍一下了……（估计几分钟就会被宣传者的朋友们尖叫着勒令删除）”</p><p class="art_p">剧照中，王源、王俊凯的角色造型提前曝光。记者注意到，照片中二人均面带微笑，王源与第一部中的造型与第一部中的“苍白少年”相比有了较大的改变，而初次亮相的王俊凯造型也令人眼前一亮，戴着头饰和耳坠，橙红的瞳孔看上去十分惊艳。</p><p class="art_p">虽然郭敬明秒删了照片，依然被不少网友截图保存并在网上传开。王源、王俊凯粉丝自然对爱豆的新作很期待，不过更多网友对《爵迹2》十分不屑，许多言辞犀利的评论被网友自发点赞顶到前排：“这片拍的就像一坨狗shi居然还能有续集？被谁续的？”“这种烂片会有人去看？”“知道删还发出来，专拍烂片，卡司再强也没卵用，赚点粉丝钱得了，赚不到我路人的票房，再特么见”。</p><p class="art_p"><span class="img_descr">上游新闻·重庆晨报记者 赵欣</span></p><p class="art_p">更多精彩内容敬请关注@新浪女性（微博）</p>', 1525358796, 0, 12),
(163, '拿什么来拯救你们的肌肉小腿', 'uploads/1kWV-fzyqqip919642486974.jpg', '<p class="art_p">（文：整形医生修志夫 微博）</p><p class="art_p">很多姑娘的美腿毁在了小腿肚上，而罪魁祸首主要就是这两块叫做“腓肠肌”的肌肉。</p><p class="art_p">在踮起脚尖的时候，这两块“腓肠肌”更明显。</p><p class="art_p">“腓肠肌”的主要作用就是用来屈膝、足跖屈（脚趾往脚心的方向勾的动作）和固定膝关节，小步行走时也会提供动力。</p><p class="art_p">“腓肠肌”肥大的原因有多种，天生肌肉较肥厚的，后天运动导致的，也有一些是疾病因素导致如肌营养不良症导致的肌肉假性肥大。</p><p class="art_p">▼肌营养不良症导致肌肉假性肥大</p><p class="art_p">健康发达的腓肠肌“给力”，但是不好看。</p><p class="art_p">所以这种肌肉小腿要怎么瘦呢？</p><p class="art_p">肌肉发达真的很难“瘦”！</p><p class="art_p">脂肪细胞的话，通过运动它能缩小，但肌肉通过运动反而会导致肌纤维增粗增长，结果使整个肌肉的体积增大，反而更强壮了。</p><p class="art_p">不运动倒是可以让肌肉萎缩，比如一些瘫痪的病人、渐冻症患者，肌肉会逐渐萎缩。</p><p class="art_p">但对于健康人来说，是不可能也是必须要运动的。</p><p class="art_p">▼已故科学家霍金生前罹患渐冻症几十年</p><p class="art_p">在医疗美容上倒是有瘦肌肉小腿的方法，但是并不推荐，为什么呢？</p><p class="art_p">第一种：注射肉毒素。</p><p class="art_p">这个想必大家已经很熟悉了，一般单侧注射100单位肉毒素，使肌肉松驰、萎缩，达到瘦小腿的目的。</p><p class="art_p">不推荐原因：</p><p class="art_p">①腓肠肌比较大，注射后效果不理想且维持时间短 ；</p><p class="art_p">②如果把注射剂量加大，又容易发生中毒反应；</p><p class="art_p">③可能会影响腿部的活动。</p><p class="art_p">第二种：腓肠肌神经阻断</p><p class="art_p">是通过剪断腓肠肌神经来让部分小腿肌肉萎缩，效果是永久的。</p><p class="art_p">不推荐的原因：</p><p class="art_p">腓肠肌神经阻断是不可逆！术后可能会导致跑、跳、行走的功能障碍发生。</p><p class="art_p">第三种：射频瘦小腿</p><p class="art_p">通过射频释放能量作用于腓肠肌，阻断营养，同时射频作用于组织液，使其蒸发，从而达到瘦小腿的目的。</p><p class="art_p">不推荐原因：</p><p class="art_p">目前国内开展此项手术的医院很少，效果并不理想。</p><p class="art_p">所以，对于这种肌肉型的大腿肚子，基本没有太理想的治疗方法，不过还是可以做一些预防：</p><p class="art_p">1、少做爆发性的运动如跳跃、奔跑等。</p><p class="art_p">2、避免发胖。</p><p class="art_p">小腿肌肉发达，但并不代表没有脂肪。一胖，小腿肚就更大了。</p><p class="art_p">3、避免膝超伸。</p><p class="art_p">膝超伸从侧面看，大腿和小腿形成一个“C”的弧形，这会让小腿的受力更多，所以小腿更粗壮。</p><p class="art_p">4、能不穿高跟鞋就不穿。</p><p class="art_p">常穿高跟鞋会让小腿肌肉处于紧绷的状态，这样小腿紧实，就会显得小腿肚大了。</p><p class="art_p">如果穿高跟鞋也尽量不要绷直，身体重心往脚跟移一些。</p><p class="art_p">5、日常可以坚持做一些拉伸动作放松小腿肌肉。</p><p class="art_p">6、在服装穿搭上来掩饰一下，比如长裙、喇叭裤等。</p><p class="art_p"><strong>更多精彩内容敬请关注@新浪微整形（微博）</strong></p><p class="art_p"><strong>声明：新浪网登载此文出于传递更多信息之目的，并不意味着赞同其观点或证实其描述。</strong></p>', 1525354638, 0, 12),
(164, '皮秒做完后这样保养 效果翻倍', 'uploads/yDKN-fzyqqip902426820233.gif', '<p class="art_p">（文：美容美学专家侯典举 微博）</p><p class="art_p">蜂巢皮秒做完之后，会有面部干燥紧绷的感觉，且皮肤尚在恢复期，比较敏感，因此在生活之中需要注意很多事项</p><p class="art_p">特别留意这些，皮肤就真的像剥了壳的鸡蛋一样喔~</p><p class="art_p">1、结痂后要等待自行脱落</p><p class="art_p">做过蜂巢皮秒激光治疗后，治疗部位之后会有结痂产生。一般无需特殊处理，结痂一般会在3-7天后自行脱落。</p><p class="art_p">注意自己不要强行去除结痂，因为强行去除结痂会导致皮肤局部的颜色不一样，也会延长做皮秒激光的修复时间。</p><p class="art_p">2、不要用刺激性护肤品</p><p class="art_p">做完蜂巢皮秒激光之后，不要使用刺激性护肤用品，也最好不要用以前的一些护肤品，由于皮肤做完皮秒激光后还比较脆弱，一些护肤品中的成分有可能对于肌肤会产生刺激。</p><p class="art_p">因此在做完皮秒激光之后，最好是等到皮肤完全修复的一个月之后再用自己买的护肤品。</p><p class="art_p">3、严格防晒</p><p class="art_p">做完蜂巢皮秒激光后爱美者要注意严格防晒，刚做完祛斑治疗的部位，对于阳光会很敏感，如果不做好防晒工作，皮肤在激光皮秒后会反弹。</p><p class="art_p">皮秒激光治疗后到3个月内也要注意防晒，外出时戴好太阳镜和口罩，并且撑好阳伞，避免色素的沉积。</p><p class="art_p">4、使用修复产品</p><p class="art_p">通常情况下，做完蜂巢皮秒后要使用医生给的专用修复品。</p><p class="art_p">5、避免摄入吸光性食物</p><p class="art_p">蜂巢皮秒激光操作后建议爱美者们尽量避免食用吸光性食物，如柠檬、茄子、芹菜、藻类。因为这些食物会让皮肤很容易吸收紫外线，所以如果做完蜂巢皮秒激光之后想吃最好是在晚上吃。</p><p class="art_p">6、术后用面膜</p><p class="art_p">做完蜂巢皮秒激光后，每天早晚敷一片医用补水面膜，这样可以缓解做完蜂巢皮秒激光后皮肤因为受热而干燥紧绷的情况，还能加快皮肤代谢。</p><p class="art_p">7、避免化妆</p><p class="art_p">做完蜂巢皮秒激光之后，最好是一个月不化妆（因皮肤完整修复需要28天）。因为化妆品的成分和香精，很容易对于没有修复完全的肌肤，造成刺激，导致做完蜂巢皮秒激光后出现皮肤敏感症状。</p><p class="art_p">做完皮秒的女神们需要特别的注意，要做好保养，这样才能美美的！</p><p class="art_p"><strong>更多精彩内容敬请关注@新浪微整形（微博）</strong></p><p class="art_p"><strong>声明：新浪网登载此文出于传递更多信息之目的，并不意味着赞同其观点或证实其描述。</strong></p>', 1525341837, 0, 12),
(165, '周笔畅名誉权纠纷案一审胜诉：感谢给予公正判决', 'uploads/xkPq-fzyqqip918534233400.jpg', '<p class="art_p">新浪娱乐讯 2018年5月2日，周笔畅[微博]名誉侵权案胜诉，周笔畅工作室两年维权之路告终。</p><p class="art_p">周笔畅代理律师孙茂成今日微博发文称收到了周笔畅诉头条号作者陈宏荣（今日头条号“猪肉荣的文剧店”）名誉权纠纷案一审判决书。</p><p class="art_p">2015年10月23日，深圳广播电影电视集团娱乐频道《城市发现》主编陈宏荣通过今日头条号“猪肉荣的文剧店”发布文章，文字中有侮辱周笔畅等内容。随后周笔畅与陈宏荣进行交涉，但陈宏荣未做任何回应。2016年2月周笔畅委托律师将陈宏荣诉至北京市朝阳区人民法院。</p><p class="art_p">经过两年多判决，法院作出一审判决：“被告陈宏荣于本判决生效后七日内，在名为‘猪肉荣的文剧店’的头条号上发布致歉声明向原告周笔畅赔礼道歉，致歉声明持续时间为七十二小时，致歉内容须经本院审核，如逾期不执行上述内容，则由本院选择一家全国范围内公开发行的报纸，刊登本判决主要内容，刊登费用由被告陈宏荣负担”。</p><p class="art_p">周笔畅工作室对于胜诉表示：胜诉让我们更加坚定了用法律维护正当权益的信心。让我们共同监督维护文明健康的舆论环境。网友表示：两年多的案子终于有了结果，不管怎样，正义还是到场了。（实习生英子/文）</p><p class="art_p">更多精彩内容敬请关注@新浪女性（微博）</p>', 1525336615, 0, 12),
(166, '“国际范”再出征 范冰冰劳模姐等携手主演特工大片', 'uploads/XNod-fzrwiaz263304993179.jpg', '<p class="art_p">范爷又将出演好莱坞大片了！2日，有消息称由英国著名导演西蒙·金伯格执导的间谍题材动作惊悚新片《355》曝光了演员阵容，美国金球奖影后杰西卡·查斯坦、奥斯卡影后玛丽昂·歌迪亚、戛纳影后佩内洛普·克鲁兹、奥斯卡女配露皮塔·尼永奥以及圣塞巴斯蒂安影后范冰冰将一起出演女特工。</p><p class="art_p">《355》将由西蒙·金伯格执导，他曾是《X战警》《火星救援》《星球大战：义军崛起》等多部大片的监制和编剧。《355》讲述了阻止一个让世界陷入混乱的武器，世界各地的顶级女特工们联合起来拯救地球，而“355”是他们组织的代号。从目前曝光的演员阵容来看，《355》可谓是“女神云集”，杰西卡·查斯坦、玛丽昂·歌迪亚、佩内洛普·克鲁兹、露皮塔·尼永奥和范冰冰都是有颜又有演技的女星，这也让观众对于这部电影充满了期待。</p><p class="art_p">《355》也将是范冰冰继《登陆之日》、《策马》以及《X战警：逆转未来》之后，出演的第四部国际电影。西蒙·金伯格在采访中透露，《355》将打造成一个系列电影，在今年戛纳电影节中，所有的女主角都将现身为电影进行宣传。</p><p class="art_p">上游新闻·重庆晨报记者 孔令强</p><p class="art_p">更多精彩内容敬请关注@新浪女性（微博）</p>', 1525336611, 0, 12),
(167, '人到中年只能尬演？40岁＋女演员何时迎来春天', 'uploads/V8X_-fzyqqip906494681253.jpg', '<p class="art_p">她们的新作让人尴尬</p><p class="art_p">李小冉、蔡少芬的新剧正在热播，但是她们在剧中的角色，要么装嫩，要么成了小几岁的演员的妈妈，再次凸显一个现实：中年女演员的发挥空间越来越小。为此，有人自己做导演，有人愤然转战电视圈。只有以中年女性为主角的好戏多了，这一批人才会和同辈男演员一样，迎来真正的春天，否则只能给年轻人做配角，一味尬演。</p><p class="art_p">最近，演员李小冉的出镜率很高。前不久的热播剧《美好生活》，观众原以为会是一部细腻的中年情感剧，但结果让人意外。她饰演一名刚失去丈夫的杂志社编辑，张嘉译换上了她丈夫的心脏，因为这种关系，他们上演了一段纠缠的爱情。</p><p class="art_p">在这部戏里，李小冉还算是适龄出演，而到了正在热播的《下一站，别离》中，她是在减龄出演。戏里李小冉是单身未婚女性，年龄设定在30岁左右，这与她实际年龄相差10岁左右。因为遗传的原因，她提前进入更年期，不得不与于和伟协议结婚，两人在日常相处中，慢慢读懂对方、收获爱情。不少观众认为，该剧表面上看是都市剧，但走的是偶像剧的老套路，两位中年实力演员演起来很尴尬，“剧本没有逻辑，打光和化妆的风格过于偶像化，让人出戏”。</p><p class="art_p">最近蔡少芬出演一个妈妈角色时也遭遇了尴尬，不过，与李小冉要“装嫩”的境况不同，她只能“往老了演”。前日收官的《海上嫁女记》中，她扮演的何念晴诠释了那一代保守与焦虑的女性，敢于踏出追爱的脚步，与沈方舟获得圆满爱情。奇怪的是，剧中她的女儿由张檬出演，两位演员只相差15岁，且在扮相上并没有母女的感觉，更像姐妹，蔡少芬不得不“往老了演”，这点引起观众吐槽。</p><p class="art_p">这样的尴尬在国产剧中并不罕见。宋丹丹在《美好生活》中演过张嘉译的母亲，前者只比后者大9岁，就此，两人同框的画面充满违和感。戚薇在《北京女子图鉴》中前期扮过刚毕业的大学生，在观众看来，再好的化妆术也无法让她有青涩稚嫩的感觉，好在这个角色迅速成长，演员才避免了更多的抨击。</p><p class="art_p">她们只能当绿叶？</p><p class="art_p">在互联网谈话节目《圆桌派》里，这几年已经无戏可拍的蒋雯丽，面对目前的情形只好感慨“怀念自己的青春时光”。更多同龄女演员只好在一部戏里给自带流量的年轻演员当绿叶。回归家庭、生下两个孩子之前，梅婷的最新作品是《父母爱情》，她凭借戏里的角色拿下多个表演奖项，奠定实力派的地位，但是复出之后，她就只能饰演“皇后”这类角色，不仅是配角，角色还非常脸谱化，留给演员的发挥余地实在太小。</p><p class="art_p">此外，陈数、秦海璐分别在《择天记》和《南方有乔木》里给流量演员做陪衬，观众评价一般。既然没有满意的剧本角色可演，创作上有想法的秦海璐，索性自己做导演，她执导的处女作《一意孤行》正在筹备中，将于年内开机。</p><p class="art_p">不过，有的中年女演员则在主动为自己争取表演的空间，表现中年女性的状态。因为早前专心于家庭，姚晨错过了事业上的发展机会。她目前正在与正午阳光团队合作拍摄《都挺好》，和其组成“父女档”的是老戏骨倪大红。剧中，姚晨饰演的苏明玉是集干练、坚韧、忠诚于一身的事业女性，同时又有面对家庭问题时的凌厉与纠结。演员有实力，如果剧本优秀，该剧必定不负期待。《我的前半生》中，马伊琍作为中年失婚女人，袁泉作为自由独立的女强人，都演出了现代女性的困境和挣扎，只是故事掺杂了狗血元素，导致纯粹性打了折扣。</p><p class="art_p">殷桃也一度没戏可拍，去年凭借《鸡毛飞上天》获得好口碑，她已经是现实主义题材女主角的热门人选。她和王雷、李乃文合作的《爱情的边疆》即将播出，编剧是高满堂，导演是《平凡的世界》导演毛卫宁，主创阵容强大，就看故事能否打动人心了。她和黄晓明合作的军旅剧《你迟到的许多年》改编自严歌苓的小说，因而也备受关注。</p><p class="art_p">当然，由中年女演员挑大梁的现实主义题材，要想好看，就必须在贴近社会真实和注意尺度之间寻找平衡。</p><p class="art_p">娱评</p><p class="art_p">鼓励多样化探索</p><p class="art_p">曾俊</p><p class="art_p">和老戏骨一样，不少中年女演员都成了年轻演员的黄金搭档，是当前行业中的标准配置。一部戏光有年轻演员是撑不起来的，而有那么一批人爱看上了年纪的演员，所以中年女演员就慢慢在演前任、妈妈、婆婆，久而久之，对这些女演员来说，有些无奈，但毕竟还有戏可演，会有开心的时候。</p><p class="art_p">这肯定不是长久之计，文艺作品需要更丰富多样。按道理，中年女性的爱情、事业和生活都很有嚼头，更能让人体会到人生的况味。但只有有了深入、深刻刻画中年女性的剧本，中年女演员才会感觉到大有作为，才能和同辈男演员一样，不用担心自己就此沉寂。</p><p class="art_p">目前电视台扛收视的压力很大，剧集采购者会顺从传统，不太敢轻易冒险，但是，网络在这些戏探索上的自由度更大。那就期望不同的影视制作公司更加努力，多一些花样，向其他国家的优秀同行学习，认真花时间创作出合乎每个年龄段的好戏。</p><p class="art_p">转战电视圈的她们前景如何？</p><p class="art_p">很难想象，名气和演技皆过硬的宋丹丹尚且遇到“无戏可演”的尴尬，何况别人？节目里，她坦言，在35岁到48岁这个自身经验、阅历最好的时间段里，却没有合乎年龄的角色找她出演。因此，她不停鼓励中年演员要在寂寞中坚持。事实上，李小冉、马伊琍等演员靠保养得宜模糊了年龄界限，可以接比自己年龄小很多的角色，其他中年女演员就只能“扮老”，慢慢走上出演妈妈、婆婆之类角色的道路。</p><p class="art_p">在观众看来，这些描绘中年女性生活、事业和家庭的戏里，剧本是最大问题：职场戏部分像过家家，编剧缺乏相应的行业积累，都市戏又极为啰嗦琐碎，不符合逻辑的桥段横行，各种俗套情节让人失望。</p><p class="art_p">国产剧没有给中年女演员很多发挥空间，国产片也是如此。只不过，电影演员到了小荧屏里，就在创作中获得了很强的话语权，担任热门网络小说的大女主。从2014年开始，周迅多次在电影里“减龄”出演，但这张脸也在慢慢失去说服力，作品评分也在走低。</p><p class="art_p">走入人生后半程的章子怡、汤唯都遭遇了同样的困惑，选择了同样的大女主路线，但由于多种限制和观众口味变化，其后续效果有待观察。</p><p class="art_p">中年女性的戏口碑好收视不行？</p><p class="art_p">有人认为，中年女演员之所以机会不多，是因为观众不爱看中年女性的戏。他们更容易接受高颜值的年轻女演员，所以，这导致当今的荧屏被杨幂、赵丽颖、Angelababy等年轻一辈女演员占据。</p><p class="art_p">可是，仍有许多观众对中年女性题材非常有兴趣。《经常请吃饭的漂亮姐姐》最近成了刷屏的爆款，它再次激发了观众的少女心：不论自己处在哪个年龄段，谈一场浪漫恋爱的心始终都在。况且这部剧还足够真实、细致。高口碑美剧《傲骨之战》第二季正在热播，它主要涉及老中青三个年龄段的女性，人物的饱满丰富、戏剧的强大张力让观众热血沸腾。</p><p class="art_p">一个残酷的事实是，在网络上支持这些高分剧的观众，平时爱看美剧、英剧，对戏剧品质有高要求，却不是撑起国产剧收视的群体。这两部分人群似乎是割裂的，这也能解释为什么一些国产剧在网络上被吐槽厉害，但收视很高；诸如《和平饭店》这样的剧，口碑很好，但收视不行。</p><p class="art_p">国产影视剧很少出现关于中年女性的好题材，除了观众原因，也与上游的制作喜好有关。因为从投资的角度看，相比年轻演员，中年女演员的确难以占据市场，这意味着投资有风险。在业内，一部电视剧在只有一个创意时，就要拉投资、签演员、初步确定播出平台，这时候播出平台只能依据故事类型和演员阵容来判断播出效果，因此流量明星就有了天然优势。这也是这么多年，流量明星备受争议，但依然被争抢的主要原因。</p><p class="art_p">更多精彩内容敬请关注@新浪女性（微博）</p>', 1525336557, 0, 12),
(168, '价值9.15亿的《巴清传》该何去何从？', 'uploads/re_o-fzyqqip950114362962.jpg', '<p class="art_p">4月25日，上市公司唐德影视发布2017年年报，其中，关于《巴清传》的消息最为引人关注。2017年，唐德影视在电视剧业务方面实现收入111970.33万元人民币，主要来源于首次发行的《巴清传》《花儿与远方》《计中计》等卫视播映权和信息网络传播权转让收入，以及《那年花开月正圆》《急诊科医生》联合摄制分账收益。其中，由范冰冰、高云翔主演的古装历史剧《巴清传》已经取得回款的收入为33225万元，占去年收入总额的29.67%。</p><p class="art_p">从《赢天下》到《巴清传》</p><p class="art_p">根据公开信息，《巴清传》号称投资5亿，是目前亚洲最大单体投资影视剧。卫视首轮播出平台为东方卫视和江苏卫视，二者为这部长达60集的电视剧首轮播映权（仅限中国大陆地区）以及三轮播出后在（江苏省/上海市）行政区区域内有线、无线电视播映权，各自付出了23250万元，平均每集单价387.5万元。</p><p class="art_p">《巴清传》在网络端卖给优酷的独播价格则是45000万元，平均每集750万元。值得注意的是，4月9日唐德影视公布的《2018年第一季度业绩预告》提到，预计2018年1至3月归属上市公司股东的净利润2350.00万至2620.00万，同比变动157.58%至187.17%。收入来源主要是电视剧《巴清传》海外电视播映权销售收入、《战时我们正年少》首轮电视播映权销售收入以及其他剧本版权转让收入等。三项相加，60集的《巴清传》网台以及海外版权已经卖出了超过9.15亿的天价。</p><p class="art_p">然而，这部剧的命运却可谓一波三折。《巴清传》由《武媚娘传奇》原班人马打造，由高翊浚执导，范冰冰、高云翔、严屹宽、马苏、张国立、郭品超领衔主演，徐帆特约出演，潘迎紫、潘虹特别出演。讲述的是秦朝传奇女商人“寡妇清”的故事。该剧“曾用名”为《赢天下》，2017年年末正式改名为《巴清传》，并拿到了广电总局发行许可证。一度有消息称，该剧将作为开年大戏登陆江苏卫视和东方卫视，但播出前被临时撤下。此后一直没有确定播出时间。</p><p class="art_p">高云翔事件影响唐德影视利益</p><p class="art_p">3月29日，该剧男主角高云翔在澳大利亚涉嫌性侵的消息曝光，给此剧的前途蒙上了一层阴影。早在2014年，广电总局就曾发布通知，要求各级广播电视播出机构暂停播出有吸毒、嫖娼等违法犯罪行为者作为主创人员参与制作的电影、电视剧；网络视听节目服务机构暂停传播有吸毒、嫖娼等违法犯罪行为者作为主创人员参与制作的电影、电视剧、网络剧、微电影以及代言的广告节目；在广播影视对外交流中，暂停有吸毒、嫖娼等违法犯罪行为者作为主创人员参与制作的电影、电视剧和各类节目的赠送、销售和交流。这三条限制条款意味着，一旦高云翔最终被判定涉嫌犯罪属实，其作为男一号出演的《巴清传》或将无缘视频网站、卫视以及海外三个播出渠道。</p><p class="art_p">股票价格方面也能显示出此消息对于主要出品方唐德影视的打击程度。3月29日，消息公布当日，唐德影视股价直接跌停。3月30日，唐德影视继续下跌2.18%。截至4月26日，唐德影视17.77元的价格已比3月29日开盘的21.44元缩水了18%。如上所述，这期间唐德影视还公布了2018年第一季度盈利预告，却依然无法提振士气。</p><p class="art_p">《巴清传》如何自救</p><p class="art_p">那么，价值9.15亿的《巴清传》该如何自救？目前来看，有三条路，一曰等，等待法律给高云翔一个说法。不过，等待时间将会相当漫长。此前，有消息称高云翔案等待审理的时间将长达18个月，澳大利亚最大的华人律师行AHL法律首席律师、澳大利亚最高法院出庭律师沈寒冰在接受相关媒体采访时对此做出解释称，“新南威尔士州，警察不够、法庭不够、法官不够，所有的案子都在排队等。如果高云翔案件发生前，法院没有其他案件，那他的案件很快就可以进入审理程序。反之，若之前有1万个案件待审理，那高云翔案可能等待的时间不止18个月。”由此可见，等下去的结果就是，至少在今年之内《巴清传》将无法播出，此举也将拖累唐德影视今年的业绩。</p><p class="art_p">另外一条路则是换。近日有传闻称，《巴清传》将用男二号郭品超替换高云翔的戏份。不过，唐德影视并未证实这种说法。北青报记者注意到，此前，影视作品拍完后又意外换角色的案例也有，比如电影《捉妖记》中井柏然替换柯震东，《大秦帝国》第三部邢佳栋替换王学兵，但前者只是一部90分钟的电影，后者替换的是男配角，体量和难度上都无法与在60集电视剧中替换男主角相提并论。</p><p class="art_p">第三条路则属于剑走偏锋，那就是通过重新剪辑，将高云翔从《巴清传》中彻底抹去，让秦始皇只通过背影或者画外音的形式出现。但是此举意味着剧情将有重大改动——从秦始皇和“寡妇清”的“二人转”变成“寡妇清”的大玛丽苏逆袭记。剧情改动后要重新接受审查，而且剧集长度也将相应减少。播出后是否会影响收视，是否会影响卫视的后期回款，是否会导致网播价下调，都难以判断。</p><p class="art_p">三条路走哪条？取决于唐德影视对高云翔案的态度以及对公司今年业绩的考量。可以想见，没有作出决策前的这段日子，将会相当煎熬。文/记者 祖薇</p><p class="art_p">更多精彩内容敬请关注@新浪女性(微博)</p>', 1525336554, 0, 12),
(169, '致那些年我们痛饮的鸡汤文', 'uploads/ICXw-fxwevmc539664443023.jpg', '<p class="art_p">文章来源微信公众号：心理有你</p><p class="art_p">一类熟悉的图，一类熟悉的文，我们用鸡汤为之冠名，曾几何时鸡汤文养育了如意林，读者般无数的杂志社，但是现在鸡汤似乎都已被下毒，就连曾经风靡的杂志现在似乎都陷入了困境。其实鸡汤还是原来味道，熟悉的套路，熟悉的味道，只是读者不再相同罢了。人们已经不再愿意听到“每个人都在带着伤奔跑”“应该积极向上地接受生活的锻造，变成更好的人”如此之类的话。道理都懂，但是困难依然是困难，该难过的依然很难过，在这个挑剔的时代，鸡汤难以存活也是情理之中。</p><p class="art_p">“我们有情绪，有伤心、有难过，可是不得不擦干眼泪继续奔跑”这样的话从来就不能治愈人，一个人身上有伤口不进行治疗，反而任由伤口溃烂化脓，却依然要坚持在路上，本身就是强人所难。</p><p class="art_p">其实曾经被很多人需要鸡汤就是因为大家都觉得心里苦，希望能在别人的故事中寻求一种慰藉，我们之所以称其为鸡汤正是因为我们希望这些文章能治疗我们内心的伤痛，可惜鸡汤不是药，怎么也起不到该有效果，但多少又能缓解一下症状。食之无味弃之可惜，名曰鸡汤，实为鸡肋。</p><p class="art_p">在内心郁闷时我们也许会选择跑步发泄一通，要么跟朋友发一通牢骚，要么就找一个树洞写下世界上最恶毒的话，但即使是这样很多人没有将做心理咨询列上可供选择的清单，毕竟真正有过心理咨询经历的人还只是少数。</p><p class="art_p">说起心理咨询，似乎很多人认为没有抑郁症，也没什么必要去做心理咨询。自己内心里的纠结跟朋友聊聊天，逛逛街，吃吃饭，就够了。但实际上，我们又有多少次因为调整不好心态，失去了曾经的热情，错过了那些珍贵的机会。</p><p class="art_p">中国人含蓄内敛，有什么话不喜欢明说，怕伤了和气。为了和气我们一直隐忍，一直不愿意翻脸，那就是平静的湖水下暗藏波涛汹涌，这似乎也成了中国人的一种种族技能，善于忍耐，懂的中庸。也是这个原因也让我们不太能接受直接摊开来说，我们隐忍愿意迁就，但时间长了会有一种长期积累的伤害造成。哪怕是有坦诚的机会也会选择含蓄，更不用说和一个刚刚认识的人袒露心声。</p><p class="art_p">相比国内，欧美的社会对个人的心理干预可谓从小做起。西方的孩子们从小就接受忏悔、祈祷，做错了事情或是有情绪可以去找牧师或是神父去倾诉，牧师、神父也会尽己之力去帮助他们，去疏通他们的情绪，打开他们的心结，也可以去找心理咨询师，以求更专业的服务，他们觉得找专业的心理咨询师来帮助自己是一件令自己骄傲的事情，因为心理咨询师能够帮助自己、能够提升自己的生活。</p><p class="art_p">而存在巨大文化差异的我们一方面不愿承认自己的问题，承认了自己的问题又会弱化自己的问题，然后去忽略它，直到不得不去花时间与自己的严重问题去对抗。</p><p class="art_p">在这个鸡汤中毒的时代，在遇到难解的心结时，不妨尝试一下心理咨询。有个专业人士能运用心理学以及相关知识，遵循心理学的原则，通过心理咨询的技术与方法，针对你和你的纠结，进行分析和疏导自然是会有效果的。毕竟尝试新的才是对旧时自我最好的突破。</p><p class="art_p">更多精彩内容敬请关注@新浪女性（微博）</p>', 1525336506, 0, 12),
(170, '越是好男人越会用消失跟你分手', 'uploads/ugzG-fxvyqwa341339761862.jpg', '<p class="art_p"><br>&nbsp; &nbsp; &nbsp; &nbsp;文章来源微信公众号：心之助</p><p class="art_p">女友聚会，必谈的一项内容是男人。</p><p class="art_p">场中有个小姐妹Icy最近恋情告急。</p><p class="art_p">“我感觉他突然就不见了，电话不接，信息不回，朋友圈也没更新，到处找不到。”</p><p class="art_p">Icy有点气急败坏，“我是又气又担心，说是出个差一周，现在都半个月了。要是飞机出事，新闻应该也能看得到啊。可是什么都没有！”</p><p class="art_p">小姐妹们纷纷安慰道：“别急别急，可能是信号不好，或者身上的东西被偷了，要不就忙，出差都事情多，又不是去玩。”“等他回来就会联系你的，说不定他自己也着急要联系你呢。”</p><p class="art_p">我在一旁听了很久，最终忍不住插口道：“他突然消失，你就当他是死了吧。”</p><p class="art_p">姐妹们一阵抱怨，七嘴八舌，说我胡说八道。</p><p class="art_p">我说：“有部电影，《He just not that into you》，我们一起去看过的嘛~~”</p><p class="art_p">场子顿时安静下来。</p><p class="art_p">好一会儿，有个声音低低地说：“电影说的是男女建立关系的时候，这和恋人关系不一样吧……”</p><p class="art_p">我说：“有些男人，就是用消失来分手。”</p><p class="art_p">不分手，你都不知道男人有多自私</p><p class="art_p">为什么男人要用消失来分手呢？</p><p class="art_p">这是大部分女人不理解也不接受的事情！</p><p class="art_p">但只要拆分一下问题，这件事也不难理解了。</p><p class="art_p">人，总是趋利性的，对自己无利的事情，正常情况下都不会去做。</p><p class="art_p">比如在分手这件事情上，设想一个男人面对面和女友谈分手，最可能面临的结果是什么？</p><p class="art_p">“肯定是会大闹一场啊！”有小姐妹说。</p><p class="art_p">“我什么都不管，先给一巴掌再说。老娘那么好，你凭什么说分手？”</p><p class="art_p">“肯定会很伤心的吧，情绪一激动保不齐会做出什么事情来。”</p><p class="art_p">“我求他不分手还不行吗？分手我就死给他看！”</p><p class="art_p">……</p><p class="art_p">那么，假如想分手的男人消失不见了，是不是就不用面对这种激烈的场面呢？</p><p class="art_p">有些人，就是害怕冲突，就是不敢面对激烈的场面。他彻底地从你生活中消失，就永远不用面对这个场景啊。</p><p class="art_p">只要一天没有开口说分手，你们之间的关系就还没有正式结束。哪一天他后悔了，或者想通了，或者找不到更好的，再续前缘的难度系数也会降低很多。</p><p class="art_p">毕竟，你们之间没有发生过什么破坏性的事件，只不过是失联了一段时间。而国人对感情的评价体系，一向以时间维度和底线宽度为标准，比如要求坚贞不渝、经久不衰、原谅包容。</p><p class="art_p">至于消失的原因，你以为编个原因很难吗？</p><p class="art_p">即使不用费劲心思想借口，就说自己想安静一段时间，思考一下这段关系的发展和走向，你拿什么理由反驳呢？</p><p class="art_p">这样你不和他在一起，舆论的矛头就会在你身上，而他只是个受害者。</p><p class="art_p">他给了自己最大限度的选择权，却不给你知情权。</p><p class="art_p">明明是他伤害了你，却要你擦干眼泪和他继续睡。</p><p class="art_p">或者，使用冷暴力，是他对你们两个人关系的掌控手段。把你冷漠一段时间，看你抓狂，再给你点小甜头，让你知道失而复得的机会，这样宣示你们之间的主动权是掌握在他手里的。</p><p class="art_p">而他还可以大言不惭地说：“男人应该以事业为重。”感情并不是他的第一位，你看重感情因为你是女人，感情才是你的事业。</p><p class="art_p">又或者，他做了什么伤害你的事情，对你心有歉意，内疚，却不知道怎么面对你。这样消失了，你撑不下去，自己提出分手，他的内疚感就可以得到舒缓。因为提出分手的人是你，断绝这段关系的人是你，不是他。</p><p class="art_p">一旦他发觉世间始终你最好，又可以站在有利的角度谈复合。不明真相的吃瓜群众，一定会劝合的，这样他又争取了舆论的支持。况且市面上多少狗血情感导师都在毁人不倦：“女人提分手都不是真分手。”你不同意复合？很明显是你的错。</p><p class="art_p">有谁在意他曾经怎么伤害你吗？</p><p class="art_p">空气像凝固了一样，令人窒息。</p><p class="art_p">半饷，有个姐妹忿忿不平的声音划破这片沉寂：“男人真贱！”</p><p class="art_p">“只考虑到自己有没有退路，不考虑女人有没有受伤。”</p><p class="art_p">“他们根本就不知道，玩消失这种分手方式，对女人是多大伤害。”</p><p class="art_p">“男人好自私！个个都是渣男！”</p><p class="art_p">……</p><p class="art_p">用消失的方式分手的，都是渣男吗？</p><p class="art_p">越是“好男人”，越会用消失和你分手</p><p class="art_p">我们眼中的“好男人”是怎么样的？</p><p class="art_p">有责任感，为家人勤奋上进，做事有担当；</p><p class="art_p">技能满分，品学兼优，硬件过硬，软件不软；</p><p class="art_p">性格谦逊平和，做事贴心周到，待人温暖有趣；</p><p class="art_p">多金善良，位高权重，进退有分寸，谈笑有内涵。</p><p class="art_p">……</p><p class="art_p">一句话：符合社会主流价值观。</p><p class="art_p">如果这样的男人才是渣男，渣男的门槛是不是也太高了些？</p><p class="art_p">没个社会地位，都没资格当渣男了是吧？</p><p class="art_p">为什么有些男人就是这么好呢？</p><p class="art_p">在他们的内心，有没有哪一刻想要偏离这个“好男人”人设呢？</p><p class="art_p">“开什么玩笑啊？这样的人为什么会想要去做个不那么好的人？”</p><p class="art_p">“这样的男人，肯定从小就是父母的骄傲，同龄人的样板，师长眼里的模范生，老板心中的头马，再娶个和他匹配的女人，简直就是人生赢家了好吗！”</p><p class="art_p">“拿走这些好条件，对他有什么好处呢？”……小姐妹们议论纷纷。</p><p class="art_p">可是问题就在这里——</p><p class="art_p">从没有人问过这些“好男人”的内心，是否真正快乐满足。就像从没有人问过女人，这种类型的男人，是不是你真正喜欢的人。</p><p class="art_p">做个好男人需要很多技能，然而现实中没有多啦A梦，每一项技能都需要日积月累地练习、实践，才能成为他行走江湖混社会的必杀技。</p><p class="art_p">这就得从小学起吧。不管是带得出手的硬技能，还是为人处世这些软技能。</p><p class="art_p">那么，是他自己打心眼里喜欢这样做，还是这样做才能给他带来好处呢？</p><p class="art_p">别忘了，人总是趋利性的，小孩也一样。</p><p class="art_p">假如其他人是野蛮生长的熊孩子，天天挨揍，而他是那个“别人家的孩子”，被赞美，被拥戴，被珍视，被敬仰……你说他愿意继续当“别人家的孩子”，还是愿意变成另一个熊孩子？</p><p class="art_p">然而我们知道，小孩要犯错，才会懂是非，要尝试，才会懂规则。在错误中成长，才是一个正常人正确的成长模式。</p><p class="art_p">那么，一直都只在对的道路上走下去的孩子呢？</p><p class="art_p">偶像包袱一旦背上了，就很难卸下来哦。</p><p class="art_p">就像你自从披上一件华丽的袍子，受到万众瞩目，从此光环加持；即使袍子下有虱子咬得你难受，你也未必会义无反顾地扯下来吧？</p><p class="art_p">一个人，叫好声越高，对光环的渴望就越强烈，犯错的成本就越大。</p><p class="art_p">所谓站得越高，摔得越惨。</p><p class="art_p">而有多少人夸你好，就有多少人等着看你出错。</p><p class="art_p">这么符合标准的好男人，一旦做错了什么，必定是众口铄金，积毁销骨。</p><p class="art_p">所以他们到底是不会犯错呢，还是不敢犯错呢？</p><p class="art_p">我们眼中的好男人，也许只是一群太过在意别人评价、从来都不敢犯错的小孩。他们这么好，只是因为，他们做错事的成本太高，要一直小心翼翼，不能越雷池半步。</p><p class="art_p">所以，其实他们未必真正懂得去面对自己的内心，未必真正懂面对亲密关系中的矛盾。</p><p class="art_p">女人会因为他们有着符合社会主流价值观的好被他们吸引，他们也用符合社会主流价值观的好吸引自己想要的女人。然而一旦问题产生，这种“好”并不足以让他们有勇气去面对问题，解决问题。</p><p class="art_p">因为，他们自己从未被真正地接纳过，从小就这样。他们要做“好”，才会被喜欢，不像别的熊孩子，挨揍之后，有抚慰，有糖吃。</p><p class="art_p">场上再次陷入沉寂中，姐妹们冷静下来，似乎有点同情这些“好男人”。</p><p class="art_p">“难道被这样分手了，我们还要原谅他们吗？”有小姐妹委屈地问。</p><p class="art_p">“这样的话，谁来为我们受到的伤害负责啊？”</p><p class="art_p">敢为自己负责的女人，才有能力幸福</p><p class="art_p">一个从未被真正接纳过的人，问题一出来，就会逃避。不管表面条件多符合社会主流价值观评判标准，内里都是情商水平和人格水平停留在幼儿园阶段的人。</p><p class="art_p">“所以说男人总是很迟才长大啊，做女人真的很累诶。”有小姐妹无奈地说。</p><p class="art_p">然而一定要等待男人真正长大，懂得面对亲密关系中的问题，女人才能找到幸福吗？是谁规定女人的幸福，必须掌握在男人手里的？</p><p class="art_p">我们来看看大概率下，分手玩消失的男人，有个什么样的女朋友。</p><p class="art_p">“回到一开始的问题，”我问道，“假设你们的男朋友现在玩消失分手，你们会怎么想？”</p><p class="art_p">“凭什么？”暴脾气的小姐妹说，“老娘对他那么好，事无巨细样样照顾周到，上至他父母下至他同事，哪一个不说我好的？他凭什么要分手？”</p><p class="art_p">“他对我那么好，怎么会突然间要分手？我一百个不相信！我宁可相信他遇到什么不得已的事情了……”</p><p class="art_p">“分什么手？我和他恋爱那么久了，大好青春都消耗在他身上，他必须对我负责！”</p><p class="art_p">“哎，闹什么，再找一个又得重新开始，还不如这样凑合着过算了，人就这一辈子。”</p><p class="art_p">……</p><p class="art_p">分手，只是感情破裂到无法进行下去的结果，从来就不是瞬间产生的问题。所以无论是谁提出来、以什么方式提出来，都改变不了事实。</p><p class="art_p">为什么当感情无法进行下去的时候，男人会用各种形式让自己从恶劣的关系中脱离出来，而女人却愿意继续忍受？</p><p class="art_p">或者这样问，为什么男人对恶劣的两性关系，更容易表现出自己的态度，而女人却浑然不觉，甚至自我感觉良好？</p><p class="art_p">会不会是因为，女人更容易“认命”？</p><p class="art_p">会不会是因为，女人觉得男人应该为我们付出的感情和青春负责？</p><p class="art_p">会不会是因为，女人经常理所当然地认为，只要找到一个好男人，所有的问题就自动治愈了？</p><p class="art_p">假如好男人是世界的一剂良药，那么万一“好男人”本身就有病呢？</p><p class="art_p">所以，你还在等男人长大，好配得上你的水平吗？</p><p class="art_p">两人同行，很难讲时刻保持步调一致。所以一段感情久了，就不是爱得有多深的问题，而是两个人步调还能否保持一致的问题。</p><p class="art_p">经常走着走着，发现两个人不在一个道上了，这也很正常。毕竟，我们是两个独立的个体，爱情可以让我们走到一起，并不能使两个人变成一个人。</p><p class="art_p">当我们还是婴儿的时候，父母怎样对我们，我们就怎样理解这个世界。</p><p class="art_p">当我们不再依仗父母的保护独立行走，可以走得更远的时候，我们走过的路，就是世界在我们面前的样子。</p><p class="art_p">有没有哪位女人，一生走过最多的路，就是男人的套路？</p><p class="art_p">当然有。而且不少。那些认为好男人可以给自己带来幸福的女人，那些“被男人宠坏了”的女人，不都是在走好男人设下的套路吗？</p><p class="art_p">一个时时要男人回应、事事须男人负责的女人，其情商水平和人格水平，也仅是停留在孩童阶段。</p><p class="art_p">这与情商水平和人格水平同样是幼儿园阶段的男人很相配嘛~~</p><p class="art_p">用心理学的话术来表达，就是焦虑型的女人，遇到回避型的男人，开启男女关系死循环模式：一个追着要别人负责，一个躲着逃避更多的责任；一个越得不到回应越想要回应，一个越被逼得紧越害怕回应。</p><p class="art_p">一个害怕活在“无人区”，一个害怕活在“冲突区”。</p><p class="art_p">这是国人喜闻乐见的国产家庭伦理剧剧情，也是现实中大部分亲密关系的真实写照。</p><p class="art_p">情商水平和人格水平双低的男女，通常都是以外在条件相称为开端，以互撕为情节，以无言为结局。</p><p class="art_p">可是从来就没有谁必须为你负责。</p><p class="art_p">男友分手玩消失要怎么办？</p><p class="art_p">真正意义上的好男人，成长在一个可以接纳他犯错，又能引导他走对的路，还能包容他按照自由意志长大的环境里。</p><p class="art_p">或许表面看起来未必符合社会主流价值观对“好男人”的条件清单，但这样成长起来的人，才是真正内心坚定温暖、勇敢面对问题并有能力解决问题的人。</p><p class="art_p">而有能力幸福的女人，是那些敢于为自己负责的人。她们不需要等待谁来为自己的幸福买单，她们自己就可以为自己买单。</p><p class="art_p">男人与爱情，只是来给她们的生活锦上添花的，她们本身就是一块锦。得之，很好；不得，也不见得有什么影响。</p><p class="art_p">需要别人“雪中送炭”的女人，还是要等把自己修炼成一块锦，才拥有幸福的能力。</p><p class="art_p">“这样说来，分手玩消失的男人没有错，都错在女人的焦虑感咯？”Icy问。</p><p class="art_p">不，我们从不排除有渣男存在的可能性！</p><p class="art_p">但无论如何，如果感情一有问题就突然消失，你就当他已经死了吧。</p><p class="art_p">面对爱人的谎言，我们的大脑会陷入瘫痪，死机了！我们无法再信任对方，无法判断哪些是真哪些是假。</p><p class="art_p">更多精彩内容敬请关注@新浪女性（微博）</p>', 1525336488, 0, 12);
INSERT INTO `pre_news` (`news_id`, `news_title`, `news_pic`, `news_content`, `news_time`, `news_like`, `news_cate_id`) VALUES
(171, '那一句对亲人无法说出的“我爱你”', 'uploads/WRT1-fxvyzus189491962204.jpg', '<p class="art_p">文章来源微信公众号：十分心理</p><p class="art_p">爱的感觉，是我们每个人都期待的，因为爱是一件让我们感觉身心愉悦与通泰的事，但爱的表达，却不是每个人都可以做到的事，因为在我们的感受中，表达爱，常常是充满危险的。</p><p class="art_p">前些天在电台做节目时，接到一位阿姨的电话，她与丈夫结婚二十多年，早已进入了平静的婚姻生活。在生活中，她对丈夫充满了期待，比如希望丈夫能早一点回家陪一陪她，或是期待丈夫能跟她讲一些体己话。但丈夫每每给她的回应都是让她失望的，比如丈夫会说“我早点回来干嘛呀，我回来你不是嫌我玩手机，就是跟我吵架”，再与这位阿姨更多的沟通下去，她自己也明白了自己在与丈夫二十多年的婚姻生活中，他们共同营造了这样一个交流模式，就是用责备对方的方式表达对对方的期待，而这种方式本身，因为缺乏必要的建设性，会让听到这话的对方感觉很不舒服，这个不舒服又会激起对方的防御：你对我抱怨，我就回击给你不满。长此以往，双方就都陷进了对对方的失望与回避之中。</p><p class="art_p">在与那位阿姨的沟通中，她自己也意识到了，在与丈夫的互动里，他们双方都在竞争婚姻中的控制权，在竞争中，他们可以让自己感觉自己在婚姻中是有力量的，而这种对有力量的需要，恰是因为他们自己的内心中，对自己对对方的不确定。比如在他们的感受中，自己没有足够的能力吸引对方来爱自己，从而就会有对失去对方的害怕，这个害怕会让他们努力营造出一些有力量的感觉来，从而抵御内心中这个害怕的侵扰，获得一些控制感。这个竞争的背后，是未表达出的对对方的需要，而这一部分，他们是没有让自己直接表达过的，他们习惯的方式是抱怨对方没有满足自己，而这个抱怨本身，便掩盖了彼此对对方最真实的需要。</p><p class="art_p">其实，这在亲密关系的互动中，是非常常见的现象。这看起来是让人非常迷惑的，我们明明爱他，需要他，可是，我们为什么要折磨他、回避他呢？</p><p class="art_p">承认爱一个人，是需要我们付出勇气的。因为，当我们爱一个人的时候，也就意味着那个人对我们很重要，我们需要他。而这种需要另一个人的感觉，会唤起我们内心中的弱小感以及很多关于丧失的恐惧。我们可以想象，一个弱小的婴儿曾经怎样期待来自母亲的爱，但是一个再好的母亲，也不可能满足孩子的全部期待。对于一个那么小的孩子，当母亲没有给予他及时的回应时，也许，在他的感受里，那就是毁灭性的。为了避免承受这种灭顶的感觉，这个婴儿就可能发展出一种能力：不让自己感觉母亲对自己那么重要，这样，当母亲不能及时给予自己所需要的满足时，也不至于让自己过于痛苦。</p><p class="art_p">我们带着这种对“强烈需要所爱客体”的恐惧长大，在我们今后的人生中，可能会发展出各种各样的应对方式，来避免体验我们内心对他人的需要，从而避免面对他不能给予我们所期待的回应的痛苦。比如像那位阿姨和他的丈夫一样，相互抱怨对方不够好，原本期待用抱怨换来对方对自己多一点关注，却没有想到，对方不一定有能力解读到抱怨背后的期待，于是就将双方陷入彼此无法接纳的境况中去。</p><p class="art_p">另有一些人，当他们期待与对方建立起爱的关系时，他们会选择一些让自己痛苦的方式来吸引对方的注意力，比如让自己生病，或是努力的照顾对方，甚至不惜牺牲自己的一些需要，来努力满足对方的需要。在他们的想象中，我努力为你付出，你就会对我好一些。却没有想到，这个付出的背后，也在制造着对方的内疚，当对方感觉“我欠你的”太多时，他就会承受极大的压力，在这个压力之下，反而会逃离这段关系。</p><p class="art_p">还有一些人，当他们体验到自己对另一个人的爱与需要时，他们会选择怀疑自己是不是产生了错觉，他们无法相信对方会对自己产生爱的情感，因为在他成长过程中，他缺少被爱的体验。即便是他已经清晰的体验到了对对方的需要，但还是要用各种各样的理由促使自己放弃向对方表达爱的情感，因为他无力确定自己有能力获得对方的爱。或者是，当他得到他所期待的情感时，他没办法让自己在那段关系中稳定下来，一旦他感受到一点点对方可能离开他的危险，也许这个危险只是存在他的想象层面的，他也会先一步离开对方，以此来避免被对方抛弃的结果，这样，在感受层面上，他就把关系的主动权牢牢地抓在了自己的手里。</p><p class="art_p">还有另外一些人，也许在他们成长的过程中，他们从父母那里得到的关爱太多了，多到了他无法承受那么大的爱的压力，于是，每当他体验到爱的时候，也就与压力直接划了等号。所以，在他的生活中，可能就会出现非常矛盾的体验，一方面，他期待感受对方的爱，另一方面，当他真正感受到爱的时候，又会害怕对方爱的情感把自己湮没，从而丧失掉自我，所以他可能会下意识的逃离。</p><p class="art_p">当然，回避爱的情感，方式远不只上面这些，这些方式，就像是我们在感受爱时，先要把爱画了妆才敢去面对，否则，当我们直面它时，我们自己就先变得很焦虑。</p><p class="art_p">这一方面与我们的文化有关，在中国的传统文化中，爱字是不可以轻易说出口的，爱的表达，常常与轻浮与色情挂上钩，所以，即便是在亲人之间，表达爱的情感时，也更多的会被采用一些比较间接的方式，比如给所爱的人买礼物，或是做美食，但很难郑重的告诉对方“我爱你”。</p><p class="art_p">另一方面，自然就是我们对爱的渴望所带给我们的压力。</p><p class="art_p">当我们爱一个人的时候，我们期待能与对方有更多的联结甚至融合，这个期待有时也会成为我们的压力源。比如对异性的爱，可能会唤醒我们很早年渴望异性父母的挫败感。当我们长到三五岁的时候，我们发现了这个世界上，妈妈和爸爸有着很特殊的亲密关系，而我们也希望自己能够拥有那样的亲密，于是，我们便对自己的异性父母产生了渴望。这个渴望是让我们害怕的，因为那也意味着我们要与同性父母竞争，那个竞争的结果不管是什么，我们注定都要承受痛苦：赢了，要被同性父母惩罚；输了，要承受挫败。所以，当我们对异性表达爱时，这种复杂的情感也同时会被唤醒，成功与失败都会让我们感觉焦虑，为了避免这个焦虑的浮现，于是我们往往会选择不那么直接的表达方式。</p><p class="art_p">对同性表达爱的情感同样也会让我们感觉不安，因为那也会唤醒我们恐惧同性恋的焦虑，虽然友谊便是同性恋焦虑的升华，而之所以要把它升华之后我们才能接受，那本身就是为了解决我们自己的恐惧。</p><p class="art_p">所以，在这个世界上，要表达“我爱你”，还真是困难重重。若要能发展出自由表达的能力，恐怕只有理解理解再理解，理解人性的多样性与隐密性这一条路好走了。</p><p class="art_p">更多精彩内容敬请关注@新浪女性（微博）</p>', 1525336409, 0, 12),
(172, '指甲4个小变化不容忽视', 'uploads/M5Fm-fyssmmc105626455634.jpg', '<p class="art_p">文章来源：微信公众号“春雨健康科普（chunyuyuedu）”</p><p class="art_p">在养生圈，有很多关于指甲的传闻。</p><p class="art_p">比如，指甲上没有小月牙不健康，指甲容易断说明身体缺钙……</p><p class="art_p">实际上，这些说法都是缺乏科学依据的。</p><p class="art_p">健康的手指甲和脚趾甲是平滑、轻微弯曲、坚硬的，大多数人的指甲其实满足这个标准。</p><p class="art_p">那么，什么样的指甲暗示身体可能有问题呢？主要有以下4种。&nbsp;</p><p class="art_p">1。 指甲比以前脆了</p><p class="art_p">指甲突然变得比以前脆了，可能暗示某种潜在疾病，例如霉菌感染、甲状腺问题。</p><p class="art_p">此外，营养不良，特别是缺少铁元素、维生素A（β-胡萝卜素）、蛋白质也会引起指甲变脆。</p><p class="art_p">如果在某次体检时发现或单纯怀疑自己有上述疾病，建议就医。</p><p class="art_p">2。 指甲的颜色变了</p><p class="art_p">指甲变色可能是由外伤和潜在疾病引起的。</p><p class="art_p">在指甲末端附近出现黑色或蓝色的斑点可能是淤伤，由于外伤或重压造成的；</p><p class="art_p">指甲边缘清晰的小黑斑点一般是由单纯的外伤所导致的；</p><p class="art_p">暗淡或白色指甲可能在暗示贫血；</p><p class="art_p">蓝灰色的指甲表明心脏病或肺部疾病；</p><p class="art_p">有麻点的棕色斑点表示牛皮癣；</p><p class="art_p">黄色或棕色的指甲往往是由香烟或雪茄里的尼古丁造成的。</p><p class="art_p">3。 趾甲内生，长到肉里了</p><p class="art_p">趾甲内生是脚趾甲长到周围的皮肤里，甚至嵌到肉里了，有时会引起感染或伤残性疼痛。</p><p class="art_p">鞋子不合脚，过紧或过窄可能会造成此种情况。</p><p class="art_p">此外，脚部损伤、足部畸形或运动时脚反复受到压迫和冲击都会引起趾甲内生。</p><p class="art_p">但是，非物理原因，疾病原因如真菌感染也会导致趾甲内生。</p><p class="art_p">如果出现了趾甲内生，可以先排除真菌感染的情况。</p><p class="art_p">如果是其他情况，需要注意脚部护理。</p><p class="art_p">不错误的修剪趾甲，使用指甲刀而不是剪刀；</p><p class="art_p">修剪过的趾甲要比趾尖的肉稍微长一点，不要把趾甲剪得太短；</p><p class="art_p">长在皮肤里的脚趾甲会引发疼痛，可以使用热敷或在温水中泡脚来减轻肿胀和疼痛；</p><p class="art_p">泡脚能软化趾甲，这样可以尝试在趾甲边缘和皮肤之间塞入棉花，有助于减轻不适，而且可以帮助趾甲长出来，不挤压到周围的肉。</p><p class="art_p">4。 指甲奇形怪状</p><p class="art_p">增厚的指甲可能由血液循环问题引发的，往往伴随着外伤和动脉硬化的出现，此外真菌感染也会让趾甲增厚。</p><p class="art_p">扁平的甲可能暗示着雷诺病。扁平甲很薄，勺子形状的扁平甲有时是营养不良引起的，比如缺铁。</p><p class="art_p">弯曲厚重的脚趾甲是趾甲老化和被不合脚的鞋挤压导致的普遍结果。&nbsp;</p><p class="art_p">参考资料：哈佛家庭医生全书。 皮肤、毛发和甲</p><p class="art_p">文：大王</p><p class="art_p">本文为春雨医生原创作品，授权新浪健康发布，如需转载请联系春雨医生。</p>', 1525319841, 0, 13),
(173, '内裤一天不换 你知道有多脏吗', 'uploads/vAAv-fyctevp632597441858.jpg', '<p class="art_p">文章来源：科普中国微平台</p><p class="art_p">本文专家：周晓博，上海交通大学医学博士</p><p class="art_p">内裤是每个人生活中的必需品，千万不要小看内裤对我们身体健康的影响。</p><p class="art_p">有的人能做到勤洗勤换内裤，有的人则发扬了“新三年，旧三年，缝缝补补又三年”的精神，常常穿烂、变形之后才舍得扔掉内裤……</p><p class="art_p">那么，你知道内裤穿一天的卫生状况有多令人堪忧吗？我们应该多久换洗一次内裤呢？又该如何挑选适合自己的内裤？今天就和大家聊聊换洗内裤的那点儿事。</p><p class="art_p">内裤穿一天后的卫生状况是什么样？</p><p class="art_p">一条内裤穿一天之后的卫生状况到底是怎样的呢？</p><p class="art_p">微生物学家曾进行过研究，一条脏内裤平均带有0.1克粪便，在人的排泄物中，有四分之一的水分和未消化的食物残渣，还有脱落的肠上皮细胞以及肠道微生物。</p><p class="art_p">除了常规的大肠杆菌双歧杆菌等，如果宿主本身患病，还可能有甲肝病毒、轮状病毒等。</p><p class="art_p">甚至有报道说“一克粪便中含有1000万个病毒、100万个细菌、1000个寄生虫包囊和100个虫卵。”</p><p class="art_p">其实早在1964年，英国的卡梅伦医生就检查了英格兰牛津郡940位男子的内裤，发现这些人的内裤上都有从“黄蜂颜色”到“大量粪便”等不同等级的粪便污染情况。</p><p class="art_p">内裤该多久洗一次？</p><p class="art_p">这取决于大家对脏内裤的耐受程度，&nbsp;不过，从性价比的角度来说，建议每天换洗 1 次。</p><p class="art_p">考虑到去污，杀菌，便利等多方面因素，推荐用洗衣消毒液浸泡后，然后用洗衣液或洗衣粉手洗。或者使用清洁剂揉洗后，在太阳下晾晒，通过紫外线起到杀菌作用。</p><p class="art_p">内裤该多久换一次？</p><p class="art_p">从时间成本上来看，很多人不能做到每天洗，不能勤洗但至少要做到勤换。</p><p class="art_p">不止一份临床研究指出过，勤换内裤是避免泌尿生殖系统患病的一个重要保护性因素。</p><p class="art_p">在一份“育龄女性尿路感染危险因素”的研究中指出，根据多因素非条件logistic回归分析，更换内裤频率与对降低尿路感染具有显著性影响。而另一份对于阴道炎的研究中也指出，每日更换内裤的女性阴道炎发病率最低。</p><p class="art_p">所以，为了避免疾病，内裤每天一换还是有必要的，有时候出汗多，一天两换也可以接受。</p><p class="art_p">内裤穿多久要换新的呢？</p><p class="art_p">很多时候我们发现，新买回来的内裤，柔软蓬松。但是过不了一段时间，洗干净晾干后变硬了。</p><p class="art_p">这是因为内裤一般都是全棉或是混棉材质，全新内裤线与线之间距离较远，细小的纤维能够张开，所以呈一种蓬松、柔软的状态。</p><p class="art_p">但是，使用过程中的内裤，因为穿得多了、洗得多了、搓得多了，棉线上的纤维会脱落甚至断裂，所以就会变得越来越硬。</p><p class="art_p">而内裤作为棉织物，棉的耐光性和耐热性一般，这样暴晒会使棉纤维老化脱落，所以久而久之内裤裆部就会变得更硬。</p><p class="art_p">正常使用情况下，一般3~6个月就会出现这种情况，所以当发现内裤变硬不再舒适时，就应该及时更新。</p><p class="art_p">如何挑选适合自己的内裤？</p><p class="art_p">内裤的材质主要有棉、锦纶、莫代尔等。</p><p class="art_p">经济性+性价比：选棉质</p><p class="art_p">其中棉产量高，历史应用最久，舒适度大多数人都可以接受，如果考虑经济性和性价比这是不二的选择。</p><p class="art_p">经常出汗：选锦纶或莫代尔</p><p class="art_p">如果经常运动爱出汗，那么吸湿的棉则非常不推荐，而锦纶或者莫代尔的排湿能力具有明显优势，价格上虽较高一筹，但能在夏天也带来清爽与舒适。</p><p class="art_p">还有其他小众材料如加入了纳米银抗菌涂料等，都是针对特定需求而衍生出来的产品。</p><p class="art_p">选择适合自己需求的材质后，还有一点很重要，内裤不建议选择太紧身的，因为密闭的私处环境会更加温湿，因而成为微生物滋生的温床，适度宽松透气才是合适的选择。</p><p class="art_p">因此，每日更换，并且兼顾清洁与杀菌是健康的重要保护性因素。此外，我们需要及时发现内裤手感的变化，如果有变硬毛糙的情况，应及时换新，并结合个人的生活模式选择适应的内裤。</p>', 1525315538, 0, 13),
(174, '空心西红柿是打了激素？', 'uploads/oDsP-fyqzcxi172497229609.jpg', '<p class="art_p">文章来源：科普中国微平台</p><p class="art_p">本文专家：王思露，国家二级公共营养师，中华人民共和国HACCP食品体系内部审核员</p><p class="art_p">本文审稿：刘少伟，华东理工大学食品药品监管研究中心副主任、教授，美国宾夕法尼亚州立大学食品科学博士，美国堪萨斯州立大学博士后</p><p class="art_p">西红柿，作为一种既可当蔬菜也可当水果的“两栖食物”，一直以来受到各种人群的青睐。</p><p class="art_p">最近，网络上出现了一篇关于《空心西红柿是因为打了激素》的文章，言论一起顿时引来一片热议。有人表示再也不敢吃西红柿了。</p><p class="art_p">那么，空心西红柿真的和激素有关吗？今天就为大家科普一下！</p><p class="art_p">西红柿为何会出现“空心”呢？</p><p class="art_p">网传西红柿空心主要是由于在生长过程中使用了“植物激素”，其实这一税法没有任何科学依据。换一种角度说，西红柿空心可能和很多原因相关，但唯独和“激素”没有关联。</p><p class="art_p">其实，西红柿空心主要是由于一些“外界条件”和“人为因素”所导致。比如在生长过程中养分供应不足、外界温度过高、施肥不当、光照不充分以及受精不当都有可能导致西红柿出现“空心”的情况。</p><p class="art_p">“空心果实”对身体是否有害？</p><p class="art_p">说到植物的“空心现象”，其实是瓜果等植物果实在种植生长过程中经常会遇到的一种“生理性病害”，换一种角度说空心西红柿其实就是一种“畸形果实”。</p><p class="art_p">与正常西红柿相比，只是形态（长相）上有所不同，可能吃起来口感略差，其余像江湖中流传的“营养价值降低、对身体有害（有毒）”的传言并不存在。</p><p class="art_p">畸形西红柿如何产生？和植物激素有没有关系？</p><p class="art_p">西红柿在生长过程中能否发育成“正常形状果实”主要取决于“西红柿发芽分化”的质量。比如，西红柿在幼苗期花序形成之时遇到氮肥施多、水分多以及低温等情况，这时花芽就会过度分化，西红柿果实就会变为“桃形、指形或者是瘤形”等畸形果实。</p><p class="art_p">有人说“西红柿畸形”是由于使用了植物激素，植物激素在一定程度上的确会促进西红柿果实的坐果和果实膨大，使用浓度过高可能会导致西红柿出现空心的情况。</p><p class="art_p">但这样操作其实没有任何意义，植物激素（植物生长调节剂）在使用的过程中一般用量“微小”，一旦大量使用反而还会起到反作用。所以农民在种植过程中一般不会大量使用，也就不会出现由于激素所导致的“空心”问题。</p><p class="art_p">植物激素有毒吗？</p><p class="art_p">植物激素，一直以来受到各方的热议，对于它有毒致癌的言论没有任何科学依据。到目前为止，没有发生过任何由于植物激素残留所导致的食品安全事件。就算在植物生长的过程中使用植物激素，对于消费者而言也不会存在安全风险，相对安全，您大可不必担心。</p><p class="art_p">对于植物激素而言，现代农业的发展离不开这类物质，我们国家对于植物生长调节剂的使用有着明确的管理规定，在使用类别和用量方面做出了严格的限制。更何况对于植物而言，它起到的作用非常重要（促进生长、提高产量、提升价值），对于人体作用极小，请大家放心且“理性”对待。</p><p class="art_p">该如何挑选西红柿？</p><p class="art_p">看外表</p><p class="art_p">购买西红柿建议选择颜色粉红、表皮有斑点的；另外西红柿果蒂部位需圆润、带有些许淡绿色，这样的柿子相对较甜较沙。</p><p class="art_p">轻捏辨“饱满”</p><p class="art_p">在购买西红柿的时候可以轻轻捏一下，手感饱满、水分充足的是优质西红柿，如果出现空空的感觉相比之下就可能是口感不太好的“空心西红柿”。</p>', 1525312116, 0, 13),
(175, '每天一个鸡蛋可降低中风危险', 'uploads/jEMu-fyqnicm053673486031.jpg', '<p class="art_p">鸡蛋营养丰富，但其中的胆固醇含量令人担忧。每个鸡蛋大概含141–234毫克胆固醇。</p><p class="art_p">近期，中国一项研究对2.8万名没有心血管疾病人群进行了近10年的随访发现，每天一个鸡蛋不增加心血管死亡风险。</p><p class="art_p">研究组同期进行了超过160万人的Meta分析发现，每天一个鸡蛋似乎可降低9%的中风危险。</p><p class="art_p">作者认为，该研究支持将鸡蛋作为健康饮食的一部分。</p><p class="art_p">之前美国和日本的研究也证明，吃鸡蛋降低中风的危险。还有研究发现，吃鸡蛋还可降低25%的脑出血风险。</p><p class="art_p">一个鸡蛋7种功效</p><p class="art_p">1、健脑益智</p><p class="art_p">鸡蛋黄中的卵磷脂、甘油三脂、胆固醇和卵黄素，对神经系统和身体发育有很大的作用，可避免老年人的智力衰退，并可改善各个年龄组的记忆力。</p><p class="art_p">2、保护肝脏</p><p class="art_p">鸡蛋中的蛋白质，对肝脏组织损伤有修复作用。蛋黄中的卵磷脂可促进肝细胞的再生，还可提高人体血浆蛋白量，增强肌体的代谢功能和免疫功能。</p><p class="art_p">3、防治动脉硬化</p><p class="art_p">美国营养学家和医学工作者用鸡蛋来防治动脉粥样硬化，获得了出人意料的惊人效果。</p><p class="art_p">4、预防癌症</p><p class="art_p">鸡蛋中含有较多的维生素B2，可以分解和氧化人体内的致癌物质。鸡蛋中的微量元素，如硒、锌等，也都具有防癌作用。</p><p class="art_p">5、延缓衰老</p><p class="art_p">鸡蛋含有人体几乎所有需要的营养物质，故被人们称做“理想的营养库”，营养学家称之为“完全蛋白质模式”，是不少长寿者的延年经验之一。</p><p class="art_p">6、有助减肥</p><p class="art_p">鸡蛋的营养价值虽然很高，但是它的脂肪含量和碳水化合物的含量很低，经常食用不会摄入多余的热量，更不会引起肥胖。早上吃一个鸡蛋可以让一个上午都感觉很饱，不会有想吃东西的欲望，从而减少热量的摄入，因此，经常食用鸡蛋可以达到减肥的效果。</p><p class="art_p">7、保护视力</p><p class="art_p">蛋黄中的两种抗氧化物质——叶黄素和玉米黄素，能保护眼睛不受紫外线伤害。它们还有助于减少患白内障的风险。早上吃个鸡蛋，对用眼过度的电脑族也大有益处。</p><p class="art_p">煮鸡蛋营养最高</p><p class="art_p">因为就鸡蛋营养的吸收和消化率来讲，煮蛋为100%，炒蛋为97%，嫩炸为98%，老炸为81.1%，开水、牛奶冲蛋为92.5%，生吃为30%~50%。</p><p class="art_p">但要注意，儿童还是吃蒸蛋羹、蛋花汤最适合。</p><p class="art_p">每天一个即可</p><p class="art_p">因为鸡蛋是高蛋白食品，如果食用过多，可导致代谢产物增多，同时也增加肾脏的负担。</p>', 1525311541, 0, 13),
(176, '为什么突然起身容易头晕', 'uploads/yRdL-fztkpip257846354249.jpg', '<p class="art_p">想必很多人都有这个经历，躺着或是蹲坐着的时候，突然站起来，就会开始感觉到头晕、眼前模糊、严重的时候还会有晕厥的症状，稍过几分钟又恢复正常。这不禁让人怀疑，这是贫血还是低血糖了？亦或是其他更加严重的病的前兆呢？下面就来解释一下其中原因吧！</p><p class="art_p">一、很可能是体位性低血压惹的祸</p><p class="art_p">1、体位性低血压究竟是什么？</p><p class="art_p">体位性低血压又叫直立性脱虚。通俗易懂来说，就是在突然站立的情况下，头部的位置迅速升高，心脏还没来得及把足够的血液运输到大脑，大脑暂时性的缺氧，产生晕眩或者昏倒等现象。</p><p class="art_p">2、易发人群</p><p class="art_p">体位性低血压一般容易出现在儿童、身体虚弱的女性、高龄老人、正在服用某种特殊药物的患者。但并不是说明，出现体位性低血压就是说明身体虚弱不健康。</p><p class="art_p">二、如何预防体位性低血压</p><p class="art_p">1、在蹲下起身时，最好保持较为缓慢的速度，不要太迅速的站立，让身体能有时间做出反应。不仅能给大脑时间反应，也能避免很多磕磕碰碰等意外发生。</p><p class="art_p">2、在感冒发烧、腹泻、酒后等情况下，要多加注意，并及时的补充水分。虽说头晕目眩看起来似乎和补充水分没有直接的关系，但其实人体水分充足时，能加快血液流通速度。特别是夏天炎热的天气，容易出汗，如果身体水分流失过多又不及时补水，会直接影响到血液流通的速度。</p><p class="art_p">3、如果是在服用降压药、镇静药等药物时期，要提高注意，站立起身时最好扶靠支撑物，避免突然昏厥摔倒。</p><p class="art_p">4、平时应多运动，加强锻炼，增强自身体质方能提高免疫力，同时还需要均衡饮食。</p><p class="art_p">5、以上几点，是在平日里需要注意的。在必要时，也可以接受艾灸治疗，持之以恒便可以收到明显的效果。</p><p class="art_p">三、难道真与贫血和低血糖无关？</p><p class="art_p">其实并不是的，贫血和低血糖患者也会出现这种状况，不过与体位的突然变化并没有直接的关系，而是在任何情况下都有可能出现晕眩等现象。所以应诊断区分贫血、低血糖、体位低血压这三者的表现。</p><p class="art_p">如果这种因体位的突然变化而产生晕厥现象，只是偶尔出现一两次，则不需过于担心。但如果平时里，经常伴随着脸色苍白、手脚无力等现象，就应该及时就医检查，对症下药，以免耽误治疗。</p>', 1525226184, 1, 13),
(177, '红枣泡水有八大禁忌', 'uploads/kxyQ-fyiphwc232063227270.jpg', '<p class="art_p">红枣泡水可以帮助我们养肝排毒，不过在用红枣泡水时是有讲究的。如果方法出现了错误就会影响到我们的身体健康。今天，要为大家介绍的就是红枣泡水的禁忌，千万别错过了。</p><p class="art_p"><strong>红枣泡水方法</strong></p><p class="art_p">干红枣大小各异，虽然在营养和食疗功效上差别不大，但从口味上建议，泡水泡茶时选用大个的红枣，特别是新疆产的红枣，口感很甜，最好将其撕成几半再用；熬粥、泡酒等则可随意选择。</p><p class="art_p">大枣有益健康，但并非多多益善。中等大小的红枣，一次食用最好别超过15个，过量食用有损消化功能，引起胃酸过多和腹胀。</p><p class="art_p">红枣泡水可以帮助我们养肝排毒，不过在用红枣泡水时是有讲究的。如果方法出现了错误就会影响到我们的身体健康。今天，要为大家介绍的就是红枣泡水的禁忌，千万别错过了。</p><p class="art_p"><strong>红枣泡水方法</strong></p><p class="art_p">干红枣大小各异，虽然在营养和食疗功效上差别不大，但从口味上建议，泡水泡茶时选用大个的红枣，特别是新疆产的红枣，口感很甜，最好将其撕成几半再用；熬粥、泡酒等则可随意选择。</p><p class="art_p">大枣有益健康，但并非多多益善。中等大小的红枣，一次食用最好别超过15个，过量食用有损消化功能，引起胃酸过多和腹胀。</p><p class="art_p">糖尿病患者慎服。红枣中所含有的糖分比较多，如果是晒干之后的红枣含有的糖分就更多。如果是糖尿病患者进补，千万不要选择红枣，或者是服用红枣的时候一定要慎重，避免身体出现血糖增加的情况。如果糖尿病患者日常没有节制，那就很有可能导致病情恶化。</p><p class="art_p">大枣吃法有讲究。大枣的吃法也是非常具有讲究的，想要令身体获得最大的营养物质，减少出现副作用，那么泡水或者是水煮是最好的选择。因为这样不仅不会改变药效也不会导致营养流失，并且还可以有效的避免服用之后出现拉肚子的可能。</p><p class="art_p">那么，红枣要如何清洗呢？</p><p class="art_p"><strong>红枣的清洗方法</strong></p><p class="art_p">不要使用普通的洗涤剂清洗红枣，洗涤剂本身含有的化学成分容易残留在红枣上，对人体健康不利。但是，绝对不要在水中浸泡过长时间，否则红枣内的维生素会悉数流失，使营养价值降低，而且溶解于水的农药有可能会反渗入红枣中。</p><p class="art_p">红枣泡水虽然功效多，也要多注意以上几点哦。</p>', 1525049870, 0, 13),
(178, '四类食物难吃却能延寿', 'uploads/3o9D-fyssmmc078832556227.jpg', '<p class="art_t">一碗香米饭和一个糙窝头摆在面前，你会选择哪一样？在一块西瓜和一瓣柚子之间，你会选择哪个做餐后水果？餐桌上看到洋葱和大蒜，你会忽略它们吗？ 生活中，我们往往热爱那些甜美、精细、香浓的食物，不喜欢苦涩、粗糙、味淡的食物。而恰恰在这些口感差的食物当中，存在着令人意想不到的营养和健康元素。</p><p class="art_t"><strong>粗糙的食物</strong></p><p class="art_t">有些人偏爱精细的香米和白面制品，不喜欢糙米和窝头，因为它们吃着“扎嗓子”。其实“扎嗓子”的正是宝贵的膳食纤维，它能刺激肠道蠕动，帮助排 便，并改变肠道微生物的种类及数目，降低致癌物生成。膳食纤维广泛存在于口感粗糙的食物当中，包括全谷类的米、麦及杂豆，如糙米、燕麦、红豆等。为预防便秘和肠癌，每日应保证摄入20—30克的膳食纤维。建议早餐放弃传统的白米粥，改喝杂粮粥，精细白米饭改为全麦馒头。</p><p class="art_t"><strong>发涩的食物</strong></p><p class="art_t">单宁、植酸和草酸的存在会让食物发涩，这是因为它们会和口中的唾液蛋白结合并沉淀，使唾液失去润滑性，舌上皮组织收缩，产生涩味。但这些物质都具有很强的抗氧化性，对预防糖尿病和高血脂有益。比如橄榄、紫葡萄皮、苹果皮、核桃仁皮，它们都比果肉具有更强的抗氧化作用。建议苹果和葡萄最好带皮一起吃，吃核桃仁时不要把那层褐色的皮去掉。</p><p class="art_t"><strong>味酸的食物</strong></p><p class="art_t">和甜橙、西瓜、香蕉相比，爱吃沙果、山楂等酸味水果的人相对要少。其实，这类水果的酸味主要来自于柠檬酸、苹果酸等有机酸，这些天然的酸性物质能促进铁等矿物质的吸收。如果感觉酸味食物口感不好，可以把它们做成水果羹，加少量冰糖，放入冰箱冷冻，口感会大有改善。</p><p class="art_t"><strong>发苦的食物</strong></p><p class="art_t">食物中的天然苦味物质也具有一定的保健作用，如柠檬和柚子当中的柚皮甙，茶里面的茶多酚，红酒里面的多酚以及巧克力里面的多酚，都是有助预防癌症和心脏病的成分。苦瓜中的奎宁精，还能提高人体免疫力，帮助控制血糖。所以，水果一定要搭配着吃，不能光吃甜的，还要适当吃点苦的、酸的。吃过的柚子皮，不要扔掉，可以切成条后做成蜂蜜柚子茶。对于苦瓜而言，将其用热水焯一下，冰镇后可蘸蜂蜜食用。</p>', 1525049691, 0, 13),
(179, '夏季谨防4种皮肤病', 'uploads/C8nW-fzrwiay937193773573.jpg', '<p class="art_p">北京中医药大学附属第三医院皮肤科副主任医师　徐国梅</p><p class="art_p">夏天日光强烈、气候炎热，有时湿度又大，容易引发各种皮肤问题，以下几种需要特别注意。</p><p class="art_p"><strong>痱子</strong>。痱子是由于汗孔阻塞引起汗腺的轻度发炎，初期时皮肤发红，然后出现针头大小的红色丘疹或丘疱疹，密集成片，其中有些丘疹呈脓性。生了痱子后剧痒、疼痛，搔抓后还会有灼痛等表现。痱子多出现在颈、胸背、肘窝、腘窝等部位，小孩可发生在头部、颈背部等多汗部位。常用干毛巾擦汗或用温水勤洗澡等方法可防止痱子的发生。痱子发生后，要避免搔抓，防止继发感染。可局部外用洗剂，如1%薄荷炉甘石洗剂、1%薄荷酊，清凉止痒。瘙痒明显时建议口服抗组胺药。</p><p class="art_p"><strong>日光性皮炎</strong>。日光性皮炎又称“多形性日光疹”，是一种常见的光感性皮肤病。表现为面、颈、前臂伸侧、手背等暴露部位出现红斑、丘疹、风团和水疱等皮疹。病变与日光照射密切相关，多于照射后皮损明显加重、痒感加剧。适当避光后则有好转，皮疹常反复发作，日久可发生苔藓样改变、色素增加。一般到秋季以后逐渐减轻，可持续多年。好发于中年女性、室外工作者及水面工作者。另外，有些食物（如灰菜、苋菜）和药物（如喹诺酮类药物）中含有光敏性物质，对这些物质过敏的人如大量进食并接受强光照射后会出现皮肤反应，引起皮肤红、肿、痛、水疱、发疹等症状。</p><p class="art_p">日光性皮炎较轻的患者可用抗组胺药，症状重者或疗效欠佳者口服小剂量糖皮质激素控制症状，用阿司匹林或吲哚美辛减轻疼痛感。</p><p class="art_p"><strong>虫咬性皮炎。</strong>主要和夏季蚊虫叮咬有关，以叮咬处出现丘疹、风团、水疱等为主要表现，中间可见虫子刺吮点，散在分布或数个成群。可发生于身体各部位，并伴有不同程度的瘙痒、刺痛感。症状明显者可以在医生指导下服用抗组织胺药物如盐酸西替利嗪、氯雷他定等。虫咬性皮炎重在预防：少带孩子去花草茂盛的地方玩耍，不在草丛中坐卧休息；使用蚊帐，小儿夏季常在室内活动，室内防蚊非常重要；减少接触宠物；被褥、床板经常在太阳下晾晒。</p><p class="art_p"><strong>花斑癣。</strong>又称汗斑，是一种慢性皮肤角质层真菌感染，多见于成年人，好发于胸、背、颈和上臂等处。开始时为黄豆大小的圆形斑，呈灰白色、褐色或淡黄色，上面附着细小糠皮样鳞屑，以后皮损越来越多，有时可融合成片。去除鳞屑后留下暂时性色素减退斑。病程缓慢，多年不愈，夏重冬轻，无自觉症状或有微痒。出现这种情况，需要在医生的指导下抗真菌治疗。</p>', 1525048952, 0, 13),
(180, '冠心病患者饮食有4大原则', 'uploads/0tS_-fykuffc507944359113.jpg', '<p class="art_p">据最新的中国心血管病报告，我国心血管病患者达2.9亿，每5个成年人里就有1人患有心血管病；我国一年约有350万人死于心血管病，每8秒就有1个人死于心血管病，占总死亡原因的41%，居死因之首。</p><p class="art_p">一说起饮食调理某种病，总有人讥笑“想得美”，不过，在广东省专科护士、健康管理师、广东省中医院“塑心学堂”讲师王晶晶看来，对于冠心病而言，饮食调整其实真的算一种疗法，她建议，患者首先要给自己订立日常饮食的四大原则，然后是正确选择有降脂、降压之功的食物。</p><p class="art_p"><strong>冠心病饮食有四大原则</strong></p><p class="art_p">冠心病与不良饮食、不良生活习惯息息相关，看看它的危险因素，除了年纪问题、家族史外，其他因素最“知名”的就是三高——高血压、高胆固醇血症、高血糖问题，而诱发因素就包括高盐、高脂、高糖的不良饮食因素，还有吸烟、好酒、少运动、肥胖等不良生活因素。所以，好好吃、好好喝，还真能帮到冠心病患者。首先，患者要遵循以下四大饮食原则：</p><p class="art_p"><strong>饮食原则1：</strong></p><p class="art_p"><strong>限制食盐，适当补钾</strong></p><p class="art_p">吃得太咸容易得高血压，而且盐摄入过多，会影响降压药物的效果，增加降压药用量。相反，如果每日食盐摄入量从10g减少至5g，收缩压和舒张压可下降10/5mmHg，这可是医学临床研究证实了的。</p><p class="art_p">建议：每日盐摄入量少于6g，一般饮料瓶一瓶盖正好10g盐，一般啤酒盖正好6g盐。</p><p class="art_p">钾能阻止过高食盐引起的血压升高，对轻度高血压具有降压作用，其机制可能与肾素释放减少有关。增加钾摄入量有利于钠和水的排出，有利于高血压病的防治。</p><p class="art_p">建议：多吃含钾高食物，如龙须菜、豌豆苗、莴笋、芹菜、丝瓜、茄子等。</p><p class="art_p"><strong>饮食原则2：</strong></p><p class="art_p"><strong>限制饱和脂肪酸、胆固醇</strong></p><p class="art_p">清淡饮食有利于高血压防治，而过量油腻饮食，不仅可致消化不良，还可令气血流通失常，甚至导致卒中、猝死。</p><p class="art_p">建议：每日供给40~50克脂肪，每天饮食里的胆固醇总量在300~400微克/天。</p><p class="art_p"><strong>饮食原则3：</strong></p><p class="art_p"><strong>补充钙镁和维生素</strong></p><p class="art_p">钙有利尿降压作用，能减少患高血压病的可能，增加镁的摄入，能使外周血管扩张，血压下降；维生素C和叶酸等，则有利于降血压。</p><p class="art_p">建议：多吃富含钙的食物，如牛奶、虾、鱼类、蛋类；多吃富含镁的食物，如香菇、菠菜、豆制品类等。</p><p class="art_p"><strong>饮食原则4：</strong></p><p class="art_p"><strong>控制能量摄入</strong></p><p class="art_p">因为肥胖而得冠心病者，限制能量摄入，体重就会降低，血压也随之下降，这也是肥胖型冠心病患者非常重要的治疗措施。</p><p class="art_p">建议：保持理想体重，一般蛋白质摄入占13~15%，脂肪占20%~25%，碳水化合物占65%。</p><p class="art_p">减肥不宜过“狠”，比如轻度肥胖者，限制脂肪、糖类，使总能量摄入低于消耗量，同时增加体力劳动和活动即可，比如每月体重真正下降0.5~1kg，慢慢来，更靠谱。</p><p class="art_p"><strong>这些食物可降压降脂</strong></p><p class="art_p">王晶晶指出，冠心病患者，通过食物选择，多吃某些东西，真的能保护血管、降压降脂。</p><p class="art_p">有降压功能的食物包括：芹菜、胡萝卜、番茄、荸荠、黄瓜、木耳、海带、香蕉等。</p><p class="art_p">有降脂功能的食物包括：山楂、香菇、大蒜、洋葱、海鱼。</p><p class="art_p">草菇、香菇、平菇、蘑菇、黑木耳、银耳等营养丰富，味道鲜美，还对防治高血压病、脑出血、脑血栓等有较好的效果。</p><p class="art_p">普洱茶的降脂和降血黏作用较好；多吃柑、橘、甜橙，柠檬，它们含的橘皮苷，可以加强毛细血管的硬度，预防动脉硬化，还能扩张冠状动脉，降血压；常吃苹果，能使血液里的胆固醇下降，降血压；牛奶是补充蛋白质和钙的好来源，并且可降低体内胆固醇。</p><p class="art_p"><strong>冠心病药膳食疗推介</strong></p><p class="art_p">芹菜红枣汤：芹菜根5个，红枣10个，水煎服，食枣饮汤，每日2次。</p><p class="art_p">山楂粥：红山楂5个，去核切碎，用蜂蜜1匙调匀，加在玉米面粥中服食，每日服1~2次。</p><p class="art_p">菊花山楂饮：菊花、生山楂各15~20克，水煎或开水冲浸，每日1剂，代茶饮用。</p><p class="art_p"><strong>一日饮水有门道</strong></p><p class="art_p">在人体中，水的比重占70%，大脑组织中水的比重也达80%，血液里的水更高达90%，就连骨骼里也有15%左右的水，可想而知饮水对于保障机体健康多么重要。</p><p class="art_p">对于冠心病患者来说，王晶晶建议“五一二”的每日饮水法：</p><p class="art_p">五个“一杯”：即起床后喝一杯温白开水，早餐时喝一杯豆浆，午餐时喝一杯量的汤，晚餐时喝一杯量的粥，睡前再喝一杯牛奶；二个“二杯”：即上午、下午各喝两杯茶。这样按时满足水分需求，又不会因短时大量补水造成身体不适。</p><p class="art_p"><strong>小贴士：教您健康煮食</strong></p><p class="art_p">蛋类：少用油煎炸，多用蒸煮，不能生吃，恰当加热。</p><p class="art_p">肉类：猛火快炒，适当加醋，荤素搭配，少加碱性原料，不应长时间冲洗、浸泡。</p><p class="art_p">蔬菜：先洗后切、切块不宜太细小，切后不浸泡，不用碱焯水、要火大水多、出锅前放盐，猛火快炒。</p><p class="art_p">大米：蒸煮饭前，减少米的淘洗次数，用沸水煮饭更能保存大米营养。</p><p class="art_p">面食：以蒸为主，加碱会破坏维生素B1。</p><p class="art_p">豆浆：切记煮沸才饮用。</p>', 1525048883, 0, 13),
(203, '媒体:香港为何不可再“食老本”和“等运到”', NULL, '<p class="art_p">香港的发展向来与国家紧密相连，如今享受粤港澳大湾区规划及“一带一路”建设保留席位的好运，更应善用机遇奋力拼搏，切莫在政治争拗中沉沦。</p><p class="art_p">香港特首林郑月娥日前说，香港不可以再“食老本”和“等运到”。此言引发热议。其实在香港社会，有关“食老本”忧虑已经存在多年。一则因香港有着引以为傲的家底，二则来自全球和国内一线城市的竞争越发激烈，香港社会近年却时有自耗自误，不免让人忧心。</p><p class="art_p"><strong>危机感</strong></p><p class="art_p">香港特首林郑月娥日前在香港立法会称，自去年10月发布施政报告至今，特区政府已落实了报告中提出的33项措施，约85%正按计划推动。香港不可以再“食老本”和“等运到”，而是需要巩固提升优势产业，开拓新经济，增强对外联系，用好“一国两制”及中央对香港支持的独特优势。“香港社会不应该、也不可以再内耗，争议不断，裹足不前。”</p><p class="art_p">关于“食老本”的忧虑和讨论，在香港社会早已有之。早在2012年，前行政长官梁振英便在网志中引用过余光中的观点：香港以前是中国内地的后门，广州、深圳都是腹地，在港英时期有着优越地理位置。但现在不同了，广州、深圳都起来了，上海也是一大竞争对手，香港的重要性已不同于冷战时期。</p><p class="art_p">翻阅媒体报道也不难发现类似提醒：香港的优势行业正面对内地一线城市的“挤出效应”，而深圳及上海等城市，于创新创业方面都迅速冒起，为香港带来竞争压力。内地城市与香港的竞争力差距愈缩愈小，在面对内地自贸区兴起时，香港有可能被边缘化。</p><p class="art_p">类似担心并非多余。在去年的中国城市竞争力排行榜上，香港排名第二，连续5年落后于上海。今年3月，内地和香港媒体一度热议“深圳GDP首超香港”，深圳市统计局方面随后解释说深港GDP实际尚有一定差距。但深圳无疑正呈强劲追赶之势。</p><p class="art_p">中国城市竞争力研究会主席桂强芳去年曾说，深圳的GDP在过去4年的平均增速均比香港快，如单看经济增长速度，深圳很快会在竞争力上超越香港。香港在创新方面竞争力不如深圳，要加强推动研发和创新。此番林郑月娥说，“香港要有更大的危机感。”此言不虚。</p><p class="art_p"><strong>有根基</strong></p><p class="art_p">说起“食老本”，香港确实有着过人的资本。简单的低税制、容许资金自由进出以及发达的银行和金融系统，保证了香港过往的亮眼发展。自1995年起，香港连续23年被评为“全球最自由经济体”。在12项评估因素中，香港有8项取得90分或以上，在“财政健康”“贸易自由”和“金融自由”方面更排名世界第一。司法制度优质、社会风气廉洁、监管制度高效以及市场开放也是传统优势。</p><p class="art_p">在经济发展方面，从1997年到2016年，香港GDP从1.37万亿港元增长到2.49万亿港元，年均增长3.2%。对于高度发达的经济体，上述增速值得肯定。在人均GDP方面，香港同样位居世界前列。作为全球最富裕经济体之一，香港发展自有根基。</p><p class="art_p">改革开放初期，香港是内地走向世界的桥头堡，并以内地作为其产业转移和发展的广阔腹地，在数十年中取得令人瞩目的成绩。1997年香港GDP相当于内地的18%以上，如今占比约3%。一方面需要看到，随着中国成长为世界第二大经济体，整个经济总量显著增大，香港仍发挥着不可替代的作用。一方面香港要以积极有为的心态加快发展，在中国城市的竞争中力争上游。</p><p class="art_p"><strong>“食老本”？</strong></p><p class="art_p">不可否认的是，当整个社会“食老本”的焦虑指数走高，说明优势正处在消减中，背后或许蕴藏着经济发展的结构性问题。对比邻居深圳，30多年前，其经济总量只有香港的千分之二。但经过数十年发展，尤其是近年把握全球互联网时代，大力培育高新产业，实现经济再起飞。目前坐拥腾讯、华为等特大型企业。</p><p class="art_p">有香港观察人士指出，如今香港经济发展仍依赖传统行业支持，有“食老本”之嫌。而监管条例越发复杂，经营成本增加，创新变得更加困难。作为亚太区金融市场的领导者，却至今缺乏金融创新的范例。</p><p class="art_p">而在依靠创新科技的新经济方面，有关方面的扶持不够有力有效，近期有民调指出，逾七成企业行政人员及四成半市民均认为，香港在培育创科文化方面落后于其他发达城市。</p><p class="art_p">专心谋发展已是时不我待，让人痛心的是，近年香港社会泛政治化的阴影下，议会拖延流会阻碍利民政策出台，社会上“港独”力量叫嚣作乱，离间人心，阻碍香港发展，白白断送良机。</p><p class="art_p">有评论称，曾几何时，香港以效率见称，港人的优势是做事爽快利落。但近年宝贵的时间往往荒废在内耗上，处事变得拖拉，令长期困扰香港的一些问题日趋严重，重大工程项目一再延误，包括港珠澳大桥、广深港高铁、西九文化区等。而事实是，问题并非不能解决。</p><p class="art_p"><strong>求转变</strong></p><p class="art_p">在香港传统优势相对减弱，新的增长点尚未有效形成之际，特区政府锐意谋求变革，比如将科技创新作为施政重点之一，力求奋起直追实现经济转型。香港交易所近期也迎来上市机制重大改革，小米随即提出申请。联系2013年阿里巴巴谋求在港上市失败而转赴美国，因时而变由此可见。</p><p class="art_p">在全球经济格局深度调整、国际竞争日趋激烈背景下，香港若要维持优势地位，抓紧机遇并适应变革势在必行。面对来自全球和国内一线城市的强劲竞争对手，拥有不错家底的香港，更要爱惜羽毛。</p><p class="art_p">拥有传统优势的香港金融、物流贸易、旅游、专业服务四大支柱产业，需积极谋求转型，寻求多元化发展。致力发展创新科技，也将为经济注入新活力。更重要的是，香港的发展向来与国家紧密相连，如今享受粤港澳大湾区规划及“一带一路”建设保留席位的好运，更应善用机遇奋力拼搏，切莫在政治争拗中沉沦。</p>', 1525693708, 0, 1);
INSERT INTO `pre_news` (`news_id`, `news_title`, `news_pic`, `news_content`, `news_time`, `news_like`, `news_cate_id`) VALUES
(204, '自媒体涉嫌诽谤伊利案侦办细节:潘刚本人书面报案', NULL, '<p class="art_p">新华社北京5月7日电题：网络自媒体不是“法外之地”</p><p class="art_p">——邹光祥、刘成昆涉嫌诽谤罪案件追踪</p><p class="art_p">新华社记者</p><p class="art_p">近年来，一些网络自媒体编造传播经济领域虚假信息事件时有发生。这些谣言以其较大传播力和迷惑性，频频引发市场波动和公众恐慌，扰乱市场经济秩序，影响经济健康发展。究其原因，少数自媒体野蛮生长，以谣博名、以谣博利是根源。</p><p class="art_p">“我投资了1800万元，还背负了600万元的外债，这个消息出来之后，没有人愿给我们贷款了。新生的小牛也不敢养，都给卖了。”4月29日，内蒙古呼和浩特市。面对记者，和林格尔县盛乐镇奶农王国兴充满愤慨。</p><p class="art_p">3月26日，一条“伊利股份董事长潘刚被带走协助调查”的消息，在各网站和社交媒体上大量传播，让与伊利公司相关的奶农、上下游合作商、企业员工以及资本市场投资者感到恐慌。</p><p class="art_p">接到伊利公司和潘刚本人书面报案后，呼和浩特公安机关依法开展侦查，以涉嫌寻衅滋事罪、诽谤罪将犯罪嫌疑人邹光祥、刘成昆抓获。目前检察机关已依法对二人批准逮捕。</p><p class="art_p">记者从公安机关了解到侦办此案的经过。</p><p class="art_p"><strong>谣言来源：影射伊利高层的“网络小说”</strong></p><p class="art_p">“今晚我要写篇短篇小说，超级重磅，大家敬请期待。”3月24日，微信公众号“天禄财经”作者刘成昆发了一条微信朋友圈。</p><p class="art_p">随后几天里，“天禄财经”公众号发表了《出乌兰记——盘先生在美丽坚》《出美丽坚记——盘先生回乌兰配合调查》《出乌兰记——童话故事》三篇文章。</p><p class="art_p">这些文章暗示性、指向性特征十分明显，让看完文章的人直接把“小说”所指与伊利公司董事长潘刚联系在一起。</p><p class="art_p">微信公众号“光祥财经”作者邹光祥看到刘成昆所写的第一篇文章后，主动添加刘成昆的微信，询问有关情况。刘成昆告诉邹光祥：“他最近回来了，下了飞机在机场就被带走去调查，最近刚回来，这两天的事儿，所以我的消息有点滞后。”</p><p class="art_p">3月26日上午，邹光祥打电话向伊利公司求证，被告知是谣言后，他仍写下了《公司聚焦：伊利股份董事长潘刚或“失联”》的文章称：“光祥财经获悉，潘刚已于近期回国，但很快被有关部门带走并协助调查。”</p><p class="art_p">文章发出后，伊利公司联系邹光祥，表示所发文章内容失实，请其删除稿件。邹光祥再次询问刘成昆并得知刘并没有直接证据后，依旧没有删除稿件。于是，潘刚被带走调查的消息继续在互联网和社交平台大量转发。</p><p class="art_p">3月26日晚，呼和浩特公安机关接到伊利公司及其董事长潘刚报案。潘刚在书面报案材料中写道：“不实文章谣传我被有关部门带走调查并‘失联’的情况纯属捏造，恶意诽谤。”</p><p class="art_p">伊利公司也第一时间发布声明和公告辟谣，但仍难以阻止谣言的继续传播。</p><p class="art_p"><strong>造谣传谣：让公众号“火”起来以博取名利</strong></p><p class="art_p">据伊利公司对外发布的公告，董事长潘刚因患先天性主动脉缩窄正在国外接受治疗。在潘刚本人的书面报案材料中，他也向公安机关提供了他于当地时间3月26日上午11︰16在美国取药的书面证明，证明其当时未离开美国回国。公安机关核实，潘刚于2017年9月5日出境后，未有入境记录。</p><p class="art_p">4月30日，记者与潘刚视频连线，他表示，“治病期间，正常的文件的审批都是通过网络进行审批，一些重要的会议也通过电话、视频参与。”</p><p class="art_p">办案人员先后依法对犯罪嫌疑人刘成昆、邹光祥进行传讯。邹光祥承认了其捏造了“潘刚被带走接受调查”的消息，其信息来源正是刘成昆发布在微信公号“天禄财经”上的“小说”。</p><p class="art_p">在“小说”开头，刘成昆虽然标明“本故事纯属虚构，如有雷同，纯属巧合。勿将现实人物对号入座”，然而文章中“盘盘去美丽坚果国弗哈大学讲课”“30岁就当了匈奴股份董事长”这些令人浮想联翩的关联词语以及与潘刚本人的经历高度相似的情节真的是完全虚构，纯属巧合吗？</p><p class="art_p">在警方依法提取的刘成昆手机存储的聊天记录中，或许能找到答案。</p><p class="art_p">早在策划“天禄财经”微信号时，刘成昆就与同行商量如何通过爆企业、老板“黑料”赚取“粉丝”和“广告费”。</p><p class="art_p">在商讨涉及“伊利公司董事长外逃”的“新闻”时，刘成昆对该同行表示：“只能用小说的形式，信息源不扎实。”</p><p class="art_p">“可以搞一个小说系列，不好写还没实锤的都用小说写法写，估计很快粉丝就有了。”</p><p class="art_p">刘成昆第二篇文章《出美丽坚记——盘先生回乌兰配合调查》发出后，面对影射伊利的质疑，刘成昆在微信中对某自媒体从业者说：“这个事不怕啊，反正我说是小说，再说了我又没指名道姓。”</p><p class="art_p">刘成昆到案后也承认，故事情节确是影射伊利公司和潘刚本人。“我的直接目的就是想引起公众的注意，做大公众号，让‘天禄财经’公众号火起来后，我也可以成为社会名人，就有人给我投资广告了，这是我的赢利点。”</p><p class="art_p"><strong>自媒体非“法外之地”：情节严重将追究刑责</strong></p><p class="art_p">“造成这个局面我觉得很痛心，很后悔，也很害怕。我向伊利公司员工、奶农、经销商道歉。”看到其散布的虚假信息已然造成的严重后果，刘成昆后悔不迭。</p><p class="art_p">依据我国刑法，捏造事实诽谤他人，情节严重的构成诽谤罪。</p><p class="art_p">诽谤罪多为自诉，严重危害社会秩序和国家利益的诽谤罪适用公诉程序除外。刘成昆和邹光祥的行为是否适用于公诉程序？</p><p class="art_p">依据最高人民法院、最高人民检察院《关于办理利用信息网络实施诽谤等刑事案件的司法解释》，同一诽谤信息实际被点击、浏览次数达到五千次以上，或者被转发次数达到五百次以上的，应当认定为情节严重；引发公共秩序混乱的，应当认定为严重危害社会秩序和国家利益。</p><p class="art_p">北京通达法正司法鉴定中心出具的鉴定意见书显示，邹光祥在微信公众号“光祥财经”发布的文章，点击数达574.5万次；刘成昆在微信公众号“天禄财经”发布的3篇文章，点击数达10993次。</p><p class="art_p">根据上海证券交易所法律部等有关部门提供的证明材料，3月26日伊利股票市值较前一交易日减少60.78亿元。</p><p class="art_p">“根据相关证据和犯罪嫌疑人供述，符合诽谤的构成要件”，呼和浩特市人民检察院侦查监督处检察官邢浩宇说，“诽谤信息被大量转发，混淆视听，蛊惑群众，对整个市场造成恶劣影响，适用公诉程序。”</p><p class="art_p">对于刘成昆在文章中声明内容纯属虚构是否影响其行为构成诽谤，邢浩宇表示，根据相关法律，行为人虽然没有直接提及被诽谤人的姓名，但从诽谤的内容足以推知被诽谤人明确身份的，可以认定为诽谤。</p><p class="art_p">记者在采访中还了解到，伊利公司近日还受到另一起案件的困扰。2018年3月8日，“中国资讯报道”网站刊登一篇题为《内蒙古伊利集团公司如此欺压奶农，谁来保护弱势奶农利益诉求》的文章，两天内被70多家网站转载，点击量1万余次，此文与事实严重不符，影响企业的正常生产经营秩序。</p><p class="art_p">接到报案后，呼和浩特公安机关依法立案，经侦查锁定苏某某、陈某某、郭某、侯某某、史某某、郭某某等6名犯罪嫌疑人。公安机关查明，伊利公司曾发现郭某某的奶站有串奶行为、影响奶品安全，要求后者关停奶站。郭某某向伊利公司索赔400万元未果后，指使史某某捏造上述不实文章，并且经苏某某、陈某某、郭某、侯某某等人，有偿发帖，借此进一步向伊利公司索赔1600万元。</p><p class="art_p">经侦查，公安机关还发现，长期以来，苏某某利用其掌控的53家网站及其他有密切联系的网站，以收费发帖、删帖非法牟利；陈某某、郭某利用二人掌控的150余家网站发帖非法牟利；侯某某供认，其长期从事有偿发帖删帖，非法获利约10万元。目前，6名犯罪嫌疑人均已被检察机关批准逮捕。</p><p class="art_p">公安机关表示，利用网络散布虚假信息，特别是图一己之利损害公共利益，扰乱正常社会秩序和市场秩序，社会危害性极大。网络空间不是“法外之地”，对利用网络从事非法活动的违法犯罪人员，公安机关将依法惩处。（完）</p>', 1525692914, 1, 1),
(205, '中纪委重要部门保密制度曝光 员工:至少得经住3问', NULL, '<p class="art_p">来源：长安街知事</p><p class="art_p">涉密事项多、密级高，常绷安全“弦”，常念保密“经”……在中央纪委机关，有这样一个重要部门，每天都要处理大量涉密工作。他们的保密工作是如何做的？日前，中纪委揭开了它的神秘面纱。</p><p class="art_p">会议处是中央纪委办公厅下属的10个处室之一，2014年3月成立。顾名思义，会议是其核心职责，但并不限于此，还包括活动、调研，工作点多面广，综合了组织协调、文字综合、服务保障等多方面内容。</p><p class="art_p">2014年3月成立以来，会议处共筹备组织各类会议活动1800余次，起草领导讲话、主持词、活动方案、会议纪要、情况报告等各类文稿2100余件，整理加工有关讲话400余篇270余万字……一组组数字，显示出会议处的巨大工作量。为了每次会议活动的顺畅高效，确保会前准备、会中服务、会后督办等各环节“零差错”，背后蕴含着大量的繁细工作。</p><p class="art_p">“会务工作远非看上去的‘摆摆桌牌’‘发发文件’‘摁摁电梯’那么简单，需要统筹协调多方面工作。”会议处的同志说，会务工作是“系统工程”，需要统筹布局。</p><p class="art_p">工作中，会议处要求处内同志对基本情况、最新进展、工作程序等做到“一口清”，“历史传统是什么？有什么政策文件依据？其他单位怎么做的？”会议处同志笑着说，“你得至少经得住领导三问”。</p><p class="art_p">处里面出去的每份文件，都要经过双人校核，像常委会会议纪要这样的重要文稿，在印发前会经过4轮双人校核，以确保无误。</p><p class="art_p">对照会议处每月值班表，翻开会议处值班本，发现值班记录涵盖一年365天，每天除了有固定值班的同志，还记录着或多或少的加班同志，有的名字下面还标注着“12点”，这是指加班超过了晚上12点。</p><p class="art_p">会议处涉密事项多、密级高。常绷安全“弦”，常念保密“经”，在会议处显得尤为重要，必须严格执行各项安全保密制度。“处里3个房间，每个房间都确定了安全保密责任人。”会议处负责同志说，纪律要求融入日常习惯，就是出去上个厕所，大家都会自觉把门锁上，更不用说锁好文件柜、及时销毁涉密文件等“常规动作”了。</p><p class="art_p">“机关许多处室都很优秀，是我们学习的榜样。与其把这次获评看作是对过往工作的肯定，不如看作是对今后工作的激励和鞭策。”会议处负责同志用质朴的语言道出了全处同志的深切感受：对我们来说，最根本的是永葆政治本色，一如既往踏踏实实干好工作，享受奋斗的幸福，为党风廉政建设和反腐败事业作更多贡献。</p>', 1525691275, 0, 1),
(206, '韩板门店迎旅游热:场景模型前体检“文金”握手', NULL, '<p class="art_p">上个月27号文在寅与金正恩在板门店握手，成为历史性的一刻——而这也引发了人们对板门店的“兴趣”。</p><p class="art_p">不过，板门店可不能随便“观光”，于是近日，不少韩国游客纷纷前往一处“板门店模型”前，模仿韩朝领导人握手并拍照留念。</p><p class="art_p"><img  src="https://n.sinaimg.cn/translate/126/w555h371/20180507/aGJA-hacuuvu2516429.jpg" alt=""></p><p class="art_p">在上月两国领导人举行会晤后，许多韩国游客都希望能前往板门店亲身感受一下那历史性的一刻。但由于板门店非军事区禁止游客入内，韩国游客只好选择前往位于韩国京畿道南杨州市的“板门店模型”进行体验。他们在“板门店模型”前，试着“还原”两国领导人握手的场景，希望以这样的方式表达对朝鲜半岛和平统一的愿望。</p><p class="art_p"><img  src="https://n.sinaimg.cn/translate/132/w559h373/20180507/sTeP-hacuuvu2516488.jpg" alt=""></p><p class="art_p">游客 钟先生：我很惊讶，这么多人来，感觉很真实，像是自己也参与到了韩朝和平进程中。</p><p class="art_p">游客 柏女士：能和平统一就太好了，跨过那条线就到了朝鲜境内了，韩国人民都盼望着朝鲜半岛能实现和平统一。</p><p class="art_p">这处“板门店模型”是在2000年为拍摄电影而建造的。虽然其形象逼真，但由于缺少真正板门店前的那种严肃氛围，一直很少有游客前来参观。而上月的两国领导人会晤却使这里成为了热门景点儿。</p>', 1525686433, 1, 1),
(207, '知名电视主持人摊上事了！证监会找上门 罚款超1亿', NULL, '<p class="art_p">多年来，电视股评栏目是不少投资者获取证券资讯的一个重要渠道，也诞生了不少知名证券节目主持人，而廖英强便是其中的佼佼者，被粉丝戏称为“讲投资的郭德纲”。</p><p class="art_p">5月6日，证监会公布的行政处罚决定书显示，廖英强利用其知名证券节目主持人的影响力，在其微博、博客上公开评价、推荐股票，在推荐前使用其控制的账户组买入相关股票，并在荐股后的下午或次日集中卖出。证监会决定对廖英强没收违法所得4300万元，并处8600万元罚款，合计1.29亿元。</p><p class="art_p"><strong>8个月操纵39只股票</strong></p><p class="art_p">每经小编（微信号：nbdnews）了解到，证监会指出，2015年3月至2015年11月，廖英强利用其知名证券节目主持人的影响力，发布了含有荐股内容的博客60篇，平均点击次数为110399次，在其微博、博客“午间解盘”栏目视频中公开评价、推荐“佳士科技”等39只股票共46次，在推荐前使用其控制的账户组买入相关股票，并在公开荐股下午开盘后或次日集中卖出相关股票，违法所得共计43104773.84元，并处86209547.68元罚款。</p><p class="art_p">例如2015年3月20日、2015年5月14日，廖英强分别推荐了兴发集团、清新环境，下午开盘，散户就冲进去将股价拉升了。而提前布好局的廖英强顺势将股票卖出，获利，完成一次割韭菜。</p><p class="art_p">利用影响力，廖英强的市场操纵愈发得心应手，39只个股的操纵失手次数仅3次。包括了2015年7月27日买入的“桐昆股份”、2015年10月27日买入的“兴业银行”、2015年11月26日买入的“太阳电缆”，亏损金额分别为879249.50元、52744.18元和3574.63元，合计亏损93.45万元。</p><p class="art_p">当然，这一系列操纵行为并非用一个账户完成。证监会调查显示，2015年3月至2015年11月，廖英强控制其本人及“张某萍”等13个证券账户进行的操纵。上述账户不少和廖英强有亲戚或工作关系，比如柴某英是廖英强的表姐，柴某玉是柴某英的妹妹；柴某美是廖英强的母亲；廖某杰是廖英强的哥哥；金某是廖某杰妻子的姐姐，他们账户的资金来源大多数来自于廖英强及其妻子郭某琳。</p><p class="art_p">据券商中国，在证监会行政处罚落地之前，2015年9月就有小散举报廖英强非法推荐股票，理由是“忽悠”老股民购买炒股书籍和视频，利用股民非法推荐股票。</p><p class="art_p">百度“廖英强”贴吧，成了不少小散的“泄愤地”，这个累积发帖3万、2383人关注的贴吧大多数都在说廖英强是骗子。</p><p class="art_p">每经小编（微信号：nbdnews）了解到，2012年2月至2016年4月，廖英强在上海某电视财经频道《谈股论金》节目担任嘉宾主持人。《谈股论金》作为品牌节目，始终保持较高人气，收视率表现稳定。</p><p class="art_p">廖英强还从2014年9月开始担任《谈股论金之英强开讲》的嘉宾主持。上述两档节目在上海地区的收视率均高于同时段所有频道财经类节目在上海地区的平均收视率。</p><p class="art_p">同时，廖英强作为大股东的上海股轩文化创意有限公司（以下简称股轩文化）通过“爱股轩”网站的APP，以解盘视频“金钱风暴”“股动钱潮”等节目以及新浪微博、博客、土豆网等互联网平台进行推广和宣传。2015年，股轩文化在上海举办多场培训讲座，讲座视频放在“爱股轩”网站上，以提高股轩文化知名度。随着股轩文化的推广活动，廖英强也积累了一定粉丝，其博客点击率很高。</p><p class="art_p">廖英强通过在电视台主持高收视率的证券类品牌节目积累名气和受众，再辅以互联网各类平台宣传和推广，开办培训讲座等形式，具有了相当的知名度和影响力。</p><p class="art_p">这位1970年1月出生的天津汉子，在上海滩有了自己的小圈子。</p><p class="art_p">公开报道显示，交易日的每天上午，廖英强都会坐在办公桌的电脑前，分析当天的走势。而在两小时的股评节目《谈股论金》吸引了众多的观众，廖英强形成了自己独特的风趣幽默风格，枯燥单调的数字曲线和技术讲解在他口中变得妙趣横生、引人入胜，被粉丝戏称为“讲投资的郭德纲”。</p><p class="art_p">据统计，廖英强在各类的社交媒体、音视频平台上的培训讲座吸引了大量的粉丝，节目上线后的8个月的时间里，他的收听量达到了3200万次。从2011年开通博客至今，已经拥有1.3亿的点击量。</p><p class="art_p">多名“黑嘴”抢帽子交易被罚</p><p class="art_p">据火山财富，这已经不是第一起证券从业人员提前买股，再利用电视、微博等方式公开评价其买入个股，“忽悠”散户“抬轿子”。</p><p class="art_p">2016年6月，证监会就通报了针对国开证券经纪人朱炜明的行政处罚案。</p><p class="art_p">证监会官网披露的细节显示，朱炜明父朱某荣、其母孙某英和祖母张某英都于朱炜明所在营业部开立上海股东账户和深圳股东账户，而朱炜明实际控制朱氏账户组。朱炜明在作为证券从业人员期间，控制朱氏账户组买卖海螺型材、神剑股份、襄阳轴承等共计132只股票，盈利452.67万元。</p><p class="art_p">令人诧异的是，朱炜明操纵手法与廖英强如出一辙，其在“谈股论金”栏目中通过明示股票名称或描述股票特征的方法公开“荐股”。</p><p class="art_p">在2013年3月1日至2014年8月25日期间，朱炜明在“谈股论金”栏目中通过明示股票名称或描述股票特征的方法，公开评价、推介利源精制、万马股份、博晖创新、三泰电子等10只个股，并在公开荐股前先行建仓买入，公开荐股后的3个交易日内卖出，获利43.93万元。</p><p class="art_p">一不愿具名私募经理告诉火山财富：</p><p class="art_p">“其实这么多年，电视股评栏目一直存在于电视台，也是不少投资者获取证券资讯的一个重要渠道，如此公开的一个栏目，主持人自然很多，栏目很难进行监管主持人的非法炒股荐股记录，所以，也只能呼吁证券从业人员在录制节目或者是通过其他形式发布投资咨询，恪守职业道德，不要被利欲熏心。”</p><p class="art_p">据新华社报道，近年来“抢帽子”操纵案件呈现三大特点：</p><p class="art_p">首先，涉案者善于借助主流媒体影响市场。朱炜明案中，“谈股论金”是某知名财经频道证券类谈话节目，收视率在证券节目中长期排名靠前，受众广、影响大。</p><p class="art_p">其次，荐股渠道向各类新兴媒体渗透。借助博客、微博等社交工具精准、高效传播的优势，一些所谓“大V”以节目嘉宾身份亮相，利用其高人气大肆推荐股票，误导投资者的同时谋取私利。</p><p class="art_p">再次，反向交易隐蔽性强。例如，为逃避查处，朱炜明借用他人证券账户先建仓布局，后配合其荐股行为进行反向交易。</p><p class="art_p">值得一提的是，2017年2月10日，中国证监会主席刘士余在全国证券期货工作监管会议上点名批评“黑嘴”称，有些券商的分析师“语不惊人死不休”，预测指数能到个位，“全球没有券商经济学家这么预测的，黑嘴又多年没打了”。</p>', 1525685370, 0, 1),
(208, '他在汶川地震中救出10余人：含被埋近125小时女孩', NULL, '<p class="art_p">澎湃新闻记者 王万春 陈绪厚 王鑫</p><p class="art_p">[编者按]</p><p class="art_p">汶川，北川，绵竹，都江堰。。。。。。</p><p class="art_p">穿越北纬31°的这些城镇与村庄，2008年5月12日，承受了最为沉重的关注与哀伤。</p><p class="art_p">十年生死两茫茫。2018年3月下旬起，澎湃新闻沿着北纬31°那些触动心弦的地名行走，寻访一个个家庭的故事。</p><p class="art_p">逝者已矣，来者可追。度尽劫波再回望，为不负逝者，更为不负生者。</p><p class="art_p">4月的汶川映秀镇，午后的太阳晒在身上感觉很暖和。68岁的杨云青泡上了一杯清茶，坐在家门口椅子上跟村民聊天。</p><p class="art_p">他身后是他家开的“震中饭店”。饭店四周挂满了汶川地震时杨云青抢险救援、记者采访他的照片以及他和部队官兵、央视主持人的合影，但这些似乎没能为饭店带来更多的生意，因生意冷清，饭桌和椅子都已收起。</p><p class="art_p">“没有雇人了，我们自己开，没有生意，雇人工资都开不起。”杨云青说，当天他儿子去都江堰，饭店也就没有开张。</p><p class="art_p">杨云青是映秀镇中滩堡村人，地震时是小河边组组长。地震中，他们家10余口人遇难，包括他的妻子；他会开吊车，在废墟上连续奋战九天九夜救出10余个人，包括映秀小学最后一个救出的小女孩尚婷和在废墟中被埋近125小时后创造生命奇迹的蒋雨航。</p><p class="art_p">“夫妻七十老来伴。”震后，杨云青再婚，他会跟现在的老伴儿一起到公墓去给遇难的妻子烧纸、上香。他告诉澎湃新闻（www.thepaper.cn），蒋雨航也会在逢年过节发信息问候他。他一边说，一边翻出了发给他的祝福、问候短信给澎湃新闻记者看，短信中蒋雨航称呼他为“干爹”，“蒋雨航现在在上海当兵，考了军校后已经提干。”</p><p class="art_p"><strong>地震一家10余口遇难</strong></p><p class="art_p">一天没有生意，但杨云青并不着急。</p><p class="art_p">经历了地震，他觉得人只要不干亏心事，对得起自己的良心，安安稳稳地活着比什么都好，“地震让我从富人变成了穷人，现在来看，钱啊这些都是身外之物，活好当下最重要。”</p><p class="art_p">从1986年就开始开吊车的杨云青，一直从事着与工程相关的工作，采砂、修路、开吊机。他说，地震前，他拥有两个沙场、两台吊机、两套房子、一个农家乐、一个小卖部，“这些都是我的固定资产，为了让儿女们过得好，我拼命挣钱攒下来的。”</p><p class="art_p">他家中排行老三，有一儿两女。如果算上哥哥、姐姐和弟弟一大家子近40人。汶川地震前夜（5月11日），杨云青做东请客，一大家子36口人在他家吃饭，“4月14日我买了辆轿车，他们恭喜后我说做顿饭大家聚餐。”</p><p class="art_p">地震当天，中午过后杨云青去镇上的保险公司办事情。路上，他遇到了自己的妻子袁秀芳，但两人并没有说话闲聊，他们不知道这是夫妻见的最后一面。</p><p class="art_p">刚走进保险公司准备办理时一阵摇晃，他夺门而出，站不稳摔倒在地，趴在地上的他看到了一副末日场景，“后来我看电影《2012》，当时的场景跟电影中的一模一样。”</p><p class="art_p">好在保险公司六层的办公楼刚建一年，楼体垮塌到二楼后再没有崩塌，杨云青躲过一劫。他站起来往街上宽敞的地方跑，但左侧一排3层的房子，右侧一排6层的，“不敢乱跑，就一直看着楼顶，灰尘大，睁不开眼睛。”</p><p class="art_p">当天，他儿子跟大女儿去都江堰为新买的轿车装低音炮音响。返回时，在彩虹桥遇到了一起交通事故，堵车耽误了1个多小时。地震发生时，重新上路的轿车乱跑，儿子以为爆胎了，但前面的车子被滚石砸遍了，车子撞在路边停靠后，滚落的石块砸遍了引擎盖。傍晚时分，被困在路上的儿子和大女儿，通过收音机才得知汶川地震了，“如果不是那个交通事故堵一会儿，他们车子要滚到岷江里，百花大桥都塌成那样了。”</p><p class="art_p">5月13日，徒步回到映秀的儿子，也参与到了救援当中。当天，儿子在映秀派出所坝子上救援，帮忙给派出所寻找枪支时发现了被压在废墟中的母亲。</p><p class="art_p">长约2米、宽约80公分、厚约40公分，足有一吨重的水泥板压住了袁秀芳。“那天街上碰到，我俩没说话，如果说了话稍微站一会儿，可能会躲过一劫，”杨云青感叹。</p><p class="art_p">与此同时，遇难的还有杨云青大哥的妻子、两个儿媳、三个孙子；姐姐的两个孙子；弟弟的一个孙子。一大家子共10人失去性命，他说：“不包括亲家母这些亲戚，亲戚也有遇难的。”</p><p class="art_p">　<strong>　救援创造生命奇迹</strong></p><p class="art_p">5月13日，杨云青跟弟弟跑去映秀电厂借吊车，他们跪在了电厂负责人的面前，对方扶起他们并答应。</p><p class="art_p">杨云青父子二人都会开吊车。路抢通之后，5月14日9时许，杨氏父子开吊机开始抢险救援，直至24日凌晨3时许，他们在映秀小学和幼儿园、漩口中学及派出所轮番开吊机救援。杨云青的大哥帮他们四处找油，“后来直接到部队油库里去接油。”一家人联动，投入到救灾当中。</p><p class="art_p">14日一早，一名到映秀小学的家长发现了在废墟中的学生张春梅。杨云青得知消息后，迅速加入了消防官兵的救援行动中，杨云青主要操作吊机，他吊起坍塌的水泥板。张春梅当时被压在梁底下，腿部无法动弹，但可以开口说话。杨云青让侄子撬开钢筋，自己操作吊机，吊起了坍塌的梁柱，消防官兵救出了张春梅。</p><p class="art_p">5月15日，杨云青到渔子溪半山腰的遇难者公墓，看到妻子袁秀芳的尸袋，脚朝下、头朝上，他把她摆正后磕了3个头，继续投入救援行动中。</p><p class="art_p">当天夜里，在映秀小学救援的杨云青与上海的消防官兵一起烤火，听到废墟里有喊声。但夜色太沉，无法及时救援，直至16日一早，杨云青开着吊机，跟上海消防官兵、山东消防官兵一起展开救援，最终从废墟中救出映秀小学四年级学生尚婷，“她是映秀小学最后1个幸存者，在废墟里被埋105个小时，视神经受伤。”</p><p class="art_p">5月17日16时许，上海消防局的领导喊他操作吊机，“你儿子技术没你好，还是需要你亲自上。”在都汶高速公司的宿舍楼废墟旁，有女人守在旁边哭泣，杨云青上前安慰：“你不要着急，不要担心，我们全力以赴救人。”当天，在上海消防官兵及杨云青的救援下，他们用菜刀砍断了钢丝床棉线、吊起了水泥板，于当天17时12分，救出了废墟中被困125个小时、创造生命奇迹的贵州凯里男孩蒋雨航，“抬出来先蒙住了眼睛，那么长时间没见光，怕刺激受不了”。当时，守在一旁哭泣的女子就是蒋雨航的母亲龙金玉。</p><p class="art_p">“映秀小学挖出200多具遗体，学前班就有54个遇难，还有20人是教师。”时隔十年，杨云青对这些数字仍记忆犹新。</p><p class="art_p">“我们父子在废墟上救了9天9夜，自己家里10余人遇难，我们也救了10余人。”杨云青说，2008年6月29日，时任中央政治局常委、国家副主席的习近平在映秀考察抗争救灾工作时握着杨云青的手说，“谢谢老杨，我代表党中央、全国人民感谢你，你是我们的骄傲。”</p><p class="art_p">杨云青的这些事迹，通过媒体、电视画面，也被远在马尔康的女子刘明玉看在眼里。</p><p class="art_p">　<strong>　再婚后幸福美满</strong></p><p class="art_p">2008年11月份，杨云青跟弟弟一起喝茶时，他弟弟说：“我们给二哥找个老伴。”</p><p class="art_p">弟弟告诉他，有个马尔康的女子，经人介绍留了电话，提议杨云青去见见。弟弟所说的女子，就是刘明玉。</p><p class="art_p">当年11月20日，杨云青到都江堰修吊机。得知刘明玉在都江堰后，他试图打电话联系约着吃饭，但一连打了几十个电话，对方一直无法接通。</p><p class="art_p">“我以为她不愿意，就准备放弃回映秀了。”杨云青说，临走时，刘明玉回电话了，原来是手机没电关机，双方见面，除了他们二人，还有杨云青的亲属、刘明玉的姐姐和姐夫，亲戚们都觉得他们两人合适。</p><p class="art_p">刘明玉则觉得，她知道杨云青的事迹后，认为这样的人可以作为一个依靠。她原本在马尔康政府机关上班。2008年年底退休后，她和杨云青结婚。这在映秀传为一段佳话。</p><p class="art_p">“她一个月退休工资4000多元，我一个月领养老保险1000多元，她也不嫌弃我，还担心我够不够花。”杨云青说，此前他把精力都花在挣钱、存钱上了，结果灾难之后他才发现，那些都是虚的，“钱财是身外之物。”</p><p class="art_p">在抗震救灾捐款活动中，杨云青从袁秀芳遇难的5000元抚恤金里拿出1000元捐了出去。</p><p class="art_p">但最值得杨云青念叨的是，当初他救出的蒋雨航还认他为义父，逢年过节会发消息问候他；还有尚婷、张春梅，这些名字现在他耳熟能详，经常被人们提起。</p><p class="art_p">杨云青说，蒋雨航被救出后在医院救治时，他的母亲龙金玉说：“杨伯伯就是你的父亲，你就是他的儿子。”蒋雨航告诉澎湃新闻，是杨云青给了他第二次生命，“当然要认成干爹。”</p><p class="art_p">2008年12月，蒋雨航在贵州凯里应征入伍，他的愿望是从军像当初救他的人一样，去拯救别人；2012年，他考上了昆明消防指挥学校。10年过去了，蒋雨航现在已经是上海消防部队的副连级干部，经常带队出现在一些灾难现场。</p><p class="art_p">汶川地震时读小学四年级的尚婷，目前在成都温江读大学，是健康服务与管理专业大一学生。尚婷告诉澎湃新闻，震后10年间，她去了上海五六次，除了世博会期间由学校组织外，其余都是去上海接受治疗，目前已恢复得很好。</p><p class="art_p">张春梅高位截肢后，成了一名残疾运动员，“现在是游泳冠军”，杨云青说。</p><p class="art_p">10年过去了，当初遇难的杨家人，或再婚、或再生育，都重组家庭重生。杨云青现在有糖尿病、高血压，妻子刘明玉把他照顾得很好。</p><p class="art_p">每年袁秀芳的生日、清明、七月半、大年初三时，刘明玉就跟杨云青一道带着子孙们去公墓祭拜袁秀芳。“你安心地去吧，我现在替你照顾二哥。”刘明玉在袁秀芳坟前的这句话，让杨云青铭刻于心。</p>', 1525682760, 0, 1),
(209, '北青报：那么老派的诺贝尔文学奖 竟因这丑闻停更', NULL, '<p class="art_p">每年诺贝尔文学奖颁奖时，就会有人“心疼”村上春树。今年份的“心疼”从年底挪到了现在。挪威当地时间5月4日，诺贝尔文学奖的评奖机构瑞典学院宣布，鉴于“当前瑞典学院的危机”，公信力下降，2018年诺贝尔文学奖将推迟颁发，2019年同时颁发今明两年的文学奖。要想得奖，今年的村上君在跑步时可能还得多想些什么。</p><p class="art_p">诺贝尔文学奖自1901年设立以来七次停颁，大多发生在两次世界大战期间，另有七次因缺乏合适的候选人而延迟一年颁奖。今年是诺贝尔文学奖历史上，第一次在候选人充足的情况下，非因不可抗力出现暂停和延迟。</p><p class="art_p">和历史上这十四次事件有本质不同的是，这一次“危机”源自瑞典学院历史上最大的丑闻。</p><p class="art_p">2017年11月，18名女性向瑞典当地媒体《今日新闻》（Dagens Nyheter）指控法国摄影师让-克劳德·阿尔诺多次实施性侵，但并未向警方报案。瑞典媒体后续又报道称，阿尔诺曾七次向博彩公司提前泄露诺奖获奖者名单。阿尔诺对上述指控皆予以否认。</p><p class="art_p">这不是阿尔诺第一次受到性侵指控。1996年，一名青年布艺家安娜-卡琳·布伦曾写信给当时瑞典学院常任秘书斯图尔·阿廉，指控阿尔诺的性侵行为，但阿廉并未重视，信件直至这次丑闻大规模曝光才得以重见天日。</p><p class="art_p">阿尔诺的妻子卡塔琳娜·佛洛思登松是瑞典学院的成员，夫妻二人在斯德哥尔摩共同经营了一家由瑞典学院提供资助的的文艺俱乐部，名为“论坛”。</p><p class="art_p">在“MeToo”运动全球化扩张的进程中，瑞典媒体对阿尔诺的性丑闻穷追不舍。舆论压力下，三名瑞典学院成员要求学院将佛洛思登松除名，在学院拒绝这一请求后，三人提出辞职。</p><p class="art_p">实际上，自1786年瑞典国王古斯塔夫三世创设学院以来，成员一直是终身制的。1989年，艾哈迈德·萨尔曼·鲁西迪因作品《撒旦诗篇》而被伊朗精神领袖赛义德·鲁霍拉·霍梅尼下达追杀令。瑞典学院拒绝谴责这一行为后，三名成员提出辞职申请，被学院拒绝。2005年，瑞典学院成员克努特·阿伦因不赞同2004年诺贝尔文学奖颁给奥地利作家艾尔弗雷德·耶利内克提出辞职，瑞典学院对其置之不理——克努特·阿伦从1996年起便不再是瑞典学院的活跃成员，2012年辞世后，他的位置也无人接替。</p><p class="art_p">瑞典学院十八世纪的终身制体制已经无法应对当下现代社会的问题了。由于不满瑞典学院处理方式，共有五名成员宣布辞职。另有两人因其他原因退出。迫于压力，佛洛思登松及时任常任秘书的萨拉·达纽斯也于今年4月12日宣布辞职。</p><p class="art_p">算上一直无人接替的克努特·阿伦的空位在内，瑞典学院负责诺贝尔文学奖评审的十八名成员已经有八位无法履职。现任瑞典国王兼诺贝尔文学奖世袭资助人卡尔十六世·古斯塔夫宣布将对瑞典学院进行改革，但就现状而言，今年的诺贝尔奖文学奖只能被耽误了。</p><p class="art_p">值得一提的是，此番瑞典学院性丑闻闹得沸沸扬扬，除了受到“MeToo” 运动的影响，也受到了国内法律修正案的影响。</p><p class="art_p">瑞典首相斯蒂凡·洛夫文2014年执政以来一直在准备对瑞典做出历史性的改革，其中一项就是修改对性侵犯的界定。就在对阿尔诺的指控发出后的一个月，瑞典议会将修改性侵定义提上会议日程。修改后，强奸罪的构成要件，不再必须包含对受害人使用暴力、进行威胁、或利用受害人的弱势地位，如果性行为没有得到双方口头或以实际行动表现出的同意意思表示，即有可能构成强奸。这项法案已经于今年3月颁布，并将于今年7月1日起正式施行。</p><p class="art_p">2012年6月起受到两名女性的强奸指控后，维基解密创始人朱利安·阿桑奇放弃保释，潜逃至厄瓜多尔驻英国大使馆寻求避难，以避免被引渡至瑞典。随着瑞典修法获得通过，阿桑奇案再次引发关注，也从一个侧面证明新法对于仍在诉讼时效内的性侵案件具有追溯力。但新法并未减轻被害人的举证责任，所以受到十八人指控的阿尔诺能否进入司法视野，尚未可知。</p><p class="art_p">瑞典是全球性别差距最小的国家之一，根据世界经济论坛《2017年全球性别差距报告》瑞典位列全球性别平等国家的前五位。在瑞典官方关于国家性别平等状况的介绍中称，“性别平等是瑞典社会的一大基石，它意味着男女两性在生活的各个方面都拥有同等的机会、权利和义务，每个人均有权工作并养活他们自己，兼顾事业和他们的家庭生活，而且无需担心遭受虐待或暴力。”</p><p class="art_p">官方说明中“无需担心遭受虐待或暴力”并不是政治谎言，但想要真正实现它仍然任重道远。诺贝尔文学奖风波的直接原因在于机制僵化，但根源在于成员伴侣的性丑闻。由此可见，即便在一个法律积极改革、性别平等状况全球名列前茅的国家，女性仍然无法摆脱权力结构下被支配的不利地位。想要从根本上涤荡社会积弊，仅靠立法和法律制裁的事后惩罚是远远不够的。法律制裁固然会起到一定威慑作用，但如果不能进一步扭转性别不平等的社会文化，丑闻仍然会层出不穷，并引发连锁反应，殃及无辜。诸君，试想一下村上春树现在那“平地风波听受惯，频年哀乐事心违”的心情吧！</p><p class="art_p">（文/戴桃疆）</p><p class="art_p">点击进入专题：<br/> 受性丑闻风波影响 2018年将不颁发诺贝尔文学奖</p>', 1525682485, 1, 1),
(235, 'Koko', NULL, 'koko', 1526222362, NULL, 14);
